Book.create!([
  {source_id: 15260, pre_title: "", title: "Ég er sjálfsörugg, hugrökk og snjöll", post_title: "", slug: "eg-er-sjalfsorugg-hugrokk-og-snjoll-15260", description: "Ég er sjálfsörugg, hugrökk og snjöll er skemmtileg litabók. Henni er ætlað að byggja upp sjálfstraust, ímyndunarafl og sköpunargleði stelpna. Hvetjandi bók.", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, category_id: 1, book_author_id: nil},
  {source_id: 15261, pre_title: "", title: "Nú er háttatími", post_title: "", slug: "nu-er-hattatimi-15261", description: "Dásamleg bók fyrir litla barnið, sem þér þykir vænt um. Hér er nóg að skoða, nóg að snerta og finna fyrir háttinn.", long_description: "", page_count: 10, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, category_id: 1, book_author_id: nil},
  {source_id: 15262, pre_title: "", title: "Ding! Dong! Komum að leika!", post_title: "", slug: "ding-dong-komum-ad-leika-15262", description: "Hringið dyrabjöllunni á meðan þið lesið og sjáið hverjir koma í rigningar-lautarferð Gurru Grís. Skemmtileg hljóðbók sem er tilvalin fyrir alla aðdáendur Gurru.", long_description: "", page_count: 12, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, category_id: 1, book_author_id: nil},
  {source_id: 15263, pre_title: "", title: "Klukkubókin", post_title: "", slug: "klukkubokin-15263", description: "Fyrstu skrefin við að læra á klukku og byrja í skóla. Á meðan börnin lesa um söguhetjurnar og færa klukkuvísana á réttan stað læra þau um leið á klukku.", long_description: "", page_count: 12, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, category_id: 1, book_author_id: nil},
  {source_id: 15264, pre_title: "", title: "Töfraeinhyrningurinn hennar Gurru gríss", post_title: "", slug: "tofraeinhyrningurinn-hennar-gurru-griss-15264", description: "Kata kind ætlar að gista hjá Gurru grís. Þeim finnst gaman að leika sér með Glitfótu Gæðingi en mest dreymir þær um að leika sér með töfrandi einhyrning. Getur pabbi töfrað eitthvað fram svo að draumur þeirra rætist?", long_description: "", page_count: 30, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, category_id: 1, book_author_id: nil},
  {source_id: 15265, pre_title: "", title: "Sögutaskan mín", post_title: "", slug: "sogutaskan-min-15265", description: "Sögutaskan mín er askja sem inniheldur fjórar þroskandi bækur sem auka skilning yngstu barnanna á ýmsu í lífi þeirra. Með hjálp fjögurra félaga læra börnin hugtök yfir tilfinningar, veðrið, fjölskylduna og staðsetningu.", long_description: "", page_count: 48, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, category_id: 1, book_author_id: nil},
  {source_id: 15331, pre_title: "", title: "Íslensku dýrin", post_title: "Harðspjaldabók - Fallega myndskreytt bók sem fagnar 60 ára afmæli!", slug: "islensku-dyrin-15331", description: "Sígild barnabók, myndskreytt af Halldóri Péturssyni, er nú aftur fáanleg. Þessi vinsæla barnabók hefur komið inn á mörg heimili landsins síðastliðin 60 ár. >>>Bók fyrir yngstu börnin.<<<", long_description: "", page_count: 6, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15332, pre_title: "", title: "ILLVERK", post_title: "", slug: "illverk-15332", description: "Gamalt mál er sett af stað þegar ungur drengur rekst á mannabein í grunni húsarústa á Eskifirði. Við uppgröft finnast líkamsleifar þeirra sem saknað hafði verið í áraraðir ásamt dagbók Sigurveigar húsfreyju að Gáratúni og áttu skrif hennar eftir að varpa ljósi á ýmis svik, leyndardóma og djúp ástarmál.", long_description: "", page_count: 328, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, category_id: 2, book_author_id: nil},
  {source_id: 15268, pre_title: "", title: "Einhyrningar, álfar og hafmeyjur", post_title: "", slug: "einhyrningar-alfar-og-hafmeyjur-15268", description: "Skemmtilegur kassi með bók og 15 púslbitum til að raða á rétta staði með líflegum myndum til að leika með.", long_description: "", page_count: 10, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, category_id: 1, book_author_id: nil},
  {source_id: 15269, pre_title: "", title: "Límmiðafjör STAFIR", post_title: "", slug: "limmidafjor-stafir-15269", description: "Meðan börnin dunda sér við að finna límmiða, svara spurningum og lita myndirnar læra þau um leið stafina og stafrófið. Límmiðafjör er nýjasta bókin í röðinni Litlir lærdómshestar en fyrir eru bækurnar Tölur og Dýr.", long_description: "", page_count: 28, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, category_id: 1, book_author_id: nil},
  {source_id: 15270, pre_title: "", title: "Snertu og finndu", post_title: "", slug: "snertu-og-finndu-15270", description: "Einstaklega falleg bók sem er sérstaklega hönnuð fyrir þig og litla barnið. Hér er nóg að skoða, nóg að snerta og finna. Myndirnar eru litríkar og sérstaklega valdar til þess að þroska skynjun barnsins og veita ánægjulega samverustund.", long_description: "", page_count: 12, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, category_id: 1, book_author_id: nil},
  {source_id: 15271, pre_title: "", title: "Í Hjarta Mínu", post_title: "", slug: "i-hjarta-minu-15271", description: "Þegar Ómar segir systkinum sínum frá þungbærri áætlun sinni, skerast þau í leikinn og reyna að ýta honum í átt að betri stað.\r\nHinn vonlausi Ómar tekur á sig lífið og upplifir hluti sem hann gat aðeins dreymt um frá unga aldri. En þunglyndi togar hann niður á botninn.\r\nNær hann að berjast á móti erfiðum hindrunum sem hann sjálfur hefur orsakað?\r\nEða gefst hann upp sigraður á líkama og sál, því lífið var honum ofviða?", long_description: "", page_count: 238, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, category_id: 2, book_author_id: nil},
  {source_id: 15272, pre_title: "", title: "Langafi minn Supermann", post_title: "", slug: "langafi-minn-supermann-15272", description: "Sylvía segir frá atburðaríku sumri þegar hún fékk að fara ein til Ólafsfjarðar og gista í Brekkugötu 9 hjá langömmu sinni og langafa.\r\nSylvía sér langafa sinn í öðru ljósi og góðvilji hans lætur hana sífellt spyrja sig, er langafi minn Supermann?", long_description: "", page_count: 81, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, category_id: 3, book_author_id: nil},
  {source_id: 15273, pre_title: "", title: "Náðu árangri - í námi og lífi", post_title: "", slug: "nadu-arangri-i-nami-og-lifi-15273", description: ">>>Náðu árangri - í námi og lífi<<< er námsráða- og hvatningarbók með andlegu ívafi. Guðjón Ari fjallar um þá þætti sem hafa hjálpað honum að ná árangri í námi sem og á öðrum vettvangi eins og körfubolta. Markmiðasetning, sjálfstraust, hugarfar, þrautseigja, álit annarra og bein námstækni eru á meðal þessara þátta.\r\nGuðjón útskrifaðist úr Verzlunarskóla Íslands 2019 sem dúx skólans og hefur einnig unnið til verðlauna í körfuknattleik og spilað fyrir meistaraflokk Fjölnis.", long_description: "", page_count: 187, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, category_id: 4, book_author_id: nil},
  {source_id: 15274, pre_title: "", title: "helgustur", post_title: "", slug: "helgustur-15274", description: ">>>helgustur<<< fjallar um ævi manns sem lendir á glapstigum sem barn og er sendur í sveit á nokkra bæi þar sem margvíslegt ofbeldi er allsráðandi. Kynferðisofbeldið brenglar hugmyndir hans um tengsl kynjanna og lífið almennt eru allbrenglaðar.", long_description: "", page_count: 104, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, category_id: 5, book_author_id: nil},
  {source_id: 15275, pre_title: "", title: "Jólasveinar nútímans", post_title: "", slug: "jolasveinar-nutimans-15275", description: "Erfitt er að kveðja gamlar venjur, sérstaklega fyrir ellismelli eins og jólasveinana. Sumir sveinar ná ekki takt við tímann en aðrir eru að venjast nýrri tíð. Í gamla daga voru þeir algjörir prakkarar og villingar, en í dag þurfa þeir að haga sér. Sumir halda sér við gamla siði, á meðan aðrir gangast undir reglum nútímans. Ketkrókur er orðin vegan, Giljagaur vill ennþá mjólk beint frá spenanum, Skyrgámur er með mjólkuróþol, Grýla er Instagram-stjarna og Leppalúði er heimavinnandi húsfaðir.", long_description: "", page_count: 60, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, category_id: 1, book_author_id: nil},
  {source_id: 15276, pre_title: "", title: "Spurningabókin 2020", post_title: "Hvaða dýr er svarta ekkjan?", slug: "spurningabokin-2020-15276", description: "Hvað eru margir reitir á bingóspjaldi? Hvert er íslenska heitið yfir Halloween? Hvaða náttúrufyrirbæri er stundum kallað \"bláa gullið\"? Hvort fæðast kanínur blindar eða sjáandi? Hvaða tómatsósa er í uppáhaldi hjá Ed Sheeran? Þetta og margfalt fleiri stórskemmtilegar spurningar í þessari frábæru bók sem ætti að vera til á hverju heimili.", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, category_id: 6, book_author_id: nil},
  {source_id: 15277, pre_title: "", title: "Fótboltaspurningar 2020", post_title: "", slug: "fotboltaspurningar-2020-15277", description: "Með hvaða liði lék Steven Lennon fyrst eftir að hann kom til Íslands? Stuðningsmannaklúbbur hvaða liðs kallar sig Silfurskeiðina? Hvernig eru sokkarnir á litinn í aðalbúningi Frakka? Með hvaða liði lék Bruno Fernandes áður en hann gekk til liðs við Manchester United? Hjá hvaða liði hóf James Milner feril sinn? Hverrar þjóðar er Lautaro Martínez? En Kim Little? Frá hvaða landi eru liðin 07 Vestur og Skála? En Haka og Honka? Þetta og margfalt fleiri spurningar í þessari bráðskemmtilegu bók sem allir knattspyrnuunnendur ættu að spreyta sig á.", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, category_id: 7, book_author_id: nil},
  {source_id: 15278, pre_title: "", title: "140 vísnagátur", post_title: "", slug: "140-visnagatur-15278", description: "Hér eru á ferðinni 140 vísnagátur, sumar auðveldar, en aðrar strembnari. Dæmi um eina:\r\n\r\nEkki lengra augað sér,\r\ner á mörgum diski.\r\nBrún á jökli einnig er\r\nendilöng á fiski.\r\n\r\nJá, við hvað er átt? Þessi bók er bráðskemmtileg dægrastytting og auðvitað hin besta heilaleikfimi. Höfundurinn hefur áður sent frá sér tvær bækur af sama meiði og hafa þær notið mikilla vinsælda.", long_description: "", page_count: 70, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, category_id: 5, book_author_id: nil},
  {source_id: 15279, pre_title: "", title: "Pétrisk-íslensk orðabók með alfræðiívafi", post_title: "", slug: "petrisk-islensk-ordabok-med-alfraediivafi-15279", description: "Séra Pétur Þorsteinsson hefur í rúma þrjá áratugi fundið skrýtin og skemmtileg orð til að setja í stað hinna hversdagslegu og litlausu. Hvað skyldu þessi t.d. merkja: Bjórbarn, Brottfararbox, herming, strokufangi, töfratvenna og úreltur? Svörin færðu auðvitað í þessari bók sem umfram allt er bráðskemmtileg.", long_description: "", page_count: 108, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, category_id: 4, book_author_id: nil},
  {source_id: 15280, pre_title: "", title: "Gljúfrabúar og giljadísir", post_title: "Eyfirskir fossar", slug: "gljufrabuar-og-giljadisir-15280", description: "Um árabil hefur áhugaljósmyndarinn Svavar Alfreð Jónsson heimsótt fossa í heimahéraði sínu, Eyjafirði, og tekið myndir af mörgum þeirra. Útkoman er glæsileg og minnir okkur á þau ómetanlegu lífsgæði sem felast í því að eiga stutt að fara út í óspjallaða og ægifagra náttúruna. Fegurðin er aðgengilegri en okkur grunar eins og myndefnið hér vitnar um.", long_description: "", page_count: 104, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, category_id: 8, book_author_id: nil},
  {source_id: 15281, pre_title: "", title: "Vestmannaeyjar", post_title: "Af fólki og fuglum og ýmsu fleiru", slug: "vestmannaeyjar-15281", description: "Hér fjallar Sigurgeir um æskuslóðir sínar í byggðinni fyrir ofan hraun í Vestmannaeyjum og fólkið sem þar bjó, s.s. kynni sín af sérstæðum persónum eins og séra Halldóri Kolbeins, Nýju í Suðurgarði og Munda í Draumbæ. Eiinnig fjallar hann um  eftirminnilega félaga á lífsleiðnni, t.d. Bjarnhéðin Elíasson, Sævar í Gröf, ÁSa í Bæ og Guðmund kantor. Óvenjulegt brúðkaupsferðalag þeirra hjóna, Sigurgeirs og Katrínar, ber á góma og jólahald á Kleppi, þar sem hann starfaði á námsárum sínum. Er þá fátt nefnt, en hvarvetna fær leiftrandi frásagnargáfa höfundarins sín notið og víða glampar á kímni höfundarins", long_description: "", page_count: 250, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, category_id: 4, book_author_id: nil},
  {source_id: 15282, pre_title: "", title: "Siddi gull", post_title: "Æviminningar Sigmars Ó. Maríussonar gullsmiðs", slug: "siddi-gull-15282", description: "Hér segir Þistilfirðingurinn Sigmar Ó. Maríusson gullsmiður – Siddi gull – frá viðburðaríkri ævi sinni. Ríflega tvítugur að aldri missti hann báða fæturna í bílslysi á Heiðarfjalli á Langanesi og hafði þá ýmislegt dunið á honum áður og átti enn eftir að gera. En ... hann hefur aldrei gefist upp og ávallt horft á björtu hliðarnar, sama hversu útlitið væri í rauninni dökkt. Þessu eru öllu gerð skil í frásögn sem spannar allt frá mesta draugagangi á Íslandi, Hvammsundrunum svokölluðu, til hins ofdekraða Samma. Þessi bók lætur engann ósnortinn.", long_description: "", page_count: 220, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, category_id: 9, book_author_id: nil},
  {source_id: 15283, pre_title: "", title: "Látra-Björg", post_title: "", slug: "latra-bjorg-15283", description: "Látra–Björg var kraftaskáld, sjómaður, fökkukona — goðsögn í lifanda líf og ráðgáta eftir að hún lést á vergangi í Móðuharðindunum. Samtíðarmenn hennar óttuðust hana. Með kvæðum sínum var hún sögð geta futt til fjöll, laðað að sér fsk og deytt menn eða fært þeim gæfu.\r\nÍ þessari bók eru saman komin öll helstu kvæði Látra–Bjargar og um leið skýringar Helga Jónssonar frá Þverá í Dalsmynni\r\nog samantekt hans á æviferli hennar.Í sérstökum viðauka er svo að fnna Látrabréfð, eina varðveitta prósatextinn eftir Látra–Björgu, sagður hafa fundist á skemmuvegg á Látrum árið 1740.", long_description: "", page_count: 128, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, category_id: 5, book_author_id: nil},
  {source_id: 15284, pre_title: "", title: "Fimmaurabrandararnir 2", post_title: "", slug: "fimmaurabrandararnir-2-15284", description: "Getur feitt fólk fylgst grannt með fréttum? * Laddi er — án gríns — ekkert fyndinn.* Ég keypti klósettbursta fyrir viku síðan ...\r\nen er nú búinn að skipta aftur yfir í klósettpappír.* Ég var á leið í búðina, svo konan bað mig um að setja tómatsósu á\r\ninnkaupalistann ... og nú get ég ekki lesið neitt á honum!* Hvað kallast lúsmý sem treðst framfyrir?\r\nSkjúsmý! Í hvers konar fötum gengur starfsfólk Sorpu? Í ruslafötum! Þetta og margt fleira brjálæðislega fyndið í þessari geggjuðu bók!", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, category_id: 4, book_author_id: nil},
  {source_id: 15285, pre_title: "", title: "Íslensku fuglarnir og þjóðtrúin", post_title: "", slug: "islensku-fuglarnir-og-thjodtruin-15285", description: "Teknir eru til skoðunar allir íslenskir, reglubundnir varpfuglar, í þjóðtrúnni. Dæmi: Á Horni í Sléttuhreppi boðaði það rigningu ef lómurinn stóð á öðrum fæti og skrækti. Í Heklu átti að vera bústaður hrafna með glóandi klær og nef úr járni.\r\nVegna smæðar, litar og atferlis, eða með öðrum orðum vegna þess hversu jarðbundinn hann er og gjarn á að skjótast í felur í holum og gjótum eða þéttu kjarri, í stað þess að bjarga sér á flugi, var músarrindillinn löngum talinn skyldari mús en fugli. Jónas Jónasson frá Hrafnagili nefnir forn ráð við gulu. Eitt var það að hræra arnarheila út í þemur mörkum af víni og drekka svo. Ríflega 250 myndir prýða bókina.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, category_id: 4, book_author_id: nil},
  {source_id: 15286, pre_title: "", title: "Vegan - Eldhús grænkerans", post_title: "", slug: "vegan-eldhus-graenkerans-15286", description: "Þessi einstaka bók leggur fræðilegan grundvöll að grænkerafæði og kynnir fyrir lesandanum meira en 100 grænkeramatartegundir – þar\r\ná meðal rótargrænmeti, baunir og linsur, hnetur og fræ, kryddjurtir og kryddduft – skýrir næringarinnihaldið og hvernig skuli elda þær og bera fram. Hér kynnist þið öllu því sem vita þarf um þetta mataræði.T.d.: HVAÐA FÆÐUTEGUNDIR GETA KOMIÐ Í STAÐ KJÖTS OG MJÓLKUR, HVERNIG SKIPULEGGJA MÁ SKYNSAMLEGAR MÁLTÍÐIR, AUK GRUNNUPPSKRIFTA OG NÆRINGARTAFLNA.", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, category_id: 10, book_author_id: nil},
  {source_id: 15287, pre_title: "", title: "Hvernig líður þér?", post_title: "", slug: "hvernig-lidur-ther-15287", description: "Sjáðu björninn! Hvernig líður honum? Er tígurinn glaður, leiður eða kannski hræddur? Þú kemst að því með því að snúa stóra hjólinu. Aldrei of snemmt að tala um tilfinningar! Börnin munu læra að þekkja tilfinningar og hafa gaman af hljóðinu sem kemur þegar hjólið snýst. ALDUR BARNS: 1-5 ára.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, category_id: 1, book_author_id: nil},
  {source_id: 15288, pre_title: "", title: "Hvernig líður þér, litli björn?", post_title: "", slug: "hvernig-lidur-ther-litli-bjorn-15288", description: "Sjáðu björninn! Hvernig líður honum?\r\nEr hann glaður, leiður eða kannski hræddur?\r\nÞú kemst að því með því að snúa stóra hjólinu.\r\nBörnin munu læra að þekkja tilfinningar og hafa\r\ngaman af hljóðinu sem kemur þegar hjólið snýst.", long_description: "", page_count: 14, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, category_id: 1, book_author_id: nil},
  {source_id: 15289, pre_title: "", title: "Hvernig líður þér, litli tígur?", post_title: "", slug: "hvernig-lidur-ther-litli-tigur-15289", description: "Sjáðu tígurinn! Hvernig líður honum?\r\nEr hann glaður, reiður eða kannski þreyttur?\r\nÞú kemst að því með því að snúa stóra hjólinu.\r\nBörnin munu læra að þekkja tilfinningar og hafa\r\ngaman af hljóðinu sem kemur þegar hjólið snýst.", long_description: "", page_count: 14, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, category_id: 1, book_author_id: nil},
  {source_id: 15290, pre_title: "Fyrsta hljóðabókin min", title: "Tilfinningar", post_title: "", slug: "tilfinningar-15290", description: "Aldrei of snemmt að tala um tilfinningar. Þessi hljóðabók býður börnum að uppgötva heiminn í gegnum átta mismunandi hljóð, fallegar myndskreytingar og margar áhugaverðar staðreyndir. Börn fara í gegnum mismunandi tilfinningar daglega. Þeim líkar að tengja sína eigin reynslu við það sem gerist í bókinni.\r\nALDUR BARNS: 3-7 ára.", long_description: "", page_count: 20, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, category_id: 1, book_author_id: nil},
  {source_id: 15291, pre_title: "", title: "Dýrin í skóginum - Litað með vatni!", post_title: "Fræðandi töfralitabók og afþreying um dýrin fyrir listræn börn", slug: "dyrin-i-skoginum-litad-med-vatni-15291", description: "Í þessari fræðandi töfralitabók geturðu framkallað litríkar dýramyndir, aðeins með því að strjúka vatni yfir þær. Þegar þær þorna er hægt að lita þær aftur og aftur. >>>Góð bók fyrir börn frá þriggja ára aldri.<<<", long_description: "", page_count: 56, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15292, pre_title: "", title: "Dýrin í skóginum - Litað með vatni!", post_title: "Fræðandi töfralitabók og afþreying um dýrin fyrir listræn börn", slug: "dyrin-i-skoginum-litad-med-vatni-15292", description: "Í þessari fræðandi töfralitabók geturðu framkallað litríkar dýramyndir, aðeins með því að strjúka vatni yfir þær. Þegar þær þorna er hægt að lita þær aftur og aftur. >>>Góð bók fyrir börn frá þriggja ára aldri.<<<", long_description: "", page_count: 56, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 11, book_author_id: nil},
  {source_id: 15293, pre_title: "", title: "Hjól", post_title: ">>>Litrík harðspjaldabók með hreyfanlegum hjólum<<<", slug: "hjol-15293", description: "Kynntu barninu ýmsar gerðir hjóla á skemmtilegan og gagnvirkan hátt þar sem hægt er að snúa hjólunum fram og til baka. >>>Góð bók fyrir yngstu börnin.<<<", long_description: "", page_count: 12, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15294, pre_title: "", title: "Litlir könnuðir - LÍKAMINN", post_title: "Fræðandi harðspjaldagluggabók", slug: "litlir-konnudir-likaminn-15294", description: "Lífleg og fræðandi bók fyrir börn sem hafa áhuga á líkamanum.\r\nEftirfarandi bækur eru í sama bókaflokki: Veröld dýranna, Þegar ég verð stór, Á ferð og flugi, Sveitabærinn, Pöddur og Hafið.\r\n>>>Tilvalin bók fyrir börn frá þriggja ára aldri.<<<", long_description: "", page_count: 16, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15295, pre_title: "", title: "Litlir könnuðir - LÍKAMINN", post_title: "Fræðandi harðspjaldagluggabók", slug: "litlir-konnudir-likaminn-15295", description: "Lífleg og fræðandi bók fyrir börn sem hafa áhuga á líkamanum.\r\nEftirfarandi bækur eru í sama bókaflokki: Veröld dýranna, Þegar ég verð stór, Á ferð og flugi, Sveitabærinn, Pöddur og Hafið.\r\n>>>Tilvalin bók fyrir börn frá þriggja ára aldri.<<<", long_description: "", page_count: 16, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 6, book_author_id: nil},
  {source_id: 15296, pre_title: "", title: "Litlir könnuðir - VERÖLD DÝRANNA", post_title: "Fræðandi harðspjaldagluggabók", slug: "litlir-konnudir-verold-dyranna-15296", description: "Lífleg og fræðandi bók fyrir börn sem hafa áhuga á dýraríkinu.\r\nEftirfarandi bækur eru í sama bókaflokki: Líkaminn, Þegar ég verð stór, Á ferð og flugi, Sveitabærinn, Pöddur og Hafið.\r\n>>>Tilvalin bók fyrir börn frá þriggja ára aldri.<<<", long_description: "", page_count: 16, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15297, pre_title: "", title: "Litlir könnuðir - VERÖLD DÝRANNA", post_title: "Fræðandi harðspjaldagluggabók", slug: "litlir-konnudir-verold-dyranna-15297", description: "Lífleg og fræðandi bók fyrir börn sem hafa áhuga á dýraríkinu.\r\nEftirfarandi bækur eru í sama bókaflokki: Líkaminn, Þegar ég verð stór, Á ferð og flugi, Sveitabærinn, Pöddur og Hafið.\r\n>>>Tilvalin bók fyrir börn frá þriggja ára aldri.<<<", long_description: "", page_count: 16, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 6, book_author_id: nil},
  {source_id: 15298, pre_title: "", title: "Jörðin", post_title: "Fræðandi bók með flipum og útskornum síðum þar sem landafræðin öðlast líf", slug: "jordin-15298", description: "Jörðin býr yfir kraumandi eldstöðvum, jöklum, eyðimörkum, miklum vatnsföllum og dularfullum hellakerfum. Leggjum af stað í ævintýralega ferð! >>>Fræðandi bók fyrir krakka á öllum aldri sem hafa áhuga á jörðinni.<<<", long_description: "", page_count: 29, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15299, pre_title: "", title: "Jörðin", post_title: "Fræðandi bók með flipum og útskornum síðum þar sem landafræðin öðlast líf", slug: "jordin-15299", description: "Jörðin býr yfir kraumandi eldstöðvum, jöklum, eyðimörkum, miklum vatnsföllum og dularfullum hellakerfum. Leggjum af stað í ævintýralega ferð! >>>Fræðandi bók fyrir krakka á öllum aldri sem hafa áhuga á jörðinni.<<<", long_description: "", page_count: 29, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 6, book_author_id: nil},
  {source_id: 15300, pre_title: "", title: "Kát krútt", post_title: "Lífleg og skemmtileg harðspjaldahljóðbók með bráðfyndnum hljóðum og fjörugu lagi!", slug: "kat-krutt-15300", description: "Kynnstu krúttlegum gæludýrum sem setja allt á annan endann í húsinu. Skemmtu þér við að hlusta á bráðfyndin hljóð og fjörugt lag. >>>Góð bók fyrir börn frá eins árs aldri.<<<", long_description: "", page_count: 8, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15301, pre_title: "", title: "Pési og Pippa - Nýi vinurinn", post_title: "Fallega myndskreytt harðspjaldabók sem fjallar um sanna vináttu", slug: "pesi-og-pippa-nyi-vinurinn-15301", description: "Þegar Pési eignast nýjan vin finnst Pippu hún vera höfð útundan. \r\nÆ, æ, aumingja Pippa! \r\nEn óvæntur atburður verður til þess að þau átta sig á því hvað sönn vinátta er dýrmæt. >>>Góð bók fyrir yngstu börnin.<<<", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15302, pre_title: "", title: "Pési og Pippa - Rauða blaðran", post_title: "Fallega myndskreytt harðspjaldabók sem fjallar um sanna vináttu", slug: "pesi-og-pippa-rauda-bladran-15302", description: "Pési er MJÖG hreykinn af rauðu blöðrunni sinni! En þegar hann missir hana óvart og hún springur, verður hann ákaflega sorgmæddur.\r\nTil allrar hamingju fær Pippa snjalla hugmynd og brátt tekur Pési gleði sína á ný! >>>Góð bók fyrir yngstu börnin.<<<", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15303, pre_title: "", title: "Fánar - Límmiðabók", post_title: "Fræðandi límmiðabók með fánum", slug: "fanar-limmidabok-15303", description: "Lærðu að þekkja alla þjóðfána heims með því að líma þá á rétta staði á kortinu. Hér er um fleiri en 190 fána að velja. >>>Góð og fræðandi afþreying fyrir börn á öllum aldri.<<<", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15304, pre_title: "", title: "Fánar - Límmiðabók", post_title: "Fræðandi límmiðabók með fánum", slug: "fanar-limmidabok-15304", description: "Lærðu að þekkja alla þjóðfána heims með því að líma þá á rétta staði á kortinu. Hér er um fleiri en 190 fána að velja. >>>Góð og fræðandi afþreying fyrir börn á öllum aldri.<<<", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 11, book_author_id: nil},
  {source_id: 15305, pre_title: "", title: "100 Drekaskutlur - Brjóttu blað og fljúgðu af stað", post_title: "Einstaklega litrík skutlubók", slug: "100-drekaskutlur-brjottu-blad-og-fljugdu-af-stad-15305", description: "Hér færðu tækifæri til að koma þér upp eigin flugflota og senda á loft ógnvekjandi her eldspúandi drekaflauga til árása á óvininn. Þú getur valið um 100 síður sem auðvelt er að losa úr bókinni og brjóta saman í skutlur.>>>Bók fyrir krakka á öllum aldri.<<<", long_description: "", page_count: 108, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15306, pre_title: "", title: "100 Drekaskutlur - Brjóttu blað og fljúgðu af stað", post_title: ">>>Einstaklega litrík skutlubók<<<", slug: "100-drekaskutlur-brjottu-blad-og-fljugdu-af-stad-15306", description: "Hér færðu tækifæri til að koma þér upp eigin flugflota og senda á loft ógnvekjandi her eldspúandi drekaflauga til árása á óvininn. Þú getur valið um 100 síður sem auðvelt er að losa úr bókinni og brjóta saman í skutlur.>>>Bók fyrir krakka á öllum aldri.<<<", long_description: "", page_count: 108, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 11, book_author_id: nil},
  {source_id: 15307, pre_title: "", title: "Farartæki - Lyftimyndir", post_title: "Harðspjaldabók þar sem eitthvað óvænt leynist undir hverjum flipa", slug: "farartaeki-lyftimyndir-15307", description: "Getur rauði bíllinn komist að því hvaðan drunurnar koma? \r\nMeð spennandi lyftimyndum á hverri opnu kemur þessi fjörlega bók virkilega á óvart! >>>Skemmtileg bók fyrir yngstu börnin.<<<", long_description: "", page_count: 8, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15308, pre_title: "", title: "Sveitin - Lyftimyndir", post_title: "Harðspjaldabók þar sem eitthvað óvænt leynist undir hverjum flipa", slug: "sveitin-lyftimyndir-15308", description: "Fylgstu með Kusu og Lamba í feluleik. Spennandi myndir spretta úr felum undan flipunum á hverri opnu! >>>Skemmtileg bók fyrir yngstu börnin.<<<", long_description: "", page_count: 8, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15309, pre_title: "", title: "Skoppa", post_title: "Harðspjalda-flipabók", slug: "skoppa-15309", description: "Hoppum og skoppum með Skoppu og hjálpum henni að finna leikfélaga sína sem eru í felum undir flipunum á hverri opnu. >>>Litrík bók fyrir eins árs og eldri.<<<", long_description: "", page_count: 8, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15310, pre_title: "", title: "Söguperlur", post_title: "11 fallegar sögur", slug: "soguperlur-15310", description: "Hér eru 11 fallegar sögur þar sem þú kynnist apatvíburunum Millu og Max sem keppa um hvort verði á undan að komast í háttinn og líka Konný kóalabangsa sem virðist aldrei geta ákveðið sig. Margir fleiri koma við sögu. >>>Falleg sögubók fyrir tveggja ára og eldri.<<<", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15311, pre_title: "", title: "Eina sögu enn", post_title: "8 fallega myndskreyttar sögur", slug: "eina-sogu-enn-15311", description: "Átta frábærar sögur sem tilvalið er að lesa með börnunum. \r\nHeillandi persónur og fallegar myndir í sérhverri sögu fylgja börnunum ljúflega inn í skemmtilegan ævintýraheim. >>>Falleg sögubók fyrir börn frá eins árs aldri.<<<", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15312, pre_title: "", title: "Mannslíkaminn", post_title: "Kannaðu stórkostlegasta undur veraldar - mannslíkamann!", slug: "mannslikaminn-15312", description: "Fræðumst um mannslíkamann og starfsemi hans á greinargóðan hátt.\r\nÁ hverri opnu sem er útskorin er helstu líffærum gerð skil í myndum og máli og útskýrt hvert hlutverk þeirra er og hvar þau eru staðsett í líkamanum. >>>Tilvalin bók fyrir krakka á öllum aldri sem hafa áhuga á mannslíkamanum.<<<", long_description: "", page_count: 16, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15313, pre_title: "", title: "Mannslíkaminn", post_title: "Kannaðu stórkostlegasta undur veraldar - mannslíkamann!", slug: "mannslikaminn-15313", description: "Fræðumst um mannslíkamann og starfsemi hans á greinargóðan hátt.\r\nÁ hverri opnu sem er útskorin er helstu líffærum gerð skil í myndum og máli og útskýrt hvert hlutverk þeirra er og hvar þau eru staðsett í líkamanum. >>>Tilvalin bók fyrir krakka á öllum aldri sem hafa áhuga á mannslíkamanum.<<<", long_description: "", page_count: 16, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 6, book_author_id: nil},
  {source_id: 15314, pre_title: "", title: "Ég er eins árs - Skoða, leita og finna", post_title: "Litrík harðspjaldabók", slug: "eg-er-eins-ars-skoda-leita-og-finna-15314", description: "Bók sem eykur orðaforða og börnin langar til að heyra meira og skoða. Sérstaklega fallegar og litríkar myndir. >>>Góð bók fyrir börn frá eins árs aldri.<<<", long_description: "", page_count: 14, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15315, pre_title: "", title: "Ég er tveggja ára - Leita og læra", post_title: "Litrík harðspjaldabók", slug: "eg-er-tveggja-ara-leita-og-laera-15315", description: "Börn hafa ánægju af því að skoða þessa bók með fallegum, skýrum og litríkum myndum. Textinn er fyrir þá fullorðnu til að lesa með barninu. >>>Góð bók fyrir börn frá tveggja ára aldri.<<<", long_description: "", page_count: 14, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15316, pre_title: "", title: "Órigamí - Japönsk pappírslist", post_title: "100 skrautblöð og 5 formgerðir", slug: "origami-japonsk-pappirslist-15316", description: "Í bókinni eru 100 blöð sem auðvelt er að losa og brjóta saman í fíngerð órigamí pappírslíkön. Einfaldar leiðbeiningar með skýringarmyndum sýna hvernig hægt er að breyta þessum litríku og skrautlegu blöðum í falleg listaverk. >>>Góð afþreying fyrir alla.<<<", long_description: "", page_count: 102, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15317, pre_title: "", title: "Órigamí - Japönsk pappírslist", post_title: "100 skrautblöð og 5 formgerðir", slug: "origami-japonsk-pappirslist-15317", description: "Í bókinni eru 100 blöð sem auðvelt er að losa og brjóta saman í fíngerð órigamí pappírslíkön. Einfaldar leiðbeiningar með skýringarmyndum sýna hvernig hægt er að breyta þessum litríku og skrautlegu blöðum í falleg listaverk. >>>Góð afþreying fyrir alla.<<<", long_description: "", page_count: 102, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 11, book_author_id: nil},
  {source_id: 15318, pre_title: "", title: "Anna á Arinhæð", post_title: "", slug: "anna-a-arinhaed-15318", description: "Anna og Gilbert Blythe hafa komið sér notalega fyrir ásamt  börnum sínum fimm í húsi sem þau kalla Arinhæð. Bókin segir frá börnunum og ýmsum uppákomum þeim tengdum en viðfangsefni hinna fullorðnu, til dæmis hjónabandsörðugleikar og erfiðir ættingjar, fá sitt rými. Sjötta bókin af átta um Önnu í Grænuhlíð lítur loksins dagsins ljós. Hún hefur ekki komið út í íslenskri þýðingu áður.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 6, category_id: 7, book_author_id: nil},
  {source_id: 15319, pre_title: "", title: "Bókapakki - Litlu orðabækurnar", post_title: ">>>3 harðspjaldabækur saman í pakka<<<", slug: "bokapakki-litlu-ordabaekurnar-15319", description: "Fallegar ljósmyndir hjálpa yngstu kynslóðinni að þekkja umhverfið og auka orðaforðann. Bækurnar þrjár eru: Fyrstu orðin, Farartæki og Sveitalíf. >>>Bækur fyrir yngstu kynslóðina.<<<", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15320, pre_title: "", title: "Bókapakki - Sígild ævintýri", post_title: ">>>4 sögubækur saman í pakka<<<", slug: "bokapakki-sigild-aevintyri-15320", description: "Hans og Gréta, Þrír grísir, Aladdín og töfralampinn, Lísa í Undralandi, Þyrnirós, Öskubuska, Gullbrá og bangsarnir, Mjallhvít, Rauðhetta, Galdrakarlinn í Oz, Gosi og Ljóti andarunginn. >>>Fallegar sígildar sögur fyrir eins árs og eldri.<<<", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15321, pre_title: "", title: "Bókapakki - Snúður og Snælda", post_title: "Allar 8 bækurnar saman í pakka", slug: "bokapakki-snudur-og-snaelda-15321", description: "Sígildar og vinsælar bækur í 60 ár fyrir yngstu lesendurna um ævintýri og uppátæki Snúðs og Snældu.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15322, pre_title: "", title: "Bókapakki - Leikum og lærum", post_title: "4 afþreyingarbækur saman í pakka", slug: "bokapakki-leikum-og-laerum-15322", description: "4 frábærar bækur til að læra og ná árangri með leik og skemmtilegum verkefnum. \r\nBækurnar sem eru í pakkanum eru eftirfarandi: Spádómsgoggar, Frádráttur, Samlagning og Litli ferðafélaginn. >>>Góðar afþreyingarbækur fyrir börn frá fjögurra ára aldri.<<<", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15323, pre_title: "", title: "Bókapakki - Leikum og lærum", post_title: "4 afþreyingarbækur saman í pakka", slug: "bokapakki-leikum-og-laerum-15323", description: "4 frábærar bækur til að læra og ná árangri með leik og skemmtilegum verkefnum. \r\nBækurnar sem eru í pakkanum eru eftirfarandi: 100 Drekaskutlur, Frádráttur, Samlagning og Litli ferðafélaginn. >>>Góðar afþreyingarbækur fyrir börn frá fjögurra ára aldri.<<<", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 1, book_author_id: nil},
  {source_id: 15324, pre_title: "", title: "Bókapakki - Leikum og lærum", post_title: "4 afþreyingarbækur saman í pakka", slug: "bokapakki-leikum-og-laerum-15324", description: "4 frábærar bækur til að læra og ná árangri með leik og skemmtilegum verkefnum. \r\nBækurnar sem eru í pakkanum eru eftirfarandi: 100 Drekaskutlur, Frádráttur, Samlagning og Litli ferðafélaginn. >>>Góðar afþreyingarbækur fyrir börn frá fjögurra ára aldri.<<<", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 11, book_author_id: nil},
  {source_id: 15325, pre_title: "", title: "Bókapakki - Leikum og lærum", post_title: "4 afþreyingarbækur saman í pakka", slug: "bokapakki-leikum-og-laerum-15325", description: "4 frábærar bækur til að læra og ná árangri með leik og skemmtilegum verkefnum. \r\nBækurnar sem eru í pakkanum eru eftirfarandi: Spádómsgoggar, Frádráttur, Samlagning og Litli ferðafélaginn. >>>Góðar afþreyingarbækur fyrir börn frá fjögurra ára aldri.<<<", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, category_id: 11, book_author_id: nil},
  {source_id: 15326, pre_title: "", title: "Þökk til þín", post_title: "", slug: "thokk-til-thin-15326", description: "Þakklæti hefur stundum verið kallað uppspretta lífshamingjunnar. Margar leiðir eru færar til að auka tilfinningu þakklætis í daglegu lífi og læra að tjá það í orðum og verki. Í þessari fallegu verkefnabók finnur þú fjölbreyttar þakklætisæfingar og verkefni og getur skráð þína eigin þakklætisbók. Æfingarnar er hægt að iðka einn eða með öðrum, t.d. maka, vinum eða börnum.", long_description: "", page_count: 143, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 7, category_id: 4, book_author_id: nil},
  {source_id: 15327, pre_title: "", title: "Fæddur til að fækka tárum. KÁINN.", post_title: "Ævi og ljóð", slug: "faeddur-til-ad-faekka-tarum-kainn-15327", description: "Kristján Níels Jónsson, Káinn; eina íslenska kímniskáldið, orðsnjall og orðleikinn – gæddur náðargáfu – glímdi þó alla ævi við sálar-skugga. Fæddur 1859 á Akureyri. Fluttist 19 ára til Ameríku. Saga Káins er um leið saga Vestur-Íslendinga sögð af glettnislegum sjónarhóli skáldsins sem orti: \r\n\r\nEn undarlegast atvik samt ég tel\r\nað Íslendingur skyldi frjósa í hel.", long_description: "", page_count: 372, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 8, category_id: 9, book_author_id: nil},
  {source_id: 15328, pre_title: "", title: "Stund um stund", post_title: "", slug: "stund-um-stund-15328", description: ">>>Stund um stund<<< er þriðja ljóðabók höfundar og samanstendur af tveimur prósum og fimmtíu og tveimur ljóðum.\r\nÍ bókinni veltir Jóhann fyrir sér tilbrigðum hversdagsins á sinn persónulega hátt.\r\nLjóðin eru skrifuð í hefðbundnu og frjálsu ljóðaformi, ætlað að rjúfa eril hversdagsins og fá lesendur til að staldra við, stund um stund.", long_description: "", page_count: 79, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 9, category_id: 5, book_author_id: nil},
  {source_id: 15329, pre_title: "", title: "Brottnám", post_title: "", slug: "brottnam-15329", description: "Sonur Njáls er ekki mættur í vikulegt matarboð. Njáll verður áhyggjufullur og dregur Dísu út úr óreglunni til að hjálpa sér að finna hann. Eltingarleikur, kapphlaup við tímann, mótorhjólagengi og böðlarnir koma við sögu í þessari æsispennandi bók. Allt virðist gerast undir stjórn þess stóra. Dísa og Njáll eru komin hættulega nálægt því að leysa hið ósnertanlega net hans með hjálp úr óvæntri átt.", long_description: "", page_count: 301, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 10, category_id: 2, book_author_id: nil},
  {source_id: 15330, pre_title: "", title: "Sætaspætan", post_title: "", slug: "saetaspaetan-15330", description: "Sætaspætan er önnur bókin í íslensku bókaröðinni „Sögur frá Tulipop\".\r\n\r\nBókin segir söguna af því þegar vinirnir Freddi og Gló finna sjaldgæfan furðufugl í Sveppamýrinni. Eiga þau að éta hann? Eða bjarga honum? Sjáum hvað setur!\r\n\r\nHeillandi og skemmtileg bók fyrir börn á öllum aldri byggð á ástsælu persónunum úr íslenska ævintýraheiminum Tulipop.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 11, category_id: 1, book_author_id: nil},
  {source_id: 15333, pre_title: "", title: "Fuglar í búri", post_title: "Ljóð eftir afrísk-bandarísk skáld", slug: "fuglar-i-buri-15333", description: "Ljóðin eru frá því á 18. öld til dagsins í dag. 64 ljóð eftir 29 skáld ásamt einu þjóðkvæði sem gefa hugmynd um samfélag, sögu og menningu svarts fólks í bandarísku þjóðfélagi frá því þrælahald ríkti. Ljóðin birta fjölbreytilegar myndir af lífi og stöðu svartra. Skáldin í bókinni hafa öll vakið athygli langt út fyrir eigin hóp og þjóð.\r\n\r\nGaribaldi þýddi og skrifaði formála.", long_description: "", page_count: 201, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 12, category_id: 12, book_author_id: nil},
  {source_id: 15340, pre_title: "", title: "Hófí lærir um hetjur", post_title: "", slug: "hofi-laerir-um-hetjur-15340", description: "Hófí uppgötvar eftir dag með Sámi, stóra bróður sínum og hetju, að hetjur má finna alls staðar! Hvort sem við vitum það eða ekki, þá er hetja innra með okkur öllum. \r\nHér er komin fjórða bókin um íslenska fjárhundinn Hófí og vini hennar. \r\nBækurnar innihalda ríkulegan orðaforða, yndislegan boðskap og fallegar myndir. \r\nFáanleg á íslensku og ensku.", long_description: "", page_count: 44, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 13, category_id: 1, book_author_id: nil},
  {source_id: 15334, pre_title: "", title: "Takk", post_title: "", slug: "takk-15334", description: "Þetta er fyrsta ljóðabók höfundar, afar sérstæð að uppbyggingu og efnistökum. Ljóðin eru af öllu tagi; ort í gamni og alvöru um ýmsar hliðar mannlífs og jarðar; ást og trega, umhverfi og trú. Með hliðsjón af mikilli fjölbreytni í háttum og formi auk ótal vísana til þekktra ljóða og skálda, má segja að bókin sé einskonar óður til íslenskrar ljóðlistar.", long_description: "", page_count: 79, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 14, category_id: 5, book_author_id: nil},
  {source_id: 15335, pre_title: "", title: "Jarðvegur", post_title: "", slug: "jardvegur-15335", description: ">>>Jarðvegur<<< er fyrsta ljóðabók Rebekku Sifjar (f. 1992). Verkið er samfelld frásögn þar sem tekist er á við erfið málefni, sorg, missi og sársauka. Áður hafa sögur eftir hana birst í smásagnasafninu >>>Möndulhalla<<< og öðrum safnritum Blekfjelagsins en Rebekka starfar einnig sem söngkona og lagahöfundur.", long_description: "", page_count: 70, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, category_id: 5, book_author_id: nil},
  {source_id: 15336, pre_title: "", title: "Loftskeyti", post_title: "", slug: "loftskeyti-15336", description: "Loftskeyti er fjórða ljóðabók Sigrúnar Björnsdóttur (f. 1956) en áður hafa komið út eftir hana bækurnar Næturfæðing (2002), Blóðeyjar (2007) og Höfuðbending (2014). Í Loftskeytum skoðar hún mörk merkingar, dulmálslykla og áhrif skeytasendinga í fortíð og nútíð, áhrif sem verða kannski aldrei skilin til fulls.", long_description: "", page_count: 55, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, category_id: 5, book_author_id: nil},
  {source_id: 15337, pre_title: "", title: "Dýralíf", post_title: "", slug: "dyralif-15337", description: "Í vetrarmyrkri er áður óþekkt lægð í aðsigi. Ung ljósmóðir býr í íbúð sem hún erfði eftir ömmusystur sína. Upp úr kassa undan Chiquita-banönum koma þrjú handrit sem ömmusystirin vann að, >>>Dýralíf, rannsókn á því sem mannskepnan er fær um, Sannleikurinn um ljósið<<< og >>>Tilviljunin<<<. \r\nÞessi sjöunda skáldsaga Auðar Övu fjallar um brothættasta og grimmasta dýrið: manninn og leitina að mennskunni.\r\nSkáldsögur hennar eru margverðlaunaðar og hafa verið þýddar á 33 tungumál.", long_description: "", page_count: 208, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 2, book_author_id: nil},
  {source_id: 15338, pre_title: "", title: "Fjarvera þín er myrkur", post_title: "", slug: "fjarvera-thin-er-myrkur-15338", description: "Er það ábyrgð eða hugleysi að sætta sig við örlög sín? Hér tvinnast saman yfir staði og tíma, kynslóð fram af kynslóð, líf sem kannski eru jafn tíðindalítil og girðingarstaurar en halda þó öllu uppi. Kornabarn sem rétt er yfir eldhúsborð, löngu dáið þýskt skáld og döpur rokkstjarna. \r\nÞetta er þrettánda skáldsaga höfundar sem hlaut Íslensku bókmenntaverðlaunin árið 2005 fyrir >>>Sumarljós, og svo kemur nóttin<<<.", long_description: "", page_count: 496, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 2, book_author_id: nil},
  {source_id: 15339, pre_title: "", title: "Eldarnir", post_title: "Ástin og aðrar hamfarir", slug: "eldarnir-15339", description: "Jarðskjálftar skekja Reykjanesskaga, eldfjöllin eru vöknuð til lífsins eftir 800 ára hlé. Enginn þekkir þau betur en eldfjallafræðingurinn Anna Arnardóttir, forstöðumaður Jarðvísindastofnunar, sem þarf að takast á við stærsta verkefni ferilsins við stjórn almannavarna. En áferðarfallegt og hamingjuríkt líf hennar lætur ekki lengur að stjórn.\r\nFyrri skáldsögur Sigríðar eru >>>Eyland<<< og >>>Hið heilaga orð<<<.", long_description: "", page_count: 288, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 2, book_author_id: nil},
  {source_id: 15341, pre_title: "", title: "Blýhjarta", post_title: "", slug: "blyhjarta-15341", description: ">>>Blýhjarta<<< er fyrsta ljóðverk Stefaníu dóttur Páls (f. 1990) og fjallar um konubarnið, tilraunir þess til að frelsast undan sjálfu sér og útsýnið á leiðinni. Áður hafa ritverk og þýðingar eftir hana birst í tímaritum, safnbókum og leikhúsi. Hún vinnur gjarnan þvert á miðla og stundar nú nám í tónsmíðum við Listaháskóla Íslands.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, category_id: 5, book_author_id: nil},
  {source_id: 15342, pre_title: "", title: "Hús harmleikja", post_title: "", slug: "hus-harmleikja-15342", description: "Á Eyrarbakka situr Alma blaðamaður og skrifar bók um reimleika. Hún kynnist Oktavíu Bergrós, litríkri leikkonu og safnverði í Húsinu. Leikurinn æsist. Irma kvikmyndaleikstjóri kemur á svæðið ásamt eiginmanni og tveimur handritshöfundum. Leyndarmál þorpsbúa krauma undir. Dauðinn ber að dyrum. Lögregla rannsakar málið og Alma er ekki aðgerðarlaus.", long_description: "", page_count: 255, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 17, category_id: 2, book_author_id: nil},
  {source_id: 15343, pre_title: "", title: "Tók ég eftir því þá eða tók ég eftir því eftirá?", post_title: "", slug: "tok-eg-eftir-thvi-tha-eda-tok-eg-eftir-thvi-eftira-15343", description: ">>>Tók ég eftir því þá eða tók ég eftir því eftirá?<<< er persónuleg myndræn frásögn um reynslu höfundar af því að missa tökin á raunveruleikanum og fara í geðrof. Bókin styðst við samspil teikninga og texta til þess að skapa heildstætt sjálfsævisögulegt bókverk. Hún er einhvers staðar á milli þess að vera hefðbundin sjálfsævisaga og myndlist.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 18, category_id: 9, book_author_id: nil},
  {source_id: 15344, pre_title: "", title: "Minjaþing", post_title: "helgað Mjöll Snæsdóttur", slug: "minjathing-15344", description: "Mjöll Snæsdóttir fornleifafræðingur varð sjötug þann 12. febrúar 2020. Af því tilefni ákváðu vinir og samferðafólk Mjallar að gefa út greinasafn henni til heiðurs.\r\nAfmælisritið geymir úrval fræðigreina um fjölbreytt menningarsöguleg efni. Margar þeirra tengjast viðfangsefnum Mjallar í gegnum tíðina og bera því  vitni hversu víða hún hefur komið við á sínum farsæla ferli.", long_description: "", page_count: 357, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 19, category_id: 4, book_author_id: nil},
  {source_id: 15345, pre_title: "", title: "Lykilorð 2021", post_title: "Orð Guðs fyrir hvern dag", slug: "lykilord-2021-15345", description: "Í bókinni eru biblíuvers fyrir hvern dag ársins auk ljóða erindis eða fleygs orðs. Uppbygging hennar og innihald bíður upp á fjölbreytta notkun fyrir þá sem leyfa Orði Guðs að vekja sig til umhugsunar og hafa jákvæð áhrif á líf sitt. Auk þess að vera gefin út á bókarformi eru textar hvers dags lesnir í hljóðvarpi og birtast að hluta til á helstu samfélagsmiðlum. >>>>Lykilorð<<<< hafa komið út árlega á íslensku frá því árið 2006.", long_description: "", page_count: 144, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 20, category_id: 4, book_author_id: nil},
  {source_id: 15346, pre_title: "", title: "Brot úr spegilflísum", post_title: "", slug: "brot-ur-spegilflisum-15346", description: ">>>Brot úr spegilflísum<<< er fyrsta ljóðabók Þórhildar Ólafsdóttur en hún hefur áður þýtt úr tyrknesku bókina Memed mjói eftir Yashar Kemal og birt ljóð og smásögu í TMM. \r\nÞórhildur dregur hér upp myndir af núinu, sorgum, augnablikum og heiman af æskuslóðum.", long_description: "", page_count: 107, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 21, category_id: 5, book_author_id: nil},
  {source_id: 15347, pre_title: "", title: "Miðbæjarrottan", post_title: "borgarsaga", slug: "midbaejarrottan-15347", description: "Rannveig er lattelepjandi miðbæjarrotta sem elskar fátt meira en borgarlífið. Þegar Karlotta frænka hennar týnist í Reykjavík, læðist að henni efi um að borgin sé góður staður fyrir rottur. Rannveig leitar aðstoðar hjá styttum bæjarins, sem margar muna tímana tvenna. \r\nAuður Þórhallsdóttir er bæði höfundur texta og mynda en þetta er hennar þriðja bók.", long_description: "", page_count: 40, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 21, category_id: 3, book_author_id: nil},
  {source_id: 15348, pre_title: "", title: "Refurinn ráðsnjalli", post_title: "", slug: "refurinn-radsnjalli-15348", description: "Refurinn ráðsnjalli býr í greni í skógi einum ásamt Fóu og yrðlingunum þeirra fjórum. Í nágrenninu búa þrír stórbændur. Refurinn ráðsnjalli hefur þann háttinn á að læðast í forðabúr þeirra á kvöldin og næla sér í eitthvað gott í kvöldmatinn, án þess að biðja um leyfi! Bændurnir eru aldeilis ekki ánægðir með þetta og ákveða að koma rebba fyrir kattarnef. Þá upphefst meiriháttar eltingarleikur og við skulum spyrja að leikslokum! Skemmtileg bók frá sagnameistaranum mikla, Roald Dahl.", long_description: "", page_count: 98, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 22, category_id: 3, book_author_id: nil},
  {source_id: 15349, pre_title: "", title: "BFG", post_title: "", slug: "bfg-15349", description: ">>>BFG<<< segir frá lágvöxnum risa í Risalandi. Hann kallast Bergrisinn frómi góði. Á kvöldin veiðir hann drauma fyrir börn. Í Risalandi búa samt líka níu aðrir hræðilegir risar. Eitt kvöld neyðist BFG til að ræna Soffíu, 8 ára stúlku og fara með hana til Risalands. Þau ákveða að stöðva voðaverk vondu risanna. >>>BFG<<< er spennandi, fyndið og ljúfsárt verk, þar sem höfundur bregður á leik með tungumálið.", long_description: "", page_count: 236, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 22, category_id: 3, book_author_id: nil},
  {source_id: 15350, pre_title: "", title: "Georg og magnaða mixtúran", post_title: "", slug: "georg-og-magnada-mixturan-15350", description: "Georg er átta ára. Á heimili hans býr amma hans. Hún er mjög andstyggileg við vesalings Georg sem þarf svo oft að passa hana. Hann ákveður því að búa til mixtúru til að lækna ömmu af andstyggilegheitunum. Mixtúran reynist mögnuð! Best er að vara lesendur við því að búa hana til! >>>Georg og magnaða mixtúran<<< er mögnuð bók og skemmtileg frá hinum eina sanna Roald Dahl.", long_description: "", page_count: 106, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 22, category_id: 3, book_author_id: nil},
  {source_id: 15351, pre_title: "", title: "Papa", post_title: "", slug: "papa-15351", description: "Axel Steen starfar nú í leyniþjónustunni. Í Amsterdam kemst hann í innsta hring Papa, harðsvíraðs rússnesks glæpaforingja. Vicki Thomsen, fyrrum félagi Axels í lögreglunni, fæst við bíræfið bankarán á Nørreport. Í ljós kemur að rússnesk öfl tengjast málinu. Málin fléttast saman. >>>PAPA<<< fjallar um flótta, blekkingar, forboðna ást og svik sem hafa banvænar afleiðingar.", long_description: "", page_count: 450, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 22, category_id: 12, book_author_id: nil},
  {source_id: 15352, pre_title: "", title: "Grísafjörður", post_title: "", slug: "grisafjordur-15352", description: "Tvíburarnir Inga og Baldur eru komnir í sumarfrí. Á dagskránni er að drekka kókómalt, glápa á teiknimyndir og slappa af með tilþrifum. Fyrirætlanir þeirra virðast ætla að fjúka út í veður og vind þegar Albert, nágranni þeirra af efstu hæðinni, birtist óvænt í heimsókn hjá þeim. Honum er augljóslega mikið niðri fyrir. \r\nÍ þessari bráðskemmtilegu, fyndnu og fjörugu bók, sem segir frá ólíklegri vináttu, liggur leiðin allt frá blokkaríbúð í Reykjavík til Ísafjarðar, Alpanna, Guatemala, Glimmerfjalla og síðast en ekki síst – til Grísafjarðar!", long_description: "", page_count: 152, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 3, book_author_id: nil},
  {source_id: 15785, pre_title: "", title: "Undir yfirborðinu", post_title: "Norska laxeldisævintýrið – lærdómur fyrir Íslendinga?", slug: "undir-yfirbordinu-15785", description: "Norsk stórfyrirtæki hasla sér nú völl í laxeldi á Íslandi og beita sömu eldisaðferðum hér og í Noregi. Í þessari bók segir norski blaðamaðurinn Kjersti Sandvik sögu norska laxeldisævintýrisins og lýsir kostum þess og göllum.\r\nÞetta er bók sem enginn áhugamaður um þjóðfélagsmál, atvinnulíf og hreina íslenska náttúru getur látið fram hjá sér fara.", long_description: "", page_count: 398, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 24, category_id: 4, book_author_id: nil},
  {source_id: 15353, pre_title: "", title: "Gréta og risarnir", post_title: "", slug: "greta-og-risarnir-15353", description: "Gréta býr í fallegum skógi en risar ógna heimkynnum hennar og dýranna. Þegar risarnir komu fyrst í skóginn hjuggu þeir niður tré til að byggja heimili. Svo hjuggu þeir niður fleiri tré til að byggja stærri heimili. Húsin urðu að bæjum og bæirnir urðu að borgum og nú er varla neinn skógur eftir. Gréta veit að hún þarf að hjálpa dýrunum sem búa í skóginum, en hvernig? Sagan er innblásin af Gretu Thunberg og aðgerðum hennar til að bjarga heiminum.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 1, book_author_id: nil},
  {source_id: 15354, pre_title: "", title: "Hvíti björninn og litli maurinn", post_title: "", slug: "hviti-bjorninn-og-litli-maurinn-15354", description: "Hvað gerir pínulítill maur sem er aleinn og týndur uppi í fjöllum þegar byrjar að snjóa? Og hvað gerir hvítur björn sem ætlar að sofa heilan vetur en er vakinn aftur og aftur? Þessi fallega bók miðlar mikilvægum gildum á borð við vináttu, að hjálpast að og veita aðstoð. Sagan ýtir einnig undir mál- og líkamsvitund barna í gegnum skemmtilega frásögn.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 1, book_author_id: nil},
  {source_id: 15786, pre_title: "", title: "Hugsað upphátt", post_title: "", slug: "hugsad-upphatt-15786", description: "Hugsað upphátt geymir úrval pistla sem Guðrún Egilson skrifaði í >>>Morgunblaðið<<< á fjörutíu ára tímabili.\r\nPistlar hennar vöktu jafnan mikla athygli, enda er Guðrúnu einstaklega lagið að segja mikla sögu í stuttu máli.\r\nPistlar Guðúnar eru hispurslausir og þar er brugðið upp lifandi og minnisstæðum myndum úr lífi hennar og íslenskum hversdagsveruleika.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 24, category_id: 4, book_author_id: nil},
  {source_id: 15787, pre_title: "", title: "Líkami okkar, þeirra vígvöllur", post_title: "Þannig fer stríð með konur", slug: "likami-okkar-theirra-vigvollur-15787", description: "Í þessari mögnuðu bók lætur höfundurinn raddir kvenna heyrast og afhjúpar hvernig herir, hryðjuverkamenn og vígasveitir beita nauðgunum sem stríðsvopni í nútímaátökum til þess að niðurlægja, hræða og stunda kynþáttahreinsanir.\r\nÞessi sláandi bók, sem vakið hefur heimsathygli, er ákall til okkar um að hlusta og hafast að gegn vanræktasta glæpi heimsins.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 24, category_id: 4, book_author_id: nil},
  {source_id: 15355, pre_title: "", title: "Svefnfiðrildin", post_title: "", slug: "svefnfidrildin-15355", description: "Sunna er fjörug stelpa. Einn daginn þarf hún að fara til læknis sem segir henni frá svefnfiðrildunum en þau hjálpa okkur að sofna og hvílast vel á nóttinni. Svefnfiðrildin eru stórmerkileg og Sunna getur varla beðið eftir að segja öllum frá töfrum þeirra! \r\nSagan útskýrir mikilvægi svefns og hvíldar fyrir börnum og í bókinni má einnig finna góð ráð til foreldra varðandi svefn barna og svefndagbók.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 3, book_author_id: nil},
  {source_id: 15356, pre_title: "", title: "Vertu þú!", post_title: "", slug: "vertu-thu-15356", description: "Hvað er það sem gerir mig að mér og þig að þér? Það skiptir ekki máli hverju við klæðumst, hverjum við verðum skotin í, hvaða kyni við upplifum okkur í, hvaðan við erum eða hverju við höfum áhuga á. Við megum öll vera eins og okkur langar til.\r\n >>>Vertu þú!<<< segir litríkar sögur af fjölbreytileikanum.", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 1, book_author_id: nil},
  {source_id: 15788, pre_title: "", title: "Kisa litla í felum", post_title: "", slug: "kisa-litla-i-felum-15788", description: "Lísa er flutt heim til ömmu sinnar ásamt pabba sínum og bróður. Án gömlu vinanna er hún ákaflega einmana. Hana langar í kettling, en pabbi hennar hefur sagt henni að amma hennar vilji ekki dýr inn á heimili sitt. Svo gerist það einn daginn að hræddur og hungraður kettlingur flækist til hennar. Lísa felur hann fyrst úti í gróðurhúsi og síðan inni í fataskáp í herberginu sínu – en hve lengi skyldi hún geta falið kettlinginn þar?", long_description: "", page_count: 128, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 25, category_id: 3, book_author_id: nil},
  {source_id: 15357, pre_title: "", title: "Íslandsdætur", post_title: "", slug: "islandsdaetur-15357", description: "Hér er sögð saga rúmlega fjörutíu framúrskarandi kvenna sem spanna tímann allt frá upphafi byggðar á Íslandi til dagsins í dag. Sumar eru þekktar fyrir hugrekki og staðfestu eða fyrir að hafa upplifað ótrúlegar þrekraunir og tekist á við þær af seiglu og áræðni. Aðrar voru brautryðjendur, landkönnuðir, menntakonur, sjómenn eða þekktar fyrir vísindastörf, íþróttaafrek, ritstörf, tónsmíðar eða aðrar listir. Hér er einnig sögð saga kvenna sem börðust fyrir jafnrétti kynjanna og gáfu kost á sér til forystu til að bæta samfélagið.", long_description: "", page_count: 108, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 6, book_author_id: nil},
  {source_id: 15358, pre_title: "", title: "Engin sóun", post_title: "Leiðarvísir að einfaldara, sorplausu heimili", slug: "engin-soun-15358", description: "Sorplaust líf er hugmyndafræði sem miðar að því að forðast alla sorpmyndun og sóun eins og frekast er mögulegt. Þessi bók býður upp á hagnýtar, þrautreyndar lausnir til að lifa innihaldsríkara, heilbrigðara og einfaldara lífi með sjálfbærum, sorplausum leiðum sem eru nú þegar fyrir hendi. Afþakka það sem við þurfum ekki, draga úr því sem við þurfum, endurnýta það sem við notum, endurvinna það sem við getum ekki afþakkað, dregið úr eða endurnýtt og jarðgera afganginn.", long_description: "", page_count: 368, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 4, book_author_id: nil},
  {source_id: 15359, pre_title: "", title: "Lífsgæðadagbókin", post_title: "", slug: "lifsgaedadagbokin-15359", description: "Grunnurinn að lífsgæðum okkar byggist á því að við hlúum vel að grunnstoðunum fjórum; næringu, hreyfingu, samskiptum og svefni. Í þessari bók má skrifa niður stöðuna á þessum þáttum en með því verðum við meðvitaðri um að næra þessar grunnstoðir dag frá degi. >>>Lífsgæðadagbókin<<< hjálpar þér að rækta samband þitt við sjálfa(n) þig og að forgangsraða í þágu lífsgæða og hamingju.", long_description: "", page_count: 192, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 4, book_author_id: nil},
  {source_id: 15791, pre_title: "", title: "Hulduheimar", post_title: "Huldufólksbyggðir á Íslandi", slug: "hulduheimar-15791", description: "Bókin >>>Hulduheimar<<< hefur að geyma um eitt hundrað huldufólkssögur úr öllum landshornum. Farinn er hringurinn kringum landið og sagðar huldufólkssögur úr flestum héruðum. Sögurnar greina frá litríkum samskiptum huldufólksins við íbúa mannheima og bregða upp myndum úr veröld sem oftast var hulin augum, falin á bak við standberg og klettaþil. Ljósmyndir og annað myndefni fylgir hverjum stað auk staðbundinna korta.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 25, category_id: 4, book_author_id: nil},
  {source_id: 15360, pre_title: "", title: "Framkoma", post_title: "", slug: "framkoma-15360", description: "Langflest þurfum við á einhverjum tímapunkti að koma fram og tala fyrir framan hóp fólks. Framkoma getur haft mikil áhrif á það hvernig aðrir meðtaka það sem við segjum. Við höfum öll eitthvað fram að færa en við sannfærum engan ef við getum ekki komið því frá okkur á réttan hátt. Í >>>Framkomu<<< er farið yfir grundvallaratriði þess að koma sér á framfæri á fjölbreyttum vettvangi. Í bókinni eru birt góð ráð frá reynslumiklu fólki í fjölmiðlum og atvinnulífinu.", long_description: "", page_count: 208, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 4, book_author_id: nil},
  {source_id: 15797, pre_title: "", title: "Sá stóri, sá missti og sá landaði", post_title: "", slug: "sa-stori-sa-missti-og-sa-landadi-15797", description: "Bók fyrir allt áhugafólk um laxveiði, bæði byrjendur og lengra komna. Sigurður Héðinn, hinn landsþekkti veiðimaður, fer nákvæmlega yfir hvernig stórlaxinn tekur fluguna og skoðar líka veiðitækni og veiðigræjur. Að sjálfsögðu fylgja fjölmargar veiðisögur auk þess sem í bókinni er stórglæsilegur flugukafli, með bæði uppskriftum og myndum.", long_description: "", page_count: 146, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 26, category_id: 11, book_author_id: nil},
  {source_id: 15361, pre_title: "", title: "Bókin sem þú vildir að foreldrar þínir hefðu lesið", post_title: "(og börnin þín fagna að þú gerir)", slug: "bokin-sem-thu-vildir-ad-foreldrar-thinir-hefdu-lesid-15361", description: "Hvað stendur í vegi fyrir góðum tengslum við börn og hvað getur styrkt þau? Bókin hjálpar þér m.a. að skilja hvernig þitt eigið uppeldi hefur áhrif á þig, sættast við að þú munir gera mistök og hvað þú getur gert í þeim, brjóta á bak aftur neikvæð mynstur, vinna með tilfinningar, þínar eigin og barnsins þíns og skilja hvað mismunandi hegðun gefur til kynna.", long_description: "", page_count: 252, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 4, book_author_id: nil},
  {source_id: 15362, pre_title: "", title: "Rennismíði fyrir grunnnám málmiðna", post_title: "", slug: "rennismidi-fyrir-grunnnam-malmidna-15362", description: "Bókin fjallar um undirstöðuatriði í rennismíði og er ætluð nemendum í grunndeild málmiðna. Sagt er frá rennibekknum og einstökum hlutum hans, grunnatriðum við rennsli, uppfestingu vinnslustykkja, formi og gerðum rennistáls o.fl. Bókin er að hluta til byggð á kennslubókum Þorsteins Guðlaugssonar.", long_description: "", page_count: 108, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 27, category_id: 4, book_author_id: nil},
  {source_id: 15370, pre_title: "", title: "Það sem ég hef lært", post_title: "", slug: "thad-sem-eg-hef-laert-15370", description: "Bókin >>>Það sem ég hef lært<<< er fyrsta bók Önnu Lóu Ólafsdóttur. Hún hefur s.l ár skrifað pistla og birt á netsíðu og Facebook undir nafninu Hamingjuhornið. Bókin, sem er bæði innihaldsrík og falleg, er byggð á þessum skrifum hennar en í henni fjallar  Anna Lóa fjallar um hamingjuna, sorgina, ástina, sambönd, breytingar, sjálfstraust, kvíða ofl.", long_description: "", page_count: 178, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, category_id: 4, book_author_id: nil},
  {source_id: 15363, pre_title: "", title: "Samskipti", post_title: "", slug: "samskipti-15363", description: "Með góðum aðferðum í samskiptum getum við gert okkar eigið líf betra á sama tíma og við bætum líf fólksins í kringum okkur. >>>Samskipti<<< er leiðarvísir að jákvæðum samskiptum á mörgum sviðum, hvort sem er í einkalífinu, á vinnustað eða í félagslífinu. Farið er ítarlega yfir alls kyns samskipti milli einstaklinga, samskipti í hópum, hagnýtar aðferðir í samskiptum við börn, samskipti við ókunnuga og samskipti á netinu. Í bókinni má finna ótal góð ráð, æfingar, hugleiðingar og dæmisögur til að auka færni okkar á sviði samskipta.", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 4, book_author_id: nil},
  {source_id: 15364, pre_title: "", title: "Stærðfræði 3A", post_title: "vigrar - hornaföll - þríhyrningar - hringir - ákveður - stikun", slug: "staerdfraedi-3a-15364", description: "Bókin er hluti af nýjum kennslubókaflokki í stærðfræði fyrir framhaldsskóla. Hún er ætluð nemendum á 3. þrepi (fyrsta áfanga). Áður hafa komið út Stærðfræði 1 (fyrir nemendur sem hafa ekki hlotið nægan undirbúning fyrir nám á 2. þrepi) og Stærðfræði 2A og 2B (fyrir nám á 2. þrepi (fyrsta áfanga).", long_description: "", page_count: 136, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 27, category_id: 4, book_author_id: nil},
  {source_id: 15365, pre_title: "", title: "Klettaborgin", post_title: "", slug: "klettaborgin-15365", description: "Sólveig Pálsdóttir var ung send í sveit austur í Skaftafellsýslu. Klettaborgin byggir á minningum Sólveigar frá æsku og fram undir tvítugt og persónugalleríið er fjölbreytt. Hún skrifar um fólk og atburði, sem hafa haft áhrif á líf hennar, og lýsir horfnum heimi og annars konar lífsgildum en við þekkjum í dag. Klettaborgin geymir fallegar sögur og dýrmætar minningar um dýrmætar manneskjur.", long_description: "", page_count: 192, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 2, book_author_id: nil},
  {source_id: 15366, pre_title: "", title: "Snuðra og Tuðra", post_title: "", slug: "snudra-og-tudra-15366", description: "Bækurnar um Snuðru og Tuðru hafa kætt börn í áratugi. Systurnar eru uppátækjasamar og meira að segja stundum svolítið óþekkar en þær læra alltaf eitthvað af reynslunni í hverri sögu.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 1, book_author_id: nil},
  {source_id: 15367, pre_title: "", title: "Snuðra og Tuðra fara í sund", post_title: "", slug: "snudra-og-tudra-fara-i-sund-15367", description: "Snuðru og Tuðru finnst afskaplega gaman að fara í sund eins og flestum börnum. Þegar mamma þeirra er upptekin í vinnunni og kemst ekki með þeim í laugina ákveða þær að taka málin í sínar eigin hendur.", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 1, book_author_id: nil},
  {source_id: 15368, pre_title: "", title: "Snuðra og Tuðra í jólaskapi", post_title: "", slug: "snudra-og-tudra-i-jolaskapi-15368", description: "Snuðra og Tuðra hlakka ótrúlega til jólanna á hverju ári. Þær eru staðráðnar í að vera þægar og góðar og hjálpa til við að skreyta heimilið og jólatréð. Þegar aðfangadagur rennur loks upp er spennan í hámarki hjá systrunum og þá getur ýmislegt gerst!", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, category_id: 1, book_author_id: nil},
  {source_id: 15369, pre_title: "", title: "Þjálffræði", post_title: "", slug: "thjalffraedi-15369", description: "Bókin fjallar um ýmsar hliðar líkamsþjálfunar og er undirstöðurit í íþróttafræðum. Hún er ætluð fyrir nám á íþróttabrautum framhaldsskóla og sem kennsluefni í þjálfaramenntun íþróttahreyfingarinnar. Hún er unnin í nánu samstarfi við Íþrótta- og Ólympíusamband Íslands.", long_description: "", page_count: 589, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 27, category_id: 11, book_author_id: nil},
  {source_id: 15425, pre_title: "", title: "Allt í plati", post_title: "", slug: "allt-i-plati-15425", description: "Það voru mikil tíðindi þegar >>>Allt í plati<<< kom fyrst út árið 1980 og fram steig nýr og spennandi höfundur með glænýja blöndu af textabók og teiknimyndasögu. Núna þekkja allir bókaormar bækur Sigrúnar Eldjárn en fyrsta bókin hennar, sagan um vinina Eyvind og Höllu, hugsanablöðrur og krókófíla, höfðar eins vel til barna nú og þá – enda hugmyndaflugið í hæstu hæðum og allt er í plati!", long_description: "", page_count: 43, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 1, book_author_id: nil},
  {source_id: 15407, pre_title: "", title: "Tengdadóttirin", post_title: "", slug: "tengdadottirin-15407", description: "Í >>>Tengdadótturinni<<<, sem kom fyrst út 1952–54, segir frá ástum og örlögum jafnt sem daglegu lífi til sveita í byrjun 20. aldar. Guðrún frá Lundi hefur einstakt lag á að gæða sögupersónur sínar lífi svo lesandinn hverfur hundrað ár aftur í tímann.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15371, pre_title: "", title: "Fríða og Ingi bróðir", post_title: "", slug: "frida-og-ingi-brodir-15371", description: "Fríða og Ingi bróðir gerist í Reykjavík 1950 og er ríkulega myndskreytt í anda þess tíma. Fríða er sex ára og nýbúin að eignast bróður. Hún lofar að passa hann alltaf. Það er hins vegar ekki auðvelt. Reynt er að stela honum, hann týnist í miðbænum og á 17. júní dettur Ingi í Tjörnina. Það er fullt starf fyrir Fríðu að vera stóra systir hans Inga bróður.", long_description: "", page_count: 92, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, category_id: 1, book_author_id: nil},
  {source_id: 15372, pre_title: "", title: "Ljóð 2010-2015", post_title: "", slug: "ljod-2010-2015-15372", description: ">>>Ljóð 2010–2015<<< hefur að geyma þrjár fyrstu ljóðabækur Þórdísar Gísladóttur, sem hafa lengi verið ófáanlegar. Þetta eru bækurnar >>>Leyndarmál annarra<<< (2010) sem hlaut bókmenntaverðlaun Tómasar Guðmundssonar, >>>Velúr<<< (2014) sem var tilnefnd til Íslensku bókmenntaverðlaunanna og >>>Tilfinningarök<<< (2015). \r\nÚlfhildur Dagsdóttir bókmenntafræðingur ritar formála.", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 5, book_author_id: nil},
  {source_id: 15373, pre_title: "", title: "Álabókin", post_title: "Sagan um heimsins furðulegasta fisk", slug: "alabokin-15373", description: "Állinn er leyndardómsfyllsti fiskur veraldar. Aristóteles og Sigmund Freud reyndu árangurslaust að öðlast skilning á tilvist álsins, en þessi dularfulla lífvera er enn ráðgáta og nú óttumst við að hann verði útdauða. \r\n\r\nBók um þekkingarleit, lífið sjálft, hvernig á að lifa því og áskorunina sem bíður okkar allra; að deyja. \r\n\r\n>>>Álabókin<<< hlaut Sænsku bókmenntaverðlaunin 2019.", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 9, book_author_id: nil},
  {source_id: 15374, pre_title: "", title: "Tunglið er diskókúla", post_title: "", slug: "tunglid-er-diskokula-15374", description: "„Hér er bók um það hvernig heimurinn springur stundum út í villtu streymi og rambar á mörkum draums og veruleika, erótíkur og klósettferða, ástar og losta, regnboga og ælu. Þetta er skáldið sem þú átt eftir að taka eftir næstu árin, flæðið er farið í gang,“ segir Elísabet Jökulsdóttir. „Loksins, Loksins!“ segir Mikael Torfason. \r\n>>>Tunglið er diskókúla<<< er fyrsta bók Loka sem er fæddur árið 1993.", long_description: "", page_count: 120, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 5, book_author_id: nil},
  {source_id: 15375, pre_title: "", title: "Hetjusögur", post_title: "", slug: "hetjusogur-15375", description: "Ljóðin í bókinni eru ort upp úr ritinu >>>Íslenskar ljósmæður<<< I-III sem séra Sveinn Víkingur bjó til prentunar og kom út hjá Kvöldvökuútgáfunni á Akureyri 1962-1964. Þar eru prentaðir æviþættir og endurminningar 100 ljósmæðra.\r\n>>>Hetjusögur<<< er fjórða ljóðabók Kristínar Svövu sem er sagnfræðingur, fædd í Reykjavík árið 1985.", long_description: "", page_count: 114, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 5, book_author_id: nil},
  {source_id: 15376, pre_title: "", title: "Næsti!", post_title: "Raunir heimilislæknis", slug: "naesti-15376", description: "Heimilislæknir til tuttugu ára hefur Elín fengið sig fullsadda af umkvörtunum og óhamingju fólks. Hún er líka búin að fá nóg af manninum sínum, skíðaáhugamanninum Axel. Í kjölfar endurnýjaðra kynna við gamlan kærasta tekur Elín að brjóta upp mynstrið ... til þess að standa svo í rjúkandi rústum. Meinfyndin saga sem hlaut Norsku bókmenntaverðlaunin 2019.", long_description: "", page_count: 272, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 12, book_author_id: nil},
  {source_id: 15377, pre_title: "", title: "Þagnarbindindi", post_title: "", slug: "thagnarbindindi-15377", description: "Ljóðsagan >>>Þagnarbindindi<<< miðlar hugarheimi konu sem reynir að ná tökum á minningum um missi og ótta, um samskipti við konurnar sem hafa verið henni nánastar og henda reiður á svo mörgu sem hún hefur aldrei sagt. \r\nÞetta er fyrsta bók Höllu Þórlaugar, sem er fædd 1988. Hún hlaut Nýræktarstyrk Miðstöðvar íslenskra bókmennta fyrir þetta verk.", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 5, book_author_id: nil},
  {source_id: 15378, pre_title: "", title: "Myndskreytt handbók um skrautlegar skepnur", post_title: "", slug: "myndskreytt-handbok-um-skrautlegar-skepnur-15378", description: "Vissir þú að hrafnar geta hermt eftir hljóðum eins og páfagaukar? Það er ekki nóg með að moskítóflugur bíti – þær spræna líka á mann. Krossfiskar eru heilalausir en kolkrabbar með fleiri heilasellur en mannskepnan ... og þrjú hjörtu! \r\nBráðskemmtilegar staðreyndir og fyrsta flokks teikningar eftir hina sænsku Maju Safström.", long_description: "", page_count: 120, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 6, book_author_id: nil},
  {source_id: 15379, pre_title: "", title: "Afnám haftanna - Samningar aldarinnar?", post_title: "", slug: "afnam-haftanna-samningar-aldarinnar-15379", description: "Afnám fjármagnshaftanna, sem sett voru á í kjölfar falls bankanna 2008, er stórmerkileg saga sem fjölmargir komu að. Íslendingar hafa aldrei átt jafn mikið undir samningamönnum sínum og þegar samið var við kröfuhafana. Afar fróðleg bók þar sem Sigurður Már Jónsson, fyrrum upplýsingafulltrúi ríkisstjórnarinnar, fer yfir hvað gerðist á bak við tjöldin.", long_description: "", page_count: 344, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, category_id: 4, book_author_id: nil},
  {source_id: 15380, pre_title: "", title: "Björgunarsveitin mín", post_title: "", slug: "bjorgunarsveitin-min-15380", description: "Björgunarsveitin mín er gefin út í tilefni af 70 ára afmæli Flugbjörgunarsveitarinnar. Arngrímur Hermannsson safnaði saman áhugaverðum frásögnum frá fjölmörgum félögum svo að úr verður einkar áhugaverð, fróðleg, spennandi og skemmtileg bók. Hér eru hvoru tveggja leitar- og björgunarsögur og ferðasögur félaga sveitarinnar á þessu 70 ára tímabili.", long_description: "", page_count: 308, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, category_id: 13, book_author_id: nil},
  {source_id: 15381, pre_title: "", title: "Sjáðu!", post_title: "", slug: "sjadu-15381", description: ">>>Sjáðu!<<< er myndaævintýri fyrir allra yngstu börnin þar sem upplagt er að benda, skoða og undrast. Stutt vers leiða lesendur í gegnum furðuheim sem vekur spurningar og vangaveltur. Þykk spjöldin henta yngstu bókaormunum vel en allir fá eitthvað fyrir sinn snúð því >>>Sjáðu!<<< er bók sem vex með barninu.", long_description: "", page_count: 20, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 1, book_author_id: nil},
  {source_id: 15382, pre_title: "", title: "Fíllinn fljúgandi", post_title: "", slug: "fillinn-fljugandi-15382", description: "Hvernig kemst fíll alla leið frá Afríku til Íslands? Geta fílar kannski flogið? >>>Fíllinn fljúgandi<<< er skemmtileg og spennandi saga fyrir yngstu bókaormana um það hvernig forvitinn fílsungi og hugrakkur strákur verða bestu vinir. Myndir Auðar Ýrar auðga sögu Þorgríms svo úr verður gullfalleg bók.", long_description: "", page_count: 43, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 1, book_author_id: nil},
  {source_id: 15383, pre_title: "", title: "Hryllilega stuttar hrollvekjur", post_title: "", slug: "hryllilega-stuttar-hrollvekjur-15383", description: ">>>Hryllilega stuttar hrollvekjur<<< geymir tuttugu smásögur sem eru hver annarri hræðilegri. Hér má meðal annars lesa um vampírur, mannát, uppvakninga, drauga og skrímslin sem leynast undir rúminu þínu. Metsöluhöfundurinn Ævar Þór Benediktsson var myrkfælinn þangað til hann varð sautján ára. Hér skrifar hann um allt sem hann var hræddur við.", long_description: "", page_count: 153, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15384, pre_title: "", title: "Blokkin á heimsenda", post_title: "", slug: "blokkin-a-heimsenda-15384", description: "Dröfn hefur aldrei hitt ömmu sína svo að óvænt ferðalag á eyjuna hennar, þar sem allir búa saman í einni stórri blokk, hljómar spennandi. En ferðin verður fljótlega dálítið skrýtin. Getur verið að einhver eyjarskeggja vilji Dröfn og fjölskyldu hennar illt? >>>Blokkin á heimsenda<<< hlaut Barnabókaverðlaun Guðrúnar Helgadóttur 2020 og var að mati dómnefndar bæði grípandi og gamansöm.", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15385, pre_title: "", title: "Þín eigin saga", post_title: "", slug: "thin-eigin-saga-15385", description: "Litríkar og spennandi bækur þar sem ÞÚ ræður hvað gerist! Mundu bara að ef sagan endar illa má alltaf reyna aftur – það eru mörg mismunandi sögulok! Tvær nýjar bækur í vinsælum bókaflokki fyrir byrjendur í lestri.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15386, pre_title: "", title: "Knúsípons", post_title: "", slug: "knusipons-15386", description: "", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15387, pre_title: "", title: "Risaeðlur", post_title: "", slug: "risaedlur-15387", description: "", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15388, pre_title: "", title: "Milljarðastrákurinn", post_title: "", slug: "milljardastrakurinn-15388", description: "Fyndin og ljúfsár bók úr smiðju metsöluhöfundarins David Walliams. Jói Smálki er ríkasti tólf ára strákur í heimi. Hann á allt sem hugurinn girnist: sinn eigin formúlu-kappakstursbíl, þúsund skópör og jafnvel einkaþjón sem er órangútan. En það er eitt sem hann saknar sárlega: vin ... David Walliams fer hér á kostum í frábærlega fyndinni bók.", long_description: "", page_count: 278, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 3, book_author_id: nil},
  {source_id: 15389, pre_title: "", title: "Gullfossinn", post_title: "", slug: "gullfossinn-15389", description: "Sóldís og Sumarliði eru önnum kafin við að finna not fyrir allt tæknilega dótið sem leyndist í koparegginu úr fortíðinni. Á meðan heldur dularfull stelpa af stað eftir leynigöngum, slúðrandi fréttahaukur hoppar um á priki og rustarnir í dalnum leita allra leiða til að endurheimta völdin. >>>Gullfossinn<<< er spennandi framtíðarsaga og framhald >>>Silfurlykilsins<<< og >>>Kopareggsins<<<.", long_description: "", page_count: 218, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15390, pre_title: "", title: "Iðunn og afi pönk", post_title: "", slug: "idunn-og-afi-ponk-15390", description: "Þegar glænýja hjólið hennar Iðunnar hverfur grunar hún systurnar í Súluhöfða strax um græsku. Þá er verst að mamma og pabbi eru farin í ferðalag og barnapían afi pönk hugsar meira um lúsmý og eldgamlar hljómsveitir en tapað hjól. Stórskemmtileg saga um flókna ráðgátu og fjörugar persónur eftir einn af okkar færustu höfundum.", long_description: "", page_count: 133, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15391, pre_title: "", title: "Verstu kennarar í heimi", post_title: "", slug: "verstu-kennarar-i-heimi-15391", description: "Ef þú kannast við Verstu börn í heimi skaltu búa þig undir dálítið miklu, miklu verra ... VERSTU KENNARA Í HEIMI! Hér er sagt frá ótta herra Knúts, ógnarþríhjóli fröken Ógnar, doktor Ofsa og stól þúsund prumpa, ógnarsal frú Sléttu, og hryllingssöguni um hjónin Turtil og Dúfu & tárahafið. Ég varaði þig við! Hér er David Walliams upp á sitt besta.", long_description: "", page_count: 308, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 3, book_author_id: nil},
  {source_id: 15392, pre_title: "", title: "SVARTA KISA – Hundadagur", post_title: "", slug: "svarta-kisa-hundadagur-15392", description: "", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 3, book_author_id: nil},
  {source_id: 15393, pre_title: "", title: "SVARTA KISA – Í Svartaskóla", post_title: "", slug: "svarta-kisa-i-svartaskola-15393", description: "", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 3, book_author_id: nil},
  {source_id: 15394, pre_title: "", title: "Tíminn minn 2021", post_title: "", slug: "timinn-minn-2021-15394", description: "Hlý og fallega myndskreytt dagbók eftir íslensku listakonuna Björgu Þórhallsdóttur, sem nú er einn vinsælasti listamaður Noregs. Þessi dagbók er sniðin fyrir konur, full af jákvæðni og góðum ráðum. Tíminn minn er uppáhalds dagbók fjölda íslenskra kvenna.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 8, book_author_id: nil},
  {source_id: 15395, pre_title: "", title: "Barnaræninginn", post_title: "", slug: "barnaraeninginn-15395", description: "Eftir hallarbyltinguna í Hafnarlandi ríkir friður og ró. En ekki lengi. Brátt er Barnaræninginn aftur kominn á stjá og Eyrdís verður að stöðva hann, þó að það gæti orðið hennar bani! >>>Barnaræninginn<<< er jafnspennandi og >>>Draumaþjófurinn<<<, fyrri bókin um rotturnar í Hafnarlandi sem naut mikilla vinsælda og var tilnefnd til Barnabókaverðlauna Reykjavíkur.", long_description: "", page_count: 224, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15396, pre_title: "", title: "Þín eigin undirdjúp", post_title: "", slug: "thin-eigin-undirdjup-15396", description: "Sjöunda bókin í einum vinsælasta bókaflokki síðari ára. Hér ert þú söguhetjan og stjórnar ferðinni. Þér er boðið um borð í kafbát þar sem þrír stórskrítnir skipstjórar ráða ríkjum. Á leið ykkar um undirdjúpin leitið þið að sokknum fjársjóði, kannið hinn banvæna Bermúdaþríhyrning og eltið uppi heimsins stærsta sjávarskrímsli. Ný og spennandi bók eftir margfaldan verðlaunahöfund.", long_description: "", page_count: 336, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15397, pre_title: "", title: "Nærbuxnavélmennið", post_title: "", slug: "naerbuxnavelmennid-15397", description: "Gutti er enn innsti koppur í búri í Rumpinum, samfélagsmiðstöðinni í gömlu nærbuxnaverksmiðjunni, en Ólína vinkona hans er orðin aðalstjarnan í íþróttaliði bæjarins. Þess vegna þarf Gutti að glíma við það aleinn þegar vélmennið BlúnduRASS 3000 gengur af göflunum. Sprenghlægilegt framhald á >>>Nærbuxnaverksmiðjunni<<< og >>>Nærbuxnanjósnurunum<<< sem glatt hafa unga sem aldna.", long_description: "", page_count: 120, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15656, pre_title: "", title: "Dagbókin mín", post_title: "", slug: "dagbokin-min-15656", description: ">>>Dagbókin mín<<< er til að æfa hugann í jákvæðni, þakklæti og seiglu. Dagbókin skýrir í máli og myndum hvernig heilinn virkar og hvernig er hægt að þjálfa hann í að hugsa jákvætt.\r\nBókin inniheldur 65 daga dagbók sem fær einstaklinga til að hugsa um hvað þeir eru þakklátir fyrir, líðan þeirra og hegðun, auk verkefna til að auka skilning þeirra á tilfinningum og hvernig hægt er að setja sér markmið.", long_description: "", page_count: 94, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, category_id: 4, book_author_id: nil},
  {source_id: 15399, pre_title: "", title: "Andlitslausa konan", post_title: "", slug: "andlitslausa-konan-15399", description: "Skelfilegur glæpur er framinn í brúðkaupi sem Eddu á Birkimelnum er boðið í. Rannsókn málsins er snúin en varpar óvænt ljósi á veislu sem forsætisráðherra undirbýr í gamla Þingvallabænum og setur leyndardómsfull áramótaplön barnabarns Eddu líka í uppnám. Fimmta bókin um eftirlaunaþegann Eddu sem leysir flókin sakamál með hyggjuvitið að vopni.", long_description: "", page_count: 329, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15400, pre_title: "", title: "Brúin yfir Tangagötuna", post_title: "", slug: "bruin-yfir-tangagotuna-15400", description: "Það er vinnslustopp í rækjunni, gatan sundurgrafin og bærinn fullur af forvitnum túristum. Ef ekki væri fyrir nágrannakonuna handan við brúna væri líf Halldórs ansi dapurlegt. En þótt það séu bara níu og hálft skref úr anddyrinu hans að dyrunum hennar er leiðin þangað furðulega flókin. Launfyndin, ísfirsk ástarsaga úr samtímanum eftir verðlaunahöfund sem hvarvetna vekur athygli.", long_description: "", page_count: 216, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15401, pre_title: "", title: "Morðin í Háskólabíó", post_title: "", slug: "mordin-i-haskolabio-15401", description: "Prestur á líknardeildinni leitar til Stellu með hinstu játningu um nauðgun og hvarf tólf ára stúlku norður á Ströndum tuttugu árum fyrr. Gömul kærasta verður fyrir dularfullri líkamsárás og skjólstæðingur Stellu ferst í sprengingu á kosningahátíð í Háskólabíó. En ekkert mál er of flókið fyrir harðasta og kjaftforasta lögmann landsins.", long_description: "", page_count: 272, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15965, pre_title: "", title: "Narfi", post_title: "", slug: "narfi-15965", description: "Bækurnar um Narfa náhval eru einkar hlýjar og skemmtilegar léttlestrarbækur. Narfi er mjög vinsæll í Kanada en þar hafa bækurnar hlotið margar viðurkenningar. Þá eru þær einkar vinsælar meðal umsjónarmanna skólabókasafna, enda hafa fjölmargir æft sig í að lesa sér til gagns með því að kynnast hinum glaðbeitta og geðþekka Narfa og vinum hans.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 3, book_author_id: nil},
  {source_id: 15402, pre_title: "", title: "Váboðar", post_title: "", slug: "vabodar-15402", description: "Fyrsta smásagnasafn verðlaunahöfundarins Ófeigs Sigurðssonar geymir ná- og fjarskyldar sögur af draumum og fyrirboðum, sérhæfðum rannsóknum og stórhuga áformum, öpum og máfum, bókum og hnífum, dyntóttu landi og ráðvilltri þjóð. Ágengar, fyndnar og frumlegar sögur þar sem misjafnlega venjulegt fólk glímir af veikum mætti við aðsteðjandi ógnir og óttann undir niðri.", long_description: "", page_count: 197, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15403, pre_title: "", title: "Undir Yggdrasil", post_title: "", slug: "undir-yggdrasil-15403", description: "Þorgerður Þorsteinsdóttir mátti bíða lengi eftir óskabarninu sínu. Því harmþrungnari eru atburðirnir eftir þingið á Þórsnesi þegar tátlan er aðeins sjö ára gömul. Þorgerður kýs heldur að leita styrks hjá skapanornunum en Hvítakristi ömmu sinnar, Auðar djúpúðgu, og leitin að sannleikanum leiðir hana á óvæntar slóðir. Sögulegar skáldsögur Vilborgar Davíðsdóttur hafa notið mikilla vinsælda enda varpa þær nýstárlegu ljósi á Íslandssöguna, ekki síst örlög og aðstæður kvenna.", long_description: "", page_count: 329, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15404, pre_title: "Hrímland", title: "Skammdegisskuggar", post_title: "", slug: "skammdegisskuggar-15404", description: "Sæmundur hefur verið rekinn úr Svartaskóla fyrir hættulegt fikt við svartagaldur. Garún vill losa Hrímland undan erlendum kúgurum og til þess að knýja fram byltinguna sem hún þráir leitar hún til Sæmundar – með ófyrirséðum afleiðingum.  Mögnuð og myrk samtímafurðusaga sem nálgast þjóðsagnaarfinn og Íslandssöguna á ferskan og frumlegan hátt.", long_description: "", page_count: 490, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15405, pre_title: "", title: "Truflunin", post_title: "", slug: "truflunin-15405", description: "Árið er 2034. Heimurinn hefur kvíslast í tvær víddir sem snertast á litlu svæði í miðbæ Reykjavíkur. Múr umlykur svæðið og inn fyrir hann fer einungis þrautþjálfað vísindafólk og hermenn, svokallaðir agentar. Einn þeirra er félagssálfræðingurinn Halla sem falið er að finna agentinn F sem sendi frá sér torrætt neyðarkall áður en hún hvarf sporlaust. >>>Truflunin<<< er grípandi framtíðartryllir eftir höfund sem á fáa sína líka.", long_description: "", page_count: 303, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15406, pre_title: "", title: "Ofurnarfi og Gréta glytta", post_title: "", slug: "ofurnarfi-og-greta-glytta-15406", description: "", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 3, book_author_id: nil},
  {source_id: 15408, pre_title: "", title: "Á krossgötum", post_title: "", slug: "a-krossgotum-15408", description: "", long_description: "", page_count: 476, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15897, pre_title: "", title: "Sögur Belkíns", post_title: "Stofnun Vigdísar Finnbogadóttur", slug: "sogur-belkins-15897", description: ">>>Sögur Belkíns<<< er fyrsta prósaverkið sem Aleksander Púshkín lauk við. Brugðið er á leik með þekkt stef, s.s. rómantíska hetju, draugasöguna, hugljúfar ástir og óvænt endalok. Sögurnar eru fullar lífsgleði, glettni og umhyggju fyrir sögupersónum og höfðu sumar ófyrirséð áhrif á rússneskar bókmenntir. Inngangur um verkið og þýðingasögu sagnanna fylgir þýðingunni.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, category_id: 12, book_author_id: nil},
  {source_id: 15409, pre_title: "", title: "Hrundar vörður", post_title: "", slug: "hrundar-vordur-15409", description: "", long_description: "", page_count: 454, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15410, pre_title: "", title: "Narfi – Einhyrningur hafsins", post_title: "", slug: "narfi-einhyrningur-hafsins-15410", description: "", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 3, book_author_id: nil},
  {source_id: 15976, pre_title: "", title: "Einvígi allra tíma", post_title: "Spassky vs. Fischer í Reykjavík 1972", slug: "einvigi-allra-tima-15976", description: ">>>Einvígi allra tíma<<< er ný bók þar sem Guðmundur G. Þórarinsson - forseti Skáksambands Íslands á tíma einvígisins - fjallar um hinn magnaða aðdraganda einvígis og sögunum úr innsta hring atburða á meðan á einvíginu stóð. Höfundur gerir einnig upp hinn einstæða eftirleik áranna eftir einvígið þar sem Spassky flúði heimalandið og Fisher endaði á Íslandi - landflótta og eftirlýstur.", long_description: "", page_count: 308, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 35, category_id: 4, book_author_id: nil},
  {source_id: 15411, pre_title: "", title: "Ísskrímslið", post_title: "", slug: "isskrimslid-15411", description: "Enn og aftur kemur David Walliams með frábæra bók. Þegar Elsa, tíu ára munaðarlaust götubarn í Lundúnum árið 1899, heyrir að dularfullt ísskrímsli – mammútur, hafi fundist á norðurslóðum – ákveður hún að kanna málið nánar. Fyndið, ljúft og spennandi ævintýri. Bókin er í frábærri þýðingu meistara Guðna Kolbeinssonar.", long_description: "", page_count: 422, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 3, book_author_id: nil},
  {source_id: 15412, pre_title: "", title: "Hjartastaður", post_title: "", slug: "hjartastadur-15412", description: "Harpa Eir flýr úr borginni til að bjarga dóttur sinni úr slæmum félagsskap. Stefnan er tekin austur á land og leiðin liggur jafnt um stórbrotna náttúruna og hrjóstrugt landslag hugans. Þessi magnaða og margbrotna ferðasaga er eitt þekktasta verk Steinunnar Sigurðardóttur og fyrir hana hlaut hún Íslensku bókmenntaverðlaunin 1995. Guðni Elísson ritar eftirmála.", long_description: "", page_count: 444, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15413, pre_title: "", title: "Hvítt haf", post_title: "", slug: "hvitt-haf-15413", description: "Árið 1944 hafa Þjóðverjar hertekið Noreg. Ingrid snýr aftur til æskustöðva sinna á Barrey sem hefur verið auð og yfirgefin. Hún býr sig undir kulda og einsemd en fljótlega kemst hún að því að hún er ekki ein á eyjunni. >>>Hvítt haf<<< er önnur bókin í þríleik um fólkið á Barrey sem hlotið hefur fjölmörg verðlaun og viðurkenningar.", long_description: "", page_count: 255, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 12, book_author_id: nil},
  {source_id: 15414, pre_title: "", title: "Sumarbókin", post_title: "", slug: "sumarbokin-15414", description: "Sígild bókmenntaperla sem segir frá Soffíu litlu og ömmu hennar og sumardvöl þeirra á smáeyju undan strönd Finnlands. Þessi tæra og látlausa en djúpvitra frásögn af örheimi eyjunnar, af náttúrunni og veðrinu og af heimspekilegum samræðum ömmu og Soffíu hefur heillað lesendur í næstum hálfa öld en fæst nú í fyrsta skipti á íslensku.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 12, book_author_id: nil},
  {source_id: 15415, pre_title: "", title: "Eldum björn", post_title: "", slug: "eldum-bjorn-15415", description: "Sumarið 1852 hverfur smalastúlka frá Kengis, nyrst í Svíþjóð. Óttast er að björn hafi grandað henni og presturinn í þorpinu leitar verksummerkja með samíska piltinn Jussa sér við hlið. En fyrr en varir fjölgar fórnarlömbum og ótti og hjátrú taka völd. >>>Eldum björn<<< er einstök saga, geysivel skrifuð, myrk, hjartnæm og heillandi, eftir höfund metsölubókarinnar >>>Rokkað í Vittula<<<.", long_description: "", page_count: 456, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 12, book_author_id: nil},
  {source_id: 15416, pre_title: "", title: "Dóttirin", post_title: "", slug: "dottirin-15416", description: "Þegar Torunn ákveður að taka upp eftirnafnið Neshov bresta veik böndin milli hennar og móður hennar. Gamli vonbiðillinn Kai Roger færir henni tvo grísi þótt hún hafi endanlega gefist upp á svínarækt. Eini ljósi punkturinn er afleysingapresturinn Frank sem er svo ansi myndarlegur … Hér lýkur Anne B. Ragde sagnabálkinum vinsæla sem hófst með >>>Berlínaröspunum<<<.", long_description: "", page_count: 327, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 12, book_author_id: nil},
  {source_id: 15417, pre_title: "", title: "Hvernig á að kenna ömmu og afa að lesa", post_title: "", slug: "hvernig-a-ad-kenna-ommu-og-afa-ad-lesa-15417", description: "Skemmtileg bók fyrir alla þá sem eru að læra að lesa. Það er gaman að lesa en enn skemmtilegra að lesa fyrir ömmu og afa. Þessi bók er í hinni vinsælu ritröð Hvernig á ... sem notið hefur mikilla vinsælda hér á landi sem erlendis. Hvernig á að lesa fyrir ömmu og afa er fyrir alla lesendur, sérstaklega glænýja lesendur.", long_description: "", page_count: 26, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 1, book_author_id: nil},
  {source_id: 15418, pre_title: "", title: "Saga býflugnanna", post_title: "", slug: "saga-byflugnanna-15418", description: "Í þessari grípandi skáldsögu fléttast þrír þræðir í þétta frásögn sem hverfist í senn um margslungin sambönd fólks og samspil manns og náttúru. >>>Saga býflugnanna<<< eftir metsöluhöfundinn Maju Lunde sló í gegn um allan heim og hefur hlotið fjölda viðurkenninga. „Snjöll og sláandi fögur skáldsaga.“ >>>Stavanger Aftenblad<<<.", long_description: "", page_count: 422, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 12, book_author_id: nil},
  {source_id: 15419, pre_title: "", title: "Bálviðri", post_title: "", slug: "balvidri-15419", description: "Á aðfangadag árið 1617 skellur aftakaveður á Vardø, austustu byggð Norður-Noregs, þegar allir karlmenn þorpsins eru á sjó. Konurnar horfa á eiginmenn, unnusta, feður, bræður og syni farast. Þær eru einar eftir og þurfa að bjarga sér. Rúmu ári síðar kemur nýr fógeti ásamt konu sinni til að sveigja íbúana undir veraldlegt og andlegt yfirvald. Sjálfstæðu konurnar á eyjunni horfast þá í augu við breytta tíma.", long_description: "", page_count: 384, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 12, book_author_id: nil},
  {source_id: 15420, pre_title: "", title: "Innræti", post_title: "", slug: "innraeti-15420", description: "Margræð og ísmeygileg ljóð um þau ólíku hlutverk sem kona gegnir í lífinu og bregða í senn ljósi á hversdaginn og snúa upp á hann. Arndís Þórarinsdóttir, sem kunn er fyrir barnabækur sínar, hlaut sérstaka viðurkenningu í samkeppninni um Ljóðstaf Jóns úr Vör 2019. „Innræti er dásamleg ljóðabók – uppfull af ást, húmor, ótta og smá kvíða. Ég get ómögulega mælt nógsamlega með henni.“ Katrín Lilja/Lestrarklefinn.", long_description: "", page_count: 58, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 5, book_author_id: nil},
  {source_id: 15421, pre_title: "", title: "Kyrralífsmyndir", post_title: "", slug: "kyrralifsmyndir-15421", description: ">>>Kyrralífsmyndir<<< Lindu Vilhjálmsdóttur eru ljóð ort í kófinu sem lagðist yfir samfélagið á útmánuðum 2020, þegar allt breyttist skyndilega: Kunnuglegir hlutir urðu framandi, eðlileg samskipti lögðust nánast af og óttinn náði undirtökum. Ljóð Lindu eru meitlaðar svipmyndir úr þessu sérkennilega andrúmslofti og ljósmyndir hennar magna áhrifin enn frekar.", long_description: "", page_count: 60, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 5, book_author_id: nil},
  {source_id: 15422, pre_title: "", title: "Handbók um ómerktar undankomuleiðir", post_title: "", slug: "handbok-um-omerktar-undankomuleidir-15422", description: "Þessi níunda ljóðabók Antons Helga er ljóðsaga þar sem undirliggjandi atburðarás minnir á dramatíska óperu sem þó er ekki laus við að vera fyndin. Sagan lýsir annasömum degi hjá ónefndri persónu sem bregst við margvíslegu áreiti umhverfisins með því að ferðast til annarra staða og stunda. Snjöll, kímin og harmræn ljóð um ævintýri hversdagsins.", long_description: "", page_count: 68, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 5, book_author_id: nil},
  {source_id: 15423, pre_title: "", title: "Er nokkur í Kórónafötum hér inni? / Sendisveinninn er einmana / Róbinson Krúsó snýr aftur", post_title: "", slug: "er-nokkur-i-koronafotum-her-inni-sendisveinninn-er-einmana-robin-15423", description: "Fyrstu þrjár ljóðabækur Einars Más Guðmundssonar komu út 1980 og 1981 og vöktu geysimikla athygli. Tónninn var nýstárlegur, yrkisefnin óvenjuleg, skáldinu mikið niðri fyrir. Hér eru þessar sögufrægu bækur þrjár saman í einni, í tilefni þess að liðnir eru fjórir áratugir síðan skáldið Einar þusti fram á sjónarsviðið – og ljóðin eru enn fersk, fyndin og forvitnileg.", long_description: "", page_count: 142, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 5, book_author_id: nil},
  {source_id: 15424, pre_title: "", title: "Neihyrningurinn", post_title: "", slug: "neihyrningurinn-15424", description: "Krúttudúllubossalegur einhyrningur kemur í heiminn í Hjartaskógi. Allir snúast í kringum hann og fóðra stanslaust á sykruðum lukkusmára. En litla krílið hagar sér samt hreint ekki einhyrningslega! Hann segir bara: NEI!. Fjölskylda hans fer því fljótlega að kalla hann NEIhyrning. Mjög fyndin og skemmtileg bók, ein allra vinsælasta barnabókin í Þýskalandi.", long_description: "", page_count: 40, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 3, book_author_id: nil},
  {source_id: 15426, pre_title: "Ferðin á heimsenda", title: "Týnda barnið", post_title: "", slug: "tynda-barnid-15426", description: "Húgó og Alex ætla sér að finna vorið þótt stundum gangi allt á afturfótunum. Nú eru þau á nýjum slóðum og kynnast meðal annars gömlum karli með unglingaveiki, risaskordýrum og dularfullri konu sem býr yfir leyndarmáli úr fortíð Húgós. Þessi bráðfyndna fantasía fyrir 8–12 ára lesendur er framhald bókarinnar >>>Leitin að vorinu<<< sem kom út í fyrra.", long_description: "", page_count: 188, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 3, book_author_id: nil},
  {source_id: 15427, pre_title: "", title: "Hetja", post_title: "", slug: "hetja-15427", description: "Hetja og Björg eru bestu vinkonur og vilja helst alltaf vera saman. Þess vegna verður Hetja skelfingu lostin þegar svarta hyldýpið flytur hana á brott – og Björg gráti nær þegar hún finnur vinkonu sína hvergi í haganum. >>>Hetja<<< er fyrsta bók leikkonunnar og hestakonunnar Bjarkar Jakobsdóttur, æsispennandi saga um vináttu stúlku og hryssu og baráttu þeirra fyrir að finna hvor aðra aftur.", long_description: "", page_count: 219, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 3, book_author_id: nil},
  {source_id: 15428, pre_title: "", title: "Hulduheimar", post_title: "", slug: "hulduheimar-15428", description: "Tvær glænýjar og geysispennandi sögur um vinkonurnar Evu, Sólrúnu og Jasmín og ævintýri þeirra í Hulduheimum. Í >>>Sápukúlutindi<<< snýr Naðra drottning aftur og leggur álög á vesalings Teit konung og í >>>Sykursæta bakaríinu<<< reyna stormálfarnir að spilla árlegri kökukeppni. Skemmtilegar og ríkulega myndskreyttar léttlestrarbækur sem eiga marga aðdáendur.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 3, book_author_id: nil},
  {source_id: 15429, pre_title: "", title: "Sápukúlutindur", post_title: "", slug: "sapukulutindur-15429", description: "", long_description: "", page_count: 123, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 3, book_author_id: nil},
  {source_id: 15430, pre_title: "", title: "Sykursæta bakaríið", post_title: "", slug: "sykursaeta-bakariid-15430", description: "", long_description: "", page_count: 126, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 3, book_author_id: nil},
  {source_id: 15431, pre_title: "", title: "Kalli breytist í grameðlu", post_title: "", slug: "kalli-breytist-i-gramedlu-15431", description: "Kalli McGuffin er ofurvenjulegur strákur með óvenjulega krafta: Þegar hann verður kvíðinn breytist hann í dýr – sem kemur honum í alls konar klípur. En sem betur fer á Kalli ráðsnjalla vini sem reynast honum vel. Þetta er sjálfstætt framhald sögunnar >>>Kalli breytist í kjúkling<<< sem fékk krakka á aldrinum 6–10 ára til að grenja úr hlátri í fyrra.", long_description: "", page_count: 295, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 3, book_author_id: nil},
  {source_id: 15432, pre_title: "", title: "Artemis Fowl", post_title: "", slug: "artemis-fowl-15432", description: "Artemis Fowl er afburðagreindur og bráðsnjall tólf ára glæpamaður. Hann rænir Holly Short til að komast yfir gullsjóð álfanna en hún er bara ekki sátt við að láta ræna sér. Og álfarnir eiga alls konar vopn og græjur og eru sannarlega engin lömb að leika sér við. Fyrsta bókin í hinum geysivinsæla flokki um Artemis Fowl.", long_description: "", page_count: 280, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 3, book_author_id: nil},
  {source_id: 15433, pre_title: "", title: "Heillaspor – gildin okkar", post_title: "", slug: "heillaspor-gildin-okkar-15433", description: "Heilræðabækur Gunnars Hersveins hafa notið mikilla vinsælda og dregið fram það sem skiptir raunverulega máli. >>>Heillaspor – gildin okkar<<< fjallar um gildin sem leggja grunn að farsælu lífi fyrir ungt fólk og fjölskyldur þess. Fallegar myndir og hugarljós hjálpa lesandanum að finna svörin til að stíga heillaspor í lífinu.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 6, book_author_id: nil},
  {source_id: 15434, pre_title: "", title: "HUNDMANN – Taumlaus", post_title: "", slug: "hundmann-taumlaus-15434", description: "Bækurnar um HUNDMANN hafa slegið í gegn um allan heim og hafa selst í tugum milljóna eintaka. Dav Pilkey, sem einnig samdi bækurnar um Kaptein Ofurbrók, fer hér á kostum. Húrrandi grín og vitleysisgangur, en oft ansi gott og ristir dýpra en við fyrstu sýn. Þetta er bók sem krakkar elska að lesa aftur og aftur.", long_description: "", page_count: 220, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, category_id: 3, book_author_id: nil},
  {source_id: 15435, pre_title: "", title: "Danskvæði um söngfugla og slöngur", post_title: "", slug: "danskvaedi-um-songfugla-og-slongur-15435", description: "Tíundu Hungurleikarnir eru að hefjast í Panem. Hinn átján ára Kóríolanus Snow býr sig undir hlutverk sem getur gjörbreytt framtíð hans. Hann hefur fengið það niðurlægjandi verkefni að leiðsegja stúlkunni úr tólfta umdæmi. En örlög þeirra eru samtvinnuð og inni á leikvanginum verður barist til síðasta blóðdropa. Hér er forsaga metsölubókanna um Hungurleikana sögð.", long_description: "", page_count: 569, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 7, book_author_id: nil},
  {source_id: 15436, pre_title: "", title: "Gata mæðranna", post_title: "", slug: "gata-maedranna-15436", description: "Marín er á leið út í lífið eftir stúdentspróf, óviss um næstu skref. Leit að leiguherbergi leiðir hana hús úr húsi, frá einni konu til annarrar, og allar eiga þær sögur og drauma. >>>Gata mæðranna<<< gerist á sjöunda áratugnum og hlutverk kynjanna eru skýr; heima í götunni ráða konurnar ríkjum. Hárbeittur húmor og innsæi í grípandi sögu um leyndarmál og ósögð orð, ástleysi og ást.", long_description: "", page_count: 243, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 2, book_author_id: nil},
  {source_id: 15437, pre_title: "", title: "Aprílsólarkuldi", post_title: "", slug: "aprilsolarkuldi-15437", description: "Þegar Elísabet Jökulsdóttir býður lesendum í innra ferðalag gildir miðinn alla leið. Hér er lýst föðurmissi Védísar, skólastúlku sem er einstæð móðir; ástinni sem kemur næstum jafn óvænt og ferlinu inn í sjúkdóm, öllu í einni augnablikseilífð. Reykjavík fær á sig sérstakan blæ með lýsingum í ekta Elísabetarstíl sem hér er djúptær, litaður flæði og frelsi ljóðsins – hvergi skortir skarpskyggni né húmor.", long_description: "", page_count: 143, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 2, book_author_id: nil},
  {source_id: 15438, pre_title: "", title: "Blóðrauður sjór", post_title: "", slug: "blodraudur-sjor-15438", description: "Þegar heildsalinn Flosi kemur heim í kvöldmat er Guðrún kona hans horfin og á borðinu er krafa um að hann greiði himinhátt lausnargjald, annars verði hún drepin. Í örvæntingu leitar hann til Áróru sem kann að þefa uppi illa fengið fé. Mannránið gæti tengst alþjóðlegri glæpastarfsemi – eða er skýringin ef til vill mun nærtækari? Hröð og æsispennandi saga með óvæntum vendingum.", long_description: "", page_count: 314, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 2, book_author_id: nil},
  {source_id: 15439, pre_title: "", title: "Dauði skógar", post_title: "", slug: "daudi-skogar-15439", description: "Það hefur rignt linnulaust í marga daga þegar skógurinn rennur niður hlíðina og setur allt af stað í lífi landeigandans og fjölskylduföðurins Magnúsar. Litlu seinna koma sprengjurnar í ljós. Launfyndin og margræð saga um það þegar hversdagurinn fer á hvolf og dauðinn ber að dyrum. Jónas Reynir hefur vakið mikla athygli fyrir skáldsögur sínar og ljóð og hlotið bæði Bókmenntaverðlaun Tómasar Guðmundssonar og Maístjörnuna.", long_description: "", page_count: 180, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 2, book_author_id: nil},
  {source_id: 15440, pre_title: "", title: "Uppskriftabók föður míns", post_title: "", slug: "uppskriftabok-fodur-mins-15440", description: "Julien elst upp á litlu veitingahúsi í franskri sveit. Töfraheimur eldhússins heillar hann og hann þráir ekkert heitar en að feta í fótspor listakokksins pabba síns. En faðirinn, sem burðast með sára reynslu úr fortíðinni, hefur aðrar og háleitari hugmyndir um framtíð sonarins. Ljúfsár og hjartnæm þroskasaga full af unaðslegri matargerð.", long_description: "", page_count: 203, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15441, pre_title: "", title: "Hin konan", post_title: "", slug: "hin-konan-15441", description: "Þú þykist viss um hvernig þetta endar. Þú heldur að málið snúist um afbrýðisama eiginkonu. Þú heldur að hún hugsi ekki um annað en nýju konuna, þessa ungu og fallegu. Þú heldur þig hafa náð samhenginu í ástarþríhyrningnum. Eða hvað? Hvað gera konur ekki í nafni ástarinnar?", long_description: "", page_count: 420, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15442, pre_title: "", title: "Blekkingaleikur", post_title: "", slug: "blekkingaleikur-15442", description: "Martin Benner lögmaður býr í Stokkhólmi og gengur flest í haginn. En þegar hann kaupir hlut látins vinar síns í antíkverslun í New York fara óþægilegir atburðir að gerast í kringum hann og fólk hverfur – dularfullur félagsskapur virðist vera á hælum hans og vilja honum illt. Svöl og harðsoðin spennusaga, uppfull af snjöllum ráðgátum og villandi vísbendingum.", long_description: "", page_count: 432, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15443, pre_title: "", title: "Snerting hins illa", post_title: "", slug: "snerting-hins-illa-15443", description: "Fyrir >>>Engla Hammúrabís<<< hlaut Seeck Finnsku glæpasagnaverðlaunin. Hér vinna Annika Lehto og Daniel Kuisma aftur saman og nú við að rekja slóð finnska diplómatans Westerlunds. Brátt uppgötva þau að sannleikurinn er enn hræðilegri en nokkurn hefði grunað.", long_description: "", page_count: 416, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15444, pre_title: "", title: "Eftir endalokin", post_title: "", slug: "eftir-endalokin-15444", description: "Max og Pip eru samhent hjón, bestu vinir, elskendur – ekkert virðist hagga þeim. En þegar sonur þeirra veikist segja læknarnir að þau verði að taka ákvörðun. Í fyrsta skipti eru þau ekki sammála ... Einlæg frásögnin lýsir leiðinni frá nístandi sorg og söknuði til sáttar. Þegar einar dyr lokast opnast stundum aðrar. Áhrifamikil og afar persónuleg saga.", long_description: "", page_count: 416, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15445, pre_title: "", title: "Tvö líf Lydiu Bird", post_title: "", slug: "tvo-lif-lydiu-bird-15445", description: "Lydia og Freddie hafa verið óaðskiljanleg og ástfangin frá unglingsárum en á 28 ára afmælisdegi Lydiu lendir Freddie í slysi og deyr. Áfallið er ólýsanlegt og eina huggun Lydiu er að í svefni hittir hún Freddie og allt er sem fyrr – en í vöku þarf hún að takast á við sorgina og nýjar aðstæður. Heillandi og rómantísk saga um krossgötur lífsins, erfiðleika, ást og hamingjuleit.", long_description: "", page_count: 480, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15446, pre_title: "", title: "Á byrjunarreit", post_title: "", slug: "a-byrjunarreit-15446", description: "Þegar búið er að veita hörkutólinu Jack Reacher orðu fyrir sérstaklega lofsverða breytni í þágu lands og þjóðar er hann sendur í endurmenntun. Í bekknum eru tveir aðrir nemendur, annar frá FBI og hinn frá CIA. Verkefni þeirra er að hafa uppi á nafnlausum Bandaríkjamanni og því sem hann er að selja fyrir hundrað milljónir dala. Hörkuspennandi saga um vel falið leyndarmál.", long_description: "", page_count: 416, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15447, pre_title: "", title: "Stormboði", post_title: "", slug: "stormbodi-15447", description: "Karen Eiken Hornby er kölluð til Noorö, nyrstu eyjar Doggerlands, til að stýra rannsókn á voveiflegum dauða aldraðs manns. Þegar annað morð er framið fer hana að gruna að málið tengist ekki bara gömlum ættarerjum, heldur einnig ættingjum hennar sjálfrar .... Maria Adolfsson hefur slegið í gegn með glæpasögum sínum sem gerast á ímynduðum eyjaklasa í Norðursjónum.", long_description: "", page_count: 430, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15448, pre_title: "", title: "Gegnum vötn, gegnum eld", post_title: "", slug: "gegnum-votn-gegnum-eld-15448", description: "Tekla er ungur og eldklár læknir á bráðadeild – en hún á sér leyndarmál. Þegar stórbruni verður í háhýsi í borginni bjargar hún lífi manns sem enginn veit hver er. Grunsemdir vakna um hryðjuverk og lögreglan vaktar rænulausan sjúklinginn. Er hann hugsanlega maður sem Tekla þekkir? Fantagóð spennusaga, sú fyrsta um bráðalækninn Teklu Berg í Stokkhólmi.", long_description: "", page_count: 457, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15449, pre_title: "", title: "Eplamaðurinn", post_title: "", slug: "eplamadurinn-15449", description: "Tíu ára gamall drengur hverfur sporlaust úr skólanum. Vísbendingarnar hrannast upp en virðast benda hver í sína áttina. Uppgjafahermaður finnst látinn, leynileg skilaboð uppgötvast í herbergi drengsins og móðir hans virðist hafa eitthvað að fela. Og hver er Eplamaðurinn sem krakkarnir tala um? Ný og æsispennandi saga eftir höfund >>>Líkblómsins<<<.", long_description: "", page_count: 320, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15450, pre_title: "", title: "Silkiormurinn", post_title: "", slug: "silkiormurinn-15450", description: "Rithöfundurinn Owen Quine hverfur eftir að hafa nýlokið við afar eldfima sögu sem mun leggja líf margra í rúst. Þegar afskræmt lík Quines finnst við ógnvekjandi aðstæður fer allt á fleygiferð. Cormoran Strike, einkaspæjarinn mikilúðlegi, verður að hafa sig allan við að leysa úr öllum flækjunum.", long_description: "", page_count: 619, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15451, pre_title: "", title: "Kóngsríkið", post_title: "", slug: "kongsrikid-15451", description: "Bræðurnir Roy og Carl alast upp í norsku sveitaþorpi þar sem Carl verður fyrir skelfilegu ofbeldi og flýr á endanum fortíð sína. Tuttugu árum síðar birtist hann með háfleygar áætlanir. Gamlar væringar og fólskuverk leita upp á yfirborðið og Roy þarf að velja á milli bróður síns og framtíðardraumanna. Enginn stenst Jo Nesbø snúning þegar kemur að óvæntum fléttum.", long_description: "", page_count: 576, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 12, book_author_id: nil},
  {source_id: 15452, pre_title: "", title: "Við skjótum títuprjónum", post_title: "", slug: "vid-skjotum-tituprjonum-15452", description: "Hallgrímur Helgason kann þá list að hræra upp í fólki, brýna það, um leið og hann bendir á mótsagnirnar, tvöfeldnina og tilgangsleysið allt í kringum okkur. Ljóðabálkurinn er ortur á árunum 2016–2020 og talar beint inn í samtímann. Púlsinn er tekinn á líðan þjóðar sem hrósar sínu lífshappi en útvistar erfiðum málum með læktakkanum.", long_description: "", page_count: 63, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 5, book_author_id: nil},
  {source_id: 15453, pre_title: "", title: "Árhringur", post_title: "Ljóðræna dagsins", slug: "arhringur-15453", description: "Heilluð, steinrunnin,\r\nheld ég á djúpið.\r\nKlyfjuð ósýnilegri\r\nbyrði dagsins.\r\n\r\nBjörg Björnsdóttir yrkir hér um hina eilífu hringrás árstíðanna, hversdaginn og  náttúruna þannig að hinn ytri heimur og innri kallast á í fallegri ljóðrænu. Árhringur er fyrsta ljóðabók Bjargar", long_description: "", page_count: 40, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 5, book_author_id: nil},
  {source_id: 15454, pre_title: "", title: "Fjölskyldulíf á jörðinni", post_title: "", slug: "fjolskyldulif-a-jordinni-15454", description: "Ljóðabókin >>>Fjölskyldulíf á jörðinni<<< er áttunda bók Dags Hjartarsonar. Hér er ort af listfengi um hið hversdagslega í lífinu, allt það sem færir hamingjuna. Ljóðin eru persónuleg, tær og sterk í einfaldleika sínum, en skilja eftir ljúfsára kennd um fallvaltleika alls sem er.", long_description: "", page_count: 56, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 5, book_author_id: nil},
  {source_id: 15455, pre_title: "", title: "Konan sem datt upp stigann – saga af kulnun", post_title: "", slug: "konan-sem-datt-upp-stigann-saga-af-kulnun-15455", description: "Inga Dagný Eydal segir hér sögu sína en hún hefur í nokkur ár glímt við afleiðingar þess að hafa veikst af of mikilli streitu. Þetta er saga konu sem tekst á við kulnun af æðruleysi en kemur líka oft auga á broslegu hliðarnar. Hún lítur gagnrýnum augum til fortíðar um leið og hún horfir fram á veginn í leit að lausnum og bata.", long_description: "", page_count: 144, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 4, book_author_id: nil},
  {source_id: 15456, pre_title: "", title: "Lifandi mál lifandi manna", post_title: "- um esperantotímabil Þórbergs Þórðarsonar", slug: "lifandi-mal-lifandi-manna-15456", description: "Alþjóðamálið esperanto var í huga Þórbergs Þórðarsonar það tæki sem gat bjargað menningu smáþjóðanna og um leið sameinað allar þjóðir heimsins í eitt ríki. Hér er safnað saman öllu sem meistari Þórbergur ritaði um esperanto og á esperanto og birtist margt af því hér á íslensku í fyrsta sinn í þýðingu Kristjáns Eiríkssonar.", long_description: "", page_count: 415, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 4, book_author_id: nil},
  {source_id: 15457, pre_title: "", title: "Guðrúnarkviða", post_title: "", slug: "gudrunarkvida-15457", description: "Þú ert stödd í jarðarför.\r\nÞinni eigin jarðarför og ritningarlestur er að hefjast.\r\n\r\nLjóðsaga um konu sem rankar við sér í kistu í eigin jarðarför. Í huga hennar koma svipleiftur úr liðinni ævi, en kirkjugestir fá líka orðið í þessari frumlegu kviðu eftir verðlaunaskáldið Eyrúnu Ósk Jónsdóttur.", long_description: "", page_count: 104, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 5, book_author_id: nil},
  {source_id: 15458, pre_title: "", title: "Vatnaleiðin", post_title: "", slug: "vatnaleidin-15458", description: "Árið 2009 dvaldi Óskar Árni Óskarsson um sex mánaða skeið sem gestalistamaður í Vatnasafninu í Stykkishólmi. Hann hélt dagbók flesta daga sem hér birtist nú á prenti í endurskoðaðri útgáfu. Bókin hefur að geyma lýsingar á því sem fyrir augu bar þessa mánuði, hugleiðingar og ljóð og ljósmyndir sem Einar Falur Ingólfsson tók á vordögum 2018.", long_description: "", page_count: 120, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 9, book_author_id: nil},
  {source_id: 15459, pre_title: "", title: "Bubbi Morthens – ferillinn í fjörutíu ár", post_title: "", slug: "bubbi-morthens-ferillinn-i-fjorutiu-ar-15459", description: "Bubbi Morthens hefur verið þjóðargersemi frá því hann kvaddi sér hljóðs með Ísbjarnarblús árið 1980 og markaði þar með nýtt upphaf í íslenskri dægurtónlist. Árni Matthíasson rekur hér ævintýralegan tónlistarferil hans og byggir á samtímaheimildum og viðtölum við fjölda fólks – þar á meðal Bubba sjálfan. Sagan er sögð í beinskeyttum texta og aragrúa ljósmynda.", long_description: "", page_count: 272, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 4, book_author_id: nil},
  {source_id: 15460, pre_title: "", title: "1900 og eitthvað", post_title: "", slug: "1900-og-eitthvad-15460", description: "Fyrsta ljóðabók Ragnheiðar Lárusdóttur hlaut Bókmenntaverðlaun Tómasar Guðmundssonar árið 2020.\r\n„1900 og eitthvað er heillandi uppvaxtarsaga prestsdóttur að vestan ... hófstillt verk um stórfengleika hins smáa og áhrifamátt hversdagsleikans.“\r\n\tÚr umsögn dómnefndar", long_description: "", page_count: 52, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 5, book_author_id: nil},
  {source_id: 15461, pre_title: "", title: "Hundalíf með Theobald", post_title: "", slug: "hundalif-med-theobald-15461", description: "Í daglegum gönguferðum ræða ungur hundur og gamall maður um lífið og tilveruna. Gamli maðurinn heitir Þráinn og sambýlishundur hans er franski bolabíturinn Theobald. Þetta eru örsögur og örstutt samtöl þar sem blandast saman gaman og alvara – vangaveltur um það sem skiptir máli í lífinu eða glás af dýrmætum augnablikum, eins og Theobald segir.", long_description: "", page_count: 184, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 9, book_author_id: nil},
  {source_id: 15462, pre_title: "", title: "Agathe", post_title: "", slug: "agathe-15462", description: "Agathe gengur inn á læknastofu lífsþreytts geðlæknis í París á fimmta áratug liðinnar aldar - og allt breytist.\r\nÁhrifamikil saga um meginþætti mannlegrar tilvistar. \r\n&#9733;&#9733;&#9733;&#9733;&#9733;\r\n„Snjöll og skörp saga um vanda þess að ná alvöru sambandi við annað fólk. “\r\n\tIndependent", long_description: "", page_count: 132, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 12, book_author_id: nil},
  {source_id: 15463, pre_title: "", title: "Þerapistinn", post_title: "", slug: "therapistinn-15463", description: "Sara býr sig undir notalega helgi á meðan eiginmaðurinn skreppur í fjallakofa með vinum. En hann mætir aldrei í fjallakofann. Er hann lífs eða liðinn? Þessi fyrsta bók höfundar var meiriháttar metsölubók í heimalandinu og er væntanleg á yfir 30 tungumálum. „Ráðgátan er dularfull og lausnin blasir engan veginn við.“ – Kolbrún Bergþórsdóttir.", long_description: "", page_count: 368, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 12, book_author_id: nil},
  {source_id: 15464, pre_title: "", title: "Arfur Stiegs Larsson", post_title: "Lykillinn að morðinu á Olof Palme", slug: "arfur-stiegs-larsson-15464", description: "Stieg Larsson, þekktasti glæpasagnahöfundur heims, rannsakaði morðið á Olof Palme.\r\nÞessi bók er einstök lýsing á raunverulegum atburðum, og geymir auk þess fjölda áður óbirtra skrifa Stiegs sem kallast á við Millennium-þríleik hans. \r\n„Heillandi úttekt og sannfærandi frásögn.\"\r\nWall Street Journal", long_description: "", page_count: 439, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 4, book_author_id: nil},
  {source_id: 15465, pre_title: "", title: "Grikkur", post_title: "", slug: "grikkur-15465", description: "Myndlistarmaður á áttræðisaldri deilir fyrrum æskuheimili sínu í fáeina sólarhringa með fjögurra ára dóttursyni á meðan einkadóttir hans og tengdasonur fara í ráðstefnuferð. \r\n„Virkilega góður skemmtilestur sem skilur heilmikið eftir sig ... bráðsnjöll og vel skrifuð ... bæði kómísk og drungaleg ...“ RSS, Lestrarklefinn. \r\n* * * *, RB Mbl.", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 12, book_author_id: nil},
  {source_id: 15466, pre_title: "", title: "Hæstiréttur í hundrað ár", post_title: "Ritgerðir", slug: "haestirettur-i-hundrad-ar-15466", description: "Í ár er liðin öld frá því að Hæstiréttur Íslands tók til starfa árið 1920. Í riti þessu sem hefur að geyma 18 ritgerðir eftir 22 höfunda er fjallað um valda málaflokka og réttarsvið sem hafa verið til umfjöllunar á þeim hundrað árum sem liðin eru frá stofnun réttarins og hann hefur komið að því að móta.", long_description: "", page_count: 556, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 38, category_id: 4, book_author_id: nil},
  {source_id: 15467, pre_title: "", title: "Þetta voru bestu ár ævi minnar, enda man ég ekkert eftir þeim", post_title: "", slug: "thetta-voru-bestu-ar-aevi-minnar-enda-man-eg-ekkert-eftir-theim-15467", description: "„Afhverju þessi sóun á pappír?“ \r\nÞannig hljómaði niðurlagið á fyrstu gagnrýninni sem ég fékk á ljóðabókina mína,  skrifar höfundur í eftirmála þessa ljóðasafns sem hefur að geyma ljóðabækur hans þrjár;  >>>Með byssuleyfi á eilífðina<<< (1988), >>>Úr þotuhreyflum guða<<< (1989) og >>>Hún spurði hvað ég tæki með mér á eyðieyju<<< (1993) auk nokkurra áður óbirtra ljóða.", long_description: "", page_count: 208, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 5, book_author_id: nil},
  {source_id: 15468, pre_title: "", title: "Lygalíf fullorðinna", post_title: "", slug: "lygalif-fullordinna-15468", description: "Þroskasaga unglingsstúlku sem lendir í sársaukafullri eldskírn á leið sinni inn í heim fullorðinna. Fyrsta skáldsaga Elenu Ferrante síðan Napólí-fjórleikurinn sló í gegn. Bókin hefur hlotið frábæra dóma, hér heima og um veröld víða. \r\n„[Ferrante] er mikill rithöfundur en hún er lesendavæn og höfðar til stórs hóps. Þetta eru alvöru bókmenntir.“– SDM, Kiljunni.", long_description: "", page_count: 368, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, category_id: 12, book_author_id: nil},
  {source_id: 15469, pre_title: "", title: "Ættarfylgjan", post_title: "", slug: "aettarfylgjan-15469", description: "Fjölskrúðug ættarsaga um Toimi-fjölskylduna, skrifuð af sprúðlandi frásagnargleði en tekst um leið á við dýpri spurningar um innra líf, svo að sorg og gleði vega salt í áhrifamikilli sögu.\r\nÆttarfylgjan sló rækilega í gegn árið 2019, og var tilnefnd til allra helstu bókmenntaverðlauna Svía.", long_description: "", page_count: 478, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 12, book_author_id: nil},
  {source_id: 15594, pre_title: "", title: "Kúgun kvenna", post_title: "", slug: "kugun-kvenna-15594", description: "John Stuart Mill gerði sér vel ljóst að jafnrétti fengist ekki með lagabreytingum einum heldur þyrfti að vinda ofan af aldalöngum bábiljum og kynbundnum yfirráðum sem hefðu komið í veg fyrir að konur nytu sín til jafns við karla. >>>Kúgun kvenna<<< er mikilvægt innlegg í jafnréttisbaráttu enn þann dag í dag – rúmum 150 árum eftir að verkið kom út fyrst.", long_description: "", page_count: 384, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 38, category_id: 4, book_author_id: nil},
  {source_id: 15470, pre_title: "", title: "Þögla stúlkan", post_title: "", slug: "thogla-stulkan-15470", description: "Heil fjölskylda er myrt á fólskulegan hátt á heimili sínu. Eina vitnið er hin tíu ára gamla Nicole.\r\nBækur Hjorths & Rosenfeldts um sálfræðinginn Sebastian Bergman hafa farið sannkallaða sigurför um heiminn.", long_description: "", page_count: 571, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 12, book_author_id: nil},
  {source_id: 15471, pre_title: "", title: "Tíbrá", post_title: "", slug: "tibra-15471", description: "Fjórir ungir menn fara saman á veiðar – misjafnlega vanir skotvopnum. Hvað gæti farið úrskeiðis? \r\nHörkuspennandi en líka skemmtisaga um glæp. Ein af metsölubókum sumarsins 2020.\r\n\"Fyndinn, frjór og snjall.\"\r\nVikan", long_description: "", page_count: 295, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 2, book_author_id: nil},
  {source_id: 15472, pre_title: "", title: "107 Reykjavík", post_title: "Skemmtisaga fyrir lengra komna", slug: "107-reykjavik-15472", description: "Þegar Stefanía Brown-Huntington skilur við breska jarlinn og flytur aftur til Íslands ríður á miklu að vinkvennaklíku Hallgerðar takist að ná henni til sín. \r\nÆrslafengin atburðarás um reykvíska samtíð þar sem raunverulegar persónur sem ímyndaðar mæta til leiks í fyndinni og hárbeittri háðsádeilu.", long_description: "", page_count: 303, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 2, book_author_id: nil},
  {source_id: 15473, pre_title: "", title: "Strendingar", post_title: "fjölskyldulíf í sjö töktum", slug: "strendingar-15473", description: "Skemmtileg og áhrifamikil skáldsaga um hálft ár í lífi fjölskyldu, þar sem allir sjö hugarheimar hennar fá rödd. Þannig fléttast saman innra líf og ytri atburðir í marglaga og minnisstæðri sögu.  \r\nYrsa Þöll Gylfadóttir vakti mikla athygli fyrir síðustu skáldsögu sína, Móðurlífið, blönduð tækni.", long_description: "", page_count: 264, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 2, book_author_id: nil},
  {source_id: 15474, pre_title: "", title: "Berskjaldaður", post_title: "Barátta Einars Þórs fyrir lífi og ást", slug: "berskjaldadur-15474", description: "Einar Þór Jónsson vakti þjóðarathygli fyrir skýra en hógværa framgöngu sem talsmaður Geðhjálpar. Fáa grunaði þó að þessi látlausi og geðþekki maður ætti sér magnaða lífssögu að baki. \r\nGunnhildur Arna Gunnarsdóttir skrifar þessa áhrifamiklu  baráttusögu Einars Þórs Jónssonar af innsæi og næmi.", long_description: "", page_count: 381, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 9, book_author_id: nil},
  {source_id: 15746, pre_title: "", title: "Birtingaljóð og laust mál", post_title: "", slug: "birtingaljod-og-laust-mal-15746", description: "Bókin >>>Birtingaljóð<<< og laust mál geymir kveðskap og ritgerðir eftir feðginin Sigurð Ágústsson tónskáld, Ásthildi Sigurðardóttur húsfreyju í Birtingaholti og Sigurfinn Sigurðsson skrifstofumann á Selfossi. Í upphafi bókar er ritsmíð Ragnheiðar Guðnýjar Magnúsdóttur um Birtingaholt. Af öðrum greinum ber hæst samantekt Sigurðar um kórastarf í Hreppum. Eftir Ástríði eru skrif um vinnukonuna Kaju og Móeiði Skúladóttur. Þá eru í bókinni bernskuminningar Sigurfinns sem varð vegna berkla að liggja rúmfastur þrjú löng æskuár.", long_description: "", page_count: 232, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 39, category_id: 4, book_author_id: nil},
  {source_id: 15476, pre_title: "", title: "Herra Hnetusmjör", post_title: "- hingað til -", slug: "herra-hnetusmjor-15476", description: "Herra Hnetusmjör, Árni Páll Árnason, hefur verið hvirfilbylur í íslensku tónlistarlífi. En lífsganga hans hefur verið sannkölluð rússibanareið.\r\nSóli Hólm skrifar af blússandi krafti en sýnir líka strákinn á bak við ímyndina í einlægri og stórskemmtilegri bók.", long_description: "", page_count: 238, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 9, book_author_id: nil},
  {source_id: 15477, pre_title: "", title: "Háspenna lífshætta á Spáni", post_title: "", slug: "haspenna-lifshaetta-a-spani-15477", description: "Systkinin Sóley og Ari eru á leið í langþráð frí til Spánar með foreldrum sínum. En það sem átti að vera þægilegt frí til að hlaða batteríin breytist snarlega þegar skuggalegir menn fara að birtast í kringum þau.\r\nSjálfstætt framhald metsölubókarinnar >>>Friðbergur forseti<<<.", long_description: "", page_count: 226, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 7, book_author_id: nil},
  {source_id: 15478, pre_title: "", title: "Huldugáttin", post_title: "", slug: "huldugattin-15478", description: "Hver er þessi dularfulla kona sem reynir að nálgast William? Og hver fjárinn er huldugátt?\r\nWilliam og Iscia standa frammi fyrir myrkum öflum og saman leggja þau í hættuför til að komast að hinu sanna. \r\n>>>Huldugáttin<<< er önnur bókin um William Wenton og er æsispennandi, hugmyndarík og skemmtileg.", long_description: "", page_count: 247, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 7, book_author_id: nil},
  {source_id: 15479, pre_title: "", title: "Fíasól og furðusaga", post_title: "um krakka með kött í maga", slug: "fiasol-og-furdusaga-15479", description: "Sjá! Boða ég mikinn og frábæran fögnuð! \r\nFædd er nú stelpa kröftug og mögnuð!\r\nSprenghlægilegt kvæði um fæðingu Fíusólar. En undirtónninn er hyldjúpur og tilfinningaþrunginn þar sem Pippa, systir Fíusólar, glímir við höfnun og óöryggi þegar tilkynnt er um fjölgun í Grænalundi í Grasabæ.", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 3, book_author_id: nil},
  {source_id: 15480, pre_title: "", title: "Lára og Ljónsi", post_title: "", slug: "lara-og-ljonsi-15480", description: "Bækurnar um Láru eru sjálfstæðar sögur Birgittu Haukdal um töfrana í hversdegi Láru og bangsans Ljónsa. Lára kynnist ævintýraheimi leikhússins í fyrsta sinn á ógleymanlegri sýningu þar sem álfar svífa um sviðið, og svo lærir hún um stafina og hvernig þeir mynda orð. Litríkar og fallegar bækur sem krakkar hrífast af.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 1, book_author_id: nil},
  {source_id: 15481, pre_title: "", title: "Lára fer í leikhús", post_title: "", slug: "lara-fer-i-leikhus-15481", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 1, book_author_id: nil},
  {source_id: 15482, pre_title: "", title: "Lára lærir að lesa", post_title: "", slug: "lara-laerir-ad-lesa-15482", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 1, book_author_id: nil},
  {source_id: 15483, pre_title: "", title: "Smásögur heimsins V", post_title: "Evrópa", slug: "smasogur-heimsins-v-15483", description: "Fimmta og síðasta bindið í ritröðinni Smásögur heimsins. Meðal evrópskra höfunda hér má nefna Virginu Woolf, Frans Kafka, James Joyce, Vladimir Nabokov, Italo Calvino, Karen Blixen, Heinrich Böll, Tove Jansson, Lailu Stien, Michel Tournier og Kerstin Thorvall.", long_description: "", page_count: 310, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 12, book_author_id: nil},
  {source_id: 15484, pre_title: "", title: "Syngdu með Láru og Ljónsa", post_title: "", slug: "syngdu-med-laru-og-ljonsa-15484", description: "Falleg bók með tónspilara sem inniheldur fjölbreytt lög sungin af Birgittu Haukdal. Lögin henta hvort sem er í fjörugu ferðalagi eða á rólegum stundum fyrir háttinn. Krakkar geta bæði hlustað á lögin með söng Birgittu og spreytt sig á að syngja þau sjálf með undirspili. Bókin er skreytt litríkum og fallegum myndum sem gaman er að skoða.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 1, book_author_id: nil},
  {source_id: 15485, pre_title: "", title: "Guðjón Samúelsson húsameistari", post_title: "", slug: "gudjon-samuelsson-husameistari-15485", description: "Guðjón Samúelsson (1887-1950) arkitekt og húsameistari ríkisins frá 1920 til dauðadags, teiknaði margar af þekktustu byggingum landsins. Í þessari bók skoðar höfundurinn, Pétur H. Ármannson, verk og hugmyndir Guðjóns út frá sérstöðu hans sem háskólamenntaðs arkitekts sem var mótaður af straumum og stefnum í norrænni og evrópskri byggingarlist. Bókin er prýdd fjölda ljósmynda og teikninga.", long_description: "", page_count: 448, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 38, category_id: 4, book_author_id: nil},
  {source_id: 15486, pre_title: "", title: "Öll með tölu", post_title: "", slug: "oll-med-tolu-15486", description: "Í þessari skemmtilegu bók er farið á handahlaupum yfir tölurnar frá 0 til 7.500.000.000. Á hverri síðu eru ævintýralegar myndir þar sem hægt er að telja börn og fullorðna, fylgja þeim eftir og sjá hvernig líf þeirra fléttast saman. Skarpskyggnir lesendur munu líka finna þar ýmis leyndamál. >>>Öll með tölu<<< hlaut Barna- og unglingabókmenntaverðlaun Norðurlandaráðs 2019.", long_description: "", page_count: 58, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 1, book_author_id: nil},
  {source_id: 15487, pre_title: "", title: "Gunnhildur og Glói", post_title: "", slug: "gunnhildur-og-gloi-15487", description: "Suma daga finnst Gunnhildi allt ljótt og leiðinlegt. En einn slíkan dag hittir hún Glóa álfastrák á leikskólalóðinni, hann leggur geislastein í lófa hennar og sýnir henni heiminn í nýju ljósi. Sagan af Gunnhildi og Glóa kom fyrst út árið 1985 og heillaði unga sem aldna. Hér er þessi sígilda saga Guðrúnar Helgadóttur loksins komin út að nýju.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 1, book_author_id: nil},
  {source_id: 15488, pre_title: "", title: "Dimmuborgir", post_title: "", slug: "dimmuborgir-15488", description: "Bókmenntarýnirinn Elmar Arnarsson er búinn að loka sig af með stafla af jólabókum þegar honum berast óvænt nýjar upplýsingar um andlát besta vinar síns 25 árum fyrr. Elmar, sem hefur ætíð verið sannfærður um að Felix hafi verið myrtur, verður heltekinn af því að komast að sannleikanum og brátt snýst tilvera þessa hlédræga manns gjörsamlega á hvolf.", long_description: "", page_count: 266, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 2, book_author_id: nil},
  {source_id: 15489, pre_title: "", title: "Yfir bænum heima", post_title: "", slug: "yfir-baenum-heima-15489", description: "Það er vor í litlum bæ við sjóinn. Atvinna er stopul og lífið basl. En skyndilega breytist allt. Götur fyllast af hermönnum, braggar rísa og dansinn dunar. Það er komið stríð og yfir vofir ógnin: Flugvélar óvinanna. Hér segir frá þremur mæðgum og fólkinu þeirra í ólgusjó hernámsáranna; umróti, ást og lífsháska sem markar djúp spor. Viðburðarík og heillandi skáldsaga.", long_description: "", page_count: 317, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 2, book_author_id: nil},
  {source_id: 15490, pre_title: "", title: "Þagnarmúr", post_title: "", slug: "thagnarmur-15490", description: "Í kjallara í Reykjavík finnast hrottaleg leyndarmál múruð inn í vegg. Konráð er löngu hættur í lögreglunni en stöðugt með hugann við illvirki fortíðar og ákafi hans vekur spurningar: Hvers vegna sagði hann ósatt um atburði dagsins þegar faðir hans var myrtur? Hverju hefur hann þagað yfir öll þessi ár? Spennandi og átakanleg saga um ofbeldi, varnarleysi og þungbæra þögn.", long_description: "", page_count: 303, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 2, book_author_id: nil},
  {source_id: 15491, pre_title: "", title: "Atómstöðin", post_title: "", slug: "atomstodin-15491", description: "Atómstöðin er ein af umdeildustu skáldsögum Halldórs Laxness. Í henni segir frá norðanstúlkunni Uglu sem kemur í höfuðstaðinn til að læra á orgel en þar mæta henni tveir ólíkir heimar og ólík lífsgildi takast á. Þroskasaga Uglu er um leið beitt þjóðarsaga; róttæk, kímin og heimspekileg. Útgáfan er með nútímastafsetningu.", long_description: "", page_count: 187, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 2, book_author_id: nil},
  {source_id: 15492, pre_title: "", title: "Tregasteinn", post_title: "", slug: "tregasteinn-15492", description: "Kona er myrt á heimili sínu en nokkru áður hafði hún beðið Konráð, fyrrverandi lögreglumann, að finna fyrir sig barn sem hún fæddi fyrir næstum hálfri öld og lét strax frá sér. Hann neitaði bón hennar en einsetur sér nú að bæta fyrir það. Áhrifamikil og snjöll glæpasaga um skömm og örvæntingu, ákafa eftirsjá og langvarandi bergmál illra verka.", long_description: "", page_count: 306, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 2, book_author_id: nil},
  {source_id: 15493, pre_title: "", title: "Fórnarlamb 2117", post_title: "", slug: "fornarlamb-2117-15493", description: "Mynd af látinni flóttakonu á strönd Miðjarðarhafsins vekur upp fortíðardjöfla og Assad sérsveitarmaður rambar á barmi taugaáfalls. Í kjölfarið hefst taugatrekkjandi niðurtalning að hryllilegum hamförum þar sem Ghaalib, miskunnarlausi böðullinn frá Írak, heldur um stjórntaumana. Áttunda bókin um hina vinsælu Deild Q eftir glæpasagnakóng Dana.", long_description: "", page_count: 504, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 12, book_author_id: nil},
  {source_id: 15494, pre_title: "", title: "Hamlet", post_title: "", slug: "hamlet-15494", description: ">>>Hamlet<<< er stórbrotinn og sígildur harmleikur, í senn heimspekilegur og blóði drifinn, eitt allra frægasta leikrit meistara Williams Shakespeares. „Að vera eða ekki vera“– nístandi efi, svik og bágt siðferði er meðal þess sem brotið er til mergjar í þessu áhrifaríka stórvirki sem hér birtist í nýrri og einkar athyglisverðri þýðingu Þórarins Eldjárns.", long_description: "", page_count: 205, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 5, book_author_id: nil},
  {source_id: 15495, pre_title: "", title: "800-fastan", post_title: "", slug: "800-fastan-15495", description: "Hér byggir metsöluhöfundurinn Michael Mosley á nýjustu vísindarannsóknum um þyngdartap og lotubundnar föstur, blóðsykur og meltingu. Í bókinni er fjöldi girnilegra uppskrifta og leiðbeiningar um hvernig má laga þær að eigin smekk og markmiðum, auk matseðla sem auðvelda fólki að skipuleggja mataræðið.", long_description: "", page_count: 280, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 4, book_author_id: nil},
  {source_id: 15496, pre_title: "", title: "Hver ertu og hvað viltu?", post_title: "Leiðarvísir fyrir þá sem vilja komast lengra", slug: "hver-ertu-og-hvad-viltu-15496", description: "Spennandi þroskaferðalag sem dýpkar skilninginn á því hver þú ert í rauninni og hvernig þú getur breytt því sem þú vilt breyta í eigin fari. Einstæð leið til að hjálpa þér að finna hugrekki til að standa með þér sjálfum og stefna þangað sem hugur þinn stendur til. Og allt er útskýrt á mannamáli og hið flókna gert einfalt.", long_description: "", page_count: 208, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 4, book_author_id: nil},
  {source_id: 15497, pre_title: "", title: "Stjórnmál", post_title: "", slug: "stjornmal-15497", description: "Í þessari bók er fjallað með skýrum og greinargóðum hætti um nokkur grundvallarhugtök nútímastjórnmála. Markmiðið er að öðlast betri skilning á hinum pólitíska veruleika með hjálp hugtaka og kenninga stjórnmálafræðinnar. Bókin er skrifuð með almenna lesendur í huga, ekki síst ungt fólk og nemendur. Fyrir áhugafólk um stjórnmál og þjóðfélagsumræðu er bókin hvalreki.", long_description: "", page_count: 216, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 38, category_id: 4, book_author_id: nil},
  {source_id: 15498, pre_title: "", title: "Dóttir – leið mín til tveggja heimsmeistaratitla", post_title: "", slug: "dottir-leid-min-til-tveggja-heimsmeistaratitla-15498", description: "Katrín Tanja, tvöfaldur heimsmeistari í CrossFit, rekur hér leið sína til hæstu tinda. Í CrossFit fékk hún útrás fyrir óstöðvandi orku og baráttulöngun, tókst á við sigra og ósigra og náði á toppinn með því að þjálfa líkamann að þolmörkum. Bók sem veitir sýn á eina fremstu íþróttakonu heims og það sem hún hefur gert til að ná árangri og verða fyrirmynd.", long_description: "", page_count: 287, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 9, book_author_id: nil},
  {source_id: 15499, pre_title: "", title: "Prjónað á mig og mína", post_title: "", slug: "prjonad-a-mig-og-mina-15499", description: "Fjölbreyttar og fallegar prjónaflíkur fyrir konur og 1–12 ára krakka. Í bókinni eru 40 uppskriftir, m.a. af sléttprjónuðum peysum og peysum með laufamunstri og lögð er áhersla á að frágangurinn taki sem minnstan tíma. Áður hafa komið út bækurnar >>>Prjónað af ást<<< og >>>Prjónastund<<< eftir sama höfund og notið mikilla vinsælda hér á landi.", long_description: "", page_count: 193, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 11, book_author_id: nil},
  {source_id: 15891, pre_title: "", title: "Villinorn", post_title: "", slug: "villinorn-15891", description: "Fjórða og fimmta bókin í danska bókaflokknum >>>Villinorn<<< um Klöru og baráttu hennar við ill öfl í villtri náttúrunni eftir danska verðlaunahöfundinn Lene Kaaberbøl. Villinornarbækurnar hafa komið út á 17 tungumálum.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, category_id: 3, book_author_id: nil},
  {source_id: 15572, pre_title: "", title: "Hestar", post_title: "", slug: "hestar-15572", description: "Íslenski hesturinn hefur þolað margt í þúsund ára þjónustuhlutverki við kaldlynda þjóð í köldu landi. Hér stígur þarfasti þjónninn fram rétt eins og hann er og var allt frá því að fyrsta fylfulla merin steig óstyrkum fæti á íslenska strönd seint á níundu öld; laus við keppnisrembing, upphafningu og tildur.\r\nHjörleifur Hjartarson og Rán Flygenring sýndu það með bók sinni >>>Fuglar<<< að íslensk fuglafræði er annað og meira en upptalning á þurrum staðreyndum. Nú er komið að hrossunum.", long_description: "", page_count: 184, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, category_id: 6, book_author_id: nil},
  {source_id: 15896, pre_title: "", title: "Rannsóknir í heimspeki", post_title: "", slug: "rannsoknir-i-heimspeki-15896", description: "Rannsóknir í heimspeki er talið eitt allra merkasta heimspekirit tuttugustu aldar. Ritið kom fyrst út árið 1953 að Ludwig Wittgenstein, höfundi þess, látnum. Wittgenstein var ástríðufullur hugsuður gæddur miklum persónutöfrum en var mörgum samtíðarmönnum hálfgerð ráðgáta. Djúpstæð greining hans á tungumálinu og tengslum þess við mannshugann og umheiminn er leiðarstefið í þessu höfuðriti hans.", long_description: "", page_count: 260, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, category_id: 4, book_author_id: nil},
  {source_id: 15835, pre_title: "", title: "Two Lands, One Poet", post_title: "The Reflections of Stephan G. Stephansson Through Poetry", slug: "two-lands-one-poet-15835", description: "Tvímála útgáfa (íslenska og enska) á safni ljóða eftir Stephan G. Stephansson, eitt af lykilskáldum íslenskra og íslensk-kanadískra bókmennta. Enski hluti bókarinnar geymir sögulegt yfirlit yfir glímu þýðenda beggja vegna hafs við ljóð Klettaskáldsins, meðal annars þeirra Bernards Scudders og Jakobínu Johnson. Jón Atli Benediktsson, rektor Háskóla Íslands, ritar formála.", long_description: "", page_count: 296, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 42, category_id: 5, book_author_id: nil},
  {source_id: 15500, pre_title: "", title: "Á fjarlægum ströndum", post_title: "Tengsl Spánar og Íslands í tímans rás. Stofnun Vigdísar Finnbogadóttur", slug: "a-fjarlaegum-strondum-15500", description: "Safn greina eftir 14 höfunda um samskipti Spánar og Íslands í tímans rás. Sagt er frá ferðum um Jakobsveginn fyrr og nú, hvalveiðum Spánverja við Íslandsstrendur, gömlum orðasöfnum, saltfisksölu, íslenskum sjálfboðaliðum í spænsku borgarastyrjöldinni, íslenskum gítarnemum, sólarlandaferðum, spænskukennslu á Íslandi, þýðingum bókmenntaverka, lántökuorðum o.fl. Einnig eru minningabrot Spánverja og Íslendinga í bókinni.", long_description: "", page_count: 250, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, category_id: 4, book_author_id: nil},
  {source_id: 15834, pre_title: "", title: "Rigmarole and Flies", post_title: "", slug: "rigmarole-and-flies-15834", description: "Rigmarole and Flies er fyrsta enska þýðingin á Þulum Theodóru Thoroddsen og Flugum Jóns Thoroddsen. Ármann Jakobsson ritar formála og Christopher Crocker, þýðandi bókarinnar, inngang. Þulur (1916) Theodóru hafa fyrir löngu fest sig í sessi í íslenskum bókmenntum og menningu. Flugur Jóns komu út árið 1922, ekki löngu fyrir ótímabært andlát hans í Kaupmannahöfn, og er fyrsta útgefna ljóðabókin á íslensku í óbundnu máli.", long_description: "", page_count: 59, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 42, category_id: 5, book_author_id: nil},
  {source_id: 15846, pre_title: "", title: "Öflugir strákar: Trúðu á sjálfan þig", post_title: "", slug: "oflugir-strakar-trudu-a-sjalfan-thig-15846", description: "Þessi skemmtilega uppsetta sjálfstyrkingarbók er byggð á hinu sívinsæla námskeiði, Öflugir strákar og fjallar um það hvernig þú getur eflt sjálfan þig. Bókin kennir þér meðal annars hvernig þú getur verið ánægðari með þig og öðlast meira sjálfstraust. Hvernig þú getur náð betri árangri í því sem þú hefur áhuga á og tekist á við mótlæti. Hvernig þú getur einbeitt þér að því sem skiptir máli og haft hugrekki til að fara út fyrir þægindarammann. Orri óstöðvandi og Magga Messi koma víða við sögu auk þess sem í bókinni er að finna ýmsar áhugaverðar frásagnir af flottum fyrirmyndum og öflugum strákum.", long_description: "", page_count: 180, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 43, category_id: 6, book_author_id: nil},
  {source_id: 15845, pre_title: "", title: "Sonur grafarans", post_title: "", slug: "sonur-grafarans-15845", description: "Sonur grafarans á margt óuppgert þegar afturgöngurnar í kirkjugarðinum taka til máls. Í myrku andrúmi ljóðanna leynast óvæntar myndir, leikandi húmor og snjallar persónugervingar. Brynjólfur Þorsteinsson vakti verðskuldaða athygli og fékk góða dóma fyrir fyrstu ljóðabókina sína. Hann fylgir henni eftir með ljóðabálk um eftirlífið og drauga.", long_description: "", page_count: 74, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, category_id: 5, book_author_id: nil},
  {source_id: 15501, pre_title: "Íslenskir ræktunarhættir", title: "Yrkja vildi eg jörð", post_title: "", slug: "yrkja-vildi-eg-jord-15501", description: "Í bókinni segir frá íslenskum jarðræktarháttum. Fjallað er um vinnubrögð og verkfæri til ræktunar fóðurs fyrir búfé, sum séríslensk en önnur erlend, löguð að hérlendum aðstæðum. Bjarni Guðmundsson, fv. prófessor við Landbúnaðarháskóla Íslands, hefur áður skrifað um þróun landbúnaðarins, má þar nefna Íslenskir sláttuhættir og Íslenskir heyskaparhættir.", long_description: "", page_count: 397, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 38, category_id: 4, book_author_id: nil},
  {source_id: 15502, pre_title: "", title: "Íslenskir vettlingar", post_title: "25 nýjar útfærslur á gömlum mynstrum", slug: "islenskir-vettlingar-15502", description: "Uppskriftirnar í þessari bók eru nýjar útfærslur á vettlingum frá 19. og 20. öld sem varðveittir eru á Heimilisiðnaðarsafninu á Blönduósi. Áhersla er lögð á að hafa úrvalið fjölbreytt, bæði í mynsturgerð og prjóntækni, svo hér geta allir fundið eitthvað við sitt hæfi. Bókin er einnig fáanleg í enskri þýðingu.", long_description: "", page_count: 272, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 11, book_author_id: nil},
  {source_id: 15844, pre_title: "", title: "Herbergi í öðrum heimi", post_title: "", slug: "herbergi-i-odrum-heimi-15844", description: "„Hjartað slær óumbeðið og viðstöðulaust frá vöggu til grafar. Ég get ekki annað en vantreyst svo fáránlegum stöðugleika.“\r\n>>>Herbergi í öðrum heimi<<< hefur að geyma sjö smásögur. María Elísabet skrifar af öryggi og innsæi um þrá fólks eftir tengslum og uppgjöri. Með hárfínu skopskyni og hugmyndaauðgi kveður nýr höfundur sér hljóðs á eftirminnanlegan hátt.", long_description: "", page_count: 212, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, category_id: 2, book_author_id: nil},
  {source_id: 15841, pre_title: "", title: "Harry Potter og leyniklefinn", post_title: "", slug: "harry-potter-og-leyniklefinn-15841", description: "Um sumarið var versti afmælisdagur Harrys Potter og hann gat ekki beðið eftir að komast aftur í Hogwartskóla. En svo fær hann viðvaranir frá einkennilegum húsálfi, Dobby, sem segir að fari hann í skólann muni hræðilegir hlutir gerast.\r\nHin stórbrotnu ævintýri Harrys Potter halda áfram.", long_description: "", page_count: 315, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 7, book_author_id: nil},
  {source_id: 15842, pre_title: "", title: "Vertu ósýnilegur", post_title: "Flóttasaga Ishmaels", slug: "vertu-osynilegur-15842", description: "Vertu ósýnilegur, flóttasaga Ishmaels, vakti gríðarlega athygli þegar hún kom fyrst út 2017. Hún hlaut Barnabókaverðlaun Reykjavíkur, Fjöruverðlaunin og Bókmenntaverðlaun starfsfólks bókaverslana.\r\n\"Skyldulesning fyrir allar manneskjur.\"\r\nSteinunn Inga Óttarsdóttir, Kbl.", long_description: "", page_count: 235, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, category_id: 7, book_author_id: nil},
  {source_id: 15843, pre_title: "", title: "Strandir 1918: Ferðalag til fortíðar", post_title: "", slug: "strandir-1918-ferdalag-til-fortidar-15843", description: "Árið 1918 var merkilegt ár í sögu þjóðarinnar. Hér er athygli beint að viðburðum og daglegu lífi á Ströndum á þessum tíma, birtar dagbækur, ferðasögur og fræðigreinar. Fjallað er um líf og störf fólks í Strandasýslu fyrir rúmri öld, á tímum sem standa okkur býsna nærri. Samt er margt við daglegt amstur fólks fyrir 100 árum framandi í samtímanum.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 45, category_id: 4, book_author_id: nil},
  {source_id: 15893, pre_title: "", title: "Villinorn 4. Blóðkindin", post_title: "", slug: "villinorn-4-blodkindin-15893", description: "", long_description: "", page_count: 182, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, category_id: 3, book_author_id: nil},
  {source_id: 15887, pre_title: "", title: "Orri óstöðvandi & Orri óstöðvandi - Hefnd glæponanna", post_title: "", slug: "orri-ostodvandi-orri-ostodvandi-hefnd-glaeponanna-15887", description: "Orri óstöðvandi er ofurhetjuútgáfan af Orra sem hann breytir sér í þegar hann þarf á hugrekki og sjálfstrausti að halda. Bækurnar eru fyndnar, skemmtilegar og sjálfstyrkjandi fyrir krakka á aldrinum 7 til 12 ára. Vignir Rafn glæðir sögurnar lífi.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 46, category_id: 3, book_author_id: nil},
  {source_id: 15856, pre_title: "", title: "Skrautleg skrúðganga", post_title: "", slug: "skrautleg-skrudganga-15856", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15857, pre_title: "", title: "Einstakur hópur", post_title: "", slug: "einstakur-hopur-15857", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15858, pre_title: "", title: "Nýr vinur", post_title: "", slug: "nyr-vinur-15858", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15859, pre_title: "", title: "Partýpúkar", post_title: "", slug: "partypukar-15859", description: "", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15860, pre_title: "", title: "Púkar á flækingi", post_title: "", slug: "pukar-a-flaekingi-15860", description: "", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15861, pre_title: "", title: "Bók 1", post_title: "", slug: "bok-1-15861", description: "", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15862, pre_title: "", title: "Bók 2", post_title: "", slug: "bok-2-15862", description: "", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15863, pre_title: "", title: "Sígildar sögur", post_title: "", slug: "sigildar-sogur-15863", description: "Sögusafn fjölskyldunnar! \r\nTímalausar sögur Disney lifa áfram í þessu heillandi sögusafni sem myndskreytt er með fallegum klassískum teikningum. Bók sem gleður alla fjölskylduna.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 48, category_id: 1, book_author_id: nil},
  {source_id: 15848, pre_title: "", title: "Dúna", post_title: "", slug: "duna-15848", description: ">>>Dúna<<< er mest selda vísindaskáldsaga allra tíma. Sagan gerist á eyðimerkurplánetunni Arrakis þar sem Páll Atreides og fjölskylda hans hafa verið svikin og örlagaríkri atburðarás hrint af stað. >>>Dúna<<< er epísk blanda af ævintýri, dulspeki, umhverfishyggju og pólitík.", long_description: "", page_count: 700, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 49, category_id: 12, book_author_id: nil},
  {source_id: 15849, pre_title: "", title: "Jólasmábækur", post_title: "", slug: "jolasmabaekur-15849", description: "Frábærar bækur í nýjum bókaflokki sem nefnist Smábækur. Skemmtileg dægradvöl fyrir smáar hendur í aðdraganda jóla. Ps. þær eru fullkomnar fyrir jólasveina!", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 1, book_author_id: nil},
  {source_id: 15850, pre_title: "", title: "Binna B Bjarna", post_title: "", slug: "binna-b-bjarna-15850", description: "Binna er snjöll og sjálfstæð stelpa sem veit hvað hún vill. Hún og besti vinur hennar, hann Jónsi, eru uppátækjasamir krakkar. Þrjár nýjar bækur eru komnar út. \r\n\r\nGóðar lestrarbækur fyrir 5 ára + með stóru letri og góðu línubili.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15851, pre_title: "", title: "Heyrðu Jónsi", post_title: "", slug: "heyrdu-jonsi-15851", description: "Jónsi er hlédrægur strákur sem þarf að takast á við alls konar áskoranir, stundum með hjálp besta vinar síns, hennar Binnu B.  Þrjár nýjar bækur eru nú komnar út.  \r\n\r\nGóðar lestrarbækur fyrir 5 ára + með stóru letri og góðu línubili.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15852, pre_title: "", title: "Stjáni og stríðnispúkarnir", post_title: "", slug: "stjani-og-stridnispukarnir-15852", description: "Stjáni fann nokkra stríðnispúka í kommóðunni sinni. Stjáni og stríðnispúkarnir lenda í ýmis konar hremmingum og ævintýrum sem stórskemmtilegt er að fylgjast með.\r\n\r\nBækurnar eru spennandi valkostur með sniðugum litmyndum og fjölbreyttum texta.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15853, pre_title: "", title: "Fróði Sóði", post_title: "", slug: "frodi-sodi-15853", description: "Viltu kynnast Fróða? Hann hefur óteljandi ósiði og fær geggjaðar hugmyndir! Fróði er heimsins mesti vandræðagemsi. Þrjár sögur í hverri bók.\r\n\r\nSkemmtilegar bækur fyrir hressa krakka sem eru að ná tökum á lestri.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15854, pre_title: "", title: "Fullkomin jólagjöf", post_title: "", slug: "fullkomin-jolagjof-15854", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15855, pre_title: "", title: "Flöskuskeytið", post_title: "", slug: "floskuskeytid-15855", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, category_id: 3, book_author_id: nil},
  {source_id: 15503, pre_title: "", title: "Six Adventure Tales from Medieval Iceland", post_title: "A Reader for First-Time Visitors", slug: "six-adventure-tales-from-medieval-iceland-15503", description: "Hér eru á ferð sex stuttar sögur frá miðöldum sem allar gerast á Austfjörðum, þýddar af Theodore M. Andersson fv. prófessor í Harvard, Stanford og Indiana og höfundi fjölmargra bóka um fornnorrænar bókmenntir. Sögurnar eru gott dæmi um fornan íslenskan frásagnarstíl sem nýtur sín vel í þessari snjöllu ensku þýðingu.", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 38, category_id: 4, book_author_id: nil},
  {source_id: 15504, pre_title: "", title: "Systkinabókin", post_title: "", slug: "systkinabokin-15504", description: "Sóla er orðin stóra systir. En það er ekki eins skemmtilegt og hún hélt. Litli bróðir er hávær og alltof lítill til að leika við. Sóla grípur til sinna ráða, leggur upp í örlagaríka ferð og ekkert verður eins og áður. Litríkar og skemmtilegar bækur sem efla skilning barna á eigin tilfinningum og annarra og hjálpa þeim að færa þær í orð.", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 1, book_author_id: nil},
  {source_id: 15505, pre_title: "Risaeðlugengið", title: "Kappsundið", post_title: "", slug: "kappsundid-15505", description: "Þegar Georg grameðla, ótrúlega óþolandi stóri bróðir Gauta, skorar á þá Sölva sagtanna í kappsund getur Gauti ekki skorast undan, þótt hann hafi heyrt að í ísköldu vatninu sé RISAVAXIÐ SÆSKRÍMSLI á svamli. Krúttleg, fræðandi og fyndin saga fyrir yngsta áhugafólkið um risaeðlur og önnur forsöguleg dýr.", long_description: "", page_count: 48, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 1, book_author_id: nil},
  {source_id: 15506, pre_title: "", title: "Spæjarastofa Lalla og Maju", post_title: "", slug: "spaejarastofa-lalla-og-maju-15506", description: "Hinar sívinsælu Ráðgátubækur henta vel fyrir krakka sem vilja æfa lesturinn því letrið er stórt og setningarnar stuttar. Í >>>Skólaráðgátunni<<< finnast falsaðir peningaseðlar í Víkurbæ og vísbendingar leiða spæjarana Lalla og Maju að grunnskólanum, og í >>>Gullráðgátunni<<< hverfa 250 kíló af gulli úr rammlæstri bankahvelfingu. Hver mínúta skiptir máli!", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15507, pre_title: "", title: "Skólaráðgátan", post_title: "", slug: "skolaradgatan-15507", description: "", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15508, pre_title: "", title: "Gullráðgátan", post_title: "", slug: "gullradgatan-15508", description: "", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15509, pre_title: "", title: "Múmínálfarnir", post_title: "", slug: "muminalfarnir-15509", description: "Sígildar söguperlur fyrir nýja kynslóð múmínálfaaðdáenda. Múmínfjölskyldan siglir út í óvissuna og lendir í stormi, hittir hina rafmögnuðu hattífatta og finnur pípuhatt galdrakarlsins sem veldur miklum usla í múmíndal. Og svo vakna þau af vetrardvala og komast að því að allir eru að undirbúa komu einhvers sem kallar sig Jólin.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15510, pre_title: "", title: "Almanak Háskóla Íslands 2021", post_title: "", slug: "almanak-haskola-islands-2021-15510", description: "Auk dagatals inniheldur almanakið margvíslegar upplýsingar, s.s. um sjávarföll og  gang himintungla, helstu fyrirbæri á himni sem frá Íslandi sjást, stjörnukort, áttavitastefnur á Íslandi og tímabelti heimsins. Einnig er hér yfirlit um hnetti himingeimsins, mælieiningar, veðurfar, stærð og mannfjölda allra sjálfstæðra ríkja o.fl. Af nýju efni má nefna grein um gríðarlega fjölgun gervitungla sem breytt gæti ásýnd himinhvolfsins. Fjallað er um dvergreikistjörnur og hvort líf sé á reikistjörnunni Mars.", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, category_id: 4, book_author_id: nil},
  {source_id: 15511, pre_title: "", title: "Sögur úr múmíndal", post_title: "", slug: "sogur-ur-mumindal-15511", description: "", long_description: "", page_count: 98, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15512, pre_title: "", title: "Jól í múmíndal", post_title: "", slug: "jol-i-mumindal-15512", description: "", long_description: "", page_count: 40, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15513, pre_title: "", title: "Handleiðsla til eflingar í starfi", post_title: "", slug: "handleidsla-til-eflingar-i-starfi-15513", description: "Í þessari bók fjalla sautján sérfræðingar úr ólíkum faghópum um handleiðslu innan velferðarþjónustu og vinnumarkaðar. Sagt er frá hugmynda- og þekkingargrunni handleiðslufræða og hlutverki handleiðara. Handleiðari hefur menntun og færni til að hjálpa fagmanni til að laða fram það besta í sjálfum sér, njóta sín í starfi og sækja sér sérhæfingu í takti við þarfir og aðstæður.", long_description: "", page_count: 258, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, category_id: 4, book_author_id: nil},
  {source_id: 15514, pre_title: "", title: "Lotta og börnin í Skarkalagötu", post_title: "", slug: "lotta-og-bornin-i-skarkalagotu-15514", description: "Þau Lotta, Jónas og Mía María hafa heillað lesendur á öllum aldri áratugum saman og nú eru sögurnar af fjörugu fjölskyldulífi systkinanna í Skarkalagötu loksins fáanlegar á ný. Hér eru tvær bækur saman í einni; >>>Börnin í Skarkalagötu<<< og >>>Lotta flytur að heiman<<<. Einstakar myndir glæða sögurnar ævintýraljóma.", long_description: "", page_count: 133, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15515, pre_title: "Múmínálfarnir – stórbók", title: "Ósýnilega barnið og aðrar sögur / Eyjan hans múmínpabba / Seint í nóvember", post_title: "", slug: "osynilega-barnid-og-adrar-sogur-eyjan-hans-muminpabba-seint-i-no-15515", description: "Tove Jansson skrifaði níu sögubækur um múmínálfana ástsælu sem loks eru allar fáanlegar á íslensku í veglegu safni stórbóka. Þetta síðasta bindi geymir heillandi sögur þar sem ýmis háski steðjar að múmíndal, bæði íbúum hans og gestkomandi verum. Skemmtilestur fyrir bæði börn og fullorðna.", long_description: "", page_count: 496, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 3, book_author_id: nil},
  {source_id: 15516, pre_title: "", title: "Hansdætur", post_title: "", slug: "hansdaetur-15516", description: "Kona sem á þrjú börn með þremur mönnum í vestfirsku sjávarplássi í byrjun tuttugustu aldar á sér ekki viðreisnar von en dóttir hennar Gratíana þráir breytta tíma og betra líf. >>>Hansdætur<<< er áhrifamikil örlagasaga um harðgerðar konur, viðkvæmar sálir, andans menn, óreiðufólk af ýmsu tagi; drauma þeirra og þrár, sorgir og sigra.", long_description: "", page_count: 342, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15517, pre_title: "", title: "Opna svæðið", post_title: "Tímaritið Birtingur og íslenskur módernismi", slug: "opna-svaedid-15517", description: "Hér er ljósi varpað á tímaritið Birting sem kom út á árunum 1955-1968 og gegndi lykilhlutverki við að innleiða módernisma í íslenskar bókmenntir og listsköpun á tímabilinu. Ritið er fyrsta bókin sem gefin er út um þetta mikilvæga tímarit. Birtingi var ætlað að þjóna sem vettvangur fyrir kynningar á nýjum alþjóðlegum straumum. Í honum fór fram endurskoðun á viðteknum hefðum og lifandi umræður um nútímabókmenntir og listir. Bókin veitir innsýn í margbrotin átök innan íslensks menningarvettvangs á umbrotatíma.", long_description: "", page_count: 440, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, category_id: 4, book_author_id: nil},
  {source_id: 15518, pre_title: "", title: "Bróðir", post_title: "", slug: "brodir-15518", description: "Í tilfinningauppnámi verður unglingurinn Skorri valdur að hræðilegu slysi. Atvikið verður að örlagaríku leyndarmáli milli hans og Tinnu, barnungrar systur hans. Mörgum árum síðar kemur það upp á milli þeirra. Í dramatísku skáldsögunni >>>Bróðir<<< er fjallað á djarfan, fyndinn og áhrifamikinn hátt um það þegar gott fólk hegðar sér eins og skepnur.", long_description: "", page_count: 292, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 2, book_author_id: nil},
  {source_id: 15519, pre_title: "", title: "Sjálfstýring", post_title: "", slug: "sjalfstyring-15519", description: "Lífið virðist blasa við hæfileikaríkri, ungri konu. Hún á góða vini, ástríka fjölskyldu og er á leiðinni í inntökupróf við virtan tónlistarskóla erlendis. En eftir að vinur hennar braut á henni í partíinu um jólin upplifir hún aðeins einkennilegan doða og tengslaleysi við sjálfa sig og sína nánustu. Sagan vann rafbókasamkeppni Forlagsins 2020.", long_description: "", page_count: 76, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 50, category_id: 2, book_author_id: nil},
  {source_id: 15520, pre_title: "", title: "Strá", post_title: "", slug: "stra-15520", description: "Fullorðin kona mætir velvild ókunnugra í „Gefins, allt gefins“, ungur maður mótmælir einn uppi á heiði og afgreiðslukona í plötubúð losar sig úr þröngum þægindahring. >>>Strá<<< bar sigur úr býtum í handritasamkeppninni Nýjar raddir 2019. Í sögunum ferðast lesendur um brothætta náttúru landsins og finna fyrir ákafri löngun til að snerta aðra manneskju.", long_description: "", page_count: 114, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 50, category_id: 2, book_author_id: nil},
  {source_id: 15521, pre_title: "", title: "Verkefnastjórnun", post_title: "og verkfærið MS Project", slug: "verkefnastjornun-15521", description: "Verkefnastjórnun snýst um tíma- og kostnaðarstýringu, breytingastjórnun, fjármálastjórnun og mannauðsstjórnun. Verkefnastjóri þarf að vera gæddur ákveðnum kostum, ekki síst sterkum leiðtogahæfileikum, og geta náð fram því besta í fólki. Þessi bók hjálpar þér að verða betri verkefnastjóri og ná markmiðum þínum. Með því að lesa hana og leysa þau verkefni sem þar er að finna eykst skilningur þinn á faginu og færni í að nýta þér verkfæri verkefnastjórnunar.", long_description: "", page_count: 298, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, category_id: 4, book_author_id: nil},
  {source_id: 15522, pre_title: "Goðheimar", title: "Gjafir guðanna", post_title: "", slug: "gjafir-gudanna-15522", description: "Loki hinn lævísi klippir allt hárið af Sif og neyðist í kjölfarið til að heimsækja dvergana og fá þá til að búa til hár úr skíragulli í staðinn. Þar kemur hann auga á ýmsa dýrgripi sem hann ásælist og fyrr en varir er hann kominn á bólakaf í vafasöm viðskipti við slynga og hættulega andstæðinga. Þetta er tíunda bókin í þessum sívinsæla bókaflokki.", long_description: "", page_count: 48, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 51, category_id: 1, book_author_id: nil},
  {source_id: 15523, pre_title: "", title: "Vísnabókin", post_title: "", slug: "visnabokin-15523", description: "Fáar bækur hafa fylgt íslenskum börnum lengur en >>>Vísnabókin<<<; sígilt safn af kvæðum sem höfða til barna, prýdd bráðskemmtilegum teikningum. Bókin kom fyrst út árið 1946 en með tímanum var aukið við hana bæði vísum og teikningum. Í þessari nýju og gullfallegu útgáfu af bókinni er það efni nær allt með, í betri prentgæðum en áður hafa sést.", long_description: "", page_count: 105, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 51, category_id: 1, book_author_id: nil},
  {source_id: 15524, pre_title: "", title: "Þættir úr sögu lyfjafræðinnar á Íslandi frá 1760", post_title: "", slug: "thaettir-ur-sogu-lyfjafraedinnar-a-islandi-fra-1760-15524", description: "Í þessu vandaða riti er rakin saga lyfjagerðar og lyfsölu hér á landi í tvær og hálfa öld. Bókin er rituð í samstarfi Lyfjafræðingafélags Íslands, Háskóla Íslands og Lyfjafræðisafnsins, og veitir einstaka og oft og tíðum skemmtilega innsýn í fortíðina. Mikill fengur fyrir íslenska lyfjafræðinga og allt áhugafólk um heilbrigðis- og samfélagssögu.", long_description: "", page_count: 334, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 51, category_id: 4, book_author_id: nil},
  {source_id: 15525, pre_title: "", title: "Ein á forsetavakt", post_title: "Dagar í lífi Vigdísar Finnbogadóttur", slug: "ein-a-forsetavakt-15525", description: "Árið 1980 varð Vigdís Finnbogadóttir fyrst kvenna í heiminum lýðræðislega kjörinn þjóðhöfðingi. Átta árum síðar skrifaði Steinunn Sigurðardóttir metsölubókina >>>Ein á forsetavakt<<<, þar sem annasömum dögum í lífi forseta og einstæðrar móður er lýst af innsæi og glettni. Bókin kemur nú út á ný í tilefni þess að 90 ár eru frá fæðingu Vigdísar og 40 ár síðan hún var kjörin forseti.", long_description: "", page_count: 188, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 51, category_id: 9, book_author_id: nil},
  {source_id: 15526, pre_title: "", title: "Uppskriftabók Lillu frænku", post_title: "", slug: "uppskriftabok-lillu-fraenku-15526", description: "Edda Björgvins man enn matinn sem Lilla frænka í Ameríku eldaði upp úr húsmæðraskólabókinni sinni eftir áratuga búsetu vestanhafs. Þegar Viðar sonur Lillu dró fram gamla fjársjóðinn hennar mömmu og stakk upp á að þau leyfðu fleirum að njóta varð ekki aftur snúið. Hér má finna andblæ liðins tíma í uppskriftum að 70–80 ára gömlum réttum sem eitt sinn voru á allra borðum.", long_description: "", page_count: 104, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 51, category_id: 10, book_author_id: nil},
  {source_id: 15527, pre_title: "", title: "Pottur, panna og Nanna", post_title: "", slug: "pottur-panna-og-nanna-15527", description: "Í steypujárnspottum og -pönnum má elda næstum hvað sem er eins og sést á ótrúlega fjölbreyttum uppskriftum í þessari bók. Hér eru hægeldaðar steikur, pottréttir, brauð, snöggsteiktir og djúpsteiktir réttir, sætar kökur, súpur, meðlæti og margt annað. Einnig er fjallað ítarlega um val, meðferð og umhirðu á steypujárni og kosti þess við eldamennsku af öllu tagi.", long_description: "", page_count: 239, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 51, category_id: 10, book_author_id: nil},
  {source_id: 15528, pre_title: "", title: "Sögur handa Kára", post_title: "", slug: "sogur-handa-kara-15528", description: "Þegar Kári Stefánsson leitar ráða hjá Ólafi Ragnari vegna kynna hans af Kínverjum bregður sá síðarnefndi á það ráð að segja sögur. Í kjölfar farsóttarinnar gafst tóm til frekari skrifta, sögurnar urðu því ennþá fleiri og tóku til fleiri heimshorna. Ólafur segir frá kynnum sínum af áhrifafólki og aðkomu að brýnum verkefnum. Úr verður sagnasveigur sem er í senn stórskemmtilegur og stórfróðlegur.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 9, book_author_id: nil},
  {source_id: 15529, pre_title: "", title: "Málsvörn Jóns Ásgeirs Jóhannessonar", post_title: "", slug: "malsvorn-jons-asgeirs-johannessonar-15529", description: "Jón Ásgeir Jóhannesson vakti ungur athygli er hann stofnaði ásamt föður sínum lágvöruverðsverslunina Bónus. Hann varð brátt atkvæðamikill í viðskiptalífinu hér á landi sem erlendis en fyrr en varði hófust réttnefndar ofsóknir gegn honum. Einar Kárason fer í saumana á þessari makalausu sögu og varpar nýju ljósi á manninn og nýliðna atburði í þjóðarsögunni.", long_description: "", page_count: 412, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 9, book_author_id: nil},
  {source_id: 15530, pre_title: "", title: "Spænska veikin", post_title: "", slug: "spaenska-veikin-15530", description: "Spænska veikin var mannskæðasta farso&#769;tt sögunnar. Meðan á stóð féllu hundruð I&#769;slendinga i&#769; valinn, mest ungt fo&#769;lk i&#769; blo&#769;ma lífsins. Hér er þessi átakanlega saga rakin ítarlega í fyrsta sinn og m.a. velt vöngum um það að hvaða leyti spænska veikin hafi verið sambærileg við veirufaraldurinn sem gengur nú yfir heimsbyggðina.", long_description: "", page_count: 316, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 13, book_author_id: nil},
  {source_id: 15531, pre_title: "", title: "Silfurberg", post_title: "Íslenski kristallinn sem breytti heiminum", slug: "silfurberg-15531", description: "Allt frá 17. o&#776;ld gerðu vi&#769;sindamenn margvi&#769;slegar uppgo&#776;tvanir þar sem íslenskt silfurberg gegndi lykilhlutverki. Hundruð tonna af kristalnum voru flutt fra&#769; Helgustaðana&#769;munni í Reyðarfirði, silfurbergið barst vi&#769;ða og hafði a&#769;hrif a&#769; verk margra af nafntoguðustu vi&#769;sindamo&#776;nnum so&#776;gunnar – fra&#769; Isaac Newton til Alberts Einsteins.", long_description: "", page_count: 286, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 4, book_author_id: nil},
  {source_id: 15532, pre_title: "", title: "Fuglinn sem gat ekki flogið", post_title: "", slug: "fuglinn-sem-gat-ekki-flogid-15532", description: "Geirfuglinn hefur löngum verið sveipaður dulúð. Þessi ófleygi, svipmikli fugl sem lifði við Íslandsstrendur er aðeins til í frásögnum og á myndum. >>>Fuglinn sem gat ekki flogið<<< er óvenjuleg bók þar sem sagt er frá síðustu veiðiferðinni og í brennidepli eru tveir breskir ferðalangar sem skráðu af nákvæmni ferð sína til Íslands 1858. >>>Geirfuglabækur<<< þeirra eru einstök heimild um endalok tegundar.", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 4, book_author_id: nil},
  {source_id: 15533, pre_title: "Um Kommúnistaflokkinn og Sósíalistaflokkinn", title: "Draumar og veruleiki", post_title: "Stjórnmál í endursýn", slug: "draumar-og-veruleiki-15533", description: "Í þessu mikla riti er fjallað um lykilpersónur, átök og atburði í sögu Kommúnistaflokks Íslands og Sameiningarflokks alþýðu – Sósíalistaflokksins. Enginn hefur þar betri innsýn en Kjartan Ólafsson, sem þekkti persónulega flesta sem koma við sögu og lýsir þeim af fágætri hreinskilni. Bókina byggir hann á margvíslegum heimildum úr íslenskum og erlendum skjalasöfnum.", long_description: "", page_count: 568, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, category_id: 4, book_author_id: nil},
  {source_id: 15534, pre_title: "", title: "Hingað og ekki lengra!", post_title: "", slug: "hingad-og-ekki-lengra-15534", description: "Það mætti segja að Vigdís Fríða sé í samtökum þriggja glæpakvenda í 8. bekk. Þær brutu að minnsta kosti af sér en þeim finnst þær EKKI eiga skilið refsingu og þær standa 100% með gjörðum sínum. Höfundar skrifa af sama húmor og hlýju um lífsbaráttu ungmenna og þær gerðu í bókum sínum um Dodda sem voru meðal annars tilnefndar til Íslensku bókmenntaverðlaunanna. Stórfyndin snemmgelgjusaga.", long_description: "", page_count: 137, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 3, book_author_id: nil},
  {source_id: 15535, pre_title: "", title: "Hugmyndaheimur Páls Briem", post_title: "", slug: "hugmyndaheimur-pals-briem-15535", description: "Páll Briem var einn áhrifamesti stjórnmálamaður sinnar samtíðar og vildi lyfta umræðu um framtíð Íslands úr hjólförum stjórnarskrármálsins. Umdeildasta hugmynd hans, Miðlunin, átti að vera skref í þá átt. Hér skrifa sjö virtir sagnfræðingar um Pál í ljósi sinnar sérþekkingar og ferill hans er greindur út frá ýmsum sjónarhornum. Sjónum er sérstaklega beint að vanmetnu framlagi hans til stjórnmála- og hugmyndasögu Íslendinga.", long_description: "", page_count: 224, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, category_id: 4, book_author_id: nil},
  {source_id: 15536, pre_title: "", title: "Ókindin og Bethany", post_title: "", slug: "okindin-og-bethany-15536", description: "Ebenezer gefur ókindinni á háaloftinu allt sem hún óskar sér að éta, í skiptum fyrir töfralyf sem hefur haldið honum ungum og fallegum í rúm 500 ár. Aðalsöguhetjan er samt Bethany, vænsta stelpa en líka óttalegt hrekkjusvín, sem Ebenezer ættleiðir, öllum að óvörum. Af hverju skyldi hann hafa gert það? Gólandi fyndin og hjartnæm saga ... af ókind.", long_description: "", page_count: 248, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 3, book_author_id: nil},
  {source_id: 15537, pre_title: "", title: "Plan B", post_title: "", slug: "plan-b-15537", description: "Gyða er afkastamikið skáld sem hefur þó aldrei komið út á prenti. En nú er hún með frábæra hugmynd að skáldsögu sem er byggð á skrautlegu fólki sem hún vann með í heimaþjónustu Kaupmannahafnar. Gyða ákveður að endurnýja kynnin við gömlu vinnufélagana en flækist fljótt í litríkan lygavef. >>>Plan B<<< er frumleg og listavel spunnin saga af ferðalagi sem tekur óvænta stefnu.", long_description: "", page_count: 341, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, category_id: 2, book_author_id: nil},
  {source_id: 15538, pre_title: "", title: "Dularfulla styttan og drengurinn sem hvarf", post_title: "", slug: "dularfulla-styttan-og-drengurinn-sem-hvarf-15538", description: "Glæsileg, rík og dularfull kona hefur keypt hús í Álftabæ. Dag einn hverfur þaðan ómetanlegur gripur og á sama tíma virðist hrekkjusvín skólans hafa gufað upp. Vinirnir Milla og Guðjón G. Georgsson hefja rannsókn – og hún verður ekki hættulaus. Fyrri bókin um krakkana í Álftabæ, >>>Rannsóknin á leyndardómum Eyðihússins<<<, hlaut Íslensku barnabókaverðlaunin 2019.", long_description: "", page_count: 282, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 3, book_author_id: nil},
  {source_id: 15539, pre_title: "", title: "Drauma-Dísa", post_title: "", slug: "drauma-disa-15539", description: "Á furðusagnahátíð í útlöndum ætla Vár og vinir hennar að sjá átrúnaðargoðið sitt, hinn dularfulla höfund bókaflokksins um Dísu. En hann veldur þeim vonbrigðum og hverfur svo sporlaust. Eftir það byrjar sögusvið bóka hans, með skrímslum sínum og furðum, að renna saman við raunveruleikann. Hér lýkur mögnuðum furðusagnaþríleik sem hófst á bókunum >>>Drauga-Dísu<<< og >>>Galdra-Dísu<<<.", long_description: "", page_count: 306, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, category_id: 7, book_author_id: nil},
  {source_id: 15540, pre_title: "", title: "Andspænis", post_title: "", slug: "andspaenis-15540", description: "Verkefnið >>>Andspænis<<< hófst á myndlistarsýningu sem frændurnir Hugleikur og Þrándur héldu í Gallery Port haustið 2019. Friðrik Sólnes samdi texta við myndirnar, þar sem vættir og þjóðsagnapersónur standa andspænis hver annarri – og listamennirnir og ólík túlkun þeirra sömuleiðis.", long_description: "", page_count: 36, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 52, category_id: 8, book_author_id: nil},
  {source_id: 15639, pre_title: "", title: "Bandaríkin hjóluð", post_title: "", slug: "bandarikin-hjolud-15639", description: "Frá Janúar til Mars 2020 hjólaði ég þvert yfir Bandaríkin frá Flórida til Californiu. Leið sem er kölluð The Southern Tier. Ég lenti í allskonar ævintýrum á leiðinni. Bókin er með myndum af náttúrunni teknar á leiðinni.", long_description: "", page_count: 192, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, category_id: 11, book_author_id: nil},
  {source_id: 15579, pre_title: "", title: "Frá sál til sálar", post_title: "Ævi og verk Guðmundar Finnbogasonar sálfræðings", slug: "fra-sal-til-salar-15579", description: "Hér segir frá ævi og verkum Guðmundar. Sérstaklega er vikið að sálfræðilegum hugmyndum hans og þá ekki síst kenningunni um samúðarskilninginn sem oft hef­ur verið misskilin. Bókin er sérstök að því leyti að kafað er dýpra í hugmyndaheim Guðmundar en vant er í íslenskum ævisögum. Hugað er að því hvert hann sótti innblástur í verk sín, að viðtökum sem þau fengu og gildi þeirra nú. Endurskoðuð útgáfa.", long_description: "", page_count: 468, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, category_id: 4, book_author_id: nil},
  {source_id: 15580, pre_title: "Afmælisrit til heiðurs dr. Sigrúnu Júlíusdóttur", title: "Af neista verður glóð", post_title: "Vísindi og vettvangur í félagsráðgjöf", slug: "af-neista-verdur-glod-15580", description: "Dr. Sigrún Júlíusdóttir, prófessor emeritus við Háskóla Íslands, er frumkvöðull sem sett hefur mark sitt á þróun íslensks samfélags og velferðarmála, bæði á hinum starfstengda vettvangi og á sviði fræðaiðkunar. Rannsóknarsvið hennar er víðfeðmt og spannar m.a. fjölskyldumál og samskipti, barna- og fjölskylduvernd og rannsóknir og þróun í félagsráðgjöf ásamt faghandleiðslu. Í þessu safni greina eftir ólíka sérfræðinga og fagmenn er fjallað um mörg þessara efna.", long_description: "", page_count: 302, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, category_id: 4, book_author_id: nil},
  {source_id: 15542, pre_title: "", title: "Hallgr. Hallgrímsson I", post_title: "", slug: "hallgr-hallgrimsson-i-15542", description: "", long_description: "", page_count: 182, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 54, category_id: 9, book_author_id: nil},
  {source_id: 15585, pre_title: "", title: "Kynþáttafordómar", post_title: "Í stuttu máli I. Ritröð Félagsvísindasviðs", slug: "kynthattafordomar-15585", description: "Aukinn sýnileiki haturs og ofbeldis vestanhafs ásamt endurnýjuðum krafti baráttunnar gegn kynþáttamismunun hefur enn á ný sett rasisma á dagskrá. Í fyrstu bók nýrrar ritraðar í félagsvísindum er fjallað um kynþáttafordóma í sögu og samtíð. Sjónum er beint að alþjóðlegu samhengi kynþáttahyggju og birtingarmynd kynþáttafordóma í íslensku samfélagi.", long_description: "", page_count: 100, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, category_id: 4, book_author_id: nil},
  {source_id: 15587, pre_title: "", title: "Sendiboðinn", post_title: "", slug: "sendibodinn-15587", description: "Japan hefur um langa hríð verið lokað vegna ónefndra náttúruhamfara af mannavöldum. Eldra fólkið lifir lengi við hestaheilsu og annast um börnin sem eru viðkvæm og gömul fyrir aldur fram. Mumei býr með Yoshiro, fjörgömlum langafa sínum. Drengurinn er veikburða en laus við sjálfsvorkunn og bölsýni. Yoshiro einbeitir sér að því að annast Mumei, útvalinn sendiboða, þjakaður af samviskubiti vegna gjörða sinnar kynslóðar og afleiðinga þeirra. Áður hefur komið út á íslensku bókin >>>Etýður í snjó<<< eftir sama höfund.", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, category_id: 12, book_author_id: nil},
  {source_id: 15543, pre_title: "", title: "Hallgr. Hallgrímsson II", post_title: "", slug: "hallgr-hallgrimsson-ii-15543", description: "", long_description: "", page_count: 280, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 54, category_id: 9, book_author_id: nil}
])
