Book.create!([
  {source_id: 15260, pre_title: "", title: "Ég er sjálfsörugg, hugrökk og snjöll", post_title: "", slug: "eg-er-sjalfsorugg-hugrokk-og-snjoll-15260", description: "Ég er sjálfsörugg, hugrökk og snjöll er skemmtileg litabók. Henni er ætlað að byggja upp sjálfstraust, ímyndunarafl og sköpunargleði stelpna. Hvetjandi bók.", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, book_author_id: nil},
  {source_id: 15261, pre_title: "", title: "Nú er háttatími", post_title: "", slug: "nu-er-hattatimi-15261", description: "Dásamleg bók fyrir litla barnið, sem þér þykir vænt um. Hér er nóg að skoða, nóg að snerta og finna fyrir háttinn.", long_description: "", page_count: 10, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, book_author_id: nil},
  {source_id: 15262, pre_title: "", title: "Ding! Dong! Komum að leika!", post_title: "", slug: "ding-dong-komum-ad-leika-15262", description: "Hringið dyrabjöllunni á meðan þið lesið og sjáið hverjir koma í rigningar-lautarferð Gurru Grís. Skemmtileg hljóðbók sem er tilvalin fyrir alla aðdáendur Gurru.", long_description: "", page_count: 12, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, book_author_id: nil},
  {source_id: 15263, pre_title: "", title: "Klukkubókin", post_title: "", slug: "klukkubokin-15263", description: "Fyrstu skrefin við að læra á klukku og byrja í skóla. Á meðan börnin lesa um söguhetjurnar og færa klukkuvísana á réttan stað læra þau um leið á klukku.", long_description: "", page_count: 12, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, book_author_id: nil},
  {source_id: 15264, pre_title: "", title: "Töfraeinhyrningurinn hennar Gurru gríss", post_title: "", slug: "tofraeinhyrningurinn-hennar-gurru-griss-15264", description: "Kata kind ætlar að gista hjá Gurru grís. Þeim finnst gaman að leika sér með Glitfótu Gæðingi en mest dreymir þær um að leika sér með töfrandi einhyrning. Getur pabbi töfrað eitthvað fram svo að draumur þeirra rætist?", long_description: "", page_count: 30, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, book_author_id: nil},
  {source_id: 15265, pre_title: "", title: "Sögutaskan mín", post_title: "", slug: "sogutaskan-min-15265", description: "Sögutaskan mín er askja sem inniheldur fjórar þroskandi bækur sem auka skilning yngstu barnanna á ýmsu í lífi þeirra. Með hjálp fjögurra félaga læra börnin hugtök yfir tilfinningar, veðrið, fjölskylduna og staðsetningu.", long_description: "", page_count: 48, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, book_author_id: nil},
  {source_id: 15268, pre_title: "", title: "Einhyrningar, álfar og hafmeyjur", post_title: "", slug: "einhyrningar-alfar-og-hafmeyjur-15268", description: "Skemmtilegur kassi með bók og 15 púslbitum til að raða á rétta staði með líflegum myndum til að leika með.", long_description: "", page_count: 10, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, book_author_id: nil},
  {source_id: 15269, pre_title: "", title: "Límmiðafjör STAFIR", post_title: "", slug: "limmidafjor-stafir-15269", description: "Meðan börnin dunda sér við að finna límmiða, svara spurningum og lita myndirnar læra þau um leið stafina og stafrófið. Límmiðafjör er nýjasta bókin í röðinni Litlir lærdómshestar en fyrir eru bækurnar Tölur og Dýr.", long_description: "", page_count: 28, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, book_author_id: nil},
  {source_id: 15270, pre_title: "", title: "Snertu og finndu", post_title: "", slug: "snertu-og-finndu-15270", description: "Einstaklega falleg bók sem er sérstaklega hönnuð fyrir þig og litla barnið. Hér er nóg að skoða, nóg að snerta og finna. Myndirnar eru litríkar og sérstaklega valdar til þess að þroska skynjun barnsins og veita ánægjulega samverustund.", long_description: "", page_count: 12, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, book_author_id: nil},
  {source_id: 15271, pre_title: "", title: "Í Hjarta Mínu", post_title: "", slug: "i-hjarta-minu-15271", description: "Þegar Ómar segir systkinum sínum frá þungbærri áætlun sinni, skerast þau í leikinn og reyna að ýta honum í átt að betri stað.\r\nHinn vonlausi Ómar tekur á sig lífið og upplifir hluti sem hann gat aðeins dreymt um frá unga aldri. En þunglyndi togar hann niður á botninn.\r\nNær hann að berjast á móti erfiðum hindrunum sem hann sjálfur hefur orsakað?\r\nEða gefst hann upp sigraður á líkama og sál, því lífið var honum ofviða?", long_description: "", page_count: 238, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, book_author_id: nil},
  {source_id: 15272, pre_title: "", title: "Langafi minn Supermann", post_title: "", slug: "langafi-minn-supermann-15272", description: "Sylvía segir frá atburðaríku sumri þegar hún fékk að fara ein til Ólafsfjarðar og gista í Brekkugötu 9 hjá langömmu sinni og langafa.\r\nSylvía sér langafa sinn í öðru ljósi og góðvilji hans lætur hana sífellt spyrja sig, er langafi minn Supermann?", long_description: "", page_count: 81, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, book_author_id: nil},
  {source_id: 15273, pre_title: "", title: "Náðu árangri - í námi og lífi", post_title: "", slug: "nadu-arangri-i-nami-og-lifi-15273", description: ">>>Náðu árangri - í námi og lífi<<< er námsráða- og hvatningarbók með andlegu ívafi. Guðjón Ari fjallar um þá þætti sem hafa hjálpað honum að ná árangri í námi sem og á öðrum vettvangi eins og körfubolta. Markmiðasetning, sjálfstraust, hugarfar, þrautseigja, álit annarra og bein námstækni eru á meðal þessara þátta.\r\nGuðjón útskrifaðist úr Verzlunarskóla Íslands 2019 sem dúx skólans og hefur einnig unnið til verðlauna í körfuknattleik og spilað fyrir meistaraflokk Fjölnis.", long_description: "", page_count: 187, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, book_author_id: nil},
  {source_id: 15274, pre_title: "", title: "helgustur", post_title: "", slug: "helgustur-15274", description: ">>>helgustur<<< fjallar um ævi manns sem lendir á glapstigum sem barn og er sendur í sveit á nokkra bæi þar sem margvíslegt ofbeldi er allsráðandi. Kynferðisofbeldið brenglar hugmyndir hans um tengsl kynjanna og lífið almennt eru allbrenglaðar.", long_description: "", page_count: 104, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, book_author_id: nil},
  {source_id: 15275, pre_title: "", title: "Jólasveinar nútímans", post_title: "", slug: "jolasveinar-nutimans-15275", description: "Erfitt er að kveðja gamlar venjur, sérstaklega fyrir ellismelli eins og jólasveinana. Sumir sveinar ná ekki takt við tímann en aðrir eru að venjast nýrri tíð. Í gamla daga voru þeir algjörir prakkarar og villingar, en í dag þurfa þeir að haga sér. Sumir halda sér við gamla siði, á meðan aðrir gangast undir reglum nútímans. Ketkrókur er orðin vegan, Giljagaur vill ennþá mjólk beint frá spenanum, Skyrgámur er með mjólkuróþol, Grýla er Instagram-stjarna og Leppalúði er heimavinnandi húsfaðir.", long_description: "", page_count: 60, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, book_author_id: nil},
  {source_id: 15276, pre_title: "", title: "Spurningabókin 2020", post_title: "Hvaða dýr er svarta ekkjan?", slug: "spurningabokin-2020-15276", description: "Hvað eru margir reitir á bingóspjaldi? Hvert er íslenska heitið yfir Halloween? Hvaða náttúrufyrirbæri er stundum kallað \"bláa gullið\"? Hvort fæðast kanínur blindar eða sjáandi? Hvaða tómatsósa er í uppáhaldi hjá Ed Sheeran? Þetta og margfalt fleiri stórskemmtilegar spurningar í þessari frábæru bók sem ætti að vera til á hverju heimili.", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, book_author_id: nil},
  {source_id: 15277, pre_title: "", title: "Fótboltaspurningar 2020", post_title: "", slug: "fotboltaspurningar-2020-15277", description: "Með hvaða liði lék Steven Lennon fyrst eftir að hann kom til Íslands? Stuðningsmannaklúbbur hvaða liðs kallar sig Silfurskeiðina? Hvernig eru sokkarnir á litinn í aðalbúningi Frakka? Með hvaða liði lék Bruno Fernandes áður en hann gekk til liðs við Manchester United? Hjá hvaða liði hóf James Milner feril sinn? Hverrar þjóðar er Lautaro Martínez? En Kim Little? Frá hvaða landi eru liðin 07 Vestur og Skála? En Haka og Honka? Þetta og margfalt fleiri spurningar í þessari bráðskemmtilegu bók sem allir knattspyrnuunnendur ættu að spreyta sig á.", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, book_author_id: nil},
  {source_id: 15278, pre_title: "", title: "140 vísnagátur", post_title: "", slug: "140-visnagatur-15278", description: "Hér eru á ferðinni 140 vísnagátur, sumar auðveldar, en aðrar strembnari. Dæmi um eina:\r\n\r\nEkki lengra augað sér,\r\ner á mörgum diski.\r\nBrún á jökli einnig er\r\nendilöng á fiski.\r\n\r\nJá, við hvað er átt? Þessi bók er bráðskemmtileg dægrastytting og auðvitað hin besta heilaleikfimi. Höfundurinn hefur áður sent frá sér tvær bækur af sama meiði og hafa þær notið mikilla vinsælda.", long_description: "", page_count: 70, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, book_author_id: nil},
  {source_id: 15279, pre_title: "", title: "Pétrisk-íslensk orðabók með alfræðiívafi", post_title: "", slug: "petrisk-islensk-ordabok-med-alfraediivafi-15279", description: "Séra Pétur Þorsteinsson hefur í rúma þrjá áratugi fundið skrýtin og skemmtileg orð til að setja í stað hinna hversdagslegu og litlausu. Hvað skyldu þessi t.d. merkja: Bjórbarn, Brottfararbox, herming, strokufangi, töfratvenna og úreltur? Svörin færðu auðvitað í þessari bók sem umfram allt er bráðskemmtileg.", long_description: "", page_count: 108, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, book_author_id: nil},
  {source_id: 15280, pre_title: "", title: "Gljúfrabúar og giljadísir", post_title: "Eyfirskir fossar", slug: "gljufrabuar-og-giljadisir-15280", description: "Um árabil hefur áhugaljósmyndarinn Svavar Alfreð Jónsson heimsótt fossa í heimahéraði sínu, Eyjafirði, og tekið myndir af mörgum þeirra. Útkoman er glæsileg og minnir okkur á þau ómetanlegu lífsgæði sem felast í því að eiga stutt að fara út í óspjallaða og ægifagra náttúruna. Fegurðin er aðgengilegri en okkur grunar eins og myndefnið hér vitnar um.", long_description: "", page_count: 104, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, book_author_id: nil},
  {source_id: 15281, pre_title: "", title: "Vestmannaeyjar", post_title: "Af fólki og fuglum og ýmsu fleiru", slug: "vestmannaeyjar-15281", description: "Hér fjallar Sigurgeir um æskuslóðir sínar í byggðinni fyrir ofan hraun í Vestmannaeyjum og fólkið sem þar bjó, s.s. kynni sín af sérstæðum persónum eins og séra Halldóri Kolbeins, Nýju í Suðurgarði og Munda í Draumbæ. Eiinnig fjallar hann um  eftirminnilega félaga á lífsleiðnni, t.d. Bjarnhéðin Elíasson, Sævar í Gröf, ÁSa í Bæ og Guðmund kantor. Óvenjulegt brúðkaupsferðalag þeirra hjóna, Sigurgeirs og Katrínar, ber á góma og jólahald á Kleppi, þar sem hann starfaði á námsárum sínum. Er þá fátt nefnt, en hvarvetna fær leiftrandi frásagnargáfa höfundarins sín notið og víða glampar á kímni höfundarins", long_description: "", page_count: 250, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, book_author_id: nil},
  {source_id: 15282, pre_title: "", title: "Siddi gull", post_title: "Æviminningar Sigmars Ó. Maríussonar gullsmiðs", slug: "siddi-gull-15282", description: "Hér segir Þistilfirðingurinn Sigmar Ó. Maríusson gullsmiður – Siddi gull – frá viðburðaríkri ævi sinni. Ríflega tvítugur að aldri missti hann báða fæturna í bílslysi á Heiðarfjalli á Langanesi og hafði þá ýmislegt dunið á honum áður og átti enn eftir að gera. En ... hann hefur aldrei gefist upp og ávallt horft á björtu hliðarnar, sama hversu útlitið væri í rauninni dökkt. Þessu eru öllu gerð skil í frásögn sem spannar allt frá mesta draugagangi á Íslandi, Hvammsundrunum svokölluðu, til hins ofdekraða Samma. Þessi bók lætur engann ósnortinn.", long_description: "", page_count: 220, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, book_author_id: nil},
  {source_id: 15283, pre_title: "", title: "Látra-Björg", post_title: "", slug: "latra-bjorg-15283", description: "Látra–Björg var kraftaskáld, sjómaður, fökkukona — goðsögn í lifanda líf og ráðgáta eftir að hún lést á vergangi í Móðuharðindunum. Samtíðarmenn hennar óttuðust hana. Með kvæðum sínum var hún sögð geta futt til fjöll, laðað að sér fsk og deytt menn eða fært þeim gæfu.\r\nÍ þessari bók eru saman komin öll helstu kvæði Látra–Bjargar og um leið skýringar Helga Jónssonar frá Þverá í Dalsmynni\r\nog samantekt hans á æviferli hennar.Í sérstökum viðauka er svo að fnna Látrabréfð, eina varðveitta prósatextinn eftir Látra–Björgu, sagður hafa fundist á skemmuvegg á Látrum árið 1740.", long_description: "", page_count: 128, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, book_author_id: nil},
  {source_id: 15284, pre_title: "", title: "Fimmaurabrandararnir 2", post_title: "", slug: "fimmaurabrandararnir-2-15284", description: "Getur feitt fólk fylgst grannt með fréttum? * Laddi er — án gríns — ekkert fyndinn.* Ég keypti klósettbursta fyrir viku síðan ...\r\nen er nú búinn að skipta aftur yfir í klósettpappír.* Ég var á leið í búðina, svo konan bað mig um að setja tómatsósu á\r\ninnkaupalistann ... og nú get ég ekki lesið neitt á honum!* Hvað kallast lúsmý sem treðst framfyrir?\r\nSkjúsmý! Í hvers konar fötum gengur starfsfólk Sorpu? Í ruslafötum! Þetta og margt fleira brjálæðislega fyndið í þessari geggjuðu bók!", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, book_author_id: nil},
  {source_id: 15285, pre_title: "", title: "Íslensku fuglarnir og þjóðtrúin", post_title: "", slug: "islensku-fuglarnir-og-thjodtruin-15285", description: "Teknir eru til skoðunar allir íslenskir, reglubundnir varpfuglar, í þjóðtrúnni. Dæmi: Á Horni í Sléttuhreppi boðaði það rigningu ef lómurinn stóð á öðrum fæti og skrækti. Í Heklu átti að vera bústaður hrafna með glóandi klær og nef úr járni.\r\nVegna smæðar, litar og atferlis, eða með öðrum orðum vegna þess hversu jarðbundinn hann er og gjarn á að skjótast í felur í holum og gjótum eða þéttu kjarri, í stað þess að bjarga sér á flugi, var músarrindillinn löngum talinn skyldari mús en fugli. Jónas Jónasson frá Hrafnagili nefnir forn ráð við gulu. Eitt var það að hræra arnarheila út í þemur mörkum af víni og drekka svo. Ríflega 250 myndir prýða bókina.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, book_author_id: nil},
  {source_id: 15286, pre_title: "", title: "Vegan - Eldhús grænkerans", post_title: "", slug: "vegan-eldhus-graenkerans-15286", description: "Þessi einstaka bók leggur fræðilegan grundvöll að grænkerafæði og kynnir fyrir lesandanum meira en 100 grænkeramatartegundir – þar\r\ná meðal rótargrænmeti, baunir og linsur, hnetur og fræ, kryddjurtir og kryddduft – skýrir næringarinnihaldið og hvernig skuli elda þær og bera fram. Hér kynnist þið öllu því sem vita þarf um þetta mataræði.T.d.: HVAÐA FÆÐUTEGUNDIR GETA KOMIÐ Í STAÐ KJÖTS OG MJÓLKUR, HVERNIG SKIPULEGGJA MÁ SKYNSAMLEGAR MÁLTÍÐIR, AUK GRUNNUPPSKRIFTA OG NÆRINGARTAFLNA.", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 3, book_author_id: nil},
  {source_id: 15288, pre_title: "", title: "Hvernig líður þér, litli björn?", post_title: "", slug: "hvernig-lidur-ther-litli-bjorn-15288", description: "Sjáðu björninn! Hvernig líður honum?\r\nEr hann glaður, leiður eða kannski hræddur?\r\nÞú kemst að því með því að snúa stóra hjólinu.\r\nBörnin munu læra að þekkja tilfinningar og hafa\r\ngaman af hljóðinu sem kemur þegar hjólið snýst.", long_description: "", page_count: 14, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, book_author_id: nil},
  {source_id: 15289, pre_title: "", title: "Hvernig líður þér, litli tígur?", post_title: "", slug: "hvernig-lidur-ther-litli-tigur-15289", description: "Sjáðu tígurinn! Hvernig líður honum?\r\nEr hann glaður, reiður eða kannski þreyttur?\r\nÞú kemst að því með því að snúa stóra hjólinu.\r\nBörnin munu læra að þekkja tilfinningar og hafa\r\ngaman af hljóðinu sem kemur þegar hjólið snýst.", long_description: "", page_count: 14, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, book_author_id: nil},
  {source_id: 15290, pre_title: "Fyrsta hljóðabókin min", title: "Tilfinningar", post_title: "", slug: "tilfinningar-15290", description: "Aldrei of snemmt að tala um tilfinningar. Þessi hljóðabók býður börnum að uppgötva heiminn í gegnum átta mismunandi hljóð, fallegar myndskreytingar og margar áhugaverðar staðreyndir. Börn fara í gegnum mismunandi tilfinningar daglega. Þeim líkar að tengja sína eigin reynslu við það sem gerist í bókinni.\r\nALDUR BARNS: 3-7 ára.", long_description: "", page_count: 20, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 4, book_author_id: nil},
  {source_id: 15291, pre_title: "", title: "Dýrin í skóginum - Litað með vatni!", post_title: "Fræðandi töfralitabók og afþreying um dýrin fyrir listræn börn", slug: "dyrin-i-skoginum-litad-med-vatni-15291", description: "Í þessari fræðandi töfralitabók geturðu framkallað litríkar dýramyndir, aðeins með því að strjúka vatni yfir þær. Þegar þær þorna er hægt að lita þær aftur og aftur. >>>Góð bók fyrir börn frá þriggja ára aldri.<<<", long_description: "", page_count: 56, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15293, pre_title: "", title: "Hjól", post_title: ">>>Litrík harðspjaldabók með hreyfanlegum hjólum<<<", slug: "hjol-15293", description: "Kynntu barninu ýmsar gerðir hjóla á skemmtilegan og gagnvirkan hátt þar sem hægt er að snúa hjólunum fram og til baka. >>>Góð bók fyrir yngstu börnin.<<<", long_description: "", page_count: 12, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15294, pre_title: "", title: "Litlir könnuðir - LÍKAMINN", post_title: "Fræðandi harðspjaldagluggabók", slug: "litlir-konnudir-likaminn-15294", description: "Lífleg og fræðandi bók fyrir börn sem hafa áhuga á líkamanum.\r\nEftirfarandi bækur eru í sama bókaflokki: Veröld dýranna, Þegar ég verð stór, Á ferð og flugi, Sveitabærinn, Pöddur og Hafið.\r\n>>>Tilvalin bók fyrir börn frá þriggja ára aldri.<<<", long_description: "", page_count: 16, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15296, pre_title: "", title: "Litlir könnuðir - VERÖLD DÝRANNA", post_title: "Fræðandi harðspjaldagluggabók", slug: "litlir-konnudir-verold-dyranna-15296", description: "Lífleg og fræðandi bók fyrir börn sem hafa áhuga á dýraríkinu.\r\nEftirfarandi bækur eru í sama bókaflokki: Líkaminn, Þegar ég verð stór, Á ferð og flugi, Sveitabærinn, Pöddur og Hafið.\r\n>>>Tilvalin bók fyrir börn frá þriggja ára aldri.<<<", long_description: "", page_count: 16, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15298, pre_title: "", title: "Jörðin", post_title: "Fræðandi bók með flipum og útskornum síðum þar sem landafræðin öðlast líf", slug: "jordin-15298", description: "Jörðin býr yfir kraumandi eldstöðvum, jöklum, eyðimörkum, miklum vatnsföllum og dularfullum hellakerfum. Leggjum af stað í ævintýralega ferð! >>>Fræðandi bók fyrir krakka á öllum aldri sem hafa áhuga á jörðinni.<<<", long_description: "", page_count: 29, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15300, pre_title: "", title: "Kát krútt", post_title: "Lífleg og skemmtileg harðspjaldahljóðbók með bráðfyndnum hljóðum og fjörugu lagi!", slug: "kat-krutt-15300", description: "Kynnstu krúttlegum gæludýrum sem setja allt á annan endann í húsinu. Skemmtu þér við að hlusta á bráðfyndin hljóð og fjörugt lag. >>>Góð bók fyrir börn frá eins árs aldri.<<<", long_description: "", page_count: 8, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15301, pre_title: "", title: "Pési og Pippa - Nýi vinurinn", post_title: "Fallega myndskreytt harðspjaldabók sem fjallar um sanna vináttu", slug: "pesi-og-pippa-nyi-vinurinn-15301", description: "Þegar Pési eignast nýjan vin finnst Pippu hún vera höfð útundan. \r\nÆ, æ, aumingja Pippa! \r\nEn óvæntur atburður verður til þess að þau átta sig á því hvað sönn vinátta er dýrmæt. >>>Góð bók fyrir yngstu börnin.<<<", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15302, pre_title: "", title: "Pési og Pippa - Rauða blaðran", post_title: "Fallega myndskreytt harðspjaldabók sem fjallar um sanna vináttu", slug: "pesi-og-pippa-rauda-bladran-15302", description: "Pési er MJÖG hreykinn af rauðu blöðrunni sinni! En þegar hann missir hana óvart og hún springur, verður hann ákaflega sorgmæddur.\r\nTil allrar hamingju fær Pippa snjalla hugmynd og brátt tekur Pési gleði sína á ný! >>>Góð bók fyrir yngstu börnin.<<<", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15303, pre_title: "", title: "Fánar - Límmiðabók", post_title: "Fræðandi límmiðabók með fánum", slug: "fanar-limmidabok-15303", description: "Lærðu að þekkja alla þjóðfána heims með því að líma þá á rétta staði á kortinu. Hér er um fleiri en 190 fána að velja. >>>Góð og fræðandi afþreying fyrir börn á öllum aldri.<<<", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15305, pre_title: "", title: "100 Drekaskutlur - Brjóttu blað og fljúgðu af stað", post_title: "Einstaklega litrík skutlubók", slug: "100-drekaskutlur-brjottu-blad-og-fljugdu-af-stad-15305", description: "Hér færðu tækifæri til að koma þér upp eigin flugflota og senda á loft ógnvekjandi her eldspúandi drekaflauga til árása á óvininn. Þú getur valið um 100 síður sem auðvelt er að losa úr bókinni og brjóta saman í skutlur.>>>Bók fyrir krakka á öllum aldri.<<<", long_description: "", page_count: 108, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15307, pre_title: "", title: "Farartæki - Lyftimyndir", post_title: "Harðspjaldabók þar sem eitthvað óvænt leynist undir hverjum flipa", slug: "farartaeki-lyftimyndir-15307", description: "Getur rauði bíllinn komist að því hvaðan drunurnar koma? \r\nMeð spennandi lyftimyndum á hverri opnu kemur þessi fjörlega bók virkilega á óvart! >>>Skemmtileg bók fyrir yngstu börnin.<<<", long_description: "", page_count: 8, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15308, pre_title: "", title: "Sveitin - Lyftimyndir", post_title: "Harðspjaldabók þar sem eitthvað óvænt leynist undir hverjum flipa", slug: "sveitin-lyftimyndir-15308", description: "Fylgstu með Kusu og Lamba í feluleik. Spennandi myndir spretta úr felum undan flipunum á hverri opnu! >>>Skemmtileg bók fyrir yngstu börnin.<<<", long_description: "", page_count: 8, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15309, pre_title: "", title: "Skoppa", post_title: "Harðspjalda-flipabók", slug: "skoppa-15309", description: "Hoppum og skoppum með Skoppu og hjálpum henni að finna leikfélaga sína sem eru í felum undir flipunum á hverri opnu. >>>Litrík bók fyrir eins árs og eldri.<<<", long_description: "", page_count: 8, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15310, pre_title: "", title: "Söguperlur", post_title: "11 fallegar sögur", slug: "soguperlur-15310", description: "Hér eru 11 fallegar sögur þar sem þú kynnist apatvíburunum Millu og Max sem keppa um hvort verði á undan að komast í háttinn og líka Konný kóalabangsa sem virðist aldrei geta ákveðið sig. Margir fleiri koma við sögu. >>>Falleg sögubók fyrir tveggja ára og eldri.<<<", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15311, pre_title: "", title: "Eina sögu enn", post_title: "8 fallega myndskreyttar sögur", slug: "eina-sogu-enn-15311", description: "Átta frábærar sögur sem tilvalið er að lesa með börnunum. \r\nHeillandi persónur og fallegar myndir í sérhverri sögu fylgja börnunum ljúflega inn í skemmtilegan ævintýraheim. >>>Falleg sögubók fyrir börn frá eins árs aldri.<<<", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15312, pre_title: "", title: "Mannslíkaminn", post_title: "Kannaðu stórkostlegasta undur veraldar - mannslíkamann!", slug: "mannslikaminn-15312", description: "Fræðumst um mannslíkamann og starfsemi hans á greinargóðan hátt.\r\nÁ hverri opnu sem er útskorin er helstu líffærum gerð skil í myndum og máli og útskýrt hvert hlutverk þeirra er og hvar þau eru staðsett í líkamanum. >>>Tilvalin bók fyrir krakka á öllum aldri sem hafa áhuga á mannslíkamanum.<<<", long_description: "", page_count: 16, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15313, pre_title: "", title: "Mannslíkaminn", post_title: "Kannaðu stórkostlegasta undur veraldar - mannslíkamann!", slug: "mannslikaminn-15313", description: "Fræðumst um mannslíkamann og starfsemi hans á greinargóðan hátt.\r\nÁ hverri opnu sem er útskorin er helstu líffærum gerð skil í myndum og máli og útskýrt hvert hlutverk þeirra er og hvar þau eru staðsett í líkamanum. >>>Tilvalin bók fyrir krakka á öllum aldri sem hafa áhuga á mannslíkamanum.<<<", long_description: "", page_count: 16, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15314, pre_title: "", title: "Ég er eins árs - Skoða, leita og finna", post_title: "Litrík harðspjaldabók", slug: "eg-er-eins-ars-skoda-leita-og-finna-15314", description: "Bók sem eykur orðaforða og börnin langar til að heyra meira og skoða. Sérstaklega fallegar og litríkar myndir. >>>Góð bók fyrir börn frá eins árs aldri.<<<", long_description: "", page_count: 14, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15315, pre_title: "", title: "Ég er tveggja ára - Leita og læra", post_title: "Litrík harðspjaldabók", slug: "eg-er-tveggja-ara-leita-og-laera-15315", description: "Börn hafa ánægju af því að skoða þessa bók með fallegum, skýrum og litríkum myndum. Textinn er fyrir þá fullorðnu til að lesa með barninu. >>>Góð bók fyrir börn frá tveggja ára aldri.<<<", long_description: "", page_count: 14, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15316, pre_title: "", title: "Órigamí - Japönsk pappírslist", post_title: "100 skrautblöð og 5 formgerðir", slug: "origami-japonsk-pappirslist-15316", description: "Í bókinni eru 100 blöð sem auðvelt er að losa og brjóta saman í fíngerð órigamí pappírslíkön. Einfaldar leiðbeiningar með skýringarmyndum sýna hvernig hægt er að breyta þessum litríku og skrautlegu blöðum í falleg listaverk. >>>Góð afþreying fyrir alla.<<<", long_description: "", page_count: 102, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15318, pre_title: "", title: "Anna á Arinhæð", post_title: "", slug: "anna-a-arinhaed-15318", description: "Anna og Gilbert Blythe hafa komið sér notalega fyrir ásamt  börnum sínum fimm í húsi sem þau kalla Arinhæð. Bókin segir frá börnunum og ýmsum uppákomum þeim tengdum en viðfangsefni hinna fullorðnu, til dæmis hjónabandsörðugleikar og erfiðir ættingjar, fá sitt rými. Sjötta bókin af átta um Önnu í Grænuhlíð lítur loksins dagsins ljós. Hún hefur ekki komið út í íslenskri þýðingu áður.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 6, book_author_id: nil},
  {source_id: 15319, pre_title: "", title: "Bókapakki - Litlu orðabækurnar", post_title: ">>>3 harðspjaldabækur saman í pakka<<<", slug: "bokapakki-litlu-ordabaekurnar-15319", description: "Fallegar ljósmyndir hjálpa yngstu kynslóðinni að þekkja umhverfið og auka orðaforðann. Bækurnar þrjár eru: Fyrstu orðin, Farartæki og Sveitalíf. >>>Bækur fyrir yngstu kynslóðina.<<<", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15320, pre_title: "", title: "Bókapakki - Sígild ævintýri", post_title: ">>>4 sögubækur saman í pakka<<<", slug: "bokapakki-sigild-aevintyri-15320", description: "Hans og Gréta, Þrír grísir, Aladdín og töfralampinn, Lísa í Undralandi, Þyrnirós, Öskubuska, Gullbrá og bangsarnir, Mjallhvít, Rauðhetta, Galdrakarlinn í Oz, Gosi og Ljóti andarunginn. >>>Fallegar sígildar sögur fyrir eins árs og eldri.<<<", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15321, pre_title: "", title: "Bókapakki - Snúður og Snælda", post_title: "Allar 8 bækurnar saman í pakka", slug: "bokapakki-snudur-og-snaelda-15321", description: "Sígildar og vinsælar bækur í 60 ár fyrir yngstu lesendurna um ævintýri og uppátæki Snúðs og Snældu.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15322, pre_title: "", title: "Bókapakki - Leikum og lærum", post_title: "4 afþreyingarbækur saman í pakka", slug: "bokapakki-leikum-og-laerum-15322", description: "4 frábærar bækur til að læra og ná árangri með leik og skemmtilegum verkefnum. \r\nBækurnar sem eru í pakkanum eru eftirfarandi: Spádómsgoggar, Frádráttur, Samlagning og Litli ferðafélaginn. >>>Góðar afþreyingarbækur fyrir börn frá fjögurra ára aldri.<<<", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15323, pre_title: "", title: "Bókapakki - Leikum og lærum", post_title: "4 afþreyingarbækur saman í pakka", slug: "bokapakki-leikum-og-laerum-15323", description: "4 frábærar bækur til að læra og ná árangri með leik og skemmtilegum verkefnum. \r\nBækurnar sem eru í pakkanum eru eftirfarandi: 100 Drekaskutlur, Frádráttur, Samlagning og Litli ferðafélaginn. >>>Góðar afþreyingarbækur fyrir börn frá fjögurra ára aldri.<<<", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15326, pre_title: "", title: "Þökk til þín", post_title: "", slug: "thokk-til-thin-15326", description: "Þakklæti hefur stundum verið kallað uppspretta lífshamingjunnar. Margar leiðir eru færar til að auka tilfinningu þakklætis í daglegu lífi og læra að tjá það í orðum og verki. Í þessari fallegu verkefnabók finnur þú fjölbreyttar þakklætisæfingar og verkefni og getur skráð þína eigin þakklætisbók. Æfingarnar er hægt að iðka einn eða með öðrum, t.d. maka, vinum eða börnum.", long_description: "", page_count: 143, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 7, book_author_id: nil},
  {source_id: 15327, pre_title: "", title: "Fæddur til að fækka tárum. KÁINN.", post_title: "Ævi og ljóð", slug: "faeddur-til-ad-faekka-tarum-kainn-15327", description: "Kristján Níels Jónsson, Káinn; eina íslenska kímniskáldið, orðsnjall og orðleikinn – gæddur náðargáfu – glímdi þó alla ævi við sálar-skugga. Fæddur 1859 á Akureyri. Fluttist 19 ára til Ameríku. Saga Káins er um leið saga Vestur-Íslendinga sögð af glettnislegum sjónarhóli skáldsins sem orti: \r\n\r\nEn undarlegast atvik samt ég tel\r\nað Íslendingur skyldi frjósa í hel.", long_description: "", page_count: 372, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 8, book_author_id: nil},
  {source_id: 15328, pre_title: "", title: "Stund um stund", post_title: "", slug: "stund-um-stund-15328", description: ">>>Stund um stund<<< er þriðja ljóðabók höfundar og samanstendur af tveimur prósum og fimmtíu og tveimur ljóðum.\r\nÍ bókinni veltir Jóhann fyrir sér tilbrigðum hversdagsins á sinn persónulega hátt.\r\nLjóðin eru skrifuð í hefðbundnu og frjálsu ljóðaformi, ætlað að rjúfa eril hversdagsins og fá lesendur til að staldra við, stund um stund.", long_description: "", page_count: 79, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 9, book_author_id: nil},
  {source_id: 15329, pre_title: "", title: "Brottnám", post_title: "", slug: "brottnam-15329", description: "Sonur Njáls er ekki mættur í vikulegt matarboð. Njáll verður áhyggjufullur og dregur Dísu út úr óreglunni til að hjálpa sér að finna hann. Eltingarleikur, kapphlaup við tímann, mótorhjólagengi og böðlarnir koma við sögu í þessari æsispennandi bók. Allt virðist gerast undir stjórn þess stóra. Dísa og Njáll eru komin hættulega nálægt því að leysa hið ósnertanlega net hans með hjálp úr óvæntri átt.", long_description: "", page_count: 301, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 10, book_author_id: nil},
  {source_id: 15330, pre_title: "", title: "Sætaspætan", post_title: "", slug: "saetaspaetan-15330", description: "Sætaspætan er önnur bókin í íslensku bókaröðinni „Sögur frá Tulipop\".\r\n\r\nBókin segir söguna af því þegar vinirnir Freddi og Gló finna sjaldgæfan furðufugl í Sveppamýrinni. Eiga þau að éta hann? Eða bjarga honum? Sjáum hvað setur!\r\n\r\nHeillandi og skemmtileg bók fyrir börn á öllum aldri byggð á ástsælu persónunum úr íslenska ævintýraheiminum Tulipop.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 11, book_author_id: nil},
  {source_id: 15331, pre_title: "", title: "Íslensku dýrin", post_title: "Harðspjaldabók - Fallega myndskreytt bók sem fagnar 60 ára afmæli!", slug: "islensku-dyrin-15331", description: "Sígild barnabók, myndskreytt af Halldóri Péturssyni, er nú aftur fáanleg. Þessi vinsæla barnabók hefur komið inn á mörg heimili landsins síðastliðin 60 ár. >>>Bók fyrir yngstu börnin.<<<", long_description: "", page_count: 6, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 5, book_author_id: nil},
  {source_id: 15332, pre_title: "", title: "ILLVERK", post_title: "", slug: "illverk-15332", description: "Gamalt mál er sett af stað þegar ungur drengur rekst á mannabein í grunni húsarústa á Eskifirði. Við uppgröft finnast líkamsleifar þeirra sem saknað hafði verið í áraraðir ásamt dagbók Sigurveigar húsfreyju að Gáratúni og áttu skrif hennar eftir að varpa ljósi á ýmis svik, leyndardóma og djúp ástarmál.", long_description: "", page_count: 328, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 2, book_author_id: nil},
  {source_id: 15333, pre_title: "", title: "Fuglar í búri", post_title: "Ljóð eftir afrísk-bandarísk skáld", slug: "fuglar-i-buri-15333", description: "Ljóðin eru frá því á 18. öld til dagsins í dag. 64 ljóð eftir 29 skáld ásamt einu þjóðkvæði sem gefa hugmynd um samfélag, sögu og menningu svarts fólks í bandarísku þjóðfélagi frá því þrælahald ríkti. Ljóðin birta fjölbreytilegar myndir af lífi og stöðu svartra. Skáldin í bókinni hafa öll vakið athygli langt út fyrir eigin hóp og þjóð.\r\n\r\nGaribaldi þýddi og skrifaði formála.", long_description: "", page_count: 201, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 12, book_author_id: nil},
  {source_id: 15334, pre_title: "", title: "Takk", post_title: "", slug: "takk-15334", description: "Þetta er fyrsta ljóðabók höfundar, afar sérstæð að uppbyggingu og efnistökum. Ljóðin eru af öllu tagi; ort í gamni og alvöru um ýmsar hliðar mannlífs og jarðar; ást og trega, umhverfi og trú. Með hliðsjón af mikilli fjölbreytni í háttum og formi auk ótal vísana til þekktra ljóða og skálda, má segja að bókin sé einskonar óður til íslenskrar ljóðlistar.", long_description: "", page_count: 79, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 13, book_author_id: nil},
  {source_id: 15335, pre_title: "", title: "Jarðvegur", post_title: "", slug: "jardvegur-15335", description: ">>>Jarðvegur<<< er fyrsta ljóðabók Rebekku Sifjar (f. 1992). Verkið er samfelld frásögn þar sem tekist er á við erfið málefni, sorg, missi og sársauka. Áður hafa sögur eftir hana birst í smásagnasafninu >>>Möndulhalla<<< og öðrum safnritum Blekfjelagsins en Rebekka starfar einnig sem söngkona og lagahöfundur.", long_description: "", page_count: 70, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 14, book_author_id: nil},
  {source_id: 15336, pre_title: "", title: "Loftskeyti", post_title: "", slug: "loftskeyti-15336", description: "Loftskeyti er fjórða ljóðabók Sigrúnar Björnsdóttur (f. 1956) en áður hafa komið út eftir hana bækurnar Næturfæðing (2002), Blóðeyjar (2007) og Höfuðbending (2014). Í Loftskeytum skoðar hún mörk merkingar, dulmálslykla og áhrif skeytasendinga í fortíð og nútíð, áhrif sem verða kannski aldrei skilin til fulls.", long_description: "", page_count: 55, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 14, book_author_id: nil},
  {source_id: 15337, pre_title: "", title: "Dýralíf", post_title: "", slug: "dyralif-15337", description: "Í vetrarmyrkri er áður óþekkt lægð í aðsigi. Ung ljósmóðir býr í íbúð sem hún erfði eftir ömmusystur sína. Upp úr kassa undan Chiquita-banönum koma þrjú handrit sem ömmusystirin vann að, >>>Dýralíf, rannsókn á því sem mannskepnan er fær um, Sannleikurinn um ljósið<<< og >>>Tilviljunin<<<. \r\nÞessi sjöunda skáldsaga Auðar Övu fjallar um brothættasta og grimmasta dýrið: manninn og leitina að mennskunni.\r\nSkáldsögur hennar eru margverðlaunaðar og hafa verið þýddar á 33 tungumál.", long_description: "", page_count: 208, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15338, pre_title: "", title: "Fjarvera þín er myrkur", post_title: "", slug: "fjarvera-thin-er-myrkur-15338", description: "Er það ábyrgð eða hugleysi að sætta sig við örlög sín? Hér tvinnast saman yfir staði og tíma, kynslóð fram af kynslóð, líf sem kannski eru jafn tíðindalítil og girðingarstaurar en halda þó öllu uppi. Kornabarn sem rétt er yfir eldhúsborð, löngu dáið þýskt skáld og döpur rokkstjarna. \r\nÞetta er þrettánda skáldsaga höfundar sem hlaut Íslensku bókmenntaverðlaunin árið 2005 fyrir >>>Sumarljós, og svo kemur nóttin<<<.", long_description: "", page_count: 496, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15339, pre_title: "", title: "Eldarnir", post_title: "Ástin og aðrar hamfarir", slug: "eldarnir-15339", description: "Jarðskjálftar skekja Reykjanesskaga, eldfjöllin eru vöknuð til lífsins eftir 800 ára hlé. Enginn þekkir þau betur en eldfjallafræðingurinn Anna Arnardóttir, forstöðumaður Jarðvísindastofnunar, sem þarf að takast á við stærsta verkefni ferilsins við stjórn almannavarna. En áferðarfallegt og hamingjuríkt líf hennar lætur ekki lengur að stjórn.\r\nFyrri skáldsögur Sigríðar eru >>>Eyland<<< og >>>Hið heilaga orð<<<.", long_description: "", page_count: 288, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15340, pre_title: "", title: "Hófí lærir um hetjur", post_title: "", slug: "hofi-laerir-um-hetjur-15340", description: "Hófí uppgötvar eftir dag með Sámi, stóra bróður sínum og hetju, að hetjur má finna alls staðar! Hvort sem við vitum það eða ekki, þá er hetja innra með okkur öllum. \r\nHér er komin fjórða bókin um íslenska fjárhundinn Hófí og vini hennar. \r\nBækurnar innihalda ríkulegan orðaforða, yndislegan boðskap og fallegar myndir. \r\nFáanleg á íslensku og ensku.", long_description: "", page_count: 44, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 16, book_author_id: nil},
  {source_id: 15341, pre_title: "", title: "Blýhjarta", post_title: "", slug: "blyhjarta-15341", description: ">>>Blýhjarta<<< er fyrsta ljóðverk Stefaníu dóttur Páls (f. 1990) og fjallar um konubarnið, tilraunir þess til að frelsast undan sjálfu sér og útsýnið á leiðinni. Áður hafa ritverk og þýðingar eftir hana birst í tímaritum, safnbókum og leikhúsi. Hún vinnur gjarnan þvert á miðla og stundar nú nám í tónsmíðum við Listaháskóla Íslands.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 14, book_author_id: nil},
  {source_id: 15342, pre_title: "", title: "Hús harmleikja", post_title: "", slug: "hus-harmleikja-15342", description: "Á Eyrarbakka situr Alma blaðamaður og skrifar bók um reimleika. Hún kynnist Oktavíu Bergrós, litríkri leikkonu og safnverði í Húsinu. Leikurinn æsist. Irma kvikmyndaleikstjóri kemur á svæðið ásamt eiginmanni og tveimur handritshöfundum. Leyndarmál þorpsbúa krauma undir. Dauðinn ber að dyrum. Lögregla rannsakar málið og Alma er ekki aðgerðarlaus.", long_description: "", page_count: 255, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 17, book_author_id: nil},
  {source_id: 15343, pre_title: "", title: "Tók ég eftir því þá eða tók ég eftir því eftirá?", post_title: "", slug: "tok-eg-eftir-thvi-tha-eda-tok-eg-eftir-thvi-eftira-15343", description: ">>>Tók ég eftir því þá eða tók ég eftir því eftirá?<<< er persónuleg myndræn frásögn um reynslu höfundar af því að missa tökin á raunveruleikanum og fara í geðrof. Bókin styðst við samspil teikninga og texta til þess að skapa heildstætt sjálfsævisögulegt bókverk. Hún er einhvers staðar á milli þess að vera hefðbundin sjálfsævisaga og myndlist.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 18, book_author_id: nil},
  {source_id: 15344, pre_title: "", title: "Minjaþing", post_title: "helgað Mjöll Snæsdóttur", slug: "minjathing-15344", description: "Mjöll Snæsdóttir fornleifafræðingur varð sjötug þann 12. febrúar 2020. Af því tilefni ákváðu vinir og samferðafólk Mjallar að gefa út greinasafn henni til heiðurs.\r\nAfmælisritið geymir úrval fræðigreina um fjölbreytt menningarsöguleg efni. Margar þeirra tengjast viðfangsefnum Mjallar í gegnum tíðina og bera því  vitni hversu víða hún hefur komið við á sínum farsæla ferli.", long_description: "", page_count: 357, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 19, book_author_id: nil},
  {source_id: 15345, pre_title: "", title: "Lykilorð 2021", post_title: "Orð Guðs fyrir hvern dag", slug: "lykilord-2021-15345", description: "Í bókinni eru biblíuvers fyrir hvern dag ársins auk ljóða erindis eða fleygs orðs. Uppbygging hennar og innihald bíður upp á fjölbreytta notkun fyrir þá sem leyfa Orði Guðs að vekja sig til umhugsunar og hafa jákvæð áhrif á líf sitt. Auk þess að vera gefin út á bókarformi eru textar hvers dags lesnir í hljóðvarpi og birtast að hluta til á helstu samfélagsmiðlum. >>>>Lykilorð<<<< hafa komið út árlega á íslensku frá því árið 2006.", long_description: "", page_count: 144, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 20, book_author_id: nil},
  {source_id: 15346, pre_title: "", title: "Brot úr spegilflísum", post_title: "", slug: "brot-ur-spegilflisum-15346", description: ">>>Brot úr spegilflísum<<< er fyrsta ljóðabók Þórhildar Ólafsdóttur en hún hefur áður þýtt úr tyrknesku bókina Memed mjói eftir Yashar Kemal og birt ljóð og smásögu í TMM. \r\nÞórhildur dregur hér upp myndir af núinu, sorgum, augnablikum og heiman af æskuslóðum.", long_description: "", page_count: 107, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 21, book_author_id: nil},
  {source_id: 15347, pre_title: "", title: "Miðbæjarrottan", post_title: "borgarsaga", slug: "midbaejarrottan-15347", description: "Rannveig er lattelepjandi miðbæjarrotta sem elskar fátt meira en borgarlífið. Þegar Karlotta frænka hennar týnist í Reykjavík, læðist að henni efi um að borgin sé góður staður fyrir rottur. Rannveig leitar aðstoðar hjá styttum bæjarins, sem margar muna tímana tvenna. \r\nAuður Þórhallsdóttir er bæði höfundur texta og mynda en þetta er hennar þriðja bók.", long_description: "", page_count: 40, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 21, book_author_id: nil},
  {source_id: 15348, pre_title: "", title: "Refurinn ráðsnjalli", post_title: "", slug: "refurinn-radsnjalli-15348", description: "Refurinn ráðsnjalli býr í greni í skógi einum ásamt Fóu og yrðlingunum þeirra fjórum. Í nágrenninu búa þrír stórbændur. Refurinn ráðsnjalli hefur þann háttinn á að læðast í forðabúr þeirra á kvöldin og næla sér í eitthvað gott í kvöldmatinn, án þess að biðja um leyfi! Bændurnir eru aldeilis ekki ánægðir með þetta og ákveða að koma rebba fyrir kattarnef. Þá upphefst meiriháttar eltingarleikur og við skulum spyrja að leikslokum! Skemmtileg bók frá sagnameistaranum mikla, Roald Dahl.", long_description: "", page_count: 98, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 22, book_author_id: nil},
  {source_id: 15349, pre_title: "", title: "BFG", post_title: "", slug: "bfg-15349", description: ">>>BFG<<< segir frá lágvöxnum risa í Risalandi. Hann kallast Bergrisinn frómi góði. Á kvöldin veiðir hann drauma fyrir börn. Í Risalandi búa samt líka níu aðrir hræðilegir risar. Eitt kvöld neyðist BFG til að ræna Soffíu, 8 ára stúlku og fara með hana til Risalands. Þau ákveða að stöðva voðaverk vondu risanna. >>>BFG<<< er spennandi, fyndið og ljúfsárt verk, þar sem höfundur bregður á leik með tungumálið.", long_description: "", page_count: 236, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 22, book_author_id: nil},
  {source_id: 15350, pre_title: "", title: "Georg og magnaða mixtúran", post_title: "", slug: "georg-og-magnada-mixturan-15350", description: "Georg er átta ára. Á heimili hans býr amma hans. Hún er mjög andstyggileg við vesalings Georg sem þarf svo oft að passa hana. Hann ákveður því að búa til mixtúru til að lækna ömmu af andstyggilegheitunum. Mixtúran reynist mögnuð! Best er að vara lesendur við því að búa hana til! >>>Georg og magnaða mixtúran<<< er mögnuð bók og skemmtileg frá hinum eina sanna Roald Dahl.", long_description: "", page_count: 106, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 22, book_author_id: nil},
  {source_id: 15351, pre_title: "", title: "Papa", post_title: "", slug: "papa-15351", description: "Axel Steen starfar nú í leyniþjónustunni. Í Amsterdam kemst hann í innsta hring Papa, harðsvíraðs rússnesks glæpaforingja. Vicki Thomsen, fyrrum félagi Axels í lögreglunni, fæst við bíræfið bankarán á Nørreport. Í ljós kemur að rússnesk öfl tengjast málinu. Málin fléttast saman. >>>PAPA<<< fjallar um flótta, blekkingar, forboðna ást og svik sem hafa banvænar afleiðingar.", long_description: "", page_count: 450, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 22, book_author_id: nil},
  {source_id: 15352, pre_title: "", title: "Grísafjörður", post_title: "", slug: "grisafjordur-15352", description: "Tvíburarnir Inga og Baldur eru komnir í sumarfrí. Á dagskránni er að drekka kókómalt, glápa á teiknimyndir og slappa af með tilþrifum. Fyrirætlanir þeirra virðast ætla að fjúka út í veður og vind þegar Albert, nágranni þeirra af efstu hæðinni, birtist óvænt í heimsókn hjá þeim. Honum er augljóslega mikið niðri fyrir. \r\nÍ þessari bráðskemmtilegu, fyndnu og fjörugu bók, sem segir frá ólíklegri vináttu, liggur leiðin allt frá blokkaríbúð í Reykjavík til Ísafjarðar, Alpanna, Guatemala, Glimmerfjalla og síðast en ekki síst – til Grísafjarðar!", long_description: "", page_count: 152, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15353, pre_title: "", title: "Gréta og risarnir", post_title: "", slug: "greta-og-risarnir-15353", description: "Gréta býr í fallegum skógi en risar ógna heimkynnum hennar og dýranna. Þegar risarnir komu fyrst í skóginn hjuggu þeir niður tré til að byggja heimili. Svo hjuggu þeir niður fleiri tré til að byggja stærri heimili. Húsin urðu að bæjum og bæirnir urðu að borgum og nú er varla neinn skógur eftir. Gréta veit að hún þarf að hjálpa dýrunum sem búa í skóginum, en hvernig? Sagan er innblásin af Gretu Thunberg og aðgerðum hennar til að bjarga heiminum.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15354, pre_title: "", title: "Hvíti björninn og litli maurinn", post_title: "", slug: "hviti-bjorninn-og-litli-maurinn-15354", description: "Hvað gerir pínulítill maur sem er aleinn og týndur uppi í fjöllum þegar byrjar að snjóa? Og hvað gerir hvítur björn sem ætlar að sofa heilan vetur en er vakinn aftur og aftur? Þessi fallega bók miðlar mikilvægum gildum á borð við vináttu, að hjálpast að og veita aðstoð. Sagan ýtir einnig undir mál- og líkamsvitund barna í gegnum skemmtilega frásögn.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15355, pre_title: "", title: "Svefnfiðrildin", post_title: "", slug: "svefnfidrildin-15355", description: "Sunna er fjörug stelpa. Einn daginn þarf hún að fara til læknis sem segir henni frá svefnfiðrildunum en þau hjálpa okkur að sofna og hvílast vel á nóttinni. Svefnfiðrildin eru stórmerkileg og Sunna getur varla beðið eftir að segja öllum frá töfrum þeirra! \r\nSagan útskýrir mikilvægi svefns og hvíldar fyrir börnum og í bókinni má einnig finna góð ráð til foreldra varðandi svefn barna og svefndagbók.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15356, pre_title: "", title: "Vertu þú!", post_title: "", slug: "vertu-thu-15356", description: "Hvað er það sem gerir mig að mér og þig að þér? Það skiptir ekki máli hverju við klæðumst, hverjum við verðum skotin í, hvaða kyni við upplifum okkur í, hvaðan við erum eða hverju við höfum áhuga á. Við megum öll vera eins og okkur langar til.\r\n >>>Vertu þú!<<< segir litríkar sögur af fjölbreytileikanum.", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15357, pre_title: "", title: "Íslandsdætur", post_title: "", slug: "islandsdaetur-15357", description: "Hér er sögð saga rúmlega fjörutíu framúrskarandi kvenna sem spanna tímann allt frá upphafi byggðar á Íslandi til dagsins í dag. Sumar eru þekktar fyrir hugrekki og staðfestu eða fyrir að hafa upplifað ótrúlegar þrekraunir og tekist á við þær af seiglu og áræðni. Aðrar voru brautryðjendur, landkönnuðir, menntakonur, sjómenn eða þekktar fyrir vísindastörf, íþróttaafrek, ritstörf, tónsmíðar eða aðrar listir. Hér er einnig sögð saga kvenna sem börðust fyrir jafnrétti kynjanna og gáfu kost á sér til forystu til að bæta samfélagið.", long_description: "", page_count: 108, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15358, pre_title: "", title: "Engin sóun", post_title: "Leiðarvísir að einfaldara, sorplausu heimili", slug: "engin-soun-15358", description: "Sorplaust líf er hugmyndafræði sem miðar að því að forðast alla sorpmyndun og sóun eins og frekast er mögulegt. Þessi bók býður upp á hagnýtar, þrautreyndar lausnir til að lifa innihaldsríkara, heilbrigðara og einfaldara lífi með sjálfbærum, sorplausum leiðum sem eru nú þegar fyrir hendi. Afþakka það sem við þurfum ekki, draga úr því sem við þurfum, endurnýta það sem við notum, endurvinna það sem við getum ekki afþakkað, dregið úr eða endurnýtt og jarðgera afganginn.", long_description: "", page_count: 368, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15359, pre_title: "", title: "Lífsgæðadagbókin", post_title: "", slug: "lifsgaedadagbokin-15359", description: "Grunnurinn að lífsgæðum okkar byggist á því að við hlúum vel að grunnstoðunum fjórum; næringu, hreyfingu, samskiptum og svefni. Í þessari bók má skrifa niður stöðuna á þessum þáttum en með því verðum við meðvitaðri um að næra þessar grunnstoðir dag frá degi. >>>Lífsgæðadagbókin<<< hjálpar þér að rækta samband þitt við sjálfa(n) þig og að forgangsraða í þágu lífsgæða og hamingju.", long_description: "", page_count: 192, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15360, pre_title: "", title: "Framkoma", post_title: "", slug: "framkoma-15360", description: "Langflest þurfum við á einhverjum tímapunkti að koma fram og tala fyrir framan hóp fólks. Framkoma getur haft mikil áhrif á það hvernig aðrir meðtaka það sem við segjum. Við höfum öll eitthvað fram að færa en við sannfærum engan ef við getum ekki komið því frá okkur á réttan hátt. Í >>>Framkomu<<< er farið yfir grundvallaratriði þess að koma sér á framfæri á fjölbreyttum vettvangi. Í bókinni eru birt góð ráð frá reynslumiklu fólki í fjölmiðlum og atvinnulífinu.", long_description: "", page_count: 208, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15361, pre_title: "", title: "Bókin sem þú vildir að foreldrar þínir hefðu lesið", post_title: "(og börnin þín fagna að þú gerir)", slug: "bokin-sem-thu-vildir-ad-foreldrar-thinir-hefdu-lesid-15361", description: "Hvað stendur í vegi fyrir góðum tengslum við börn og hvað getur styrkt þau? Bókin hjálpar þér m.a. að skilja hvernig þitt eigið uppeldi hefur áhrif á þig, sættast við að þú munir gera mistök og hvað þú getur gert í þeim, brjóta á bak aftur neikvæð mynstur, vinna með tilfinningar, þínar eigin og barnsins þíns og skilja hvað mismunandi hegðun gefur til kynna.", long_description: "", page_count: 252, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15362, pre_title: "", title: "Rennismíði fyrir grunnnám málmiðna", post_title: "", slug: "rennismidi-fyrir-grunnnam-malmidna-15362", description: "Bókin fjallar um undirstöðuatriði í rennismíði og er ætluð nemendum í grunndeild málmiðna. Sagt er frá rennibekknum og einstökum hlutum hans, grunnatriðum við rennsli, uppfestingu vinnslustykkja, formi og gerðum rennistáls o.fl. Bókin er að hluta til byggð á kennslubókum Þorsteins Guðlaugssonar.", long_description: "", page_count: 108, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 24, book_author_id: nil},
  {source_id: 15363, pre_title: "", title: "Samskipti", post_title: "", slug: "samskipti-15363", description: "Með góðum aðferðum í samskiptum getum við gert okkar eigið líf betra á sama tíma og við bætum líf fólksins í kringum okkur. >>>Samskipti<<< er leiðarvísir að jákvæðum samskiptum á mörgum sviðum, hvort sem er í einkalífinu, á vinnustað eða í félagslífinu. Farið er ítarlega yfir alls kyns samskipti milli einstaklinga, samskipti í hópum, hagnýtar aðferðir í samskiptum við börn, samskipti við ókunnuga og samskipti á netinu. Í bókinni má finna ótal góð ráð, æfingar, hugleiðingar og dæmisögur til að auka færni okkar á sviði samskipta.", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15364, pre_title: "", title: "Stærðfræði 3A", post_title: "vigrar - hornaföll - þríhyrningar - hringir - ákveður - stikun", slug: "staerdfraedi-3a-15364", description: "Bókin er hluti af nýjum kennslubókaflokki í stærðfræði fyrir framhaldsskóla. Hún er ætluð nemendum á 3. þrepi (fyrsta áfanga). Áður hafa komið út Stærðfræði 1 (fyrir nemendur sem hafa ekki hlotið nægan undirbúning fyrir nám á 2. þrepi) og Stærðfræði 2A og 2B (fyrir nám á 2. þrepi (fyrsta áfanga).", long_description: "", page_count: 136, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 24, book_author_id: nil},
  {source_id: 15365, pre_title: "", title: "Klettaborgin", post_title: "", slug: "klettaborgin-15365", description: "Sólveig Pálsdóttir var ung send í sveit austur í Skaftafellsýslu. Klettaborgin byggir á minningum Sólveigar frá æsku og fram undir tvítugt og persónugalleríið er fjölbreytt. Hún skrifar um fólk og atburði, sem hafa haft áhrif á líf hennar, og lýsir horfnum heimi og annars konar lífsgildum en við þekkjum í dag. Klettaborgin geymir fallegar sögur og dýrmætar minningar um dýrmætar manneskjur.", long_description: "", page_count: 192, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15367, pre_title: "", title: "Snuðra og Tuðra fara í sund", post_title: "", slug: "snudra-og-tudra-fara-i-sund-15367", description: "Snuðru og Tuðru finnst afskaplega gaman að fara í sund eins og flestum börnum. Þegar mamma þeirra er upptekin í vinnunni og kemst ekki með þeim í laugina ákveða þær að taka málin í sínar eigin hendur.", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15368, pre_title: "", title: "Snuðra og Tuðra í jólaskapi", post_title: "", slug: "snudra-og-tudra-i-jolaskapi-15368", description: "Snuðra og Tuðra hlakka ótrúlega til jólanna á hverju ári. Þær eru staðráðnar í að vera þægar og góðar og hjálpa til við að skreyta heimilið og jólatréð. Þegar aðfangadagur rennur loks upp er spennan í hámarki hjá systrunum og þá getur ýmislegt gerst!", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15369, pre_title: "", title: "Þjálffræði", post_title: "", slug: "thjalffraedi-15369", description: "Bókin fjallar um ýmsar hliðar líkamsþjálfunar og er undirstöðurit í íþróttafræðum. Hún er ætluð fyrir nám á íþróttabrautum framhaldsskóla og sem kennsluefni í þjálfaramenntun íþróttahreyfingarinnar. Hún er unnin í nánu samstarfi við Íþrótta- og Ólympíusamband Íslands.", long_description: "", page_count: 589, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 24, book_author_id: nil},
  {source_id: 15370, pre_title: "", title: "Það sem ég hef lært", post_title: "", slug: "thad-sem-eg-hef-laert-15370", description: "Bókin >>>Það sem ég hef lært<<< er fyrsta bók Önnu Lóu Ólafsdóttur. Hún hefur s.l ár skrifað pistla og birt á netsíðu og Facebook undir nafninu Hamingjuhornið. Bókin, sem er bæði innihaldsrík og falleg, er byggð á þessum skrifum hennar en í henni fjallar  Anna Lóa fjallar um hamingjuna, sorgina, ástina, sambönd, breytingar, sjálfstraust, kvíða ofl.", long_description: "", page_count: 178, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 25, book_author_id: nil},
  {source_id: 15371, pre_title: "", title: "Fríða og Ingi bróðir", post_title: "", slug: "frida-og-ingi-brodir-15371", description: "Fríða og Ingi bróðir gerist í Reykjavík 1950 og er ríkulega myndskreytt í anda þess tíma. Fríða er sex ára og nýbúin að eignast bróður. Hún lofar að passa hann alltaf. Það er hins vegar ekki auðvelt. Reynt er að stela honum, hann týnist í miðbænum og á 17. júní dettur Ingi í Tjörnina. Það er fullt starf fyrir Fríðu að vera stóra systir hans Inga bróður.", long_description: "", page_count: 92, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 26, book_author_id: nil},
  {source_id: 15372, pre_title: "", title: "Ljóð 2010-2015", post_title: "", slug: "ljod-2010-2015-15372", description: ">>>Ljóð 2010–2015<<< hefur að geyma þrjár fyrstu ljóðabækur Þórdísar Gísladóttur, sem hafa lengi verið ófáanlegar. Þetta eru bækurnar >>>Leyndarmál annarra<<< (2010) sem hlaut bókmenntaverðlaun Tómasar Guðmundssonar, >>>Velúr<<< (2014) sem var tilnefnd til Íslensku bókmenntaverðlaunanna og >>>Tilfinningarök<<< (2015). \r\nÚlfhildur Dagsdóttir bókmenntafræðingur ritar formála.", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15373, pre_title: "", title: "Álabókin", post_title: "Sagan um heimsins furðulegasta fisk", slug: "alabokin-15373", description: "Állinn er leyndardómsfyllsti fiskur veraldar. Aristóteles og Sigmund Freud reyndu árangurslaust að öðlast skilning á tilvist álsins, en þessi dularfulla lífvera er enn ráðgáta og nú óttumst við að hann verði útdauða. \r\n\r\nBók um þekkingarleit, lífið sjálft, hvernig á að lifa því og áskorunina sem bíður okkar allra; að deyja. \r\n\r\n>>>Álabókin<<< hlaut Sænsku bókmenntaverðlaunin 2019.", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15374, pre_title: "", title: "Tunglið er diskókúla", post_title: "", slug: "tunglid-er-diskokula-15374", description: "„Hér er bók um það hvernig heimurinn springur stundum út í villtu streymi og rambar á mörkum draums og veruleika, erótíkur og klósettferða, ástar og losta, regnboga og ælu. Þetta er skáldið sem þú átt eftir að taka eftir næstu árin, flæðið er farið í gang,“ segir Elísabet Jökulsdóttir. „Loksins, Loksins!“ segir Mikael Torfason. \r\n>>>Tunglið er diskókúla<<< er fyrsta bók Loka sem er fæddur árið 1993.", long_description: "", page_count: 120, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15375, pre_title: "", title: "Hetjusögur", post_title: "", slug: "hetjusogur-15375", description: "Ljóðin í bókinni eru ort upp úr ritinu >>>Íslenskar ljósmæður<<< I-III sem séra Sveinn Víkingur bjó til prentunar og kom út hjá Kvöldvökuútgáfunni á Akureyri 1962-1964. Þar eru prentaðir æviþættir og endurminningar 100 ljósmæðra.\r\n>>>Hetjusögur<<< er fjórða ljóðabók Kristínar Svövu sem er sagnfræðingur, fædd í Reykjavík árið 1985.", long_description: "", page_count: 114, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15376, pre_title: "", title: "Næsti!", post_title: "Raunir heimilislæknis", slug: "naesti-15376", description: "Heimilislæknir til tuttugu ára hefur Elín fengið sig fullsadda af umkvörtunum og óhamingju fólks. Hún er líka búin að fá nóg af manninum sínum, skíðaáhugamanninum Axel. Í kjölfar endurnýjaðra kynna við gamlan kærasta tekur Elín að brjóta upp mynstrið ... til þess að standa svo í rjúkandi rústum. Meinfyndin saga sem hlaut Norsku bókmenntaverðlaunin 2019.", long_description: "", page_count: 272, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15377, pre_title: "", title: "Þagnarbindindi", post_title: "", slug: "thagnarbindindi-15377", description: "Ljóðsagan >>>Þagnarbindindi<<< miðlar hugarheimi konu sem reynir að ná tökum á minningum um missi og ótta, um samskipti við konurnar sem hafa verið henni nánastar og henda reiður á svo mörgu sem hún hefur aldrei sagt. \r\nÞetta er fyrsta bók Höllu Þórlaugar, sem er fædd 1988. Hún hlaut Nýræktarstyrk Miðstöðvar íslenskra bókmennta fyrir þetta verk.", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15378, pre_title: "", title: "Myndskreytt handbók um skrautlegar skepnur", post_title: "", slug: "myndskreytt-handbok-um-skrautlegar-skepnur-15378", description: "Vissir þú að hrafnar geta hermt eftir hljóðum eins og páfagaukar? Það er ekki nóg með að moskítóflugur bíti – þær spræna líka á mann. Krossfiskar eru heilalausir en kolkrabbar með fleiri heilasellur en mannskepnan ... og þrjú hjörtu! \r\nBráðskemmtilegar staðreyndir og fyrsta flokks teikningar eftir hina sænsku Maju Safström.", long_description: "", page_count: 120, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15379, pre_title: "", title: "Afnám haftanna - Samningar aldarinnar?", post_title: "", slug: "afnam-haftanna-samningar-aldarinnar-15379", description: "Afnám fjármagnshaftanna, sem sett voru á í kjölfar falls bankanna 2008, er stórmerkileg saga sem fjölmargir komu að. Íslendingar hafa aldrei átt jafn mikið undir samningamönnum sínum og þegar samið var við kröfuhafana. Afar fróðleg bók þar sem Sigurður Már Jónsson, fyrrum upplýsingafulltrúi ríkisstjórnarinnar, fer yfir hvað gerðist á bak við tjöldin.", long_description: "", page_count: 344, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 27, book_author_id: nil},
  {source_id: 15380, pre_title: "", title: "Björgunarsveitin mín", post_title: "", slug: "bjorgunarsveitin-min-15380", description: "Björgunarsveitin mín er gefin út í tilefni af 70 ára afmæli Flugbjörgunarsveitarinnar. Arngrímur Hermannsson safnaði saman áhugaverðum frásögnum frá fjölmörgum félögum svo að úr verður einkar áhugaverð, fróðleg, spennandi og skemmtileg bók. Hér eru hvoru tveggja leitar- og björgunarsögur og ferðasögur félaga sveitarinnar á þessu 70 ára tímabili.", long_description: "", page_count: 308, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 27, book_author_id: nil},
  {source_id: 15381, pre_title: "", title: "Sjáðu!", post_title: "", slug: "sjadu-15381", description: ">>>Sjáðu!<<< er myndaævintýri fyrir allra yngstu börnin þar sem upplagt er að benda, skoða og undrast. Stutt vers leiða lesendur í gegnum furðuheim sem vekur spurningar og vangaveltur. Þykk spjöldin henta yngstu bókaormunum vel en allir fá eitthvað fyrir sinn snúð því >>>Sjáðu!<<< er bók sem vex með barninu.", long_description: "", page_count: 20, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15382, pre_title: "", title: "Fíllinn fljúgandi", post_title: "", slug: "fillinn-fljugandi-15382", description: "Hvernig kemst fíll alla leið frá Afríku til Íslands? Geta fílar kannski flogið? >>>Fíllinn fljúgandi<<< er skemmtileg og spennandi saga fyrir yngstu bókaormana um það hvernig forvitinn fílsungi og hugrakkur strákur verða bestu vinir. Myndir Auðar Ýrar auðga sögu Þorgríms svo úr verður gullfalleg bók.", long_description: "", page_count: 43, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15383, pre_title: "", title: "Hryllilega stuttar hrollvekjur", post_title: "", slug: "hryllilega-stuttar-hrollvekjur-15383", description: ">>>Hryllilega stuttar hrollvekjur<<< geymir tuttugu smásögur sem eru hver annarri hræðilegri. Hér má meðal annars lesa um vampírur, mannát, uppvakninga, drauga og skrímslin sem leynast undir rúminu þínu. Metsöluhöfundurinn Ævar Þór Benediktsson var myrkfælinn þangað til hann varð sautján ára. Hér skrifar hann um allt sem hann var hræddur við.", long_description: "", page_count: 153, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15384, pre_title: "", title: "Blokkin á heimsenda", post_title: "", slug: "blokkin-a-heimsenda-15384", description: "Dröfn hefur aldrei hitt ömmu sína svo að óvænt ferðalag á eyjuna hennar, þar sem allir búa saman í einni stórri blokk, hljómar spennandi. En ferðin verður fljótlega dálítið skrýtin. Getur verið að einhver eyjarskeggja vilji Dröfn og fjölskyldu hennar illt? >>>Blokkin á heimsenda<<< hlaut Barnabókaverðlaun Guðrúnar Helgadóttur 2020 og var að mati dómnefndar bæði grípandi og gamansöm.", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15386, pre_title: "", title: "Knúsípons", post_title: "", slug: "knusipons-15386", description: "", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15387, pre_title: "", title: "Risaeðlur", post_title: "", slug: "risaedlur-15387", description: "", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15388, pre_title: "", title: "Milljarðastrákurinn", post_title: "", slug: "milljardastrakurinn-15388", description: "Fyndin og ljúfsár bók úr smiðju metsöluhöfundarins David Walliams. Jói Smálki er ríkasti tólf ára strákur í heimi. Hann á allt sem hugurinn girnist: sinn eigin formúlu-kappakstursbíl, þúsund skópör og jafnvel einkaþjón sem er órangútan. En það er eitt sem hann saknar sárlega: vin ... David Walliams fer hér á kostum í frábærlega fyndinni bók.", long_description: "", page_count: 278, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15389, pre_title: "", title: "Gullfossinn", post_title: "", slug: "gullfossinn-15389", description: "Sóldís og Sumarliði eru önnum kafin við að finna not fyrir allt tæknilega dótið sem leyndist í koparegginu úr fortíðinni. Á meðan heldur dularfull stelpa af stað eftir leynigöngum, slúðrandi fréttahaukur hoppar um á priki og rustarnir í dalnum leita allra leiða til að endurheimta völdin. >>>Gullfossinn<<< er spennandi framtíðarsaga og framhald >>>Silfurlykilsins<<< og >>>Kopareggsins<<<.", long_description: "", page_count: 218, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15390, pre_title: "", title: "Iðunn og afi pönk", post_title: "", slug: "idunn-og-afi-ponk-15390", description: "Þegar glænýja hjólið hennar Iðunnar hverfur grunar hún systurnar í Súluhöfða strax um græsku. Þá er verst að mamma og pabbi eru farin í ferðalag og barnapían afi pönk hugsar meira um lúsmý og eldgamlar hljómsveitir en tapað hjól. Stórskemmtileg saga um flókna ráðgátu og fjörugar persónur eftir einn af okkar færustu höfundum.", long_description: "", page_count: 133, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15391, pre_title: "", title: "Verstu kennarar í heimi", post_title: "", slug: "verstu-kennarar-i-heimi-15391", description: "Ef þú kannast við Verstu börn í heimi skaltu búa þig undir dálítið miklu, miklu verra ... VERSTU KENNARA Í HEIMI! Hér er sagt frá ótta herra Knúts, ógnarþríhjóli fröken Ógnar, doktor Ofsa og stól þúsund prumpa, ógnarsal frú Sléttu, og hryllingssöguni um hjónin Turtil og Dúfu & tárahafið. Ég varaði þig við! Hér er David Walliams upp á sitt besta.", long_description: "", page_count: 308, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15392, pre_title: "", title: "SVARTA KISA – Hundadagur", post_title: "", slug: "svarta-kisa-hundadagur-15392", description: "", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15393, pre_title: "", title: "SVARTA KISA – Í Svartaskóla", post_title: "", slug: "svarta-kisa-i-svartaskola-15393", description: "", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15394, pre_title: "", title: "Tíminn minn 2021", post_title: "", slug: "timinn-minn-2021-15394", description: "Hlý og fallega myndskreytt dagbók eftir íslensku listakonuna Björgu Þórhallsdóttur, sem nú er einn vinsælasti listamaður Noregs. Þessi dagbók er sniðin fyrir konur, full af jákvæðni og góðum ráðum. Tíminn minn er uppáhalds dagbók fjölda íslenskra kvenna.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15395, pre_title: "", title: "Barnaræninginn", post_title: "", slug: "barnaraeninginn-15395", description: "Eftir hallarbyltinguna í Hafnarlandi ríkir friður og ró. En ekki lengi. Brátt er Barnaræninginn aftur kominn á stjá og Eyrdís verður að stöðva hann, þó að það gæti orðið hennar bani! >>>Barnaræninginn<<< er jafnspennandi og >>>Draumaþjófurinn<<<, fyrri bókin um rotturnar í Hafnarlandi sem naut mikilla vinsælda og var tilnefnd til Barnabókaverðlauna Reykjavíkur.", long_description: "", page_count: 224, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15396, pre_title: "", title: "Þín eigin undirdjúp", post_title: "", slug: "thin-eigin-undirdjup-15396", description: "Sjöunda bókin í einum vinsælasta bókaflokki síðari ára. Hér ert þú söguhetjan og stjórnar ferðinni. Þér er boðið um borð í kafbát þar sem þrír stórskrítnir skipstjórar ráða ríkjum. Á leið ykkar um undirdjúpin leitið þið að sokknum fjársjóði, kannið hinn banvæna Bermúdaþríhyrning og eltið uppi heimsins stærsta sjávarskrímsli. Ný og spennandi bók eftir margfaldan verðlaunahöfund.", long_description: "", page_count: 336, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15397, pre_title: "", title: "Nærbuxnavélmennið", post_title: "", slug: "naerbuxnavelmennid-15397", description: "Gutti er enn innsti koppur í búri í Rumpinum, samfélagsmiðstöðinni í gömlu nærbuxnaverksmiðjunni, en Ólína vinkona hans er orðin aðalstjarnan í íþróttaliði bæjarins. Þess vegna þarf Gutti að glíma við það aleinn þegar vélmennið BlúnduRASS 3000 gengur af göflunum. Sprenghlægilegt framhald á >>>Nærbuxnaverksmiðjunni<<< og >>>Nærbuxnanjósnurunum<<< sem glatt hafa unga sem aldna.", long_description: "", page_count: 120, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15399, pre_title: "", title: "Andlitslausa konan", post_title: "", slug: "andlitslausa-konan-15399", description: "Skelfilegur glæpur er framinn í brúðkaupi sem Eddu á Birkimelnum er boðið í. Rannsókn málsins er snúin en varpar óvænt ljósi á veislu sem forsætisráðherra undirbýr í gamla Þingvallabænum og setur leyndardómsfull áramótaplön barnabarns Eddu líka í uppnám. Fimmta bókin um eftirlaunaþegann Eddu sem leysir flókin sakamál með hyggjuvitið að vopni.", long_description: "", page_count: 329, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15400, pre_title: "", title: "Brúin yfir Tangagötuna", post_title: "", slug: "bruin-yfir-tangagotuna-15400", description: "Það er vinnslustopp í rækjunni, gatan sundurgrafin og bærinn fullur af forvitnum túristum. Ef ekki væri fyrir nágrannakonuna handan við brúna væri líf Halldórs ansi dapurlegt. En þótt það séu bara níu og hálft skref úr anddyrinu hans að dyrunum hennar er leiðin þangað furðulega flókin. Launfyndin, ísfirsk ástarsaga úr samtímanum eftir verðlaunahöfund sem hvarvetna vekur athygli.", long_description: "", page_count: 216, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15401, pre_title: "", title: "Morðin í Háskólabíó", post_title: "", slug: "mordin-i-haskolabio-15401", description: "Prestur á líknardeildinni leitar til Stellu með hinstu játningu um nauðgun og hvarf tólf ára stúlku norður á Ströndum tuttugu árum fyrr. Gömul kærasta verður fyrir dularfullri líkamsárás og skjólstæðingur Stellu ferst í sprengingu á kosningahátíð í Háskólabíó. En ekkert mál er of flókið fyrir harðasta og kjaftforasta lögmann landsins.", long_description: "", page_count: 272, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15402, pre_title: "", title: "Váboðar", post_title: "", slug: "vabodar-15402", description: "Fyrsta smásagnasafn verðlaunahöfundarins Ófeigs Sigurðssonar geymir ná- og fjarskyldar sögur af draumum og fyrirboðum, sérhæfðum rannsóknum og stórhuga áformum, öpum og máfum, bókum og hnífum, dyntóttu landi og ráðvilltri þjóð. Ágengar, fyndnar og frumlegar sögur þar sem misjafnlega venjulegt fólk glímir af veikum mætti við aðsteðjandi ógnir og óttann undir niðri.", long_description: "", page_count: 197, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15403, pre_title: "", title: "Undir Yggdrasil", post_title: "", slug: "undir-yggdrasil-15403", description: "Þorgerður Þorsteinsdóttir mátti bíða lengi eftir óskabarninu sínu. Því harmþrungnari eru atburðirnir eftir þingið á Þórsnesi þegar tátlan er aðeins sjö ára gömul. Þorgerður kýs heldur að leita styrks hjá skapanornunum en Hvítakristi ömmu sinnar, Auðar djúpúðgu, og leitin að sannleikanum leiðir hana á óvæntar slóðir. Sögulegar skáldsögur Vilborgar Davíðsdóttur hafa notið mikilla vinsælda enda varpa þær nýstárlegu ljósi á Íslandssöguna, ekki síst örlög og aðstæður kvenna.", long_description: "", page_count: 329, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15404, pre_title: "Hrímland", title: "Skammdegisskuggar", post_title: "", slug: "skammdegisskuggar-15404", description: "Sæmundur hefur verið rekinn úr Svartaskóla fyrir hættulegt fikt við svartagaldur. Garún vill losa Hrímland undan erlendum kúgurum og til þess að knýja fram byltinguna sem hún þráir leitar hún til Sæmundar – með ófyrirséðum afleiðingum.  Mögnuð og myrk samtímafurðusaga sem nálgast þjóðsagnaarfinn og Íslandssöguna á ferskan og frumlegan hátt.", long_description: "", page_count: 490, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15405, pre_title: "", title: "Truflunin", post_title: "", slug: "truflunin-15405", description: "Árið er 2034. Heimurinn hefur kvíslast í tvær víddir sem snertast á litlu svæði í miðbæ Reykjavíkur. Múr umlykur svæðið og inn fyrir hann fer einungis þrautþjálfað vísindafólk og hermenn, svokallaðir agentar. Einn þeirra er félagssálfræðingurinn Halla sem falið er að finna agentinn F sem sendi frá sér torrætt neyðarkall áður en hún hvarf sporlaust. >>>Truflunin<<< er grípandi framtíðartryllir eftir höfund sem á fáa sína líka.", long_description: "", page_count: 303, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15406, pre_title: "", title: "Ofurnarfi og Gréta glytta", post_title: "", slug: "ofurnarfi-og-greta-glytta-15406", description: "", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15408, pre_title: "", title: "Á krossgötum", post_title: "", slug: "a-krossgotum-15408", description: "", long_description: "", page_count: 476, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15409, pre_title: "", title: "Hrundar vörður", post_title: "", slug: "hrundar-vordur-15409", description: "", long_description: "", page_count: 454, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15410, pre_title: "", title: "Narfi – Einhyrningur hafsins", post_title: "", slug: "narfi-einhyrningur-hafsins-15410", description: "", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15411, pre_title: "", title: "Ísskrímslið", post_title: "", slug: "isskrimslid-15411", description: "Enn og aftur kemur David Walliams með frábæra bók. Þegar Elsa, tíu ára munaðarlaust götubarn í Lundúnum árið 1899, heyrir að dularfullt ísskrímsli – mammútur, hafi fundist á norðurslóðum – ákveður hún að kanna málið nánar. Fyndið, ljúft og spennandi ævintýri. Bókin er í frábærri þýðingu meistara Guðna Kolbeinssonar.", long_description: "", page_count: 422, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15412, pre_title: "", title: "Hjartastaður", post_title: "", slug: "hjartastadur-15412", description: "Harpa Eir flýr úr borginni til að bjarga dóttur sinni úr slæmum félagsskap. Stefnan er tekin austur á land og leiðin liggur jafnt um stórbrotna náttúruna og hrjóstrugt landslag hugans. Þessi magnaða og margbrotna ferðasaga er eitt þekktasta verk Steinunnar Sigurðardóttur og fyrir hana hlaut hún Íslensku bókmenntaverðlaunin 1995. Guðni Elísson ritar eftirmála.", long_description: "", page_count: 444, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15413, pre_title: "", title: "Hvítt haf", post_title: "", slug: "hvitt-haf-15413", description: "Árið 1944 hafa Þjóðverjar hertekið Noreg. Ingrid snýr aftur til æskustöðva sinna á Barrey sem hefur verið auð og yfirgefin. Hún býr sig undir kulda og einsemd en fljótlega kemst hún að því að hún er ekki ein á eyjunni. >>>Hvítt haf<<< er önnur bókin í þríleik um fólkið á Barrey sem hlotið hefur fjölmörg verðlaun og viðurkenningar.", long_description: "", page_count: 255, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15414, pre_title: "", title: "Sumarbókin", post_title: "", slug: "sumarbokin-15414", description: "Sígild bókmenntaperla sem segir frá Soffíu litlu og ömmu hennar og sumardvöl þeirra á smáeyju undan strönd Finnlands. Þessi tæra og látlausa en djúpvitra frásögn af örheimi eyjunnar, af náttúrunni og veðrinu og af heimspekilegum samræðum ömmu og Soffíu hefur heillað lesendur í næstum hálfa öld en fæst nú í fyrsta skipti á íslensku.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15415, pre_title: "", title: "Eldum björn", post_title: "", slug: "eldum-bjorn-15415", description: "Sumarið 1852 hverfur smalastúlka frá Kengis, nyrst í Svíþjóð. Óttast er að björn hafi grandað henni og presturinn í þorpinu leitar verksummerkja með samíska piltinn Jussa sér við hlið. En fyrr en varir fjölgar fórnarlömbum og ótti og hjátrú taka völd. >>>Eldum björn<<< er einstök saga, geysivel skrifuð, myrk, hjartnæm og heillandi, eftir höfund metsölubókarinnar >>>Rokkað í Vittula<<<.", long_description: "", page_count: 456, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15416, pre_title: "", title: "Dóttirin", post_title: "", slug: "dottirin-15416", description: "Þegar Torunn ákveður að taka upp eftirnafnið Neshov bresta veik böndin milli hennar og móður hennar. Gamli vonbiðillinn Kai Roger færir henni tvo grísi þótt hún hafi endanlega gefist upp á svínarækt. Eini ljósi punkturinn er afleysingapresturinn Frank sem er svo ansi myndarlegur … Hér lýkur Anne B. Ragde sagnabálkinum vinsæla sem hófst með >>>Berlínaröspunum<<<.", long_description: "", page_count: 327, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15417, pre_title: "", title: "Hvernig á að kenna ömmu og afa að lesa", post_title: "", slug: "hvernig-a-ad-kenna-ommu-og-afa-ad-lesa-15417", description: "Skemmtileg bók fyrir alla þá sem eru að læra að lesa. Það er gaman að lesa en enn skemmtilegra að lesa fyrir ömmu og afa. Þessi bók er í hinni vinsælu ritröð Hvernig á ... sem notið hefur mikilla vinsælda hér á landi sem erlendis. Hvernig á að lesa fyrir ömmu og afa er fyrir alla lesendur, sérstaklega glænýja lesendur.", long_description: "", page_count: 26, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15418, pre_title: "", title: "Saga býflugnanna", post_title: "", slug: "saga-byflugnanna-15418", description: "Í þessari grípandi skáldsögu fléttast þrír þræðir í þétta frásögn sem hverfist í senn um margslungin sambönd fólks og samspil manns og náttúru. >>>Saga býflugnanna<<< eftir metsöluhöfundinn Maju Lunde sló í gegn um allan heim og hefur hlotið fjölda viðurkenninga. „Snjöll og sláandi fögur skáldsaga.“ >>>Stavanger Aftenblad<<<.", long_description: "", page_count: 422, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15419, pre_title: "", title: "Bálviðri", post_title: "", slug: "balvidri-15419", description: "Á aðfangadag árið 1617 skellur aftakaveður á Vardø, austustu byggð Norður-Noregs, þegar allir karlmenn þorpsins eru á sjó. Konurnar horfa á eiginmenn, unnusta, feður, bræður og syni farast. Þær eru einar eftir og þurfa að bjarga sér. Rúmu ári síðar kemur nýr fógeti ásamt konu sinni til að sveigja íbúana undir veraldlegt og andlegt yfirvald. Sjálfstæðu konurnar á eyjunni horfast þá í augu við breytta tíma.", long_description: "", page_count: 384, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15420, pre_title: "", title: "Innræti", post_title: "", slug: "innraeti-15420", description: "Margræð og ísmeygileg ljóð um þau ólíku hlutverk sem kona gegnir í lífinu og bregða í senn ljósi á hversdaginn og snúa upp á hann. Arndís Þórarinsdóttir, sem kunn er fyrir barnabækur sínar, hlaut sérstaka viðurkenningu í samkeppninni um Ljóðstaf Jóns úr Vör 2019. „Innræti er dásamleg ljóðabók – uppfull af ást, húmor, ótta og smá kvíða. Ég get ómögulega mælt nógsamlega með henni.“ Katrín Lilja/Lestrarklefinn.", long_description: "", page_count: 58, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15421, pre_title: "", title: "Kyrralífsmyndir", post_title: "", slug: "kyrralifsmyndir-15421", description: ">>>Kyrralífsmyndir<<< Lindu Vilhjálmsdóttur eru ljóð ort í kófinu sem lagðist yfir samfélagið á útmánuðum 2020, þegar allt breyttist skyndilega: Kunnuglegir hlutir urðu framandi, eðlileg samskipti lögðust nánast af og óttinn náði undirtökum. Ljóð Lindu eru meitlaðar svipmyndir úr þessu sérkennilega andrúmslofti og ljósmyndir hennar magna áhrifin enn frekar.", long_description: "", page_count: 60, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15422, pre_title: "", title: "Handbók um ómerktar undankomuleiðir", post_title: "", slug: "handbok-um-omerktar-undankomuleidir-15422", description: "Þessi níunda ljóðabók Antons Helga er ljóðsaga þar sem undirliggjandi atburðarás minnir á dramatíska óperu sem þó er ekki laus við að vera fyndin. Sagan lýsir annasömum degi hjá ónefndri persónu sem bregst við margvíslegu áreiti umhverfisins með því að ferðast til annarra staða og stunda. Snjöll, kímin og harmræn ljóð um ævintýri hversdagsins.", long_description: "", page_count: 68, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15423, pre_title: "", title: "Er nokkur í Kórónafötum hér inni? / Sendisveinninn er einmana / Róbinson Krúsó snýr aftur", post_title: "", slug: "er-nokkur-i-koronafotum-her-inni-sendisveinninn-er-einmana-robin-15423", description: "Fyrstu þrjár ljóðabækur Einars Más Guðmundssonar komu út 1980 og 1981 og vöktu geysimikla athygli. Tónninn var nýstárlegur, yrkisefnin óvenjuleg, skáldinu mikið niðri fyrir. Hér eru þessar sögufrægu bækur þrjár saman í einni, í tilefni þess að liðnir eru fjórir áratugir síðan skáldið Einar þusti fram á sjónarsviðið – og ljóðin eru enn fersk, fyndin og forvitnileg.", long_description: "", page_count: 142, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15424, pre_title: "", title: "Neihyrningurinn", post_title: "", slug: "neihyrningurinn-15424", description: "Krúttudúllubossalegur einhyrningur kemur í heiminn í Hjartaskógi. Allir snúast í kringum hann og fóðra stanslaust á sykruðum lukkusmára. En litla krílið hagar sér samt hreint ekki einhyrningslega! Hann segir bara: NEI!. Fjölskylda hans fer því fljótlega að kalla hann NEIhyrning. Mjög fyndin og skemmtileg bók, ein allra vinsælasta barnabókin í Þýskalandi.", long_description: "", page_count: 40, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15426, pre_title: "Ferðin á heimsenda", title: "Týnda barnið", post_title: "", slug: "tynda-barnid-15426", description: "Húgó og Alex ætla sér að finna vorið þótt stundum gangi allt á afturfótunum. Nú eru þau á nýjum slóðum og kynnast meðal annars gömlum karli með unglingaveiki, risaskordýrum og dularfullri konu sem býr yfir leyndarmáli úr fortíð Húgós. Þessi bráðfyndna fantasía fyrir 8–12 ára lesendur er framhald bókarinnar >>>Leitin að vorinu<<< sem kom út í fyrra.", long_description: "", page_count: 188, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15427, pre_title: "", title: "Hetja", post_title: "", slug: "hetja-15427", description: "Hetja og Björg eru bestu vinkonur og vilja helst alltaf vera saman. Þess vegna verður Hetja skelfingu lostin þegar svarta hyldýpið flytur hana á brott – og Björg gráti nær þegar hún finnur vinkonu sína hvergi í haganum. >>>Hetja<<< er fyrsta bók leikkonunnar og hestakonunnar Bjarkar Jakobsdóttur, æsispennandi saga um vináttu stúlku og hryssu og baráttu þeirra fyrir að finna hvor aðra aftur.", long_description: "", page_count: 219, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15429, pre_title: "", title: "Sápukúlutindur", post_title: "", slug: "sapukulutindur-15429", description: "", long_description: "", page_count: 123, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15430, pre_title: "", title: "Sykursæta bakaríið", post_title: "", slug: "sykursaeta-bakariid-15430", description: "", long_description: "", page_count: 126, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15431, pre_title: "", title: "Kalli breytist í grameðlu", post_title: "", slug: "kalli-breytist-i-gramedlu-15431", description: "Kalli McGuffin er ofurvenjulegur strákur með óvenjulega krafta: Þegar hann verður kvíðinn breytist hann í dýr – sem kemur honum í alls konar klípur. En sem betur fer á Kalli ráðsnjalla vini sem reynast honum vel. Þetta er sjálfstætt framhald sögunnar >>>Kalli breytist í kjúkling<<< sem fékk krakka á aldrinum 6–10 ára til að grenja úr hlátri í fyrra.", long_description: "", page_count: 295, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15432, pre_title: "", title: "Artemis Fowl", post_title: "", slug: "artemis-fowl-15432", description: "Artemis Fowl er afburðagreindur og bráðsnjall tólf ára glæpamaður. Hann rænir Holly Short til að komast yfir gullsjóð álfanna en hún er bara ekki sátt við að láta ræna sér. Og álfarnir eiga alls konar vopn og græjur og eru sannarlega engin lömb að leika sér við. Fyrsta bókin í hinum geysivinsæla flokki um Artemis Fowl.", long_description: "", page_count: 280, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15433, pre_title: "", title: "Heillaspor – gildin okkar", post_title: "", slug: "heillaspor-gildin-okkar-15433", description: "Heilræðabækur Gunnars Hersveins hafa notið mikilla vinsælda og dregið fram það sem skiptir raunverulega máli. >>>Heillaspor – gildin okkar<<< fjallar um gildin sem leggja grunn að farsælu lífi fyrir ungt fólk og fjölskyldur þess. Fallegar myndir og hugarljós hjálpa lesandanum að finna svörin til að stíga heillaspor í lífinu.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15434, pre_title: "", title: "HUNDMANN – Taumlaus", post_title: "", slug: "hundmann-taumlaus-15434", description: "Bækurnar um HUNDMANN hafa slegið í gegn um allan heim og hafa selst í tugum milljóna eintaka. Dav Pilkey, sem einnig samdi bækurnar um Kaptein Ofurbrók, fer hér á kostum. Húrrandi grín og vitleysisgangur, en oft ansi gott og ristir dýpra en við fyrstu sýn. Þetta er bók sem krakkar elska að lesa aftur og aftur.", long_description: "", page_count: 220, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15435, pre_title: "", title: "Danskvæði um söngfugla og slöngur", post_title: "", slug: "danskvaedi-um-songfugla-og-slongur-15435", description: "Tíundu Hungurleikarnir eru að hefjast í Panem. Hinn átján ára Kóríolanus Snow býr sig undir hlutverk sem getur gjörbreytt framtíð hans. Hann hefur fengið það niðurlægjandi verkefni að leiðsegja stúlkunni úr tólfta umdæmi. En örlög þeirra eru samtvinnuð og inni á leikvanginum verður barist til síðasta blóðdropa. Hér er forsaga metsölubókanna um Hungurleikana sögð.", long_description: "", page_count: 569, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15436, pre_title: "", title: "Gata mæðranna", post_title: "", slug: "gata-maedranna-15436", description: "Marín er á leið út í lífið eftir stúdentspróf, óviss um næstu skref. Leit að leiguherbergi leiðir hana hús úr húsi, frá einni konu til annarrar, og allar eiga þær sögur og drauma. >>>Gata mæðranna<<< gerist á sjöunda áratugnum og hlutverk kynjanna eru skýr; heima í götunni ráða konurnar ríkjum. Hárbeittur húmor og innsæi í grípandi sögu um leyndarmál og ósögð orð, ástleysi og ást.", long_description: "", page_count: 243, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15437, pre_title: "", title: "Aprílsólarkuldi", post_title: "", slug: "aprilsolarkuldi-15437", description: "Þegar Elísabet Jökulsdóttir býður lesendum í innra ferðalag gildir miðinn alla leið. Hér er lýst föðurmissi Védísar, skólastúlku sem er einstæð móðir; ástinni sem kemur næstum jafn óvænt og ferlinu inn í sjúkdóm, öllu í einni augnablikseilífð. Reykjavík fær á sig sérstakan blæ með lýsingum í ekta Elísabetarstíl sem hér er djúptær, litaður flæði og frelsi ljóðsins – hvergi skortir skarpskyggni né húmor.", long_description: "", page_count: 143, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15438, pre_title: "", title: "Blóðrauður sjór", post_title: "", slug: "blodraudur-sjor-15438", description: "Þegar heildsalinn Flosi kemur heim í kvöldmat er Guðrún kona hans horfin og á borðinu er krafa um að hann greiði himinhátt lausnargjald, annars verði hún drepin. Í örvæntingu leitar hann til Áróru sem kann að þefa uppi illa fengið fé. Mannránið gæti tengst alþjóðlegri glæpastarfsemi – eða er skýringin ef til vill mun nærtækari? Hröð og æsispennandi saga með óvæntum vendingum.", long_description: "", page_count: 314, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15439, pre_title: "", title: "Dauði skógar", post_title: "", slug: "daudi-skogar-15439", description: "Það hefur rignt linnulaust í marga daga þegar skógurinn rennur niður hlíðina og setur allt af stað í lífi landeigandans og fjölskylduföðurins Magnúsar. Litlu seinna koma sprengjurnar í ljós. Launfyndin og margræð saga um það þegar hversdagurinn fer á hvolf og dauðinn ber að dyrum. Jónas Reynir hefur vakið mikla athygli fyrir skáldsögur sínar og ljóð og hlotið bæði Bókmenntaverðlaun Tómasar Guðmundssonar og Maístjörnuna.", long_description: "", page_count: 180, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15440, pre_title: "", title: "Uppskriftabók föður míns", post_title: "", slug: "uppskriftabok-fodur-mins-15440", description: "Julien elst upp á litlu veitingahúsi í franskri sveit. Töfraheimur eldhússins heillar hann og hann þráir ekkert heitar en að feta í fótspor listakokksins pabba síns. En faðirinn, sem burðast með sára reynslu úr fortíðinni, hefur aðrar og háleitari hugmyndir um framtíð sonarins. Ljúfsár og hjartnæm þroskasaga full af unaðslegri matargerð.", long_description: "", page_count: 203, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15441, pre_title: "", title: "Hin konan", post_title: "", slug: "hin-konan-15441", description: "Þú þykist viss um hvernig þetta endar. Þú heldur að málið snúist um afbrýðisama eiginkonu. Þú heldur að hún hugsi ekki um annað en nýju konuna, þessa ungu og fallegu. Þú heldur þig hafa náð samhenginu í ástarþríhyrningnum. Eða hvað? Hvað gera konur ekki í nafni ástarinnar?", long_description: "", page_count: 420, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15442, pre_title: "", title: "Blekkingaleikur", post_title: "", slug: "blekkingaleikur-15442", description: "Martin Benner lögmaður býr í Stokkhólmi og gengur flest í haginn. En þegar hann kaupir hlut látins vinar síns í antíkverslun í New York fara óþægilegir atburðir að gerast í kringum hann og fólk hverfur – dularfullur félagsskapur virðist vera á hælum hans og vilja honum illt. Svöl og harðsoðin spennusaga, uppfull af snjöllum ráðgátum og villandi vísbendingum.", long_description: "", page_count: 432, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15443, pre_title: "", title: "Snerting hins illa", post_title: "", slug: "snerting-hins-illa-15443", description: "Fyrir >>>Engla Hammúrabís<<< hlaut Seeck Finnsku glæpasagnaverðlaunin. Hér vinna Annika Lehto og Daniel Kuisma aftur saman og nú við að rekja slóð finnska diplómatans Westerlunds. Brátt uppgötva þau að sannleikurinn er enn hræðilegri en nokkurn hefði grunað.", long_description: "", page_count: 416, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15444, pre_title: "", title: "Eftir endalokin", post_title: "", slug: "eftir-endalokin-15444", description: "Max og Pip eru samhent hjón, bestu vinir, elskendur – ekkert virðist hagga þeim. En þegar sonur þeirra veikist segja læknarnir að þau verði að taka ákvörðun. Í fyrsta skipti eru þau ekki sammála ... Einlæg frásögnin lýsir leiðinni frá nístandi sorg og söknuði til sáttar. Þegar einar dyr lokast opnast stundum aðrar. Áhrifamikil og afar persónuleg saga.", long_description: "", page_count: 416, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15445, pre_title: "", title: "Tvö líf Lydiu Bird", post_title: "", slug: "tvo-lif-lydiu-bird-15445", description: "Lydia og Freddie hafa verið óaðskiljanleg og ástfangin frá unglingsárum en á 28 ára afmælisdegi Lydiu lendir Freddie í slysi og deyr. Áfallið er ólýsanlegt og eina huggun Lydiu er að í svefni hittir hún Freddie og allt er sem fyrr – en í vöku þarf hún að takast á við sorgina og nýjar aðstæður. Heillandi og rómantísk saga um krossgötur lífsins, erfiðleika, ást og hamingjuleit.", long_description: "", page_count: 480, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15446, pre_title: "", title: "Á byrjunarreit", post_title: "", slug: "a-byrjunarreit-15446", description: "Þegar búið er að veita hörkutólinu Jack Reacher orðu fyrir sérstaklega lofsverða breytni í þágu lands og þjóðar er hann sendur í endurmenntun. Í bekknum eru tveir aðrir nemendur, annar frá FBI og hinn frá CIA. Verkefni þeirra er að hafa uppi á nafnlausum Bandaríkjamanni og því sem hann er að selja fyrir hundrað milljónir dala. Hörkuspennandi saga um vel falið leyndarmál.", long_description: "", page_count: 416, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15447, pre_title: "", title: "Stormboði", post_title: "", slug: "stormbodi-15447", description: "Karen Eiken Hornby er kölluð til Noorö, nyrstu eyjar Doggerlands, til að stýra rannsókn á voveiflegum dauða aldraðs manns. Þegar annað morð er framið fer hana að gruna að málið tengist ekki bara gömlum ættarerjum, heldur einnig ættingjum hennar sjálfrar .... Maria Adolfsson hefur slegið í gegn með glæpasögum sínum sem gerast á ímynduðum eyjaklasa í Norðursjónum.", long_description: "", page_count: 430, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15448, pre_title: "", title: "Gegnum vötn, gegnum eld", post_title: "", slug: "gegnum-votn-gegnum-eld-15448", description: "Tekla er ungur og eldklár læknir á bráðadeild – en hún á sér leyndarmál. Þegar stórbruni verður í háhýsi í borginni bjargar hún lífi manns sem enginn veit hver er. Grunsemdir vakna um hryðjuverk og lögreglan vaktar rænulausan sjúklinginn. Er hann hugsanlega maður sem Tekla þekkir? Fantagóð spennusaga, sú fyrsta um bráðalækninn Teklu Berg í Stokkhólmi.", long_description: "", page_count: 457, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15449, pre_title: "", title: "Eplamaðurinn", post_title: "", slug: "eplamadurinn-15449", description: "Tíu ára gamall drengur hverfur sporlaust úr skólanum. Vísbendingarnar hrannast upp en virðast benda hver í sína áttina. Uppgjafahermaður finnst látinn, leynileg skilaboð uppgötvast í herbergi drengsins og móðir hans virðist hafa eitthvað að fela. Og hver er Eplamaðurinn sem krakkarnir tala um? Ný og æsispennandi saga eftir höfund >>>Líkblómsins<<<.", long_description: "", page_count: 320, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15450, pre_title: "", title: "Silkiormurinn", post_title: "", slug: "silkiormurinn-15450", description: "Rithöfundurinn Owen Quine hverfur eftir að hafa nýlokið við afar eldfima sögu sem mun leggja líf margra í rúst. Þegar afskræmt lík Quines finnst við ógnvekjandi aðstæður fer allt á fleygiferð. Cormoran Strike, einkaspæjarinn mikilúðlegi, verður að hafa sig allan við að leysa úr öllum flækjunum.", long_description: "", page_count: 619, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15451, pre_title: "", title: "Kóngsríkið", post_title: "", slug: "kongsrikid-15451", description: "Bræðurnir Roy og Carl alast upp í norsku sveitaþorpi þar sem Carl verður fyrir skelfilegu ofbeldi og flýr á endanum fortíð sína. Tuttugu árum síðar birtist hann með háfleygar áætlanir. Gamlar væringar og fólskuverk leita upp á yfirborðið og Roy þarf að velja á milli bróður síns og framtíðardraumanna. Enginn stenst Jo Nesbø snúning þegar kemur að óvæntum fléttum.", long_description: "", page_count: 576, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15452, pre_title: "", title: "Við skjótum títuprjónum", post_title: "", slug: "vid-skjotum-tituprjonum-15452", description: "Hallgrímur Helgason kann þá list að hræra upp í fólki, brýna það, um leið og hann bendir á mótsagnirnar, tvöfeldnina og tilgangsleysið allt í kringum okkur. Ljóðabálkurinn er ortur á árunum 2016–2020 og talar beint inn í samtímann. Púlsinn er tekinn á líðan þjóðar sem hrósar sínu lífshappi en útvistar erfiðum málum með læktakkanum.", long_description: "", page_count: 63, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15453, pre_title: "", title: "Árhringur", post_title: "Ljóðræna dagsins", slug: "arhringur-15453", description: "Heilluð, steinrunnin,\r\nheld ég á djúpið.\r\nKlyfjuð ósýnilegri\r\nbyrði dagsins.\r\n\r\nBjörg Björnsdóttir yrkir hér um hina eilífu hringrás árstíðanna, hversdaginn og  náttúruna þannig að hinn ytri heimur og innri kallast á í fallegri ljóðrænu. Árhringur er fyrsta ljóðabók Bjargar", long_description: "", page_count: 40, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15454, pre_title: "", title: "Fjölskyldulíf á jörðinni", post_title: "", slug: "fjolskyldulif-a-jordinni-15454", description: "Ljóðabókin >>>Fjölskyldulíf á jörðinni<<< er áttunda bók Dags Hjartarsonar. Hér er ort af listfengi um hið hversdagslega í lífinu, allt það sem færir hamingjuna. Ljóðin eru persónuleg, tær og sterk í einfaldleika sínum, en skilja eftir ljúfsára kennd um fallvaltleika alls sem er.", long_description: "", page_count: 56, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15455, pre_title: "", title: "Konan sem datt upp stigann – saga af kulnun", post_title: "", slug: "konan-sem-datt-upp-stigann-saga-af-kulnun-15455", description: "Inga Dagný Eydal segir hér sögu sína en hún hefur í nokkur ár glímt við afleiðingar þess að hafa veikst af of mikilli streitu. Þetta er saga konu sem tekst á við kulnun af æðruleysi en kemur líka oft auga á broslegu hliðarnar. Hún lítur gagnrýnum augum til fortíðar um leið og hún horfir fram á veginn í leit að lausnum og bata.", long_description: "", page_count: 144, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15456, pre_title: "", title: "Lifandi mál lifandi manna", post_title: "- um esperantotímabil Þórbergs Þórðarsonar", slug: "lifandi-mal-lifandi-manna-15456", description: "Alþjóðamálið esperanto var í huga Þórbergs Þórðarsonar það tæki sem gat bjargað menningu smáþjóðanna og um leið sameinað allar þjóðir heimsins í eitt ríki. Hér er safnað saman öllu sem meistari Þórbergur ritaði um esperanto og á esperanto og birtist margt af því hér á íslensku í fyrsta sinn í þýðingu Kristjáns Eiríkssonar.", long_description: "", page_count: 415, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15457, pre_title: "", title: "Guðrúnarkviða", post_title: "", slug: "gudrunarkvida-15457", description: "Þú ert stödd í jarðarför.\r\nÞinni eigin jarðarför og ritningarlestur er að hefjast.\r\n\r\nLjóðsaga um konu sem rankar við sér í kistu í eigin jarðarför. Í huga hennar koma svipleiftur úr liðinni ævi, en kirkjugestir fá líka orðið í þessari frumlegu kviðu eftir verðlaunaskáldið Eyrúnu Ósk Jónsdóttur.", long_description: "", page_count: 104, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15458, pre_title: "", title: "Vatnaleiðin", post_title: "", slug: "vatnaleidin-15458", description: "Árið 2009 dvaldi Óskar Árni Óskarsson um sex mánaða skeið sem gestalistamaður í Vatnasafninu í Stykkishólmi. Hann hélt dagbók flesta daga sem hér birtist nú á prenti í endurskoðaðri útgáfu. Bókin hefur að geyma lýsingar á því sem fyrir augu bar þessa mánuði, hugleiðingar og ljóð og ljósmyndir sem Einar Falur Ingólfsson tók á vordögum 2018.", long_description: "", page_count: 120, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15459, pre_title: "", title: "Bubbi Morthens – ferillinn í fjörutíu ár", post_title: "", slug: "bubbi-morthens-ferillinn-i-fjorutiu-ar-15459", description: "Bubbi Morthens hefur verið þjóðargersemi frá því hann kvaddi sér hljóðs með Ísbjarnarblús árið 1980 og markaði þar með nýtt upphaf í íslenskri dægurtónlist. Árni Matthíasson rekur hér ævintýralegan tónlistarferil hans og byggir á samtímaheimildum og viðtölum við fjölda fólks – þar á meðal Bubba sjálfan. Sagan er sögð í beinskeyttum texta og aragrúa ljósmynda.", long_description: "", page_count: 272, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15460, pre_title: "", title: "1900 og eitthvað", post_title: "", slug: "1900-og-eitthvad-15460", description: "Fyrsta ljóðabók Ragnheiðar Lárusdóttur hlaut Bókmenntaverðlaun Tómasar Guðmundssonar árið 2020.\r\n„1900 og eitthvað er heillandi uppvaxtarsaga prestsdóttur að vestan ... hófstillt verk um stórfengleika hins smáa og áhrifamátt hversdagsleikans.“\r\n\tÚr umsögn dómnefndar", long_description: "", page_count: 52, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15461, pre_title: "", title: "Hundalíf með Theobald", post_title: "", slug: "hundalif-med-theobald-15461", description: "Í daglegum gönguferðum ræða ungur hundur og gamall maður um lífið og tilveruna. Gamli maðurinn heitir Þráinn og sambýlishundur hans er franski bolabíturinn Theobald. Þetta eru örsögur og örstutt samtöl þar sem blandast saman gaman og alvara – vangaveltur um það sem skiptir máli í lífinu eða glás af dýrmætum augnablikum, eins og Theobald segir.", long_description: "", page_count: 184, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15462, pre_title: "", title: "Agathe", post_title: "", slug: "agathe-15462", description: "Agathe gengur inn á læknastofu lífsþreytts geðlæknis í París á fimmta áratug liðinnar aldar - og allt breytist.\r\nÁhrifamikil saga um meginþætti mannlegrar tilvistar. \r\n&#9733;&#9733;&#9733;&#9733;&#9733;\r\n„Snjöll og skörp saga um vanda þess að ná alvöru sambandi við annað fólk. “\r\n\tIndependent", long_description: "", page_count: 132, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15463, pre_title: "", title: "Þerapistinn", post_title: "", slug: "therapistinn-15463", description: "Sara býr sig undir notalega helgi á meðan eiginmaðurinn skreppur í fjallakofa með vinum. En hann mætir aldrei í fjallakofann. Er hann lífs eða liðinn? Þessi fyrsta bók höfundar var meiriháttar metsölubók í heimalandinu og er væntanleg á yfir 30 tungumálum. „Ráðgátan er dularfull og lausnin blasir engan veginn við.“ – Kolbrún Bergþórsdóttir.", long_description: "", page_count: 368, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15464, pre_title: "", title: "Arfur Stiegs Larsson", post_title: "Lykillinn að morðinu á Olof Palme", slug: "arfur-stiegs-larsson-15464", description: "Stieg Larsson, þekktasti glæpasagnahöfundur heims, rannsakaði morðið á Olof Palme.\r\nÞessi bók er einstök lýsing á raunverulegum atburðum, og geymir auk þess fjölda áður óbirtra skrifa Stiegs sem kallast á við Millennium-þríleik hans. \r\n„Heillandi úttekt og sannfærandi frásögn.\"\r\nWall Street Journal", long_description: "", page_count: 439, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15465, pre_title: "", title: "Grikkur", post_title: "", slug: "grikkur-15465", description: "Myndlistarmaður á áttræðisaldri deilir fyrrum æskuheimili sínu í fáeina sólarhringa með fjögurra ára dóttursyni á meðan einkadóttir hans og tengdasonur fara í ráðstefnuferð. \r\n„Virkilega góður skemmtilestur sem skilur heilmikið eftir sig ... bráðsnjöll og vel skrifuð ... bæði kómísk og drungaleg ...“ RSS, Lestrarklefinn. \r\n* * * *, RB Mbl.", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15466, pre_title: "", title: "Hæstiréttur í hundrað ár", post_title: "Ritgerðir", slug: "haestirettur-i-hundrad-ar-15466", description: "Í ár er liðin öld frá því að Hæstiréttur Íslands tók til starfa árið 1920. Í riti þessu sem hefur að geyma 18 ritgerðir eftir 22 höfunda er fjallað um valda málaflokka og réttarsvið sem hafa verið til umfjöllunar á þeim hundrað árum sem liðin eru frá stofnun réttarins og hann hefur komið að því að móta.", long_description: "", page_count: 556, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15467, pre_title: "", title: "Þetta voru bestu ár ævi minnar, enda man ég ekkert eftir þeim", post_title: "", slug: "thetta-voru-bestu-ar-aevi-minnar-enda-man-eg-ekkert-eftir-theim-15467", description: "„Afhverju þessi sóun á pappír?“ \r\nÞannig hljómaði niðurlagið á fyrstu gagnrýninni sem ég fékk á ljóðabókina mína,  skrifar höfundur í eftirmála þessa ljóðasafns sem hefur að geyma ljóðabækur hans þrjár;  >>>Með byssuleyfi á eilífðina<<< (1988), >>>Úr þotuhreyflum guða<<< (1989) og >>>Hún spurði hvað ég tæki með mér á eyðieyju<<< (1993) auk nokkurra áður óbirtra ljóða.", long_description: "", page_count: 208, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15468, pre_title: "", title: "Lygalíf fullorðinna", post_title: "", slug: "lygalif-fullordinna-15468", description: "Þroskasaga unglingsstúlku sem lendir í sársaukafullri eldskírn á leið sinni inn í heim fullorðinna. Fyrsta skáldsaga Elenu Ferrante síðan Napólí-fjórleikurinn sló í gegn. Bókin hefur hlotið frábæra dóma, hér heima og um veröld víða. \r\n„[Ferrante] er mikill rithöfundur en hún er lesendavæn og höfðar til stórs hóps. Þetta eru alvöru bókmenntir.“– SDM, Kiljunni.", long_description: "", page_count: 368, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 15, book_author_id: nil},
  {source_id: 15469, pre_title: "", title: "Ættarfylgjan", post_title: "", slug: "aettarfylgjan-15469", description: "Fjölskrúðug ættarsaga um Toimi-fjölskylduna, skrifuð af sprúðlandi frásagnargleði en tekst um leið á við dýpri spurningar um innra líf, svo að sorg og gleði vega salt í áhrifamikilli sögu.\r\nÆttarfylgjan sló rækilega í gegn árið 2019, og var tilnefnd til allra helstu bókmenntaverðlauna Svía.", long_description: "", page_count: 478, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15470, pre_title: "", title: "Þögla stúlkan", post_title: "", slug: "thogla-stulkan-15470", description: "Heil fjölskylda er myrt á fólskulegan hátt á heimili sínu. Eina vitnið er hin tíu ára gamla Nicole.\r\nBækur Hjorths & Rosenfeldts um sálfræðinginn Sebastian Bergman hafa farið sannkallaða sigurför um heiminn.", long_description: "", page_count: 571, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15471, pre_title: "", title: "Tíbrá", post_title: "", slug: "tibra-15471", description: "Fjórir ungir menn fara saman á veiðar – misjafnlega vanir skotvopnum. Hvað gæti farið úrskeiðis? \r\nHörkuspennandi en líka skemmtisaga um glæp. Ein af metsölubókum sumarsins 2020.\r\n\"Fyndinn, frjór og snjall.\"\r\nVikan", long_description: "", page_count: 295, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15472, pre_title: "", title: "107 Reykjavík", post_title: "Skemmtisaga fyrir lengra komna", slug: "107-reykjavik-15472", description: "Þegar Stefanía Brown-Huntington skilur við breska jarlinn og flytur aftur til Íslands ríður á miklu að vinkvennaklíku Hallgerðar takist að ná henni til sín. \r\nÆrslafengin atburðarás um reykvíska samtíð þar sem raunverulegar persónur sem ímyndaðar mæta til leiks í fyndinni og hárbeittri háðsádeilu.", long_description: "", page_count: 303, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15473, pre_title: "", title: "Strendingar", post_title: "fjölskyldulíf í sjö töktum", slug: "strendingar-15473", description: "Skemmtileg og áhrifamikil skáldsaga um hálft ár í lífi fjölskyldu, þar sem allir sjö hugarheimar hennar fá rödd. Þannig fléttast saman innra líf og ytri atburðir í marglaga og minnisstæðri sögu.  \r\nYrsa Þöll Gylfadóttir vakti mikla athygli fyrir síðustu skáldsögu sína, Móðurlífið, blönduð tækni.", long_description: "", page_count: 264, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15474, pre_title: "", title: "Berskjaldaður", post_title: "Barátta Einars Þórs fyrir lífi og ást", slug: "berskjaldadur-15474", description: "Einar Þór Jónsson vakti þjóðarathygli fyrir skýra en hógværa framgöngu sem talsmaður Geðhjálpar. Fáa grunaði þó að þessi látlausi og geðþekki maður ætti sér magnaða lífssögu að baki. \r\nGunnhildur Arna Gunnarsdóttir skrifar þessa áhrifamiklu  baráttusögu Einars Þórs Jónssonar af innsæi og næmi.", long_description: "", page_count: 381, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15476, pre_title: "", title: "Herra Hnetusmjör", post_title: "- hingað til -", slug: "herra-hnetusmjor-15476", description: "Herra Hnetusmjör, Árni Páll Árnason, hefur verið hvirfilbylur í íslensku tónlistarlífi. En lífsganga hans hefur verið sannkölluð rússibanareið.\r\nSóli Hólm skrifar af blússandi krafti en sýnir líka strákinn á bak við ímyndina í einlægri og stórskemmtilegri bók.", long_description: "", page_count: 238, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15477, pre_title: "", title: "Háspenna lífshætta á Spáni", post_title: "", slug: "haspenna-lifshaetta-a-spani-15477", description: "Systkinin Sóley og Ari eru á leið í langþráð frí til Spánar með foreldrum sínum. En það sem átti að vera þægilegt frí til að hlaða batteríin breytist snarlega þegar skuggalegir menn fara að birtast í kringum þau.\r\nSjálfstætt framhald metsölubókarinnar >>>Friðbergur forseti<<<.", long_description: "", page_count: 226, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15478, pre_title: "", title: "Huldugáttin", post_title: "", slug: "huldugattin-15478", description: "Hver er þessi dularfulla kona sem reynir að nálgast William? Og hver fjárinn er huldugátt?\r\nWilliam og Iscia standa frammi fyrir myrkum öflum og saman leggja þau í hættuför til að komast að hinu sanna. \r\n>>>Huldugáttin<<< er önnur bókin um William Wenton og er æsispennandi, hugmyndarík og skemmtileg.", long_description: "", page_count: 247, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15479, pre_title: "", title: "Fíasól og furðusaga", post_title: "um krakka með kött í maga", slug: "fiasol-og-furdusaga-15479", description: "Sjá! Boða ég mikinn og frábæran fögnuð! \r\nFædd er nú stelpa kröftug og mögnuð!\r\nSprenghlægilegt kvæði um fæðingu Fíusólar. En undirtónninn er hyldjúpur og tilfinningaþrunginn þar sem Pippa, systir Fíusólar, glímir við höfnun og óöryggi þegar tilkynnt er um fjölgun í Grænalundi í Grasabæ.", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15481, pre_title: "", title: "Lára fer í leikhús", post_title: "", slug: "lara-fer-i-leikhus-15481", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15482, pre_title: "", title: "Lára lærir að lesa", post_title: "", slug: "lara-laerir-ad-lesa-15482", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15483, pre_title: "", title: "Smásögur heimsins V", post_title: "Evrópa", slug: "smasogur-heimsins-v-15483", description: "Fimmta og síðasta bindið í ritröðinni Smásögur heimsins. Meðal evrópskra höfunda hér má nefna Virginu Woolf, Frans Kafka, James Joyce, Vladimir Nabokov, Italo Calvino, Karen Blixen, Heinrich Böll, Tove Jansson, Lailu Stien, Michel Tournier og Kerstin Thorvall.", long_description: "", page_count: 310, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15484, pre_title: "", title: "Syngdu með Láru og Ljónsa", post_title: "", slug: "syngdu-med-laru-og-ljonsa-15484", description: "Falleg bók með tónspilara sem inniheldur fjölbreytt lög sungin af Birgittu Haukdal. Lögin henta hvort sem er í fjörugu ferðalagi eða á rólegum stundum fyrir háttinn. Krakkar geta bæði hlustað á lögin með söng Birgittu og spreytt sig á að syngja þau sjálf með undirspili. Bókin er skreytt litríkum og fallegum myndum sem gaman er að skoða.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15485, pre_title: "", title: "Guðjón Samúelsson húsameistari", post_title: "", slug: "gudjon-samuelsson-husameistari-15485", description: "Guðjón Samúelsson (1887-1950) arkitekt og húsameistari ríkisins frá 1920 til dauðadags, teiknaði margar af þekktustu byggingum landsins. Í þessari bók skoðar höfundurinn, Pétur H. Ármannson, verk og hugmyndir Guðjóns út frá sérstöðu hans sem háskólamenntaðs arkitekts sem var mótaður af straumum og stefnum í norrænni og evrópskri byggingarlist. Bókin er prýdd fjölda ljósmynda og teikninga.", long_description: "", page_count: 448, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15486, pre_title: "", title: "Öll með tölu", post_title: "", slug: "oll-med-tolu-15486", description: "Í þessari skemmtilegu bók er farið á handahlaupum yfir tölurnar frá 0 til 7.500.000.000. Á hverri síðu eru ævintýralegar myndir þar sem hægt er að telja börn og fullorðna, fylgja þeim eftir og sjá hvernig líf þeirra fléttast saman. Skarpskyggnir lesendur munu líka finna þar ýmis leyndamál. >>>Öll með tölu<<< hlaut Barna- og unglingabókmenntaverðlaun Norðurlandaráðs 2019.", long_description: "", page_count: 58, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15487, pre_title: "", title: "Gunnhildur og Glói", post_title: "", slug: "gunnhildur-og-gloi-15487", description: "Suma daga finnst Gunnhildi allt ljótt og leiðinlegt. En einn slíkan dag hittir hún Glóa álfastrák á leikskólalóðinni, hann leggur geislastein í lófa hennar og sýnir henni heiminn í nýju ljósi. Sagan af Gunnhildi og Glóa kom fyrst út árið 1985 og heillaði unga sem aldna. Hér er þessi sígilda saga Guðrúnar Helgadóttur loksins komin út að nýju.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15488, pre_title: "", title: "Dimmuborgir", post_title: "", slug: "dimmuborgir-15488", description: "Bókmenntarýnirinn Elmar Arnarsson er búinn að loka sig af með stafla af jólabókum þegar honum berast óvænt nýjar upplýsingar um andlát besta vinar síns 25 árum fyrr. Elmar, sem hefur ætíð verið sannfærður um að Felix hafi verið myrtur, verður heltekinn af því að komast að sannleikanum og brátt snýst tilvera þessa hlédræga manns gjörsamlega á hvolf.", long_description: "", page_count: 266, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15489, pre_title: "", title: "Yfir bænum heima", post_title: "", slug: "yfir-baenum-heima-15489", description: "Það er vor í litlum bæ við sjóinn. Atvinna er stopul og lífið basl. En skyndilega breytist allt. Götur fyllast af hermönnum, braggar rísa og dansinn dunar. Það er komið stríð og yfir vofir ógnin: Flugvélar óvinanna. Hér segir frá þremur mæðgum og fólkinu þeirra í ólgusjó hernámsáranna; umróti, ást og lífsháska sem markar djúp spor. Viðburðarík og heillandi skáldsaga.", long_description: "", page_count: 317, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15490, pre_title: "", title: "Þagnarmúr", post_title: "", slug: "thagnarmur-15490", description: "Í kjallara í Reykjavík finnast hrottaleg leyndarmál múruð inn í vegg. Konráð er löngu hættur í lögreglunni en stöðugt með hugann við illvirki fortíðar og ákafi hans vekur spurningar: Hvers vegna sagði hann ósatt um atburði dagsins þegar faðir hans var myrtur? Hverju hefur hann þagað yfir öll þessi ár? Spennandi og átakanleg saga um ofbeldi, varnarleysi og þungbæra þögn.", long_description: "", page_count: 303, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15491, pre_title: "", title: "Atómstöðin", post_title: "", slug: "atomstodin-15491", description: "Atómstöðin er ein af umdeildustu skáldsögum Halldórs Laxness. Í henni segir frá norðanstúlkunni Uglu sem kemur í höfuðstaðinn til að læra á orgel en þar mæta henni tveir ólíkir heimar og ólík lífsgildi takast á. Þroskasaga Uglu er um leið beitt þjóðarsaga; róttæk, kímin og heimspekileg. Útgáfan er með nútímastafsetningu.", long_description: "", page_count: 187, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15492, pre_title: "", title: "Tregasteinn", post_title: "", slug: "tregasteinn-15492", description: "Kona er myrt á heimili sínu en nokkru áður hafði hún beðið Konráð, fyrrverandi lögreglumann, að finna fyrir sig barn sem hún fæddi fyrir næstum hálfri öld og lét strax frá sér. Hann neitaði bón hennar en einsetur sér nú að bæta fyrir það. Áhrifamikil og snjöll glæpasaga um skömm og örvæntingu, ákafa eftirsjá og langvarandi bergmál illra verka.", long_description: "", page_count: 306, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15493, pre_title: "", title: "Fórnarlamb 2117", post_title: "", slug: "fornarlamb-2117-15493", description: "Mynd af látinni flóttakonu á strönd Miðjarðarhafsins vekur upp fortíðardjöfla og Assad sérsveitarmaður rambar á barmi taugaáfalls. Í kjölfarið hefst taugatrekkjandi niðurtalning að hryllilegum hamförum þar sem Ghaalib, miskunnarlausi böðullinn frá Írak, heldur um stjórntaumana. Áttunda bókin um hina vinsælu Deild Q eftir glæpasagnakóng Dana.", long_description: "", page_count: 504, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15494, pre_title: "", title: "Hamlet", post_title: "", slug: "hamlet-15494", description: ">>>Hamlet<<< er stórbrotinn og sígildur harmleikur, í senn heimspekilegur og blóði drifinn, eitt allra frægasta leikrit meistara Williams Shakespeares. „Að vera eða ekki vera“– nístandi efi, svik og bágt siðferði er meðal þess sem brotið er til mergjar í þessu áhrifaríka stórvirki sem hér birtist í nýrri og einkar athyglisverðri þýðingu Þórarins Eldjárns.", long_description: "", page_count: 205, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15495, pre_title: "", title: "800-fastan", post_title: "", slug: "800-fastan-15495", description: "Hér byggir metsöluhöfundurinn Michael Mosley á nýjustu vísindarannsóknum um þyngdartap og lotubundnar föstur, blóðsykur og meltingu. Í bókinni er fjöldi girnilegra uppskrifta og leiðbeiningar um hvernig má laga þær að eigin smekk og markmiðum, auk matseðla sem auðvelda fólki að skipuleggja mataræðið.", long_description: "", page_count: 280, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15496, pre_title: "", title: "Hver ertu og hvað viltu?", post_title: "Leiðarvísir fyrir þá sem vilja komast lengra", slug: "hver-ertu-og-hvad-viltu-15496", description: "Spennandi þroskaferðalag sem dýpkar skilninginn á því hver þú ert í rauninni og hvernig þú getur breytt því sem þú vilt breyta í eigin fari. Einstæð leið til að hjálpa þér að finna hugrekki til að standa með þér sjálfum og stefna þangað sem hugur þinn stendur til. Og allt er útskýrt á mannamáli og hið flókna gert einfalt.", long_description: "", page_count: 208, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15497, pre_title: "", title: "Stjórnmál", post_title: "", slug: "stjornmal-15497", description: "Í þessari bók er fjallað með skýrum og greinargóðum hætti um nokkur grundvallarhugtök nútímastjórnmála. Markmiðið er að öðlast betri skilning á hinum pólitíska veruleika með hjálp hugtaka og kenninga stjórnmálafræðinnar. Bókin er skrifuð með almenna lesendur í huga, ekki síst ungt fólk og nemendur. Fyrir áhugafólk um stjórnmál og þjóðfélagsumræðu er bókin hvalreki.", long_description: "", page_count: 216, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15498, pre_title: "", title: "Dóttir – leið mín til tveggja heimsmeistaratitla", post_title: "", slug: "dottir-leid-min-til-tveggja-heimsmeistaratitla-15498", description: "Katrín Tanja, tvöfaldur heimsmeistari í CrossFit, rekur hér leið sína til hæstu tinda. Í CrossFit fékk hún útrás fyrir óstöðvandi orku og baráttulöngun, tókst á við sigra og ósigra og náði á toppinn með því að þjálfa líkamann að þolmörkum. Bók sem veitir sýn á eina fremstu íþróttakonu heims og það sem hún hefur gert til að ná árangri og verða fyrirmynd.", long_description: "", page_count: 287, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15499, pre_title: "", title: "Prjónað á mig og mína", post_title: "", slug: "prjonad-a-mig-og-mina-15499", description: "Fjölbreyttar og fallegar prjónaflíkur fyrir konur og 1–12 ára krakka. Í bókinni eru 40 uppskriftir, m.a. af sléttprjónuðum peysum og peysum með laufamunstri og lögð er áhersla á að frágangurinn taki sem minnstan tíma. Áður hafa komið út bækurnar >>>Prjónað af ást<<< og >>>Prjónastund<<< eftir sama höfund og notið mikilla vinsælda hér á landi.", long_description: "", page_count: 193, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15500, pre_title: "", title: "Á fjarlægum ströndum", post_title: "Tengsl Spánar og Íslands í tímans rás. Stofnun Vigdísar Finnbogadóttur", slug: "a-fjarlaegum-strondum-15500", description: "Safn greina eftir 14 höfunda um samskipti Spánar og Íslands í tímans rás. Sagt er frá ferðum um Jakobsveginn fyrr og nú, hvalveiðum Spánverja við Íslandsstrendur, gömlum orðasöfnum, saltfisksölu, íslenskum sjálfboðaliðum í spænsku borgarastyrjöldinni, íslenskum gítarnemum, sólarlandaferðum, spænskukennslu á Íslandi, þýðingum bókmenntaverka, lántökuorðum o.fl. Einnig eru minningabrot Spánverja og Íslendinga í bókinni.", long_description: "", page_count: 250, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15501, pre_title: "Íslenskir ræktunarhættir", title: "Yrkja vildi eg jörð", post_title: "", slug: "yrkja-vildi-eg-jord-15501", description: "Í bókinni segir frá íslenskum jarðræktarháttum. Fjallað er um vinnubrögð og verkfæri til ræktunar fóðurs fyrir búfé, sum séríslensk en önnur erlend, löguð að hérlendum aðstæðum. Bjarni Guðmundsson, fv. prófessor við Landbúnaðarháskóla Íslands, hefur áður skrifað um þróun landbúnaðarins, má þar nefna Íslenskir sláttuhættir og Íslenskir heyskaparhættir.", long_description: "", page_count: 397, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15502, pre_title: "", title: "Íslenskir vettlingar", post_title: "25 nýjar útfærslur á gömlum mynstrum", slug: "islenskir-vettlingar-15502", description: "Uppskriftirnar í þessari bók eru nýjar útfærslur á vettlingum frá 19. og 20. öld sem varðveittir eru á Heimilisiðnaðarsafninu á Blönduósi. Áhersla er lögð á að hafa úrvalið fjölbreytt, bæði í mynsturgerð og prjóntækni, svo hér geta allir fundið eitthvað við sitt hæfi. Bókin er einnig fáanleg í enskri þýðingu.", long_description: "", page_count: 272, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15503, pre_title: "", title: "Six Adventure Tales from Medieval Iceland", post_title: "A Reader for First-Time Visitors", slug: "six-adventure-tales-from-medieval-iceland-15503", description: "Hér eru á ferð sex stuttar sögur frá miðöldum sem allar gerast á Austfjörðum, þýddar af Theodore M. Andersson fv. prófessor í Harvard, Stanford og Indiana og höfundi fjölmargra bóka um fornnorrænar bókmenntir. Sögurnar eru gott dæmi um fornan íslenskan frásagnarstíl sem nýtur sín vel í þessari snjöllu ensku þýðingu.", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15504, pre_title: "", title: "Systkinabókin", post_title: "", slug: "systkinabokin-15504", description: "Sóla er orðin stóra systir. En það er ekki eins skemmtilegt og hún hélt. Litli bróðir er hávær og alltof lítill til að leika við. Sóla grípur til sinna ráða, leggur upp í örlagaríka ferð og ekkert verður eins og áður. Litríkar og skemmtilegar bækur sem efla skilning barna á eigin tilfinningum og annarra og hjálpa þeim að færa þær í orð.", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15505, pre_title: "Risaeðlugengið", title: "Kappsundið", post_title: "", slug: "kappsundid-15505", description: "Þegar Georg grameðla, ótrúlega óþolandi stóri bróðir Gauta, skorar á þá Sölva sagtanna í kappsund getur Gauti ekki skorast undan, þótt hann hafi heyrt að í ísköldu vatninu sé RISAVAXIÐ SÆSKRÍMSLI á svamli. Krúttleg, fræðandi og fyndin saga fyrir yngsta áhugafólkið um risaeðlur og önnur forsöguleg dýr.", long_description: "", page_count: 48, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15507, pre_title: "", title: "Skólaráðgátan", post_title: "", slug: "skolaradgatan-15507", description: "", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15508, pre_title: "", title: "Gullráðgátan", post_title: "", slug: "gullradgatan-15508", description: "", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15510, pre_title: "", title: "Almanak Háskóla Íslands 2021", post_title: "", slug: "almanak-haskola-islands-2021-15510", description: "Auk dagatals inniheldur almanakið margvíslegar upplýsingar, s.s. um sjávarföll og  gang himintungla, helstu fyrirbæri á himni sem frá Íslandi sjást, stjörnukort, áttavitastefnur á Íslandi og tímabelti heimsins. Einnig er hér yfirlit um hnetti himingeimsins, mælieiningar, veðurfar, stærð og mannfjölda allra sjálfstæðra ríkja o.fl. Af nýju efni má nefna grein um gríðarlega fjölgun gervitungla sem breytt gæti ásýnd himinhvolfsins. Fjallað er um dvergreikistjörnur og hvort líf sé á reikistjörnunni Mars.", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15511, pre_title: "", title: "Sögur úr múmíndal", post_title: "", slug: "sogur-ur-mumindal-15511", description: "", long_description: "", page_count: 98, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15512, pre_title: "", title: "Jól í múmíndal", post_title: "", slug: "jol-i-mumindal-15512", description: "", long_description: "", page_count: 40, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15513, pre_title: "", title: "Handleiðsla til eflingar í starfi", post_title: "", slug: "handleidsla-til-eflingar-i-starfi-15513", description: "Í þessari bók fjalla sautján sérfræðingar úr ólíkum faghópum um handleiðslu innan velferðarþjónustu og vinnumarkaðar. Sagt er frá hugmynda- og þekkingargrunni handleiðslufræða og hlutverki handleiðara. Handleiðari hefur menntun og færni til að hjálpa fagmanni til að laða fram það besta í sjálfum sér, njóta sín í starfi og sækja sér sérhæfingu í takti við þarfir og aðstæður.", long_description: "", page_count: 258, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15514, pre_title: "", title: "Lotta og börnin í Skarkalagötu", post_title: "", slug: "lotta-og-bornin-i-skarkalagotu-15514", description: "Þau Lotta, Jónas og Mía María hafa heillað lesendur á öllum aldri áratugum saman og nú eru sögurnar af fjörugu fjölskyldulífi systkinanna í Skarkalagötu loksins fáanlegar á ný. Hér eru tvær bækur saman í einni; >>>Börnin í Skarkalagötu<<< og >>>Lotta flytur að heiman<<<. Einstakar myndir glæða sögurnar ævintýraljóma.", long_description: "", page_count: 133, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15515, pre_title: "Múmínálfarnir – stórbók", title: "Ósýnilega barnið og aðrar sögur / Eyjan hans múmínpabba / Seint í nóvember", post_title: "", slug: "osynilega-barnid-og-adrar-sogur-eyjan-hans-muminpabba-seint-i-no-15515", description: "Tove Jansson skrifaði níu sögubækur um múmínálfana ástsælu sem loks eru allar fáanlegar á íslensku í veglegu safni stórbóka. Þetta síðasta bindi geymir heillandi sögur þar sem ýmis háski steðjar að múmíndal, bæði íbúum hans og gestkomandi verum. Skemmtilestur fyrir bæði börn og fullorðna.", long_description: "", page_count: 496, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15516, pre_title: "", title: "Hansdætur", post_title: "", slug: "hansdaetur-15516", description: "Kona sem á þrjú börn með þremur mönnum í vestfirsku sjávarplássi í byrjun tuttugustu aldar á sér ekki viðreisnar von en dóttir hennar Gratíana þráir breytta tíma og betra líf. >>>Hansdætur<<< er áhrifamikil örlagasaga um harðgerðar konur, viðkvæmar sálir, andans menn, óreiðufólk af ýmsu tagi; drauma þeirra og þrár, sorgir og sigra.", long_description: "", page_count: 342, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15517, pre_title: "", title: "Opna svæðið", post_title: "Tímaritið Birtingur og íslenskur módernismi", slug: "opna-svaedid-15517", description: "Hér er ljósi varpað á tímaritið Birting sem kom út á árunum 1955-1968 og gegndi lykilhlutverki við að innleiða módernisma í íslenskar bókmenntir og listsköpun á tímabilinu. Ritið er fyrsta bókin sem gefin er út um þetta mikilvæga tímarit. Birtingi var ætlað að þjóna sem vettvangur fyrir kynningar á nýjum alþjóðlegum straumum. Í honum fór fram endurskoðun á viðteknum hefðum og lifandi umræður um nútímabókmenntir og listir. Bókin veitir innsýn í margbrotin átök innan íslensks menningarvettvangs á umbrotatíma.", long_description: "", page_count: 440, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15518, pre_title: "", title: "Bróðir", post_title: "", slug: "brodir-15518", description: "Í tilfinningauppnámi verður unglingurinn Skorri valdur að hræðilegu slysi. Atvikið verður að örlagaríku leyndarmáli milli hans og Tinnu, barnungrar systur hans. Mörgum árum síðar kemur það upp á milli þeirra. Í dramatísku skáldsögunni >>>Bróðir<<< er fjallað á djarfan, fyndinn og áhrifamikinn hátt um það þegar gott fólk hegðar sér eins og skepnur.", long_description: "", page_count: 292, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15519, pre_title: "", title: "Sjálfstýring", post_title: "", slug: "sjalfstyring-15519", description: "Lífið virðist blasa við hæfileikaríkri, ungri konu. Hún á góða vini, ástríka fjölskyldu og er á leiðinni í inntökupróf við virtan tónlistarskóla erlendis. En eftir að vinur hennar braut á henni í partíinu um jólin upplifir hún aðeins einkennilegan doða og tengslaleysi við sjálfa sig og sína nánustu. Sagan vann rafbókasamkeppni Forlagsins 2020.", long_description: "", page_count: 76, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 35, book_author_id: nil},
  {source_id: 15520, pre_title: "", title: "Strá", post_title: "", slug: "stra-15520", description: "Fullorðin kona mætir velvild ókunnugra í „Gefins, allt gefins“, ungur maður mótmælir einn uppi á heiði og afgreiðslukona í plötubúð losar sig úr þröngum þægindahring. >>>Strá<<< bar sigur úr býtum í handritasamkeppninni Nýjar raddir 2019. Í sögunum ferðast lesendur um brothætta náttúru landsins og finna fyrir ákafri löngun til að snerta aðra manneskju.", long_description: "", page_count: 114, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 35, book_author_id: nil},
  {source_id: 15521, pre_title: "", title: "Verkefnastjórnun", post_title: "og verkfærið MS Project", slug: "verkefnastjornun-15521", description: "Verkefnastjórnun snýst um tíma- og kostnaðarstýringu, breytingastjórnun, fjármálastjórnun og mannauðsstjórnun. Verkefnastjóri þarf að vera gæddur ákveðnum kostum, ekki síst sterkum leiðtogahæfileikum, og geta náð fram því besta í fólki. Þessi bók hjálpar þér að verða betri verkefnastjóri og ná markmiðum þínum. Með því að lesa hana og leysa þau verkefni sem þar er að finna eykst skilningur þinn á faginu og færni í að nýta þér verkfæri verkefnastjórnunar.", long_description: "", page_count: 298, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15522, pre_title: "Goðheimar", title: "Gjafir guðanna", post_title: "", slug: "gjafir-gudanna-15522", description: "Loki hinn lævísi klippir allt hárið af Sif og neyðist í kjölfarið til að heimsækja dvergana og fá þá til að búa til hár úr skíragulli í staðinn. Þar kemur hann auga á ýmsa dýrgripi sem hann ásælist og fyrr en varir er hann kominn á bólakaf í vafasöm viðskipti við slynga og hættulega andstæðinga. Þetta er tíunda bókin í þessum sívinsæla bókaflokki.", long_description: "", page_count: 48, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, book_author_id: nil},
  {source_id: 15523, pre_title: "", title: "Vísnabókin", post_title: "", slug: "visnabokin-15523", description: "Fáar bækur hafa fylgt íslenskum börnum lengur en >>>Vísnabókin<<<; sígilt safn af kvæðum sem höfða til barna, prýdd bráðskemmtilegum teikningum. Bókin kom fyrst út árið 1946 en með tímanum var aukið við hana bæði vísum og teikningum. Í þessari nýju og gullfallegu útgáfu af bókinni er það efni nær allt með, í betri prentgæðum en áður hafa sést.", long_description: "", page_count: 105, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, book_author_id: nil},
  {source_id: 15524, pre_title: "", title: "Þættir úr sögu lyfjafræðinnar á Íslandi frá 1760", post_title: "", slug: "thaettir-ur-sogu-lyfjafraedinnar-a-islandi-fra-1760-15524", description: "Í þessu vandaða riti er rakin saga lyfjagerðar og lyfsölu hér á landi í tvær og hálfa öld. Bókin er rituð í samstarfi Lyfjafræðingafélags Íslands, Háskóla Íslands og Lyfjafræðisafnsins, og veitir einstaka og oft og tíðum skemmtilega innsýn í fortíðina. Mikill fengur fyrir íslenska lyfjafræðinga og allt áhugafólk um heilbrigðis- og samfélagssögu.", long_description: "", page_count: 334, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, book_author_id: nil},
  {source_id: 15525, pre_title: "", title: "Ein á forsetavakt", post_title: "Dagar í lífi Vigdísar Finnbogadóttur", slug: "ein-a-forsetavakt-15525", description: "Árið 1980 varð Vigdís Finnbogadóttir fyrst kvenna í heiminum lýðræðislega kjörinn þjóðhöfðingi. Átta árum síðar skrifaði Steinunn Sigurðardóttir metsölubókina >>>Ein á forsetavakt<<<, þar sem annasömum dögum í lífi forseta og einstæðrar móður er lýst af innsæi og glettni. Bókin kemur nú út á ný í tilefni þess að 90 ár eru frá fæðingu Vigdísar og 40 ár síðan hún var kjörin forseti.", long_description: "", page_count: 188, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, book_author_id: nil},
  {source_id: 15526, pre_title: "", title: "Uppskriftabók Lillu frænku", post_title: "", slug: "uppskriftabok-lillu-fraenku-15526", description: "Edda Björgvins man enn matinn sem Lilla frænka í Ameríku eldaði upp úr húsmæðraskólabókinni sinni eftir áratuga búsetu vestanhafs. Þegar Viðar sonur Lillu dró fram gamla fjársjóðinn hennar mömmu og stakk upp á að þau leyfðu fleirum að njóta varð ekki aftur snúið. Hér má finna andblæ liðins tíma í uppskriftum að 70–80 ára gömlum réttum sem eitt sinn voru á allra borðum.", long_description: "", page_count: 104, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, book_author_id: nil},
  {source_id: 15527, pre_title: "", title: "Pottur, panna og Nanna", post_title: "", slug: "pottur-panna-og-nanna-15527", description: "Í steypujárnspottum og -pönnum má elda næstum hvað sem er eins og sést á ótrúlega fjölbreyttum uppskriftum í þessari bók. Hér eru hægeldaðar steikur, pottréttir, brauð, snöggsteiktir og djúpsteiktir réttir, sætar kökur, súpur, meðlæti og margt annað. Einnig er fjallað ítarlega um val, meðferð og umhirðu á steypujárni og kosti þess við eldamennsku af öllu tagi.", long_description: "", page_count: 239, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 36, book_author_id: nil},
  {source_id: 15528, pre_title: "", title: "Sögur handa Kára", post_title: "", slug: "sogur-handa-kara-15528", description: "Þegar Kári Stefánsson leitar ráða hjá Ólafi Ragnari vegna kynna hans af Kínverjum bregður sá síðarnefndi á það ráð að segja sögur. Í kjölfar farsóttarinnar gafst tóm til frekari skrifta, sögurnar urðu því ennþá fleiri og tóku til fleiri heimshorna. Ólafur segir frá kynnum sínum af áhrifafólki og aðkomu að brýnum verkefnum. Úr verður sagnasveigur sem er í senn stórskemmtilegur og stórfróðlegur.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15529, pre_title: "", title: "Málsvörn Jóns Ásgeirs Jóhannessonar", post_title: "", slug: "malsvorn-jons-asgeirs-johannessonar-15529", description: "Jón Ásgeir Jóhannesson vakti ungur athygli er hann stofnaði ásamt föður sínum lágvöruverðsverslunina Bónus. Hann varð brátt atkvæðamikill í viðskiptalífinu hér á landi sem erlendis en fyrr en varði hófust réttnefndar ofsóknir gegn honum. Einar Kárason fer í saumana á þessari makalausu sögu og varpar nýju ljósi á manninn og nýliðna atburði í þjóðarsögunni.", long_description: "", page_count: 412, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15530, pre_title: "", title: "Spænska veikin", post_title: "", slug: "spaenska-veikin-15530", description: "Spænska veikin var mannskæðasta farso&#769;tt sögunnar. Meðan á stóð féllu hundruð I&#769;slendinga i&#769; valinn, mest ungt fo&#769;lk i&#769; blo&#769;ma lífsins. Hér er þessi átakanlega saga rakin ítarlega í fyrsta sinn og m.a. velt vöngum um það að hvaða leyti spænska veikin hafi verið sambærileg við veirufaraldurinn sem gengur nú yfir heimsbyggðina.", long_description: "", page_count: 316, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15531, pre_title: "", title: "Silfurberg", post_title: "Íslenski kristallinn sem breytti heiminum", slug: "silfurberg-15531", description: "Allt frá 17. o&#776;ld gerðu vi&#769;sindamenn margvi&#769;slegar uppgo&#776;tvanir þar sem íslenskt silfurberg gegndi lykilhlutverki. Hundruð tonna af kristalnum voru flutt fra&#769; Helgustaðana&#769;munni í Reyðarfirði, silfurbergið barst vi&#769;ða og hafði a&#769;hrif a&#769; verk margra af nafntoguðustu vi&#769;sindamo&#776;nnum so&#776;gunnar – fra&#769; Isaac Newton til Alberts Einsteins.", long_description: "", page_count: 286, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15532, pre_title: "", title: "Fuglinn sem gat ekki flogið", post_title: "", slug: "fuglinn-sem-gat-ekki-flogid-15532", description: "Geirfuglinn hefur löngum verið sveipaður dulúð. Þessi ófleygi, svipmikli fugl sem lifði við Íslandsstrendur er aðeins til í frásögnum og á myndum. >>>Fuglinn sem gat ekki flogið<<< er óvenjuleg bók þar sem sagt er frá síðustu veiðiferðinni og í brennidepli eru tveir breskir ferðalangar sem skráðu af nákvæmni ferð sína til Íslands 1858. >>>Geirfuglabækur<<< þeirra eru einstök heimild um endalok tegundar.", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15533, pre_title: "Um Kommúnistaflokkinn og Sósíalistaflokkinn", title: "Draumar og veruleiki", post_title: "Stjórnmál í endursýn", slug: "draumar-og-veruleiki-15533", description: "Í þessu mikla riti er fjallað um lykilpersónur, átök og atburði í sögu Kommúnistaflokks Íslands og Sameiningarflokks alþýðu – Sósíalistaflokksins. Enginn hefur þar betri innsýn en Kjartan Ólafsson, sem þekkti persónulega flesta sem koma við sögu og lýsir þeim af fágætri hreinskilni. Bókina byggir hann á margvíslegum heimildum úr íslenskum og erlendum skjalasöfnum.", long_description: "", page_count: 568, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 28, book_author_id: nil},
  {source_id: 15534, pre_title: "", title: "Hingað og ekki lengra!", post_title: "", slug: "hingad-og-ekki-lengra-15534", description: "Það mætti segja að Vigdís Fríða sé í samtökum þriggja glæpakvenda í 8. bekk. Þær brutu að minnsta kosti af sér en þeim finnst þær EKKI eiga skilið refsingu og þær standa 100% með gjörðum sínum. Höfundar skrifa af sama húmor og hlýju um lífsbaráttu ungmenna og þær gerðu í bókum sínum um Dodda sem voru meðal annars tilnefndar til Íslensku bókmenntaverðlaunanna. Stórfyndin snemmgelgjusaga.", long_description: "", page_count: 137, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15535, pre_title: "", title: "Hugmyndaheimur Páls Briem", post_title: "", slug: "hugmyndaheimur-pals-briem-15535", description: "Páll Briem var einn áhrifamesti stjórnmálamaður sinnar samtíðar og vildi lyfta umræðu um framtíð Íslands úr hjólförum stjórnarskrármálsins. Umdeildasta hugmynd hans, Miðlunin, átti að vera skref í þá átt. Hér skrifa sjö virtir sagnfræðingar um Pál í ljósi sinnar sérþekkingar og ferill hans er greindur út frá ýmsum sjónarhornum. Sjónum er sérstaklega beint að vanmetnu framlagi hans til stjórnmála- og hugmyndasögu Íslendinga.", long_description: "", page_count: 224, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15536, pre_title: "", title: "Ókindin og Bethany", post_title: "", slug: "okindin-og-bethany-15536", description: "Ebenezer gefur ókindinni á háaloftinu allt sem hún óskar sér að éta, í skiptum fyrir töfralyf sem hefur haldið honum ungum og fallegum í rúm 500 ár. Aðalsöguhetjan er samt Bethany, vænsta stelpa en líka óttalegt hrekkjusvín, sem Ebenezer ættleiðir, öllum að óvörum. Af hverju skyldi hann hafa gert það? Gólandi fyndin og hjartnæm saga ... af ókind.", long_description: "", page_count: 248, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15537, pre_title: "", title: "Plan B", post_title: "", slug: "plan-b-15537", description: "Gyða er afkastamikið skáld sem hefur þó aldrei komið út á prenti. En nú er hún með frábæra hugmynd að skáldsögu sem er byggð á skrautlegu fólki sem hún vann með í heimaþjónustu Kaupmannahafnar. Gyða ákveður að endurnýja kynnin við gömlu vinnufélagana en flækist fljótt í litríkan lygavef. >>>Plan B<<< er frumleg og listavel spunnin saga af ferðalagi sem tekur óvænta stefnu.", long_description: "", page_count: 341, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15538, pre_title: "", title: "Dularfulla styttan og drengurinn sem hvarf", post_title: "", slug: "dularfulla-styttan-og-drengurinn-sem-hvarf-15538", description: "Glæsileg, rík og dularfull kona hefur keypt hús í Álftabæ. Dag einn hverfur þaðan ómetanlegur gripur og á sama tíma virðist hrekkjusvín skólans hafa gufað upp. Vinirnir Milla og Guðjón G. Georgsson hefja rannsókn – og hún verður ekki hættulaus. Fyrri bókin um krakkana í Álftabæ, >>>Rannsóknin á leyndardómum Eyðihússins<<<, hlaut Íslensku barnabókaverðlaunin 2019.", long_description: "", page_count: 282, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15539, pre_title: "", title: "Drauma-Dísa", post_title: "", slug: "drauma-disa-15539", description: "Á furðusagnahátíð í útlöndum ætla Vár og vinir hennar að sjá átrúnaðargoðið sitt, hinn dularfulla höfund bókaflokksins um Dísu. En hann veldur þeim vonbrigðum og hverfur svo sporlaust. Eftir það byrjar sögusvið bóka hans, með skrímslum sínum og furðum, að renna saman við raunveruleikann. Hér lýkur mögnuðum furðusagnaþríleik sem hófst á bókunum >>>Drauga-Dísu<<< og >>>Galdra-Dísu<<<.", long_description: "", page_count: 306, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15540, pre_title: "", title: "Andspænis", post_title: "", slug: "andspaenis-15540", description: "Verkefnið >>>Andspænis<<< hófst á myndlistarsýningu sem frændurnir Hugleikur og Þrándur héldu í Gallery Port haustið 2019. Friðrik Sólnes samdi texta við myndirnar, þar sem vættir og þjóðsagnapersónur standa andspænis hver annarri – og listamennirnir og ólík túlkun þeirra sömuleiðis.", long_description: "", page_count: 36, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 37, book_author_id: nil},
  {source_id: 15542, pre_title: "", title: "Hallgr. Hallgrímsson I", post_title: "", slug: "hallgr-hallgrimsson-i-15542", description: "", long_description: "", page_count: 182, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 38, book_author_id: nil},
  {source_id: 15543, pre_title: "", title: "Hallgr. Hallgrímsson II", post_title: "", slug: "hallgr-hallgrimsson-ii-15543", description: "", long_description: "", page_count: 280, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 38, book_author_id: nil},
  {source_id: 15544, pre_title: "", title: "Þrír skilnaðir og jarðarför", post_title: "", slug: "thrir-skilnadir-og-jardarfor-15544", description: "Sjö tengdar sögur úr íslenskum samtíma. Handrit bókarinnar var valið úr tugum handrita til að hljóta Nýræktarstyrk Miðstöðvar íslenskra bókmennta. Í umsögn dómnefndar segir m.a.: >>>Sögurnar eru grípandi, persónulýsingar skarpar og textinn er skrifaður á blæbrigðaríku máli<<<. \r\nHöfundurinn hefur áður þýtt endurminningabók Harukis Murakamis en sendir hér frá sér sína fyrstu bók.", long_description: "", page_count: 146, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 39, book_author_id: nil},
  {source_id: 15546, pre_title: "", title: "Harry Potter og leyniklefinn", post_title: "", slug: "harry-potter-og-leyniklefinn-15546", description: "Þessi sígilda bók í einstaklega fallegri og glæsilega myndskreyttri útgáfu.\r\n\r\n &#11088;&#65039;&#11088;&#65039;&#11088;&#65039;&#11088;&#65039;&#11088;&#65039; „Einmitt bók sem upplagt er að gefa börnum á tímum sem þurfa sárlega á skemmtilegum töfraljóma að halda.“ \r\nKolbrún Bergþórsdóttir, Frbl.", long_description: "", page_count: 259, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15547, pre_title: "", title: "Undir mánans fölu sigð", post_title: "", slug: "undir-manans-folu-sigd-15547", description: "Þessi sextánda ljóðabók Pjeturs Hafsteins Lárussonar veitir innsýn í hugarheim skáldsins og sýnir enn  einu sinni sterk tök hans á fallegum, ljóðrænum stíl, hnitmiðuðu myndmáli sem oft opnast fyrir lesandanum sem litríkt málverk.", long_description: "", page_count: 110, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15548, pre_title: "", title: "Augu stara á hjarta", post_title: "", slug: "augu-stara-a-hjarta-15548", description: "Hnitmiðuð ljóð Stefáns Snævarr eru í senn ljóðræn og tilvistarleg, huglæg og hlutlæg. Skáldið horfir á þjáninguna og lífsnautnina, hlutgerir tilfinningar í orðum sínum, en hver eru áhrif áhorfsins? Ljóðin eru sjálfsskoðun og sjálfstjáning. Þó eru augun sem horfa á hug og hjarta „önnur augu“. Slík klofning sjálfsins er kjarni og vandi ljóðlistarinnar: hvernig getur sá sem er hluti af heiminum ort um heiminn, sjálfur um sjálfan sig?", long_description: "", page_count: 105, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15549, pre_title: "", title: "Brostnar væntingar", post_title: "", slug: "brostnar-vaentingar-15549", description: "Hin mikla skáldsaga franska rithöfundarins  Honoré de Balzac, Brostnar væntingar, er ein þeirra bóka sem er óháð stað og tíma. Þó að hún væri skrifuð á fyrri hluta 19. aldar er hún enn ung. Og þó að umgerðin sé frönsk landsbyggð og höfuðborgin París truflar það engan. Kjarni hennar er sígilt viðfangsefni, heiðarleiki óheiðarleiki, hégómagrirnd og fánýt eftirsókn eftir frægð og auði.", long_description: "", page_count: 594, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15550, pre_title: "", title: "Skáldaskil", post_title: "", slug: "skaldaskil-15550", description: "Þríleikur í sex þáttum sem fjallar einkum um samskipti skáldvinanna Einars Benediktssonar og Þorsteins Gíslasonar. Verkið gerist á árunum 1896–1932 og við sögu koma ýmsir þekktir menn frá þessum árum auk þeirra Einars og Þorsteins: Valtýr Guðmundsson, Halldór Laxness, Þorvaldur Pálsson o.fl. Stórskemmtilegt verk sem varpar áhugaverðu ljós á íslenskt samfélag þessara ára.", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15551, pre_title: "", title: "Kórdrengur í Kaupmannahöfn", post_title: "", slug: "kordrengur-i-kaupmannahofn-15551", description: "Ungur drengur flyst til Kaupmannahafnar ásamt fjölskyldu sinni í lok sjöunda áratugar síðustu aldar. Fyrir röð óvæntra atburða á það fyrir honum að liggja að spreyta sig á sífellt erfiðari hlutverkum í gamla söngskólanum í Kóngsins Kaupmannahöfn. Við kynnumst hörðum heimi sönglistarinnar, áskorunum, hamingju og sorg.", long_description: "", page_count: 235, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15552, pre_title: "", title: "Raddir", post_title: "Annir og efri ár", slug: "raddir-15552", description: "Bókinni er ætlað að miðla fróðleik um hvernig það er að eldast, hvernig lífi fólk lifir á efri árum og væntingar til lífsins. En einnig er fjallað um æsku og störf sem áhrif hafa haft á líf greinarhöfunda. Hér segja fjórtán konur og fjórtán karlar frá. Kaflarnir eru afar ólíkir enda var leitað til fólks úr ýmsum stéttum, úr ólíkum landshlutum, fólks með mismunandi bakgrunn, menntun, ævistarf, uppeldisaðstæður og annað sem hefur haft mótandi áhrif á líf þess.", long_description: "", page_count: 362, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15553, pre_title: "", title: "Spegill fyrir skuggabaldur", post_title: "Atvinnubann og misbeiting valds", slug: "spegill-fyrir-skuggabaldur-15553", description: "Í þessari bók skyggnist Ólína Kjerúlf Þorvarðardóttir undir blæjuna sem umlykur spillingu og fyrirgreiðslupólitík á Íslandi. Hún greinir frá reynslu fólks sem orðið hefur fyrir atvinnubanni og misbeitingu ráðningarvalds. Samhliða bregður hún sögulegu ljósi á hvernig atvinnurekendavald og klíkustjórnmál hafa spunnið sameiginlega valdaþræði í íslensku samfélagi.", long_description: "", page_count: 252, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15554, pre_title: "", title: "Gosi", post_title: "", slug: "gosi-15554", description: "Ævintýri handa börnum í fagurlega myndskreyttri útgáfu.", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15555, pre_title: "", title: "Stígvélaði kötturinn", post_title: "", slug: "stigveladi-kotturinn-15555", description: "Ævintýri handa börnum í fagurlega myndskreyttri útgáfu.", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15556, pre_title: "", title: "Fjallakúnstner segir frá", post_title: "", slug: "fjallakunstner-segir-fra-15556", description: "Fjallakúnstnerinn Stefán frá Möðrudal – rótlaust lífstré kynborins sonar íslenskra öræfa hefur víða farið og fæstir hafa gert sér grein fyrir kjörviði undir hrjúfum berkinum. Listsköpun Stefáns ber svip hrikalegrar náttúru Möðrudalsöræfa, oft í tröllslíki. \r\nHér birtist á nýjan leik samtalsbók Pjeturs Hafstein Lárussonar við Stefán frá Möðrudal, einn merkasta fulltrúa íslenskrar alþýðumenningar á síðustu öld.", long_description: "", page_count: 143, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15557, pre_title: "", title: "Byltingin svikin", post_title: "", slug: "byltingin-svikin-15557", description: "Byltingin svikin er eitt af merkustu verkum Trotskys. Í þessari bók, sem hann ritaði árið 1936, gerir hann grein fyrir því hvernig Sovétríkin höfðu þróast á þeim 19 árum sem liðin voru frá októberbyltingunni.", long_description: "", page_count: 280, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15558, pre_title: "", title: "Tímamót", post_title: "", slug: "timamot-15558", description: "Tímamót er lykilskáldsaga eftir Þorstein Gíslason skáld og ritstjóra og fjallar um Ísland um aldamótin 1900. Þorsteinn samdi söguna á efstu æviárum sínum, sennilega eftir að hann flutti og birti útvarpsfyrirlestra sína 1936 um stjórnmálasögu Íslands 1896-1918.", long_description: "", page_count: 280, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15559, pre_title: "", title: "Heiman heim", post_title: "", slug: "heiman-heim-15559", description: "Uppistaðan í þessari litlu bók eru bréf, flest aðventu- eða jólabréf til barna og vandamanna höfundar, en nokkur þó til vinar um staði og stundir höfundar heima í Borgarfirði. Tvær frásagnir eru frá sögufrægum borgum í Þýskalandi. Efnið er margvíslegt, um ljósið og vatnið og unaðsstundir.", long_description: "", page_count: 84, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15560, pre_title: "", title: "Sturlunga geðlæknisins", post_title: "", slug: "sturlunga-gedlaeknisins-15560", description: "Óttar Guðmundsson geðlæknir veltir nú fyrir sér geðrænum vandamálum á þrettándu öld og skoðar  róstusamt  líf Sturlunga.  Snorri Sturluson, Sturla Sighvatsson, Þórdís Snorradóttir og Gissur Þorvaldsson ásamt fleirum leggjast öll á bekkinn hjá geðlækninum. Hefur mannlegt eðli eitthvað breyst á þessum 800 árum sem liðin eru? Áður hefur  Óttar hefur gefið út bækurnar, Frygð og fornar hetjur og Hetjur og hugarvíl um geðræn vandamál í Íslendingasögum auk fjölda annarra bóka.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15561, pre_title: "", title: "Ellert", post_title: "Endurminningar Ellerts B. Schram", slug: "ellert-15561", description: "Óþarft er að kynna landsliðsfyrirliðann, KR-inginn, alþingismanninn og ritstjórann Ellert B. Schram. Hann hefur komið víða við á langri ævi og hvarvetna verið í forystuhlutverki. Ellert rekur hér lífshlaup sitt í hispurslausri og líflegri frásögn, allt frá uppvaxtarárum á stóru heimili í Vesturbænum fram til dagsins í dag. Bókina prýðir fjöldi mynda.", long_description: "", page_count: 271, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15562, pre_title: "", title: "Bærinn sem hvarf", post_title: "í ösku og eldi 1362", slug: "baerinn-sem-hvarf-15562", description: "Í þessari bók fjallar Dr. Bjarni F. Einarsson fornleifafræðingur á Fornleifafræðistofunni um ýmsar hamfarir, einkum eldgos, sem mannkynið hefur mátt þola í aldanna rás og um áhrif þeirra á menningu og mannlíf. Í brennidepli er miðaldabýlið Bær í Öræfum sem fór í eyði á augnabliki árið 1362 þegar Öræfajökull gaus sínu stóra gosi. Gosið var af sama meiði og gosið í Vesúvíusi árið 79 e.Kr. en það eyddi Pompei og fleiri borgum á Ítalíu.", long_description: "", page_count: 319, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15563, pre_title: "", title: "Frjáls eins og fuglinn", post_title: "Ný útgáfa", slug: "frjals-eins-og-fuglinn-15563", description: "Í þessari bók rekur Mats Wibe Lund ljósmyndari minningar sínar en jafnframt er bókin yfirlit um ljósmyndaferil hans sem spannar meira en 60 ár.", long_description: "", page_count: 215, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15564, pre_title: "", title: "Af moldargólfi í ólgusjó verkalýðsmála", post_title: "", slug: "af-moldargolfi-i-olgusjo-verkalydsmala-15564", description: "Í þessar bók fjallar Magnús L. Sveinsson fyrrum formaður Verslunarmannafélags Reykjavíkur og  forseti borgarstjórnar um feril sinn, allt frá æskuárum austur á Rangárvöllum. Við kynnumst mannlífi á Suðurlandi á fjórða og fimmta áratugi síðustu aldar. Fjallað er um stjórnmálaþátttöku hans á Suðurlandi og síðar í Reykjavík. Ferill hans í hringiðu verkalýðsmála um áratugaskeið er þó fyrirferðarmestur í þessari læsilegu bók.", long_description: "", page_count: 380, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15565, pre_title: "", title: "Staldraðu við", post_title: "", slug: "staldradu-vid-15565", description: "Ljóðabókin og geisladiskurinn „Staldraðu við“ eftir Ólaf F. Magnússon, lækni og fv. borgarstjóra er önnur ljóðabók hans.\r\nÍ bókinni eru 156 ljóð ort á tímabilinu 2014-2020 og með bókinni fylgir 12 laga geisladiskur, þar af 9 lög sem ekki hafa verið útgefin áður. \r\nLjóðin fjalla um „fegurð og fljóðin,“ „ fjallkonu Íslands og tilveruna alla,“ „mannanna lesti,“ „vináttu, kærleik og dyggð,“ „ljúfmennsku, hamingju og bjartsýni,“ eins og segir í titilljóðinu „Staldraðu við.“", long_description: "", page_count: 130, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 40, book_author_id: nil},
  {source_id: 15566, pre_title: "", title: "Blóðberg", post_title: "", slug: "blodberg-15566", description: "Árið 1608 sver ung stúlka í Skagafirði, Þórdís Halldórsdóttir, eið um að hún sé hrein mey eftir að upp kemur kvittur um ástarsamband hennar við mág sinn – en slíkt var dauðasök á tímum stóradóms. Fimm mánuðum síðar fæðir hún barn. Þórdís má lifa með ásökunum um blóðskömm og er gert að sæta pyntingum segi hún ekki til barnsföður síns. Heill áratugur líður þar til dómur er kveðinn upp. Heillandi og átakanleg söguleg skáldsaga.", long_description: "", page_count: 174, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15567, pre_title: "", title: "Mæður geimfara", post_title: "", slug: "maedur-geimfara-15567", description: "Hér er á ferðinni litríkt sagnasafn um tilraunir manneskjunnar til að svífa – og nagandi vitneskjuna um þyngdaraflið sem sigrar alltaf. Sigurbjörg Þrastardóttir skrifar ljóð, leiktexta og prósa og er margkunn heima og erlendis fyrir einstakan og ísmeygilegan stíl.", long_description: "", page_count: 143, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15568, pre_title: "", title: "Sjálf í sviðsljósi", post_title: "Ingibjörg Steinsdóttir leikkona (1903–1965) og sjálfsmyndasafn hennar. Sýnisbók íslenskrar alþm. 25", slug: "sjalf-i-svidsljosi-15568", description: "Bókin fjallar um ævi og ímynd Ingibjargar Steinsdóttur, leikkonu og leikstjóra. Hún hrærðist í skáldskap og leiklist og lifði á skjön við ríkjandi viðhorf. Utan sviðs gegndi hún mörgum hlutverkum: bóndi, pólitískur aktívisti, ráðskona, spákona, eiginkona, móðir og amma.", long_description: "", page_count: 288, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15569, pre_title: "", title: "Hvílíkt torf – tóm steypa", post_title: "Úr torfbæjum í steypuhús. Sýnisbók íslenskrar alþýðumenningar 26", slug: "hvilikt-torf-tom-steypa-15569", description: "Bókin fjallar um þá byltingu í húsagerð sem varð þegar steinsteypa tók við af torfinu. Árið 1900 var efnt til mikillar rannsóknar sem átti að leiða til niðurstöðu um hvernig byggja ætti til framtíðar. Upplýsingum var safnað en þær voru aldrei nýttar. Hér eru frumheimildirnar birtar ásamt umfjöllun höfundar um þetta stórmerkilega efni.", long_description: "", page_count: 300, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15570, pre_title: "", title: "Frá degi til dags", post_title: "Dagbækur, almanök og veðurbækur 1720–1920.  Sýnisbók íslenskrar alþýðumenningar 27", slug: "fra-degi-til-dags-15570", description: "Í bókinni er rakin saga dagbókaritunar á Íslandi á tímabilinu 1720–1920. Fjallað er um rætur dagbókaritunar í tímatalsskráningum, annálaskrifum og ritun veðurbóka um leið og rakin er þróun dagbókaskrifa á tímabilinu. Í bókinni er birt efnistekin skrá yfir þessar heimildir, sem eru alls um 250 talsins.", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15571, pre_title: "", title: "Almanak HÍÞ ásamt Árbók", post_title: "", slug: "almanak-hith-asamt-arbok-15571", description: "Auk dagatals inniheldur almanakið margvíslegar upplýsingar, s.s. um sjávarföll og gang himintungla, helstu fyrirbæri á himni sem frá Íslandi sjást, stjörnukort, áttavitastefnur á Íslandi og tímabelti heimsins. Einnig er hér yfirlit um hnetti himingeimsins, mælieiningar, veðurfar, stærð og mannfjölda allra sjálfstæðra ríkja o.fl. Að vanda fylgir Árbók um helstu viðburði ársins 2019.", long_description: "", page_count: 120, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15572, pre_title: "", title: "Hestar", post_title: "", slug: "hestar-15572", description: "Íslenski hesturinn hefur þolað margt í þúsund ára þjónustuhlutverki við kaldlynda þjóð í köldu landi. Hér stígur þarfasti þjónninn fram rétt eins og hann er og var allt frá því að fyrsta fylfulla merin steig óstyrkum fæti á íslenska strönd seint á níundu öld; laus við keppnisrembing, upphafningu og tildur.\r\nHjörleifur Hjartarson og Rán Flygenring sýndu það með bók sinni >>>Fuglar<<< að íslensk fuglafræði er annað og meira en upptalning á þurrum staðreyndum. Nú er komið að hrossunum.", long_description: "", page_count: 184, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, book_author_id: nil},
  {source_id: 15574, pre_title: "", title: "Bölvun múmíunnar", post_title: "Seinni hluti", slug: "bolvun-mumiunnar-15574", description: "Múmían Hóremheb er í ræningjahöndum en Júlía, María og Charlie ætla ekki að játa sig sigruð. Þau fá far með skemmtiferðaskipinu Henriettu, rekast þar á ýmsa óvini og fyrr en varir eru þau fangar Qwacha. Við tekur æsispennandi atburðarás þar sem ekkert er sem sýnist.\r\nFyrri hluti >>>Bölvunar múmíunnar<<< hélt mörgum lesendum andvaka en enn meiri hætta er á svefnlausum nóttum við að lesa framhaldið.", long_description: "", page_count: 156, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, book_author_id: nil},
  {source_id: 15576, pre_title: "", title: "Tíkin", post_title: "", slug: "tikin-15576", description: "Damaris þráir það heitast að verða móðir en þrátt fyrir töfradrykki, smyrsl og helgiathafnir geta þau Rogelio ekki eignast barn. Þegar henni býðst að taka að sér tíkarhvolp grípur hún tækifærið feginshendi en tíkin lætur ekki temja sig frekar en náttúran.\r\nÁhrifamikil skáldsaga sem gerist í litlu þorpi við kólumbísku Kyrrahafsströndina.", long_description: "", page_count: 134, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, book_author_id: nil},
  {source_id: 15580, pre_title: "Afmælisrit til heiðurs dr. Sigrúnu Júlíusdóttur", title: "Af neista verður glóð", post_title: "Vísindi og vettvangur í félagsráðgjöf", slug: "af-neista-verdur-glod-15580", description: "Dr. Sigrún Júlíusdóttir, prófessor emeritus við Háskóla Íslands, er frumkvöðull sem sett hefur mark sitt á þróun íslensks samfélags og velferðarmála, bæði á hinum starfstengda vettvangi og á sviði fræðaiðkunar. Rannsóknarsvið hennar er víðfeðmt og spannar m.a. fjölskyldumál og samskipti, barna- og fjölskylduvernd og rannsóknir og þróun í félagsráðgjöf ásamt faghandleiðslu. Í þessu safni greina eftir ólíka sérfræðinga og fagmenn er fjallað um mörg þessara efna.", long_description: "", page_count: 302, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15581, pre_title: "", title: "Tíðni orða í tali barna", post_title: "", slug: "tidni-orda-i-tali-barna-15581", description: "Fyrsta bók sinnar tegundar á Íslandi um tíðni orða í talmáli barna. Hún sýnir hvaða orð börnum er tamast að nota þegar þau tjá sig á íslensku í samræðum við fullorðna. Orðtíðnibókin er framlag til að auka þekkingu á málfærni barna sem tala íslensku. Henni er ætlað að nýtast við kennslu á grunnorðum í íslensku fyrir börn með málþroskaröskun og börn af erlendum uppruna sem eru að læra íslensku.", long_description: "", page_count: 534, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15582, pre_title: "", title: "Andvari 2020", post_title: "", slug: "andvari-2020-15582", description: "145. árgangur tímaritsins >>>Andvara<<< en hinn 62. í nýjum flokki. Aðalgreinin í ár er æviágrip Brodda Jóhannessonar skólastjóra eftir Þórólf Þórlindsson. Aðrir höfundar Andvara eru Lára Magnúsardóttir, Steinunn Inga Óttarsdóttir, Auður Aðalsteinsdóttir, Arngrímur Vídalín, Páll Björnsson og Markus Meckl.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15585, pre_title: "", title: "Kynþáttafordómar", post_title: "Í stuttu máli I. Ritröð Félagsvísindasviðs", slug: "kynthattafordomar-15585", description: "Aukinn sýnileiki haturs og ofbeldis vestanhafs ásamt endurnýjuðum krafti baráttunnar gegn kynþáttamismunun hefur enn á ný sett rasisma á dagskrá. Í fyrstu bók nýrrar ritraðar í félagsvísindum er fjallað um kynþáttafordóma í sögu og samtíð. Sjónum er beint að alþjóðlegu samhengi kynþáttahyggju og birtingarmynd kynþáttafordóma í íslensku samfélagi.", long_description: "", page_count: 100, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15587, pre_title: "", title: "Sendiboðinn", post_title: "", slug: "sendibodinn-15587", description: "Japan hefur um langa hríð verið lokað vegna ónefndra náttúruhamfara af mannavöldum. Eldra fólkið lifir lengi við hestaheilsu og annast um börnin sem eru viðkvæm og gömul fyrir aldur fram. Mumei býr með Yoshiro, fjörgömlum langafa sínum. Drengurinn er veikburða en laus við sjálfsvorkunn og bölsýni. Yoshiro einbeitir sér að því að annast Mumei, útvalinn sendiboða, þjakaður af samviskubiti vegna gjörða sinnar kynslóðar og afleiðinga þeirra. Áður hefur komið út á íslensku bókin >>>Etýður í snjó<<< eftir sama höfund.", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, book_author_id: nil},
  {source_id: 15588, pre_title: "", title: "Litla land", post_title: "", slug: "litla-land-15588", description: "Gabríel er 10 ára og lifir áhyggjulausu lífi í úthverfi Bújúmbúra, í Afríkuríkinu Búrúndí. Allt hverfist um vinina og þeirra uppátæki. En þegar borgarastríð skellur á og þjóðarmorð er framið í nágrannaríkinu Rúanda, breytist allt. Falleg saga um vináttu og sakleysi, en líka átakanlegur vitnisburður um þau eyðandi áhrif sem stríð og ofbeldi hafa á líf og samfélög manna.", long_description: "", page_count: 340, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, book_author_id: nil},
  {source_id: 15590, pre_title: "", title: "Mitt (ó)fullkomna líf", post_title: "", slug: "mitt-o-fullkomna-lif-15590", description: "Samkvæmt Instagram-reikningi Kötu Brenner býr hún í London, starfar í auglýsingabransanum og ver frítímanum á smart kaffihúsum og veitingastöðum. Kannski fegrar hún lífið örlítið … en hver notar ekki smá filter á Instagram? \r\nÞegar Kata missir vinnuna og mögulegt ástarsamband virðist glatað neyðist hún til að flytja aftur á æskuheimilið í sveitinni.\r\nHversu lengi er haldið í draumana þegar örlögin virðast staðráðin í að bregða fyrir þig fæti?", long_description: "", page_count: 494, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, book_author_id: nil},
  {source_id: 15591, pre_title: "", title: "Ótrúleg ævintýri Brjálínu Hansen 3", post_title: "Endalok alheimsins", slug: "otruleg-aevintyri-brjalinu-hansen-3-15591", description: "Brjálína hefur í nógu að snúast heima í Plastgerði því nú er mamma hennar nánast rúmföst og Brjálína reynir eftir bestu getu að vera henni innan handar. Á æskuheimili Brjálínu, Brjálivíu, gengur mikið á því pabbi hennar og kærastan hans hafa eignast tvíburastráka.\r\n>>>Endalok alheimsins<<< er þriðja og síðasta bókin í bókaflokknum >>>Ótrúleg ævintýri Brjálínu Hansen<<<.", long_description: "", page_count: 188, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, book_author_id: nil},
  {source_id: 15593, pre_title: "", title: "Á fjarlægri strönd", post_title: "", slug: "a-fjarlaegri-strond-15593", description: "", long_description: "", page_count: 118, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, book_author_id: nil},
  {source_id: 15594, pre_title: "", title: "Kúgun kvenna", post_title: "", slug: "kugun-kvenna-15594", description: "John Stuart Mill gerði sér vel ljóst að jafnrétti fengist ekki með lagabreytingum einum heldur þyrfti að vinda ofan af aldalöngum bábiljum og kynbundnum yfirráðum sem hefðu komið í veg fyrir að konur nytu sín til jafns við karla. >>>Kúgun kvenna<<< er mikilvægt innlegg í jafnréttisbaráttu enn þann dag í dag – rúmum 150 árum eftir að verkið kom út fyrst.", long_description: "", page_count: 384, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15595, pre_title: "", title: "Samdrykkjan", post_title: "", slug: "samdrykkjan-15595", description: "Réttlæti, fegurð og hið góða eru umræðuefni drykkjubræðranna sem Platon lýsir snilldarlega í þessu lykilverki heimspekinnar. Þeir leita svara við spurningum um hlutverk ástarinnar í lífinu og víkja að ódauðleika sálarinnar í leiðinni. Eyjólfur Kjalar Emilsson þýddi og ritaði inngang.", long_description: "", page_count: 184, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15596, pre_title: "", title: "Dýralíf", post_title: "", slug: "dyralif-15596", description: "Nóbelsverðlaunahöfundurinn John Coetzee beitir hér aðferðum rithöfundarins til að nálgast eina flóknustu og erfiðustu spurningu samtímans: Hvers vegna ættum við að halda áfram að meðhöndla dýr eins og sálarlausar vélar, án tilfinninga, án hugsunar og án réttinda?", long_description: "", page_count: 224, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15597, pre_title: "", title: "Minnisblöð Maltes Laurids Brigge", post_title: "", slug: "minnisblod-maltes-laurids-brigge-15597", description: "Eina skáldsaga Rilkes hefur að geyma ljóðrænar lýsingar á glímu\r\nsöguhetjunnar við stórborgina og glundroða nútímans, grimmilegar\r\næskuminningar og brot úr evrópskri menningarsögu. Hér er á ferð tímamótaverk eftir einn af meisturum evrópskrar nútímaljóðlistar frá árinu 1910. Benedikt Hjartarson þýddi og ritaði inngang.", long_description: "", page_count: 374, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15598, pre_title: "", title: "Júdít", post_title: "", slug: "judit-15598", description: "Gyðingakonan Júdít er falleg ekkja sem vinnur hetjudáð með því að höggva höfuðið af hershöfðingjanum Hólófernesi. Dáð hennar varð fræg og margir listamenn hafa gert átökum þeirra Hólófernesar skil. Þessi fallega þýðing er frá 14. öld en á máli sem er tiltölulega auðskilið nútímalesendum. Þýðingin er prentuð með nútímastafsetningu og henni fylgir fræðandi inngangur og nafnaskrá.", long_description: "", page_count: 49, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 42, book_author_id: nil},
  {source_id: 15599, pre_title: "", title: "Það er alveg satt!", post_title: "Lífshlaup og starf kristniboðanna Kjellrunar Langdal og Skúla Svavarssonar", slug: "thad-er-alveg-satt-15599", description: "Ótrúlegar sögur vekja spurningar, gjarnan þá hvort þær séu sannar. Þannig var oft með Skúla er hann sagði frá reynslu sinni á kristniboðsakrinum. Vigfús Ingvar Ingvarsson rekur lífsferil þeirra hjóna en þau hafa víða komið við og lagt mikið af mörkum. Persónusaga þeirra hjóna er ramminn sem fyllt er inn í með fræðandi efni er gefur góða heildarmynd. Fjöldi mynda er í bókinni.", long_description: "", page_count: 262, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 43, book_author_id: nil},
  {source_id: 15601, pre_title: "", title: "Ennþá ég", post_title: "", slug: "enntha-eg-15601", description: "Louisa Clark er komin til New York og er fjarri kærasta sínum. Hún fær starf sem aðstoðarmaður ungrar konu sem er gift eldri auðkýfingi. Louisa kynnist Josh en kynnin af honum hafa ófyrirséðar afleiðingar í för með sér. Ennþá ég er sjálfstætt framhald bókanna Ég fremur en þú og Eftir að þú fórst.", long_description: "", page_count: 491, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15604, pre_title: "", title: "Sunnuhlíð. Ævintýri fóks og félaga í Kópavogi 1979-1999", post_title: "", slug: "sunnuhlid-aevintyri-foks-og-felaga-i-kopavogi-1979-1999-15604", description: "Árið 1979 tóku níu félög í Kópavogi sig saman um byggingu hjúkrunarheimilis fyrir aldraða. Safnað var fé með þátttöku flestra bæjarbúa í Kópavogi. Sunnuhlíð hóf rekstur 1982. Sunnuhlíðarsamtökin reistu svo þjónustuíbúðir fyrir aldraða og markaði það tímamót í slíkum rekstri. Ásgeir Jóhannesson, formaður þeirra til 20 ára, rifjar upp söguna.", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 45, book_author_id: nil},
  {source_id: 15605, pre_title: "", title: "Skírnir - Tímarit HÍB", post_title: "Vor og haust 2020", slug: "skirnir-timarit-hib-15605", description: "Fjölbreytt og vandað efni, m.a. um íslenskt mál, bókmenntir, náttúru og sögu, heimspeki, myndlist, stjórnmál og ýmis fræði í sögu og samtíð. Skírnir er elsta menningartímarit á Norðurlöndum og kemur út tvisvar á ári. Nýir áskrifendur velkomnir: sími 588-9060.", long_description: "", page_count: 480, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15606, pre_title: "", title: "Orð og tunga 22", post_title: "", slug: "ord-og-tunga-22-15606", description: "Tímaritið Orð og tunga birtir greinar sem lúta að máli og málfræði. Lögð er áhersla á greinar um orðfræði, þ. á m. nafnfræði og íðorðafræði, og greinar um orðabókafræði og orðabókagerð, sem og fræðilegar greinar um málrækt og málstefnu.\r\nGreinar eru að jafnaði skrifaðar á íslensku en einnig eru birtar greinar á öðrum norrænum málum og ensku.", long_description: "", page_count: 148, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 42, book_author_id: nil},
  {source_id: 15607, pre_title: "", title: "Fólk í angist", post_title: "", slug: "folk-i-angist-15607", description: "Á opnu húsi hjá fasteignasala fer allt úr skorðum þegar örvæntingarfullur og misheppnaður bankaræningi tekur alla viðstadda í gíslingu. \r\nHér er Fredrik Backman í fantagóðu formi í bók sem er í senn bráðfyndin og þrungin sorg og sársauka.", long_description: "", page_count: 384, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15608, pre_title: "", title: "Frumgerðir og eftirmyndir", post_title: "Aðferðir, eiginleikar og femínísk heimspeki", slug: "frumgerdir-og-eftirmyndir-15608", description: "Fyrir hvern er heimspeki? Er hún einangruð kvenfjandsamleg grein eða getur hún verið fyrir alls konar fólk? Í bókinni er að finna tólf ritgerðir þar sem leitað er svara við spurningum á borð við þessar. Meðal annarra viðfangsefna má nefna beitingu rökhugsunar, aðferðir við að skoða eðli hlutanna og virkni peninga í samfélaginu.", long_description: "", page_count: 300, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15609, pre_title: "", title: "Fegurðin er ekki skraut", post_title: "Íslensk samtímaljósmyndun", slug: "fegurdin-er-ekki-skraut-15609", description: "Hér fjalla átta listfræðingar, sýningastjórar og aðrir fræðimenn um íslenska samtímaljósmyndun og setja í samhengi við alþjóðlega strauma. Í bókinni er jafnframt að finna fjölda ljósmynda eftir marga helstu ljósmyndara landsins.", long_description: "", page_count: 328, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 46, book_author_id: nil},
  {source_id: 15610, pre_title: "", title: "Tvímælis", post_title: "Heimspeki menntunar og skólakerfi nútímans", slug: "tvimaelis-15610", description: "Til hvers eru skólar? Geta yfirvöld stjórnað þeim? Bætir aukin skólaganga efnahagsleg kjör okkar? Gerir hún okkur að betri mönnum? Í þessari bók eru færð rök fyrir því að hugsunarleysi um spurningar sem þessar ali af sér afglöp í stjórn og uppbyggingu skólastarfs.", long_description: "", page_count: 174, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15611, pre_title: "", title: "Gripla XXX", post_title: "", slug: "gripla-xxx-15611", description: "Gripla er alþjóðlegt, ritrýnt tímarit á sviði norrænna fræða fyrri alda og kemur út í desember á hverju ári. Tímaritið er alþjóðlegur vettvangur fyrir rannsóknir á sviði íslenskra og norrænna fræða, einkum handrita- og textafræða, bókmennta og þjóðfræða.", long_description: "", page_count: 286, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 42, book_author_id: nil},
  {source_id: 15615, pre_title: "", title: "Gleymið ekki að endurnýja", post_title: "Saga Happdrættis Háskóla Íslands", slug: "gleymid-ekki-ad-endurnyja-15615", description: "Happdrætti Háskólans hóf göngu sína árið 1934 til að fjármagna Aðalbyggingu skólans og hefur upp frá því staðið undir nær öllum nýframkvæmdum hans. Hvers kyns happdrætti eru stór hluti af menningu okkar og oft hefur verið hart deilt um slíka starfsemi, jafnt hér og erlendis. Bókin varpar einnig ljósi á byggingarsögu Háskólans.", long_description: "", page_count: 416, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15616, pre_title: "", title: "Dýraríkið", post_title: "", slug: "dyrarikid-15616", description: "Yfirgripsmikið verk um dýrafræði í tveimur bindum, prýtt fjölda mynda. Í senn fræðslurit og uppflettirit um undirstöður dýrafræðinnar, flokkun og einkenni dýra af öllum stærðum og gerðum. Dýraríkið, sem hér kemur út í fyrsta sinn, er metnaðarfyllsta ritverk Örnólfs Thorlacius, kennara og alþýðufræðara, og ber vitni ævilangrar ástríðu höfundar fyrir viðfangsefninu.", long_description: "", page_count: 838, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15617, pre_title: "", title: "Friðrik Ólafsson", post_title: "", slug: "fridrik-olafsson-15617", description: "Í þessari bók er rakinn ævintýralegur skákferill Friðriks Ólafssonar stórmeistara. Brugðið er upp nærmynd af samferðamönnum og pólitísku andrúmslofti, frásögnum af stórviðburðum og einstæðum menningararfi. Í bókinni eru raktar margar mikilvægar viðureignir Friðriks á löngum skákferli.", long_description: "", page_count: 529, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15618, pre_title: "", title: "Vá!", post_title: "Ritgerðir um fagurfræði náttúrunnar", slug: "va-15618", description: "Hvers vegna segjum við: „Vá!“ frammi fyrir ægifögru landslagi? Hvað meinum við með því? Hvernig getum við rætt um slíka upplifun og þar með rökstutt verndun náttúrufegurðar? Í ritgerðunum í þessari bók er leitast við að svara þessum spurningum með greiningu á þeim upplifunum, hugtökum og orðræðu sem vekja þær.", long_description: "", page_count: 180, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15619, pre_title: "", title: "Rannsóknir í viðskiptafræði I", post_title: "", slug: "rannsoknir-i-vidskiptafraedi-i-15619", description: ">>>Rannsóknir í viðskiptafræði I<<< veita innsýn í fjölbreyttan fræðaheim viðskiptafræðinnar. Greinasafnið byggist á rannsóknum á mörgum atvinnugreinum, s.s sjávarútvegi, iðnaði, orku, smásölu og þjónustu, en einnig á opinberri stjórnsýslu, s.s. utanríkisþjónustu og starfsemi sveitarfélaga. Flestir höfundanna eru kennarar eða nemendur við Viðskiptafræðideild Háskóla Íslands.", long_description: "", page_count: 303, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15621, pre_title: "", title: "Hálft hjarta", post_title: "", slug: "halft-hjarta-15621", description: "Elín er virtur og vinsæll ljósmyndari í New York. Þegar hún fær óvænt bréf frá æskuvini sínum á Gotlandi fara á stjá draugar fortíðar í fátækt og eymd. Fyrri bók hennar, Rauða minnisbókin, sló í gegn um allan heim.", long_description: "", page_count: 391, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15622, pre_title: "", title: "Litla biblían", post_title: "", slug: "litla-biblian-15622", description: "Ríkulega myndskreytt bók sem segir sextíu sögur úr Biblíunni. Þar læra börnin um kærleika, umhyggju, hugrekki, vináttu, virðingu og um það að hið góða sigrar! Sögur Biblíunnar eru svo sannarlega innihaldsríkar og eftirminnilegar. Inn á milli má finna bænir og vers til að fara með í önnum hversdagsins. Fremst í bókina má skrifa minningar um mikilvæg þroskaskref.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, book_author_id: nil},
  {source_id: 15623, pre_title: "", title: "Ástarsögur íslenskra karla", post_title: "Frásagnir úr raunveruleikanum", slug: "astarsogur-islenskra-karla-15623", description: "Sögurnar eru af öllu tagi, úr lífi ungra manna og eldri, gagnkynhneigðra, samkynhneigðra og kynsegins. Sumar eru rómantískar, aðrar dálítið groddalegar, sumar fyndnar, aðrar sárar og sorglegar. En í öllum sögunum er ástin yfir og allt um kring í öllum sínum margbreytileika.", long_description: "", page_count: 188, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15624, pre_title: "", title: "Hernaðarlist meistara Sun", post_title: "Stofnun Vigdísar Finnbogadóttur", slug: "hernadarlist-meistara-sun-15624", description: ">>>Hernaðarlist Meistara Sun<<< er eitt víðlesnasta og áhrifamesta fornrit Kínverja. Ritið endurspeglar „strategíska“ hugsun og heimspeki sem beita má á mörgum sviðum, s.s. í hernaði, viðskiptum og öðrum mannlegum samskiptum. Í þessari tvímála útgáfu er þýtt beint úr fornkínversku. Skýringar og inngangur þýðanda setja ritið í sögulegt, menningarlegt og heimspekilegt samhengi.", long_description: "", page_count: 145, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15625, pre_title: "", title: "Raddir frá Spáni", post_title: "Sögur eftir spænskar konur. Stofnun Vigdísar Finnbogadóttur", slug: "raddir-fra-spani-15625", description: "Í þessu smásagnasafni eru sögur eftir tuttugu og sex spænskar konur. Rithöfundarnir koma frá héruðum á meginlandi Spánar og frá Kanarí- og Baleareyjum. Smásögurnar – langar sögur og stuttar, örstuttar sögur og örsögur – spanna rúma öld og eru fjölbreyttar að efni og stíl. Þær fjalla um ástir og hatur, gleði og sorg, misrétti og ójöfnuð, vináttu og fjandskap, konur og karla, stöðu kvenna í samfélaginu og margt fleira.", long_description: "", page_count: 288, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15626, pre_title: "", title: "Leggðu lífskapal", post_title: "Skapgerðar- og tilfinningakort", slug: "leggdu-lifskapal-15626", description: "Fjölnota spilastokkur sem byggir á  hugmyndafræði jákvæðrar sálfræði um tilfinningar (80 spil) og skapgerðarstyrkleika (50 spil). Spilin nýtast einstaklingum sem vilja kynnast sjálfum sér betur út frá eigin tilfinningum og styrkleikum. Nýtast einnig fagaðilum, svo sem markþjálfum og öðrum sem vinna við meðferð fólks. Spilastokknum fylgja auðveldar notkunarleiðbeiningar.", long_description: "", page_count: 135, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, book_author_id: nil},
  {source_id: 15627, pre_title: "", title: "Til hnífs og skeiðar", post_title: "Greinasafn um íslenska matarmenningu", slug: "til-hnifs-og-skeidar-15627", description: "Bókin hefur víða skírskotun og höfðar til almennings, áhugafólks um mat og matarmenningu jafnt sem fræðimanna. Hún fjallar um færni, fjölbreytni og hugvit og hvernig íslensk matargerð og hérlendar aðstæður hafa skapað verðmæti sem sífellt koma á óvart, vekja athygli, jafnvel undrun.", long_description: "", page_count: 250, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15628, pre_title: "", title: "Sunnudagsmatur og fleiri sögur Rómafólks", post_title: "Stofnun Vigdísar Finnbogadóttur", slug: "sunnudagsmatur-og-fleiri-sogur-romafolks-15628", description: "Bókmenntir Rómafólks og Sígauna eru nátengdar munnmælahefð og endurminningum og eru nánast óþekktar hérlendis. Þessi bók hefur að geyma sögur af ýmsu tagi eftir sex rithöfunda frá 20. og 21. öld: Ilonu Ferková, Jovan Nikolic, Jess Smith, Jorge Emilio Nedich, Jorge Nedich og Matéo Maximoff. Fróðlegur inngangur um bókmenntahefð Rómafólks fylgir þýðingunum.", long_description: "", page_count: 290, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15629, pre_title: "", title: "Leiðin heim", post_title: "Vegur kristinnar íhugunar", slug: "leidin-heim-15629", description: "Leiðin heim sýnir hvernig reglubundin ástundun kristinnar íhugunar (kyrrðarbænar) getur hjálpað að finna innri frið og dýpka sambandið við Guð. Kyrrðarbæn er stunduð á meðal einstaklinga, í hópum á Íslandi og um heim allan! Leiðin heim er óbeint framhald \"Vakandi hugur vökult hjarta\". Hvað er kyrrðarbæn, hvaða áhrif getur hún haft á líf mitt? Sjá www.kyrrdarbaen.is/", long_description: "", page_count: 188, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, book_author_id: nil},
  {source_id: 15630, pre_title: "", title: "Öryggi þjóðar", post_title: "", slug: "oryggi-thjodar-15630", description: "Í þessu riti fjallar Sigurður E. Guðmundsson (1932-2019) um helstu þætti í sögu velferðarmála á Íslandi og nágrannalöndunum 1887-1947. Sigurður starfaði lengst af sem framkvæmdastjóri Húsnæðisstofnunar ríkisins. Bókin er mikilsvert og fróðlegt framlag til íslenskrar félags- og stjórnmálasögu.", long_description: "", page_count: 496, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 32, book_author_id: nil},
  {source_id: 15631, pre_title: "", title: "Bænabókin", post_title: "", slug: "baenabokin-15631", description: ">>>Bænabókin<<< – ný og ítarlegri útgáfa er í samantekt og ritstjórn Karls Sigurbjörnssonar biskups.  Þessi bók er hugsuð sem förunautur á vegi bænar og trúarlífs, leiðsögn í bænalífi, leiðbeiningar um það hvernig dýpka má og þroska trúarlíf sitt.  Hún geymir mikinn fjölda bæna gamalla og nýrra og úr ýmsum áttum, allt frá fyrstu tíð kristninnar og til okkar daga.", long_description: "", page_count: 596, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, book_author_id: nil},
  {source_id: 15632, pre_title: "", title: "Allt uns festing brestur", post_title: "", slug: "allt-uns-festing-brestur-15632", description: "Séra Davíð Þór Jónsson hefur ort 21 trúarljóð undir dróttkvæðum hætti sem hér koma fyrir almenningssjónir. Ljóðin eru ort við 13 liði hinnar klassísku messu og í raun mætti því messa með þeim í stað hinna hefðbundnu litúrgísku texta. En fyrst og fremst eru þau samin til trúarsvölunar heima í stofu, enda fæddust þau í fæðingarorlofi.", long_description: "", page_count: 36, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, book_author_id: nil},
  {source_id: 15633, pre_title: "", title: "Augljóst en hulið", post_title: "Að skilja táknheim kirkjubygginga", slug: "augljost-en-hulid-15633", description: "Í þessari bók leiðir höfundur okkur í gegnum sögu kirkjubygginga, en í stað þess að einblína á byggingarlist opnar hann augu lesandans fyrir dýpri merkingu kirkjurýmisins á nýstárlegan hátt.\r\n>>>Augljóst en hulið<<< er í senn hagnýtt uppflettirit fyrir allt áhugafólk um kirkjur og vandað fræðirit fyrir alla sem vilja rannsaka guðfræðilegan merkingarheim kirkjubygginga.", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, book_author_id: nil},
  {source_id: 15634, pre_title: "", title: "Jón Vídalín ævisaga og ritsafn", post_title: "", slug: "jon-vidalin-aevisaga-og-ritsafn-15634", description: "Í tilefni af 300 ára ártíð Jóns Þorkelssonar Vídalíns árið 2020 stendur Þjóðkirkjan að útgáfu tveggja binda verks um ævi og ritstörf biskupsins.\r\nÚtgáfa á verkum Jóns Vídalíns er löngu tímabær, en meðal verka hans hér birtast í fyrsta sinn á prenti predikanir hans yfir Faðir vor, þýðing hans á Hebreabréfinu ásamt skýringum hans og fjöldi bréfa.\r\nTveggja binda útgáfa.", long_description: "", page_count: 1440, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 47, book_author_id: nil},
  {source_id: 15636, pre_title: "", title: "Rætur og þang", post_title: "", slug: "raetur-og-thang-15636", description: "Hér birtist lesendum fyrsta bók Köllu sem hefur fengist við ljóðagerð og myndlist frá unga aldri. Bókinni er skipt í sjö kafla og ljóðin eru fjölbreytt að formi og efnistökum, fjalla meðal annars um árstíðirnar, trú, ást og kærleika, sorg og söknuð og mörg ljóðanna eru með heimspekilegu ívafi. Þá eru í sérstökum kafla ljóð sem helguð eru æskubyggðinni, Seyðisfirði. Myndir af nokkrum málverkum höfundar fylgja ljóðunum.", long_description: "", page_count: 98, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 48, book_author_id: nil},
  {source_id: 15637, pre_title: "", title: "Hugurinn einatt hleypur minn", post_title: "Kveðskapur og æviferill Guðnýjar Árnadóttur - Skáld-Guðnýjar -", slug: "hugurinn-einatt-hleypur-minn-15637", description: "Bókin geymir kvæði eftir austfirska skáldkonu sem fædd var fyrir 200 árum. Hún hafði það álit meðal samferðamanna sinna að hún hlaut heitið Skáld-Guðný. Lítið hefur áður birst af skáldskap hennar en hann varðveittist í handriti úr Hornafirði og hjá afkomendum hennar í Lóni en þar bjó hún síðustu ár sín. Ítarleg ritgerð um kveðskap hennar og æviferil eftir Helga Hallgrímsson og Rósu Þorsteinsdóttur fylgir kvæðunum.", long_description: "", page_count: 180, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 48, book_author_id: nil},
  {source_id: 15638, pre_title: "", title: "Ég skal segja ykkur það", post_title: "", slug: "eg-skal-segja-ykkur-thad-15638", description: "Flest ljóð Sólveigar hafa orðið til við dagleg störf. Ljóðin, sem birtast hér í fyrstu bók hennar, eru hugljúf og oft á tíðum ljúfsár þar sem helstu hugðarefni skáldkonunnar eru samspil náttúrunnar, búskapar og fólks sem hefur snert hjarta hennar. Hugurinn leitar einnig til bernskuslóðanna á Borgarfirði eystra þar sem fegurðin og kyrrðin heilla. Bókin er sú tuttugasta sem félagið gefur út í flokknum Austfirsk ljóðskáld.", long_description: "", page_count: 120, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 48, book_author_id: nil},
  {source_id: 15639, pre_title: "", title: "Bandaríkin hjóluð", post_title: "", slug: "bandarikin-hjolud-15639", description: "Frá Janúar til Mars 2020 hjólaði ég þvert yfir Bandaríkin frá Flórida til Californiu. Leið sem er kölluð The Southern Tier. Ég lenti í allskonar ævintýrum á leiðinni. Bókin er með myndum af náttúrunni teknar á leiðinni.", long_description: "", page_count: 192, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 49, book_author_id: nil},
  {source_id: 15640, pre_title: "", title: "Strandvegagangan: Ferðabók Jóns Eggerts og Sigfúsar", post_title: "", slug: "strandvegagangan-ferdabok-jons-eggerts-og-sigfusar-15640", description: "Sumrin 2005 og 2006 gekk Jón Eggert Guðmundsson Strandvegahringinn í kringum ísland alls 3446 km hring sem er lengsti hringur sem hægt er að fara á þjóðvegum merktum frá vegagerð ríkissins. Þetta hefur aldrey verið gert áður. Þetta er ferðasaga skrifuð af Sigfúsi Austfjörð sem var bílstjóri Jóns.", long_description: "", page_count: 162, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 49, book_author_id: nil},
  {source_id: 15641, pre_title: "", title: "Ein", post_title: "", slug: "ein-15641", description: "Þegar ung kona sem starfar í heimaþjónustu mætir til vinnu í blokk fyrir eldri borgara blasir við henni óvænt sýn. Hún óttast að eiga sök á því að hafa hrundið af stað hræðilegri atburðarás. \r\nVonir, þrár og hversdagsleiki ólíks fólks fléttast saman með látlausum en áhrifamiklum hætti.", long_description: "", page_count: 223, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15642, pre_title: "", title: "Snerting", post_title: "", slug: "snerting-15642", description: "Áhrifamikil skáldsaga sem gerist í Reykjavík og Tókýó samtímans og London á sjöunda áratugnum. Magnþrungin saga um fornar og nýjar ástir, leynd og eftirsjá, hugsjónir og veruleika. Saga sem rígheldur allt til óvæntra endaloka.", long_description: "", page_count: 303, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15644, pre_title: "", title: "Konan sem elskaði fossinn", post_title: "Sigríður í Brattholti", slug: "konan-sem-elskadi-fossinn-15644", description: "Sigríður í Brattholti hóf ein og óstudd baráttu gegn áformum um að virkja Gullfoss, baráttu fyrir málstað sem hún var tilbúin að fórna lífinu fyrir. Samhliða því þurfti Sigríður að glíma við erfiðleika á heimili sínu og í einkalífi.\r\nStórbrotin söguleg skáldsaga sem lætur engan ósnortinn.", long_description: "", page_count: 267, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15645, pre_title: "", title: "Vetrarmein", post_title: "", slug: "vetrarmein-15645", description: "Lík af ungri konu finnst á gangstétt fyrir framan þriggja hæða hús á Siglufirði. Skömmu síðar skrifar íbúi á hjúkrunarheimili í bænum á vegginn í herberginu sínu: Hún var drepin. Ari Þór Arason lögreglumaður þarf að glíma við erfitt mál. „Besta glæpasaga hans til þessa.“ Morgunblaðið", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15646, pre_title: "", title: "Herra Bóbó, Amelía og ættbrókin", post_title: "", slug: "herra-bobo-amelia-og-aettbrokin-15646", description: "Fyrsta barnabók Yrsu Sigurðardóttur frá því að hún hlaut Íslensku barnabókaverðlaunin árið 2003. Hér fer hún á kostum í sannkallaðri ærslasögu þar sem einstakur húmor hennar fær að njóta sín. Myndir Kristínar Sólar Ólafsdóttur auka enn á gildi þessarar skrautlegu og stórskemmtilegu sögu!", long_description: "", page_count: 167, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15647, pre_title: "", title: "Bráðin", post_title: "", slug: "bradin-15647", description: "Björgunarsveitir eru sendar inn í Lónsöræfi í leit að hópi fólks sem er saknað. Hvaða erindi áttu þau í óbyggðir um hávetur?  Á sama tíma gerast undarlegir atburðir á ratsjárstöðinni á Stokksnesi. Og á nesinu er gat í sjávarklöpp sem sogar til sín fólk ...", long_description: "", page_count: 350, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15648, pre_title: "", title: "Næturskuggar", post_title: "", slug: "naeturskuggar-15648", description: "Ungur maður lætur lífið í dularfullum eldsvoða á Akranesi. Athafnamenn á Skaganum villast af þröngum vegi dyggðanna í einkalífi og starfi. Og ung kona lendir í mikilum ógöngum. Lögreglukonan Elma þarf að kljást við flókið og erfitt mál samhliða því sem einkalíf hennar er í uppnámi.", long_description: "", page_count: 368, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15649, pre_title: "", title: "Sykur", post_title: "", slug: "sykur-15649", description: "Virtur embættismaður finnst myrtur og þegar lögreglukonan Sigurdís finnur falið öryggishólf í íbúð hans hrannast vísbendingar upp. Sigurdís þarf einnig að takast á við erfiðleika sem eiga sér rætur í æsku hennar. Höfundurinn hlaut glæpasagnaverðlaunin Svartfuglinn fyrir þessa mögnuðu sögu.", long_description: "", page_count: 246, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15650, pre_title: "", title: "Spegilsjónir", post_title: "", slug: "spegilsjonir-15650", description: ">>>Spegilsjónir<<< er ofin af látlausu og meitluðu máli og myndum – vefur þungrar alvöru og sárs gamans. Ljóðheimur Guðrúnar er ósvikinn og knýjandi, en á sama tíma uppfullur af húmor og lífsgleði, og minnir okkur á að staldra við það smáa á meðan við getum.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 50, book_author_id: nil},
  {source_id: 15651, pre_title: "", title: "Skáldaleyfi", post_title: "", slug: "skaldaleyfi-15651", description: "Um þessar mundir eru fjörutíu ár síðan Sigmundur Ernir sendi frá sér sína fyrstu ljóðabók en þær eru nú orðnar þrettán. Í Skáldaleyfi sýnir hann og sannar að þrátt fyrir að ljóð hans séu nú þroskaðri, dýpri og meitlaðri er alltaf stutt í lífsþyrsta unga ljóðskáldið.", long_description: "", page_count: 124, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15652, pre_title: "", title: "Ljóðasafn", post_title: "", slug: "ljodasafn-15652", description: "Þetta safn geymir fyrstu átta ljóðabækur Kristínar Ómarsdóttur sem flestar hafa verið ófáanlegar um margra ára skeið. Bækurnar í safninu komu fyrst út á árunum 1987–2017 og hafa tryggt Kristínu stöðu meðal virtustu skálda þjóðarinnar. Höfundur ritar eftirmála.", long_description: "", page_count: 464, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 50, book_author_id: nil},
  {source_id: 15653, pre_title: "", title: "Óstöðvandi skilaboð", post_title: "", slug: "ostodvandi-skilabod-15653", description: "Rödd Ásdísar er einstök í ljóðaheiminum. Ljóðin eru í senn frumleg, heit og litrík.", long_description: "", page_count: 39, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15654, pre_title: "101 Austurland", title: "Gönguleiðir fyrir alla", post_title: "", slug: "gonguleidir-fyrir-alla-15654", description: "Nákvæmar og myndskreyttar lýsingar á skemmtilegum gönguleiðum á Austurlandi sem henta fyrir alla fjölskylduna. Í bókinni eru kort, upplýsingar um hækkun, göngutíma, vegalengd, gps-hnit og allt sem göngufólk þarf að vita áður en lagt er af stað.\r\nSkúli Júlíusson  hefur starfað sem fjallaleiðsögumaður á Austurlandi frá árinu 2009.", long_description: "", page_count: 221, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 51, book_author_id: nil},
  {source_id: 15655, pre_title: "", title: "Havana", post_title: "", slug: "havana-15655", description: "Ljóðmælanda dreymir um að losna undan þyngdarafli fjölskyldunnar og feta sinn eigin veg. En leyndarmál erfast mann fram af manni líkt og búslóðir. Það ósagða bergmálar innan veggja bókarinnar á meðan ljóðmælandi leitar svara og mögulegri leið út og burt – ef slíkur staður er yfir höfuð til.", long_description: "", page_count: 72, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 50, book_author_id: nil},
  {source_id: 15656, pre_title: "", title: "Dagbókin mín", post_title: "", slug: "dagbokin-min-15656", description: ">>>Dagbókin mín<<< er til að æfa hugann í jákvæðni, þakklæti og seiglu. Dagbókin skýrir í máli og myndum hvernig heilinn virkar og hvernig er hægt að þjálfa hann í að hugsa jákvætt.\r\nBókin inniheldur 65 daga dagbók sem fær einstaklinga til að hugsa um hvað þeir eru þakklátir fyrir, líðan þeirra og hegðun, auk verkefna til að auka skilning þeirra á tilfinningum og hvernig hægt er að setja sér markmið.", long_description: "", page_count: 94, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 52, book_author_id: nil},
  {source_id: 15657, pre_title: "", title: "Skógurinn", post_title: "", slug: "skogurinn-15657", description: "Kría varð vitni að því þegar Gerða amma hennar hvarf sporlaust. Hún hikar því ekki þegar sömu örlög bíða dótturdóttur hennar 79 árum síðar og fórnar sér í hennar stað. Þá fær hún loksins að sjá hvað leynist handan við dularfulla skápinn og svör við spurningum sem hafa ásótt hana áratugum saman. >>>Skógurinn<<< er lokabindi rómaðs þríleiks en fyrri bækurnar, >>>Ljónið<<< og >>>Nornin<<<, fengu fjölda viðurkenninga.", long_description: "", page_count: 311, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 30, book_author_id: nil},
  {source_id: 15658, pre_title: "", title: "Fyrir augliti", post_title: "Dagatal", slug: "fyrir-augliti-15658", description: "Úlfar Þormóðsson hélt dagbók á árunum 2018 og 2019, alls 730 færslur. Sviðið er miðbær Reykjavíkur og samfélag Íslendinga suður í höfum – en kannski mest andi Úlfars sem fer með lesendur í óvænt ferðalög um hugmyndir og atburði líðandi stunda. Úlfar er er á margan hátt elsta ungskáld Íslands.", long_description: "", page_count: 340, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15659, pre_title: "", title: "Mobility and Transitional Iceland", post_title: "Current Transformations and Global Entanglements", slug: "mobility-and-transitional-iceland-15659", description: "Í þessu riti er með þverfaglegum hætti gerð grein fyrir álitamálum er snúa að fólksflutningum samtímans, innflytjendum og flóttafólki í íslensku samhengi. Ræddar eru þær áskoranir sem blasa við ólíkum hagsmunaaðilum þegar slíkir flutningar eiga sér stað þvert á landamæri. Fjallað er um það sem er efst á baugi í kenningarlegum og aðferðafræðilegum efnum á sviðum þverþjóðleika og hreyfanleika.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15660, pre_title: "", title: "Aldrei nema kona", post_title: "", slug: "aldrei-nema-kona-15660", description: ">>>Aldrei nema kona<<< er heimildaskáldsaga sem fylgir þremur ættliðum kvenna í Skagafirði á átjándu og nítjándu öld. Með þrautseigju og æðruleysi tekst þeim að komast af þrátt fyrir hart árferði og harðneskjulegt samfélag. Höfundur rekur æviferil langmæðgna sinna og byggir þar á heimildum úr kirkjubókum, manntölum, æviskrám, dómabókum og ýmsum heimildum öðrum. Sögutíminn spannar meira en hundrað ár, frá miðri átjándu öld fram yfir miðja hina nítjándu.", long_description: "", page_count: 272, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15661, pre_title: "", title: "Innfirðir", post_title: "", slug: "innfirdir-15661", description: "Finnski verðlaunahöfundurinn Tapio Koivukari er löngu orðinn landsþekktur fyrir skáldsögur sínar. Hann bjó á Ísafirði í mörg ár og hefur þýtt fjölda íslenskra skáldverka yfir á finnsku. Ljóðabókin >>>Innfirðir<<< er fyrsta bókin sem Tapio skrifar á íslensku. Hér fjallar hann um kynni sín af Vestfjörðum og Íslandi ásamt ferðum um aðra heimshluta uns hann staðnæmist í heimahögum á vesturströnd Finnlands. Í bókarlok ferðumst við síðan með skáldinu um innfirði hugans.", long_description: "", page_count: 128, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15662, pre_title: "", title: "Fléttur V", post_title: "#MeToo", slug: "flettur-v-15662", description: "Þverfaglegt greinasafn um #MeToo og baráttu kvenna gegn áreitni og ofbeldi. Rýnt er í umhverfið sem #MeToo-hreyfingin sprettur upp úr, ástæður þess að hún verður jafn öflug og raun ber vitni, árangur hreyfingarinnar sem og andstreymið gegn henni.", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15663, pre_title: "", title: "Vampírur, vesen og annað tilfallandi", post_title: "", slug: "vampirur-vesen-og-annad-tilfallandi-15663", description: "Furðuleg veikindi (nei, ekki Covid-19) breiðast um skólann og Milla, Rakel og Lilja ákveða að gera eitthvað í málunum. En laumulega, því enginn trúir þrettán ára stelpum sem segja að kennarinn þeirra sé vampíra. Ófyrirsjáanleg lögbrot, vinslit og drama flækjast svo fyrir því að sannreyna hvort nokkuð yfirnáttúrulegt sé á kreiki. Bókin hlýtur Íslensku barnabókaverðlaunin 2020.", long_description: "", page_count: 287, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 33, book_author_id: nil},
  {source_id: 15664, pre_title: "", title: "Síðasta dagblaðið á vinstri vængnum", post_title: "Reist á rústum Alþýðublaðsins, Tímans og Þjóðviljans", slug: "sidasta-dagbladid-a-vinstri-vaengnum-15664", description: "Sögu Þjóðviljans, Tímans og Alþýðublaðsins lauk á tíunda áratug síðustu aldar. Dagur reis upp úr rústum þessara flokksblaða vinstrimanna sem óháð dagblað með hjartað vinstra megin við miðju. En eftir dugmikla baráttu fram yfir aldamótin varð Dagur engu að síður fjórða íslenska dagblaðið til að falla í valinn á níu árum. Höfundur sem var ritstjóri Dags 1997 til 2001 lýsir hér baráttu og langdregnu dauðastríði þessa síðasta dagblaðs á vinstri vængnum. Fróðleg og spennandi frásögn byggð á áður óbirtum heimildum.", long_description: "", page_count: 224, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15665, pre_title: "", title: "Saga guðanna", post_title: "", slug: "saga-gudanna-15665", description: ">>>Saga guðanna<<< er fróðleg og yfirgripsmikil bók þar sem lesandanum er boðið í ferðalag um heim trúarbragðanna. Fjallað er á aðgengilegan hátt um það sem helstu trúarbrögð mannkyns hafa sjálf fram að færa, saga þeirra rakin í stórum dráttum og helstu einkennum þeirra lýst. Með skrifum sínum vekur höfundur upp spurningar hjá lesendum og fær þá til að leita eigin svara andspænis því markmiði trúarbragðanna að kanna djúp mannshugans og leita uppi kjarna tilverunnar. Bókin er prýdd fjölda ljósmynda sem höfundur hefur tekið á ferðum sínum.", long_description: "", page_count: 410, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15666, pre_title: "", title: "Landverðirnir", post_title: "", slug: "landverdirnir-15666", description: "Í þessum fyrsta hluta í sögunni um Landverðina eru ofurhetjurnar Atlas og Avion kynntir til sögunnar. Alveg síðan Atlas man eftir sér hefur hann verið gæddur ofurkröftum sem hann hefur notað til þess að hjálpa fólki. Dag einn tekur líf Atlasar óvænta stefnu þegar hann þarf að berjast gegn sterkasta illmenni Íslands, Azar. Í kjölfar bardagans kemst Atlas að leyndarmálum úr fortíðinni og einnig kynnist hann Avion og taka þeir höndum saman og hafa það sem markmið að bjarga Íslandi og þegnum þess.", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 54, book_author_id: nil},
  {source_id: 15667, pre_title: "", title: "Brásól Brella", post_title: "", slug: "brasol-brella-15667", description: "Brella hélt að hún gæti ekki galdrað en þegar pabbi stendur skyndilega fyrir framan hana á fjórum fótum með beitta brodda er vitað mál að hún sé norn! Brella ákveður að leita sér hjálpar hjá Vála, galdrakarlinum stórfenglega. Leiðin þangað liggur gegnum Stóraskóg þar sem sísvangir úlfar og blóðþyrstar vampírur halda til.", long_description: "", page_count: 128, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15668, pre_title: "", title: "Vonarskarð", post_title: "Örlaga- og öræfasaga", slug: "vonarskard-15668", description: "Þær eru margar og mistrúlegar staðreyndirnar í bókinni >>>Vonarskarði<<<. Sagan er römmuð inn af ótrúlegri ævintýraferð feðga sem gengu þvert yfir landið, frá Hellu og norður yfir Sprengisand, sá yngri rétt af barnsaldri. En þetta er ekki eina ferðin sem markaði líf Stolzenwald-fólksins. Stolzi eldri kom yfir hafið, steig á land í Eyjum og varð ástfanginn af fjöllunum og fólkinu á Íslandi. Hér segir líka af þeim sem heima sitja og fara jafnvel víðar en vildu.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15669, pre_title: "", title: "Öldufax", post_title: "Sjónarrönd af landi", slug: "oldufax-15669", description: "Maðurinn í náttúrunni og náttúran í manninum. Kynslóðirnar, fortíð, nútíð og framtíð. Allt eru þetta stef á ljóðbárum Valgerðar Kr. Brynjólfsdóttur. Í þessari fyrstu ljóðabók hennar mætast haf og land, enda er hún alin upp við sjóinn en býr nú við Heklurætur. Valgerður er íslenskufræðingur og hefur meðal annars starfað á Árnastofnun. Ljóð hennar hafa birst í ýmsum tíma­ritum og hér gefur að líta verk sem orðið hafa til á löngum tíma. >>>Öldufax<<< einkennist af sterku skáldmáli og áhrifa­ríkum myndum.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15670, pre_title: "", title: "Töfralandið", post_title: "", slug: "tofralandid-15670", description: "Dásamlegt samspil mynda og texta Bergrúnar býður upp á ljúfa samverustund með yngstu lesendunum. \"Ég veit að bækurnar geyma það besta og þú sérð það líka, um leið og þú lest það.\" >>>Töfralandið<<< er lofsöngur Bergrúnar um bækur.", long_description: "", page_count: 40, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 56, book_author_id: nil},
  {source_id: 15671, pre_title: "Ljósaserían", title: "Stúfur leysir ráðgátu", post_title: "", slug: "stufur-leysir-radgatu-15671", description: "Ljósaserían.is - bókaklúbbur barnanna. \r\nÁstandið á heimili jólasveinanna er hræðilegt. Grýla grætur og gólar því einhver hefur stolið vendinum hennar og það á sjálfan afmælisdaginn! Stúfur þarf að taka málin í sínar hendur. Hugrakka vinkona hans Lóa og skapvondi jólakötturinn slást með í för og saman ætla þau að leysa þessa dularfullu ráðgátu.", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15673, pre_title: "Nornasaga 2", title: "Nýársnótt", post_title: "", slug: "nyarsnott-15673", description: "Æsispennandi framahald! Katla ætlar að opna galdragátt á nýársnótt til að nornin Heiður komist aftur til Íslands. Í stað þess veldur hún sprengingu og hrindir af stað æsilegri atburðarás. Barrtré spretta upp á methraða, systkini Kötlu hverfa, úlfur sést í Öskjuhlíðinni, tröllskessa við Tjörnina og ógnarlangur ormur í Reykjavíkurhöfn.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15674, pre_title: "", title: "Samhengi hlutanna", post_title: "", slug: "samhengi-hlutanna-15674", description: "Er það satt að við séum það sem við hugsum og að við sköpum hugsanir okkar? Eða eru hugsanir og hugmyndir kannski sjálfsæð fyrirbrigði, eins og öreindir sveimandi um loftið í leit að hýsli? >>>Samhengi hlutanna<<< samanstendur af fimm smásögum úr daglega lífinu og fjórum þáttum úr lífi Jónasar.", long_description: "", page_count: 112, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 57, book_author_id: nil},
  {source_id: 15675, pre_title: "", title: "Ofurhetjan", post_title: "", slug: "ofurhetjan-15675", description: "Gulla dreymir um að verða meira en venjulegur drengur. Með hjálp vinkonu sinnar uppgötvar hann áður óþekktan hæfileika sem breytir lífi hans. Hann setur á sig grímuna og verður Ormstunga, ofurhetja sem berst gegn eineltisseggjum.\r\nHverju er Gulli tilbúinn til að fórna til að ráða að niðurlögum erkióvinarins? Æsispennandi saga um ALVÖRU ofurhetju.", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15676, pre_title: "", title: "Birta, ljós og skuggar", post_title: "", slug: "birta-ljos-og-skuggar-15676", description: "Þegar yngri sonur Birtu flytur að heiman áttar hún sig á að hún er ekki sátt við líf sitt. Þegar hún kynnist Tim finnur hún óvænt trúnaðarvin. Með hjálp Tims finnur hún styrk til að losa sig úr ómögulegum aðstæðum og kemst að því að ekkert er sem sýnist. Á sama tíma og Birta berst við erfið sambandsslit, ofsóknir og átakanleg fjölskyldumál áttar hún sig á því að það sem hún hefur alltaf þráð er nær en hana grunaði. Þetta er saga Birtu, ljóssins sem hún á stundum erfitt með að greina og skugganna sem hún forðast.", long_description: "", page_count: 276, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 58, book_author_id: nil},
  {source_id: 15677, pre_title: "", title: "Stúlka", post_title: "", slug: "stulka-15677", description: "Maryam er rænt af Boko-Haram skæruliðum og neydd í hjónaband. Hún upplifir þjáningar og hrylling samfélags karla sem stjórnast af trúarlegu ofbeldi. Hún er bara barn sem þarf að komast af með eigið barn. Þegar heimur hennar virðist stefna beint til heljar opnast henni hlið inn í annan heim sem ekki er mikið skárri, erfiði og hryllingur í óblíðum óbyggðum Norð-austur Nígeríu, í gegnum skóga og auðnir og heim til sín þar sem hennar sködduðu sál er mætt af blindri dómhörku samfélags í afneitun.", long_description: "", page_count: 172, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 58, book_author_id: nil},
  {source_id: 15678, pre_title: "", title: "Hermdu mér - naflaskoðunarkveðskapur", post_title: "", slug: "hermdu-mer-naflaskodunarkvedskapur-15678", description: "Hér segir af barni sem notaði ímyndunaraflið til að komast annað slagið inn í heim sem því þótti betri en sá sem boðið var upp á. Hér segir ennfremur af upplifunum manns í tveimur heimum: Öðrum sem á sér rætur í kvikmyndum og skáldsögum en hinum af holdi og blóði. Brennt barn verður áfram brennt barn þótt það eldist.", long_description: "", page_count: 74, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 58, book_author_id: nil},
  {source_id: 15679, pre_title: "", title: "Skepnur", post_title: "", slug: "skepnur-15679", description: "Ef kona elskar kvæntan mann þá á hún um leið í sérstöku, leynilegu og óútskýranlegu sambandi við konuna hans. Á vímuárum áttunda áratugarins verður háskólastúlkan Gillian Brauer ástfangin af ljóðum, af prófessornum Andre Harrow og eiginkonu hans, listakonunni Dorcas. Gillian heillast af bóhemsku líferni þeirra, af afvikna húsinu þeirra og dimmum leyndardómum þess. Gillian Brauer er á þröskuldi helvítis.", long_description: "", page_count: 114, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 58, book_author_id: nil},
  {source_id: 15680, pre_title: "", title: "Ég á þetta barn", post_title: "", slug: "eg-a-thetta-barn-15680", description: "Í Lagos í Nígeríu er útgöngubann vegna heimsfaraldurs. Þegar unnusta Bamba vísar honum á dyr leitar hann skjóls í húsi frænda síns sem nýlega er látinn af völdum veirunnar. Þar hittir hann konu frændans, hjákonu hans og kornabarn sem báðar segjast eiga. Hér er á ferðinni stutt saga, yndislestur fyrir alla og tilvalin fyrir þá sem gefa sér lítinn tíma til lesturs. Atburðarásin er hröð og skopið hárbeitt.", long_description: "", page_count: 76, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 58, book_author_id: nil},
  {source_id: 15681, pre_title: "Dulstafir - bók eitt", title: "Dóttir hafsins", post_title: "", slug: "dottir-hafsins-15681", description: "Líf Elísu, sextán ára unglings, gjörbreytist á einni nóttu þegar hún heyrir tónlist berast frá hafinu. Tónlistin leiðir hana niður í fjöru og alla leið að fjólubláu borginni. Elísa dregst inn í háskalega atburðarrás og kemst að því að hún er hluti af spádómi ævafornrar menningar á hafsbotni. Er hún verðug þess að bera titilinn dóttir hafsins?", long_description: "", page_count: 288, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 57, book_author_id: nil},
  {source_id: 15683, pre_title: "", title: "Bráðum áðan", post_title: "", slug: "bradum-adan-15683", description: "Líf Söruh hefur verið í uppnámi síðan hún missti mömmu sína. Fjölskyldan botnar ekkert í henni og hún á enga samleið með krökkunum í skólanum. Dag einn birtist svo Elsa frænka eftir áralanga fjarveru, bullandi um alkemíu, tímaflakk og skrímsli – íklædd málmhönskum! \r\nÁður en Sarah veit af er hún lögð af stað í stórhættulegt ferðalag gegnum tíma og rúm.", long_description: "", page_count: 296, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15684, pre_title: "", title: "Kennarinn sem hvarf sporlaust", post_title: "", slug: "kennarinn-sem-hvarf-sporlaust-15684", description: "Loks er komið að skíðaferðalaginu hjá 6. BÖ og Sara er mjög spennt. Krakkarnir hafa næstum gleymt því þegar kennaranum var rænt fyrr um veturinn enda örugg frá mannræningjanum sem er fastur bak við lás og slá. Nú er allt eins og það á að vera og ekkert getur farið úrskeiðis … eða hvað? Enn og aftur reynir á samtakamátt krakkana.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15685, pre_title: "", title: "Ég heiti Kosmó", post_title: "", slug: "eg-heiti-kosmo-15685", description: "Fjölskyldan er að liðast í sundur og hlutverk Kosmós er að koma í veg fyrir að það gerist. Hann veit nákvæmlega hvað hann þarf að gera. Það er bara eitt vandamál: Kosmó er hundur.\r\nDásamlega hugljúf og fyndin saga um viðleitni hunds til að bjarga fjölskyldunni sinni, verða stjarna og borða eins mikið og tönn á festir.", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15686, pre_title: "", title: "Brimaldan stríða", post_title: "Örlagarík skipströnd við Ísland", slug: "brimaldan-strida-15686", description: "Í þessari mögnuðu bók segir frá nokkrum af örlagaríkustu skipströndum sem orðið hafa við Ísland. Með fyrstu togurunum fjölgaði í áhöfnum og manntjónið varð enn meira.\r\nSteinar J. Lúðvíksson er manna fróðastur um sjóslysasögu Íslands og er höfundur stórvirkisins Þrautgóðir á raunastund.", long_description: "", page_count: 304, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15687, pre_title: "", title: "Tröllasaga tuttugustu aldarinnar", post_title: "", slug: "trollasaga-tuttugustu-aldarinnar-15687", description: "Fyrir tæpri hálfri öld, 1972, tók höfundur þessarar bókar, bandarískur háskólaprófessor í  rafmagnsverkfræði og mikill áhugamaður um sögu seinni heimsstyrjaldarinnar, að velta fyrir sér: \r\nEn hvað með þessa „Helför“? Heilar 6 milljónir manna myrtar á grimmilegan hátt í „gasklefum“..., er þetta nú ekki dálítið orðum aukið?\r\nMeð vísindalegum vinnubrögðum hefst Arthur Butz handa við að rekja sig eftir atburðarás stríðsáranna en vísar jafnframt skjalafalsi, áróðri, ýkju- og skröksögum miskunnarlaust á bug og kemst loks að rökréttri niðurstöðu sinni í bókarlok.", long_description: "", page_count: 556, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 59, book_author_id: nil},
  {source_id: 15688, pre_title: "", title: "Bræðurnir breyta jólunum", post_title: "", slug: "braedurnir-breyta-jolunum-15688", description: "Bergrún Íris hefur hlotið mikið lof fyrir bækur sínar, bæði sögur og myndir. Hér leiða þau Haukur Gröndal saman hreindýr sín en hann á að baki glæstan feril með hinum ýmsu hljómsveitum. Bræðurnir breyta jólunum er hjartnæmt jólaævintýri og sannkölluð veisla fyrir augu og eyru.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15689, pre_title: "", title: "Beðið eftir barbörunum", post_title: "", slug: "bedid-eftir-barborunum-15689", description: "Í áratugi hefur dómari stjórnað rólegum bæ á mærum heimsveldis. Þegar orðrómur berst um barbara handan bæjarmúranna taka fulltrúar heimsveldisins völdin. Dómarinn gerist gagnrýninn á alræði þeirra og ofbeldi, en þarf samhliða að horfast í augu við eigin takmörk, fýsnir og siðferði. Tímalaust meistaraverk eftir nóbelshöfundinn J. M. Coetzee.", long_description: "", page_count: 272, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 60, book_author_id: nil},
  {source_id: 15690, pre_title: "", title: "Hjarta Íslands", post_title: "Frá Eldey til Eyjafjarðar", slug: "hjarta-islands-15690", description: "Hér er fjallað um einstakar perlur í landinu milli fjalls og fjöru frá Suðvesturlandi til Norðvesturlands. Texti Gunnsteins er í senn aðgengilegur, lipur og fróðlegur og myndir Páls sérlega glæsilegar. Hér er á ferðinni stórvirki sem óhætt er að kalla óð til Íslands.", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15691, pre_title: "", title: "Útkall á ögurstundu", post_title: "", slug: "utkall-a-ogurstundu-15691", description: "Mánuði eftir að Gjafar VE 300 flytur 430 manns frá Heimaey við upphaf eldgossins strandar hann í foráttubrimi við Grindavík. Áhöfnin berst upp á líf og dauða. Áratug síðar lendir eigandi bátsins og 14 manna áhöfn hans á Sæbjörgu VE 56 aftur í stórkostlegri lífshættu þegar báturinn strandar á Hornafirði. Útkallsbækurnar hafa nú í 27 ár verið eitt vinsælasta lesefni Íslendinga.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 61, book_author_id: nil},
  {source_id: 15692, pre_title: "", title: "Veirufangar og veraldarharmur", post_title: "", slug: "veirufangar-og-veraldarharmur-15692", description: "Á tímum heimsfaraldurs gekk Valdimar Tómasson um mannauð stræti borgarinnar og orti háttbundinn kvæðabálk um ástandið. Hér birtist afraksturinn ásamt hárbeittum heimsósóma. Valdimar er helsta götuskáld Reykjavíkur. Hann er þekktur fyrir vandaðan skáldskap og frjóa glímu við íslenska tungu.", long_description: "", page_count: 54, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 60, book_author_id: nil},
  {source_id: 15693, pre_title: "", title: "Möndulhalli", post_title: "", slug: "mondulhalli-15693", description: "Möndulhalli er undirstaða lífs á jörðinni eins og við þekkjum það en hver eru áhrif skekkjunnar á hversdaginn? Það blasir við í smásögum þessa safnrits, þar sem áföll erfast, stormar geisa, sonur verður faðir sinn, barn tekst á við berkla, flóðhestur gengur um götur Reykjavíkur og fólk yfirgefur jörðina eða snýr þangað aftur.", long_description: "", page_count: 203, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 60, book_author_id: nil},
  {source_id: 15694, pre_title: "", title: "Heilsubók Jóhönnu", post_title: "Eiturefnin og plastið í daglegu lífi okkar. Börnin, við sjálf og ógnin við náttúruna", slug: "heilsubok-johonnu-15694", description: "Manngerð eiturefni eru orðin hluti af daglegu lífi okkar. Stundum vitum við af þeim en oftar eru þau ósýnileg. Í þessari stórfróðlegu bók er farið yfir hvar þessi efni er að finna og hvaða áhrif þau hafa á okkur. Þá er einnig fjallað um þann vanda sem taumlaus framleiðsla á plasti skapar.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15695, pre_title: "", title: "Árstíðir", post_title: "Sögur á einföldu máli", slug: "arstidir-15695", description: "Í stuttum og aðgengilegum sögum er brugðið upp ýmsum hliðum á íslenskum hversdagsleika. Þetta er fyrsta safn frumsaminna sagna ætlað þeim sem tileinka sér íslensku sem annað mál. Textarnir eru fjölbreyttir og flokkaðir eftir getustigi í samráði við sérfræðinga. Viðauki fylgir með fróðleik um land og þjóð. Bókin nýtist jafnt við kennslu sem og til yndislestrar.", long_description: "", page_count: 247, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 60, book_author_id: nil},
  {source_id: 15697, pre_title: "", title: "Vegahandbókin", post_title: "", slug: "vegahandbokin-15697", description: "Vegahandbókin er lykillinn að landinu og því frábær jólagjöf. Í máli, myndum og með kortum vísar bókin til vegar. Gildir þá einu hvort staðirnir koma fyrir í fornsögum, þjóðsögum eða sögu síðustu áratuga, sagan er rakin og sérkennum lýst.\r\nBókin er líka í snjalltækjaútgáfu (Appi).Í henni er að finna alla þá staði sem eru í bókinni ásamt þúsundum þjónustuaðila um allt land.Ítarleg kort eru í bókinni af öllu vegkerfi landsins. Þá er í henni ítarlegur hálendiskafli og sérstök 24 síðan kortabók og fleira og fleira.", long_description: "", page_count: 608, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 61, book_author_id: nil},
  {source_id: 15698, pre_title: "", title: "Sönn íslensk sakamál S1-S4", post_title: "", slug: "sonn-islensk-sakamal-s1-s4-15698", description: "Sjónvarpsþættirnir Sönn íslensk sakamál nutu mikilla vinsælda og gegndi Sigursteinn Másson hlutverki þular þáttanna, ásamt því að vera umsjónarmaður og handritshöfundur þeirra í upphafi.\r\n\r\nNú hafa Sönn íslensk sakamál öðlast nýtt líf í hljóðbókarformi Storytel Original og í þessum spennuþrungnu seríum fer Sigursteinn yfir bæði ný og gömul mál þar sem ekki eru öll kurl komin til grafar.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15699, pre_title: "", title: "Sólarhjólið", post_title: "", slug: "solarhjolid-15699", description: "Í >>>Sólarhjólinu<<< ægir saman alls kyns furðuhlutum ásamt fólki, dýrum og kostulegum hugmyndum. Ljóð og myndir kallast á og koma sífellt á óvart. Aðalsteinn Ásberg og Högni Sigurþórsson hafa áður sent frá sér >>>Kvæðið um Krummaling<<< sem hlaut afbragðs góðar viðtökur. <<<„Sólarhjól er þarfaþing og það má snúast heilan hring.“<<<", long_description: "", page_count: 38, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 63, book_author_id: nil},
  {source_id: 15700, pre_title: "", title: "Elli. Dagur í lífi drengs með ADHD.", post_title: "", slug: "elli-dagur-i-lifi-drengs-med-adhd-15700", description: ">>>Elli – Dagur í lífi drengs með ADHD <<< er fjörug, ríkulega myndskreytt barnabók, byggð á raunverulegum atburðum í lífi níu ára íslensk drengs með ADHD. \r\nKrakkar með ADHD upplifa sig oft misskilin og fá gjarnan neikvætt viðmót þegar þau eiga erfitt með að hlýða, vera kyrr eða vinna verkefni sem þeim finnst óspennandi. Megininntak bókarinnar að gefa raunsanna innsýn í líf barna með ADHD og benda ungum jafnt sem öldnum á að það er vel hægt að líta á ADHD með jákvæðum hætti; það er hægt að „beisla“ ADHD til góðs!", long_description: "", page_count: 44, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 64, book_author_id: nil},
  {source_id: 15701, pre_title: "", title: "Sjónarvottur", post_title: "", slug: "sjonarvottur-15701", description: "Helena Vesterberg, skarpgreind og ófullkominn rannsóknarfulltrúi sem reykir í laumi, ákveður eftir misheppnað ástarsamband að byrja upp á nýtt í friðsælu fiskiþorpi, þar sem hún varði sumrum bernsku sinnar, en þetta nýja upphaf fer ekki alveg eins og hún hugði. \r\nNjóttu þess að hlusta og lesa á Storytel Reader lesbrettinu.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15702, pre_title: "", title: "Berrössuð á tánum", post_title: "", slug: "berrossud-a-tanum-15702", description: "Haustið 1995 stökk kötturinn >>>Krúsilíus<<< alskapaður út úr höfði höfundarins og á eftir fylgdu ótal söngvar sem rötuðu til hlustenda og áhorfenda á öllum aldri í eftirminnilegum flutningi Önnu Pálínu og Aðalsteins Ásbergs. Hér birtist 25 ára afmælisútgáfa þar sem söngljóð og myndir fá að njóta sín til fulls.", long_description: "", page_count: 58, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 63, book_author_id: nil},
  {source_id: 15703, pre_title: "", title: "Brennuvargurinn", post_title: "", slug: "brennuvargurinn-15703", description: "Sögusviðið er þorp þar sem ekkert gerist – þar til hús eitt brennur til kaldra kola og kvittur kemst á kreik um fólskulegt óhæfuverk. Á sama tíma hverfur drengur og brunarannsóknarfulltrúinn Klara Larsen er kölluð á staðinn ásamt aðstoðarmanni sínum, Sebastian. Frábær danskur spennutryllir.\r\nHlustaðu og lestu á Storytel Reader lesbrettinu.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15704, pre_title: "", title: "Umskiptin", post_title: "", slug: "umskiptin-15704", description: "Anna Höglund er sænsk myndlistarkona, teiknari og rithöfundur. Á íslensku hefur áður komið út bókin >>>Sjáðu Hamlet<<< með eftirminnilegum myndum hennar. Hér birtist spennandi og myndræn frásögn fyrir alla aldurshópa um klækjabrögð og kjarkinn sem þarf til að lifa af.", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 63, book_author_id: nil},
  {source_id: 15705, pre_title: "", title: "500 dagar af regni", post_title: "", slug: "500-dagar-af-regni-15705", description: "Safn níu smásagna úr íslenskum samtíma þar sem dregin eru fram lykilaugnablik í lífi fólks. Endalok og nýtt upphaf, sakleysi og sekt, bernska og manndómur, raunveruleiki og ímyndun kallast þar á með ýmsum hætti. Bókin hlaut Nýræktarstyrk Miðstöðvar íslenskra bókmennta 2020.", long_description: "", page_count: 126, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 63, book_author_id: nil},
  {source_id: 15706, pre_title: "", title: "Sögur fyrir svefninn 1-8", post_title: "", slug: "sogur-fyrir-svefninn-1-8-15706", description: "Hrífandi ævintýri sem hjálpa börnum að finna ró fyrir svefninn með aðstoð ímyndunaraflsins. Höfundur hefur margra ára reynslu af því að kenna börnum jóga, slökun og hugleiðslu. Sögurnar leiða unga hlustendur inn í jákvæðan og uppbyggilegan ævintýraheim – og þaðan inn í draumalandið. Salka Sól gæðir sögurnar lífi með einsökum lestri sínum.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15707, pre_title: "", title: "Draumstol", post_title: "", slug: "draumstol-15707", description: ">>>Draumstol<<< er 16. ljóðabók Gyrðis, en sú fyrsta kom út 1983. Hér er hann sjálfum sér trúr og er að vanda fundvís á yrkisefni sem oftar en ekki brúa bil á milli fortíðar, nútíðar og framtíðar. Verk Gyrðis njóta sívaxandi hylli erlendis og nú í haust koma ljóð hans m.a. út á norsku og dönsku.", long_description: "", page_count: 125, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 63, book_author_id: nil},
  {source_id: 15708, pre_title: "", title: "Sögusagnir", post_title: "Þrjú tímamótaverk og einu betur", slug: "sogusagnir-15708", description: "Eftir lok síðari heimsstyrjaldar komu út þrjú skáldverk sem mörkuðu tímamót í íslenskum bókmenntum og jafnvel upphaf samtíma okkar. Þetta voru >>>Vikivaki<<< eftir Gunnar Gunnarsson, >>>Eftir örstuttan leik<<< eftir Elías Mar og >>>Uppstigning<<< eftir Sigurð Nordal. Í >>>Sögusögnum<<< fjallar Jón Karl Helgason um einkenni þessara verka, stöðu þeirra í bókmenntasögunni og tengir þau við skrif erlendra skálda.", long_description: "", page_count: 280, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 63, book_author_id: nil},
  {source_id: 15709, pre_title: "", title: "Berhöfða líf", post_title: "ljóðaúrval", slug: "berhofda-lif-15709", description: "Bandaríska skáldkonan >>>Emily Dickinson<<< (1830–1886) er eitt merkasta ljóðskáld síðari alda. Hér birtist heilsteypt úrval af ljóðum hennar ásamt ítarlegum inngangi þýðanda sem hefur rannsakað ljóðlist Dickinson um árabil. Dregin er upp mynd af róttæku skáldi sem gekk gegn viðteknum samfélagsvenjum; af konu sem hlýddi kröfum eigin tilfinningalífs í trássi við ýmsa ríkjandi siði og þjónaði köllun sinni af dirfsku.", long_description: "", page_count: 335, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 63, book_author_id: nil},
  {source_id: 15710, pre_title: "", title: "Reisubók Ólafs Egilssonar", post_title: "", slug: "reisubok-olafs-egilssonar-15710", description: "Sumarið 1627 hlupu sjóræningjar frá Norður-Afríku á land á nokkrum stöðum á Íslandi og höfðu með sér á fjórða hundrað manns sem ætlunin var að selja í þrældóm. Meðal hertekinna voru séra Ólafur Egilsson að Ofanleiti og kona hans Ástríður Þorsteinsdóttir, með tveimur barnungum sonum. Sá þriðji fæddist í hafi á leið til Algeirsborgar. Ráðamenn þar afréðu að senda séra Ólaf til Kaupmannahafnar í því skyni að fá konung til að leysa fanga út með fé og var hann allan veturinn á leiðinni.", long_description: "", page_count: 382, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15711, pre_title: "", title: "Dyrnar", post_title: "", slug: "dyrnar-15711", description: "Einstök og áhrifamikil skáldsaga eftir einn merkasta höfund Ungverja á seinni hluta 20. aldar. Bókin vakti gríðarlega athygli þegar hún kom fyrst út 1987, og útgáfur hennar á erlendum málum hafa einnig hlotið verðskuldað lof og verðlaun, m.a. >>>Prix Femina Étranger<<< í Frakklandi árið 2003. Óvenjuleg og áleitin saga um samband tveggja ólíkra kvenna, margbrotin frásögn sem nær auðveldlega sterkum tökum á lesandanum.", long_description: "", page_count: 298, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 63, book_author_id: nil},
  {source_id: 15712, pre_title: "", title: "Síðasta barnið", post_title: "", slug: "sidasta-barnid-15712", description: "Veturinn er harður og ofan á basl alþýðunnar bætist morðbrenna á Stokkseyri og skotárás á sýsluskrifstofunni á Eyrarbakka. Sem fyrr fléttast hlutskipti fátækra Sunnlendinga inn í líf sýslumannshjónanna sem þó eiga nóg með sitt. Studdur af Tryggva skrifara sínum þarf Eyjólfur að glíma við höfuðandstæðing sinn, óþokkann Kár Ketilsson sem bruggar sín viðbjóðslegu ráð. Embætti sýslumanns berst óvæntur liðsauki frá Englandi og ekki seinna vænna, því nú dregur að leikslokum.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15713, pre_title: "", title: "Gamlar konur detta út um glugga", post_title: "rússneskar örsögur", slug: "gamlar-konur-detta-ut-um-glugga-15713", description: "Danííl Kharms (1905-1942) var ljóðskáld, smásagnahöfundur, leikskáld og fulltrúi framúrstefnustrauma í sovéskum bókmenntum. Hann er nú talinn einn fremsti höfundur absúrdbókmennta í hinum vestræna heimi. Úrval af örsögum þessa sérkennilega furðusagnameistara veitir innsýn í andrúmsloft á tímum ógnarstjórnar og undirstrikar með sínum hætti bjargarleysi sögupersóna.", long_description: "", page_count: 143, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 63, book_author_id: nil},
  {source_id: 15715, pre_title: "", title: "43 smámunir", post_title: "örsögur", slug: "43-smamunir-15715", description: "Katrin Ottarsdóttir er færeyskur kvikmyndaleikstjóri og rithöfundur. Í gáskafullum og nýstárlegum örsögum bregður hún upp litríkum myndum og sýnir lesandanum inn í sálarlíf margra ólíkra persóna.", long_description: "", page_count: 95, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 63, book_author_id: nil},
  {source_id: 15716, pre_title: "", title: "Svo týnist hjartaslóð: Þroskasaga Betu Reynis", post_title: "", slug: "svo-tynist-hjartaslod-throskasaga-betu-reynis-15716", description: "„Ekki láta mömmu deyja,“ hvíslar lítill fimm ára drengur að óskafiðrildinu áður en hann sleppir því lausu.\r\n\r\nSagan segir frá baráttu Betu Reynis við að komast í gegnum erfið veikindi og fóta sig aftur í lífinu. Hvað tekur við hjá ungri konu sem hefur misst heilsuna og þarf að læra allt uppá nýtt? Og hvaða áhrif hefur það á líf hennar og framtíð?", long_description: "", page_count: 396, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 57, book_author_id: nil},
  {source_id: 15717, pre_title: "", title: "Krákan", post_title: "", slug: "krakan-15717", description: "Í 5. bók Hjartablóðs fylgjumst við með afkomendum Magdalenu og Ara, þeim Ester og Evu. Tvíburasysturnar eru ólíkir persónuleikar og ýmsar hremmingar henda þær og þá sérstaklega Ester, eða Krákuna eins og hún hefur verið kölluð frá blautu barnsbeini. Hörmulegur atburður á eftir að skekja sveitina og valda því að heimilið í Laufskógum er ekki lengur öruggur staður.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15718, pre_title: "", title: "Strumparnir 1-3", post_title: "", slug: "strumparnir-1-3-15718", description: "Hinir sívinsælu Strumpar eru mættir á Storytel í þremur glænýjum ævintýrum. Hlýðið á einstakan leiklestur Ladda þar sem hann rifjar upp taktana úr talsetningu sígildu teiknimyndanna sem allir muna eftir og elska.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15719, pre_title: "", title: "Flæðarmál", post_title: "", slug: "flaedarmal-15719", description: "Melkorka er horfin en enginn saknar hennar. Sóley, fyrrum samstarfskona hennar, fer að velta fyrir sér hvers vegna. Lét hún sig hverfa, var hún látin hverfa eða gufaði hún bara upp? Flæðarmál bar sigur úr býtum í handritasamkeppni Storytel, Eyranu, árið 2020. Njóttu þess að hlusta og lesa á Storytel Reader lesbrettinu.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15720, pre_title: "", title: "Handa á milli", post_title: "Heimilisiðnaðarfélag Íslands í hundrað ár", slug: "handa-a-milli-15720", description: "Með iðnbyltingunni varð til hreyfing um skipulagðan og vandaðan iðnað á heimilum. Markmið Heimilisiðnaðarfélags Íslands var að auka og efla þjóðlegan heimilisiðnað, vekja áhuga á framleiðslu nytsamra hluta og varðveita um leið þjóðleg einkenni. Hér er rakið hvernig starf félagsins hefur þróast í takt við samfélagsbreytingar án þess að missa sjónar á því markmiði að auka og efla þjóðlegan heimilisiðnað á Íslandi.", long_description: "", page_count: 303, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 65, book_author_id: nil},
  {source_id: 15721, pre_title: "", title: "Trú og þjóðfélag", post_title: "Afmælisrit til heiðurs Pétri Péturssyni prófessors", slug: "tru-og-thjodfelag-15721", description: "Hér kennir margra grasa enda endurspeglar ritið breitt rannsóknar- og áhugasvið afmælisbarnsins sem lokið hefur háskólaprófum í félagsfræði og guðfræði. Greinar um afbrotafræði, kristna siðfræði, stöðu, sögu og starfsemi kristinnar kirkju, bókmenntir, tónlist, listfræði og  áfengisneyslu í sænskum söfnuðum auk greinar í anda Pýþagórasar.", long_description: "", page_count: 300, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15722, pre_title: "", title: "Förusögur", post_title: "", slug: "forusogur-15722", description: "Í Förusögum leiðir Sigursteinn Másson hlustendur um landið með sinni einstöku frásagnargáfu og óviðjafnanlegu rödd. Þættirnir byggjast á sönnum sögum frá áhugaverðum stöðum á landinu, sem þó hafa stundum kryddast svolítið á leið sinni.\r\nNjóttu þess að hlusta og lesa á Storytel Reader lesbrettinu.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15723, pre_title: "", title: "Þegar karlar stranda", post_title: "- og leiðin í land", slug: "thegar-karlar-stranda-15723", description: "Í bókinni er að finna viðtöl við karlmenn á ólíkum aldri og af ýmsu tagi sem eiga það allir sameiginlegt að hafa strandað í lífinu og upplifað verulega vanlíðan. Þeir gátu ekki unnið og voru við það að sökkva. En þeir gripu til aðgerða, fóru að vinna í sínum málum og komust í land.", long_description: "", page_count: 215, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 44, book_author_id: nil},
  {source_id: 15724, pre_title: "", title: "Prentsmiðjubókin", post_title: "Prentsmiðjur og prentfyrirtæki frá 1530–2020, sem prentað hafa íslensk rit á Íslandi, í Kaupmannahöf", slug: "prentsmidjubokin-15724", description: ">>>Prentsmiðjubókin<<< eftir Svan Jóhannesson er stórmerkileg menningarsöguleg heimild um upphaf og rekstur prentsmiðja á Íslandi og útgáfu blaða og rita í öllum landsfjórðungum allt fram til dagsins í dag. Bókin er 464 síður að stærð og prýða hana 863 ljósmyndir, bæði mannamyndir, myndir af bókum og blöðum og myndir úr prentsmiðjum.", long_description: "", page_count: 464, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 66, book_author_id: nil},
  {source_id: 15725, pre_title: "", title: "Í fjarska norðursins", post_title: "Ísland og Grænland - viðhorfasaga í þúsund ár", slug: "i-fjarska-nordursins-15725", description: "Í fjarska norðursins er saga viðhorfa til Íslands og Grænlands frá miðöldum til samtímans. Báðar hafa þessar þjóðir verið framandi í augum annarra. Lengi voru ímyndir þeirra svipaðar en margt hefur líka greint á milli. Hér er leitað svara við því hvers vegna íbúum þessara landa hefur ýmist verið lýst sem verstu villimönnum eða fyrirmyndarfólki.", long_description: "", page_count: 381, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 65, book_author_id: nil},
  {source_id: 15726, pre_title: "", title: "Sögur handa Kára", post_title: "", slug: "sogur-handa-kara-15726", description: "Ólafur Ragnar Grímsson segir sögur af fólki og atvikum víða um veröld.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15727, pre_title: "", title: "Konur sem kjósa", post_title: "Aldarsaga", slug: "konur-sem-kjosa-15727", description: "Stórvirki um íslenskar konur og kosningaréttinn í eina öld. Fjallað er um kvennablöð og kvennaframboð, kvennafrí og kvennaverkföll, um baráttu kvenna fyrir hlutdeild í stjórn landsins og frelsi til að ráða menntun sinni og atvinnu, barneignum og ástarsamböndum. Bókina prýðir fjöldi mynda. Áður ósagðar sögur kvenna njóta sín í lifandi texta.", long_description: "", page_count: 783, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 65, book_author_id: nil},
  {source_id: 15729, pre_title: "", title: "Saga", post_title: "Tímarit Sögufélags LVIII:1", slug: "saga-15729", description: "", long_description: "", page_count: 211, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 65, book_author_id: nil},
  {source_id: 15730, pre_title: "", title: "Dauði egósins", post_title: "", slug: "daudi-egosins-15730", description: "Einstök bók sem skrifuð er handa öllum sem glíma við erfiðar aðstæður í lífinu. Í bókinni ber höfundur saman tvö ólík hugsanakerfi, annað sem hefur verið lengi við lýði í heiminum og hitt sem hefur dulist allt of lengi. Höfundur skoðar m.a. heim vísindanna, skammtafræðina, segulbylgjur og mikilvægi hjartans. Á athyglisverðan hátt tvinnar höfundur saman rökhyggju og huglæga upplifun. Með kærleikann að leiðarljósi er hægt að koma á nýrri heimssýn þar sem óttinn víkur fyrir friði og innra jafnvægi.", long_description: "", page_count: 278, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 67, book_author_id: nil},
  {source_id: 15731, pre_title: "", title: "Saga", post_title: "Tímarit Sögufélags LVIII:2", slug: "saga-15731", description: "", long_description: "", page_count: 224, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 65, book_author_id: nil},
  {source_id: 15732, pre_title: "", title: "Ástin lifir", post_title: "Mynda og sögubók um ævi Margrétar Kristjánsdóttur frá Villingaholti", slug: "astin-lifir-15732", description: "Margrét skrifar styttri og lengri endurminningar í lifandi og persónulegum stíl. Sögur frá barnæskunni í Flóanum og hjartnæmar frásagnir af ástinni sem spíraði í Flóamoldinni og átti eftir að fylgja henni yfir fjöll, haf og mörk þessa heims og hins. Bókina skreyta myndir frá æskuslóðunum í Villingaholtshreppnum, vertíðarárunum í Ólafsvík og Grindavík, Álafossi, Skagafirði, ástarævintýrum og fjölskyldu Margrétar frá Villingaholti.", long_description: "", page_count: 264, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 68, book_author_id: nil},
  {source_id: 15733, pre_title: "", title: "Annáll um líf í annasömum heimi", post_title: "", slug: "annall-um-lif-i-annasomum-heimi-15733", description: ">>>Annáll um líf í annasömum heimi<<< fjallar um líf einnar manneskju, líf sem er sérstakt og engu öðru líkt en um leið svo hversdagslegt að hér er varla nokkuð sem aðrir kannast ekki við. Í tólf köflum og jafn mörgum kvæðum fer Ólafur Páll Jónsson með lesandann í ferðalag þar sem lágstemmdur hversdagsleikinn og grimmustu áskoranir samtímans fléttast saman í heimspekilegum hugleiðingum um stöðu okkar í samtímanum. Bókin er skreytt vatnslitamyndum eftir Ásu Ólafsdóttur.", long_description: "", page_count: 166, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15734, pre_title: "", title: "Fyrir daga farsímans", post_title: "", slug: "fyrir-daga-farsimans-15734", description: "Furðulegir helgidómar á altari kirkju sem var vígð 1882. Leiðsögumaður þýskra túrista sem þarf að þola að þeir hegða sér allt öðruvísi en honum líkar. Hernaðarsaga Íslendinga, ástir í braggahverfi og söngnám á Ítalíu. Þetta og margt fleira er efni nýrra smásagna Böðvars Guðmundssonar. Sögurnar eiga það sameiginlegt að þær gerast fyrir daga farsímans. Böðvar Guðmundsson hefur verið ástsælt skáld um áraraðir og meðal ann­ars hlotið Íslensku bókmenntaverðlaunin. Hér opnar hann nokkra glugga inn í gamlan heim, þar sem líf og reynsla karlmanna er í fyrirrúmi.", long_description: "", page_count: 194, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15735, pre_title: "", title: "Kindasögur II", post_title: "", slug: "kindasogur-ii-15735", description: "Sauðkindin er fíngerð og viðkvæm sagði í einni af útvarpstilkynningum Sauðfjárverndarinnar seint á síðustu öld. Í öðru bindi >>>Kindasagna<<< eru meðal annars rifjuð upp tildrög þessara tilkynninga, sagt frá afdrifum kinda í eldgosum og sauðfjárhaldi í höfuðborginni, vikið að framliðnum kindum, forystukindum, stökkrollum og karakúlfé, auk kinda í kvæðum íslenskra skálda. Kindasögur eru sérstök grein íslenskrar sagnaskemmtunar sem á sér langa sögu og lifir enn góðu lífi – rétt eins og sauðkindin sjálf. Höfundar eru áhugamenn um sögur og sauðfé.", long_description: "", page_count: 128, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15736, pre_title: "", title: "Þjóð gegn sjálfsvígum", post_title: "Sjálfsvígsfræði", slug: "thjod-gegn-sjalfsvigum-15736", description: "Árlega eiga sér stað 30-50 sjálfsvíg á Íslandi og að auki reyna margir að svipta sig lífi.  Þetta er ástand sem ekki er hægt að una við.  Fækkun sjálfsvíga krefst samstöðu þjóðar og fræðslu um málefnið. Um það fjallar þessi bók. Sjálfsvígsatferli, sjálfsvígsaðferðum og áhættuþáttum sjálfsvíga eru gerð skil og fjallað um kenningar, rannsóknir og meðferðarleiðir ásamt tölulegum upplýsingum um sjálfsvíg. Lögð er áhersla á umfjöllun um fyrirbyggjandi aðgerðir.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15737, pre_title: "", title: "Í helgreipum ástarinnar", post_title: "", slug: "i-helgreipum-astarinnar-15737", description: ">>>Í helgreipum ástarinnar<<< er saga laundóttur konu sem ákveður að hefna sín á starfsbróður móður sinnar vegna framkomu í hennar garð. Leyndarmálið varð henni ljóst við lestur dagbókar móður sinnar án hennar vitneskju og vilja. Sagan fjallar um hömlulausar ástíður, hliðarspor og afleiðingar.", long_description: "", page_count: 125, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 69, book_author_id: nil},
  {source_id: 15738, pre_title: "", title: "Appelsínuguli drekinn", post_title: "", slug: "appelsinuguli-drekinn-15738", description: "Á fallegri eyju í fjarskanum eru spúandi eldfjöll og bullandi hverir. Þar búa líka drekar og því heitir eyjan Drekaland. Dag nokkurn hendir ófögnuður nokkur einn af drekunum, Appelsínugula drekann. Lítill karl tekur sér bólfestu í eyranu á honum, syngur þar og trallar og gerir drekanum lífið óbærilegt. Hann leitar víða lækninga við þessum leiða kvilla, en hana virðist hvergi vera að finna. En þegar sameiginlegar hremmingar henda litla karlinn og drekann, neyðast þeir til að taka höndum saman, með óvæntum afleiðingum.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15739, pre_title: "", title: "ORRI ÓSTÖÐVANDI: Bókin hennar Möggu Messi", post_title: "", slug: "orri-ostodvandi-bokin-hennar-moggu-messi-15739", description: "Þa&#769; er bo&#769;kin hennar Mo&#776;ggu Messi\r\nloksins tilbu&#769;in og hu&#769;n er\r\nekkert annað en sto&#769;rkostleg.\r\nE&#769;g vil alls ekki ljo&#769;stra of miklu upp en Magga lenti i&#769; bando&#769;ðum Blikaþja&#769;lfara sem reyndi að skemma fyrir henni Rey Cup, VERSTA FO&#769;LK I&#769; HEIMI flutti i&#769; hu&#769;sið við hliðina a&#769; henni, foreldrar hennar reyndu að stela jo&#769;lunum, hu&#769;n lenti i&#769; o&#769;tru&#769;legri uppa&#769;komu i&#769; Ska&#769;lafelli og svo varð ha&#769;lfgert stri&#769;ðsa&#769;stand a&#769; Reykjum. Sem betur fer hafði Magga mig, Orra óstöðvandi, se&#769;r til halds og trausts i&#769; gegnum þessa vitleysu.", long_description: "", page_count: 294, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 70, book_author_id: nil},
  {source_id: 15740, pre_title: "", title: "Fræðaskjóða", post_title: "Bókmenntafræði fyrir forvitna", slug: "fraedaskjoda-15740", description: ">>>Fræðaskjóða, bókmenntafræði fyrir forvitna<<< snýst eins og nafnið bendir til um bókmenntafræði. Í upphafi segir af ýmsum meginatriðum sem gott er að huga að þegar bókmenntir eiga í hlut, en síðan er rætt sérstaklega um ljóð og frásagnir, þar með talin leikrit og frásagnarljóð. Margt hefur breyst í afstöðu til bókmenntafræði síðustu áratugi og leitast er við að gera ýmsu af því skil. Þá eru tekin ófá dæmi af íslenskum bókmenntum fornum og nýjum og þau höfð sem fjölbreyttust.", long_description: "", page_count: 367, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15741, pre_title: "", title: "Síðustu dagar Skálholts", post_title: "", slug: "sidustu-dagar-skalholts-15741", description: ">>>Síðustu dagar Skálholts<<< segir ósegjanlega sögu af ringulreið við endalok mikilla mektardaga í Sultartungum, af bekkingum skólapilta, lénsveldi Stefánunga, mannfelli förufólks, hinum sannheilaga Eilífagvendi og uppsveitadrjólum sem reyna að koma á byltingu. Þegar síðasta standsfólkið er farið er öll saga staðarins fólgin í minni einnar soðbúrskerlingar sem skynjar að stráin, sem áður rembdust öll við að verða eitthvað meira og stærra, eru aldrei nema strá.", long_description: "", page_count: 192, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15742, pre_title: "", title: "Úr hugarfylgsnum augnlæknis", post_title: "Minningabrot augnlæknis", slug: "ur-hugarfylgsnum-augnlaeknis-15742", description: "Á langri ævi ber margt til tíðinda. Hrífandi frásagnir Ingimundar Gíslasonar færa lesandanum tíðarandann á seinni helmingi tuttugustu aldar í ljóslifandi minningabrotum. Ingimundur Gíslason augnlæknir á að baki langan og farsælan starfsferil, bæði á Íslandi og í Svíþjóð. Í blöðum og tímaritum hafa birst eftir hann greinar um ýmis málefni.", long_description: "", page_count: 168, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15743, pre_title: "", title: "Mótorhausasögur", post_title: "", slug: "motorhausasogur-15743", description: "Þetta rit segir sannar og sannlognar gamansögur af alls konar fólki í, undir, í kringum og ofan á bílum, hér og þar. Þar má nefna Baldur búktalara, Dóru gjafmildu, Jón góðan daginn, Benna bensínstígvél, Alexöndru Mist, Magga á 80, Dóna í Garðlist, Stefni í Óefni, Stíg í Minna-Viti og Jesú Krist ... Ritið svarar áleitnum spurningum eins og hvort hægt sé að mæðast við akstur og festast undir mælaborði... hvernig Cadillac klúðraði hönnun öskubakka, hvers vegna Bretar framleiða ekki tölvur og hvers vegna Skilnaðar-Barbie kostar meira en Útivistar-Barbie.", long_description: "", page_count: 296, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15744, pre_title: "", title: "Samvinna á Suðurlandi I–IV", post_title: "", slug: "samvinna-a-sudurlandi-i-iv-15744", description: "Saga samvinnufélaga á Suðurlandi er í raun atvinnu-, samgöngu- og félagsmálasaga landsfjórðungsins í rúm hundrað ár. Rakin er saga kaupfélaga og annarra samvinnufyrirtækja í Árnessýslu, Rangárvallasýslu, Vestur-Skaftafellssýslu og Vestmannaeyjum. Þetta er saga samstöðu og sigra, litríkra leiðtoga og stórhuga framkvæmda en einnig saga togstreitu, hatrammrar stjórnmálabaráttu, mistaka og beiskra ósigra. Verkið allt er í fjórum bindum í meðalstóru broti sem seld eru saman í fallegri öskju.", long_description: "", page_count: 1448, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15747, pre_title: "", title: "Valdimarsdagur", post_title: "", slug: "valdimarsdagur-15747", description: "Það er 15. júní árið 1938, dagur Valdimars sigursæla og danska fánans. Í leiguherbergi í miðborg Kaupmannahafnar vaknar ungur maður, kófsveittur eftir órólega nótt. Þetta er Erik. Hann hefur ákveðið að drepa mann. Við fylgjumst með ferðum unga mannsins þennan dag, aðdraganda þessarar örlagaríku ákvörðunar og loks afleiðingunum sem glæpurinn og refsingin hafa á líf hans. Í >>>Valdimarsdegi<<< segir verðlaunahöfundurinn Kim Leine sögu föðurafa síns. Jón Hallur Stefánsson þýddi.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15748, pre_title: "", title: "Stigið á strik", post_title: "", slug: "stigid-a-strik-15748", description: "Atli Jón er maður á miðjum aldri sem býr í Reykjavík. Eitt og annað plagar hann. Hann veit ekki enn hvað hann vill verða þegar hann verður stór. Eiginkonan er farin frá honum. Hann býr því einn með kettinum Brandi og lætur sér leiðast. En svo fer ýmislegt óvænt að gerast í lífi Atla. Hann kemst á snoðir um leyndarmál sem leiðir hann á vit vafasamra manna sem virðast tengjast dularfullum dauðsföllum. >>>Stigið á strik<<< er þriðja bók höfundar sem hlaut glæpasagnaverðlaunin Gaddakylfuna árið 2009.", long_description: "", page_count: 296, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15749, pre_title: "", title: "Raunvitund", post_title: "", slug: "raunvitund-15749", description: "Þegar við erum spurð einfaldra spurninga; af hverju fjölgar fólki, hversu margar ungar konur fara í nám, hve mörg okkar lifa í fátækt - svörum við rangt á kerfisbundinn hátt. Hér eru ástæður þess raktar. Ástand heimsins er, þrátt fyrir alla vankanta, betra en við höldum. Raunvitund breytir sýn okkar á heiminn og gerir okkur kleift að bregðast við áskorunum og tækifærum sem framtíðin hefur í för með sér.", long_description: "", page_count: 320, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15750, pre_title: "", title: "Sumac", post_title: "", slug: "sumac-15750", description: "Sumac hefur verið einn vinsælasti veitingastaður landsins um árabil og nú lítur matreiðslubók staðarins dagsins ljós. Áhersla er lögð á ferskt og gott hráefni sem matreitt er undir áhrifum Miðausturlanda og Norður-Afríku. Framandi krydd, eldur, fjölbreytileiki og hollar og girnilegar nýjungar eru meginstef bókarinnar sem inniheldur fleiri en hundrað uppskriftir sem prýtt hafa matseðil Sumac. Bókin er vegleg skyldueign ástríðufulla heimiliskokksins.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 23, book_author_id: nil},
  {source_id: 15752, pre_title: "", title: "Líkkistusmiðirnir", post_title: "", slug: "likkistusmidirnir-15752", description: "Líkkistusmiðirnir er hlý og gamansöm saga um manneskjur sem taka að sér verkefni sem vekur upp hugmyndir um dauðann og byrja að spyrja sig sjálfar hvað þær vilji eiginlega fá út úr lífinu. Líkkistusmiðirnir draga fram bros, skapa vangaveltur um lífið og verma lesandanum um hjartarætur. „Lesandanum hlýnar um hjartarætur.“", long_description: "", page_count: 400, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15753, pre_title: "", title: "Hundur", post_title: "Un Perro", slug: "hundur-15753", description: "Bókin er, eins og Móðir (Una Madre) eftir sama höfund og sem kom út 2019, full af húmor, trega, ást og væntumþykju. Hundur er sjálfstætt framhald af Móðir og aftur fá lesendur innsýn í líf spænskrar/katalónskrar móður og ástar hennar á fjölskyldu sinni. „Grátbrosleg, hlý og virkilega góð.“", long_description: "", page_count: 320, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15754, pre_title: "", title: "Sjö lygar", post_title: "", slug: "sjo-lygar-15754", description: "Þetta byrjaði allt með einni, saklausri lygi. Jane kunni aldrei við manninn hennar Mariu. Hann var svo hávær og plássfrekur, svo fullur af lífi. Sem virðist auðvitað frekar kaldhæðnislegt núna. Ef Jane hefði ekki logið væri eiginmaður bestu vinkonu hennar kannski enn á lífi. Þetta er tækifæri Jane til að segja sannleikann. Spurningin er bara hvort við trúum henni.", long_description: "", page_count: 384, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15755, pre_title: "", title: "Flækjurof", post_title: "", slug: "flaekjurof-15755", description: "Neyðarkall berst úr dalnum endalausa. Þyrlan er send af stað með Lísu og annað starfsfólk rannsóknarlögreglunnar. Á öðrum stað á landinu er Sigrún lögreglukona í berjamó.  Hún skilar sér ekki í vinnu eftir berjaferðina. Tengist hvarf hennar óhugnaðinum í dalnum endalausa? >>>Flækjurof<<< er lokabókin í þríleiknum um Lísu og aðstoðarfólk hennar. Hinar eru >>>Fuglaskoðarinn<<< og >>>Fléttubönd<<<.", long_description: "", page_count: 266, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 72, book_author_id: nil},
  {source_id: 15756, pre_title: "", title: "Hilduleikur", post_title: "", slug: "hilduleikur-15756", description: "Hilda er ljóðelsk kona sem komin er á Aflifunaraldur. Hún býr ein í stórri íbúð og  ætlar sér að vera drottning í sínu ríki þar til yfir lýkur. En það samræmist ekki markmiði fyrirtækisins >>>Futura Eterna<<< sem sér um skipulagningu ævikvöldsins. Spennandi og launfyndin atburðarás með óvæntum endi kallast á við óvenjulegt pestarástand samtímans með gagnrýnum undirtón.", long_description: "", page_count: 225, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 72, book_author_id: nil},
  {source_id: 15757, pre_title: "", title: "Dauðar sálir", post_title: "", slug: "daudar-salir-15757", description: "Mannabein koma í ljós við fornleifauppgröft á akri í Svörtulöndum sem breytist samstundis í flókinn glæpavettvang fyrir Kim Stone rannsóknarfulltrúa. Við flokkun beinanna verður ljóst að þarna eru bein úr fleiri en einu fórnarlambi - sem bera merki ólýsanlegs hryllings. Spennandi og hryllileg glæpasaga sem heldur lesendum föngnum.", long_description: "", page_count: 384, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15758, pre_title: "", title: "Frásaga Jóns Jónssonar af því hvernig á að ná árangri í starfi og einkalífi", post_title: "", slug: "frasaga-jons-jonssonar-af-thvi-hvernig-a-ad-na-arangri-i-starfi-15758", description: "Blaðamaðurinn Nonni, sem áður vann á jaðarsettum smáblöðum, stefnir nú hægt upp metorðastigann á ritstjórn Morgunblaðsins. Söguhetjan verður vitaskuld fyrir vonbrigðum þegar tillitslausir samstarfsmenn þykjast ekki taka eftir stílsnilld hans og rotta sig saman í hlæjandi hýenuhópum. En það er ekkert sem nokkrir kaldir á Kaffibarnum ná ekki að bæta upp. >>>Frásaga Jóns Jónssonar af því hvernig á að ná árangri í starfi og einkalífi<<< er í senn bráðfyndin og harmsöguleg lýsing á andhetju vorra tíma.", long_description: "", page_count: 112, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15759, pre_title: "", title: "Úlfakreppa&#8203;", post_title: "", slug: "ulfakreppa-8203-15759", description: "Livia er að verða fertug og fyrir dyrum stendur veislan sem hana hefur alltaf dreymt, til að bæta upp fyrir brúðkaupsveisluna sem aldrei varð. Hún er þó með leyndarmál sem hún þarf að segja Adam, en vill bíða fram yfir veisluna. Adam er líka með leyndarmál sem getur varla beðið. Segja frá / ekki segja frá?", long_description: "", page_count: 336, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15760, pre_title: "", title: "Á sviðsbrúninni", post_title: "", slug: "a-svidsbruninni-15760", description: "Eftir að bók Sveins Einarssonar leikstjóra og fyrrverandi Þjóðleikhússtjóra, >>>Mitt litla leiksvið<<<, kom út 2017 tóku að hrannast upp önnur minningabrot ásamt heimspekilegum vangaveltum sem rötuðu á þessa bók. Hér eru í bland fjörlegar frásagnir, minningaleiftur og forvitnilegar huganir sem gaman er að lesa.", long_description: "", page_count: 238, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 72, book_author_id: nil},
  {source_id: 15761, pre_title: "", title: "PAX 4", post_title: "Tilberinn", slug: "pax-4-15761", description: "Peningar, hlutir og ástkær gæludýr hverfa og Alríkur er efstur á lista yfir grunaða. Fólk veit ekki að það búa galdrar að baki, því einhver hefur búið til tilbera! Smám saman hrindir svartnornin áætlun sinni í framkvæmd. „PAX-bækurnar eru án vafa einhverjar æsilegustu og mest taugastrekkjandi bækur sem ég hef lesið! Einfaldlega hrikalega spennandi.“", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15762, pre_title: "", title: "Handbók fyrir ofurhetjur", post_title: "Fimmti hluti: Horfin", slug: "handbok-fyrir-ofurhetjur-15762", description: "Aðeins þau sem eru raunverulega sterk þora að sýna veikleika. Dag einn gerist dálítið hræðilegt. Börn fara að hverfa um nætur. Eitt af öðru, algjörlega sporlaust. \r\nHandbók fyrir ofurhetjur er bókaröð sem hefur farið eins og stormsveipur um Ísland og hin Norðurlöndin. Hér er fimmta æsispennandi bókin og upphafið á nýju ævintýri!", long_description: "", page_count: 104, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15763, pre_title: "", title: "Haninn og þröngu gallabuxurnar", post_title: "", slug: "haninn-og-throngu-gallabuxurnar-15763", description: "Haninn er svo spenntur þegar nýju, þröngu gallabuxurnar koma með póstinum; glansandi saumurinn, á gullsleginn hátt - og að koma við efnið; svo glæsilega blátt.\r\nEn hvað skyldi öllum hinum dýrunum finnast um hið nýja og töfrandi útlit?", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15764, pre_title: "", title: "Tinna Trítlimús", post_title: "Háskabrunnurinn", slug: "tinna-tritlimus-15764", description: "Tinna og Koli taka sér ferð á hendur á hættulegar slóðir.\r\nKobbi kanínupabbi hafði haldið í leiðangur en ekki skilað sér til baka. Tinna og Koli bjóðast til að fara að leita að honum þótt hættur leynist á hverju strái og margar hindranir verði á vegi þeirra ...", long_description: "", page_count: 82, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 73, book_author_id: nil},
  {source_id: 15769, pre_title: "", title: "Sumar í september", post_title: "", slug: "sumar-i-september-15769", description: "Ungur maður missir heilsuna eftir mikla vímuefnaneyslu. Eftir áralöng veikindi er hann orðinn miðaldra án þess að nokkuð hafi gerst í lífi hans síðan um tvítugt. Dásamlegir hlutir eiga sér stað þegar hann kynnist konu á internetinu. Hér er sagt frá því hvernig þau hittust og hvað gerðist. Þetta er saga sem er jafn sönn og ástin sem hún fjallar um.", long_description: "", page_count: 342, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 74, book_author_id: nil},
  {source_id: 15772, pre_title: "", title: "Brosað gegnum tárin", post_title: "", slug: "brosad-gegnum-tarin-15772", description: ">>>Brosað gegnum tárin<<< er bók um ástina og hamingjuna, um sorgina og harmleikinn. Ævisaga heitra tilfinninga og dramatískra viðburða. Bók sem enginn leggur frá sér – ósnortinn. Hún heldur áfram þar sem metsölubók Bryndísar Schram >>>Í sól og skugga<<< endaði. Hún segir söguna alla, þar sem höfundur horfist í augu jafnt við hamingjuna sem harmleikinn.", long_description: "", page_count: 230, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 75, book_author_id: nil},
  {source_id: 15775, pre_title: "", title: "Litla gula hænan", post_title: "", slug: "litla-gula-haenan-15775", description: ">>>Litla gula hænan<<< er frægari en aðrar dæmisögur. Fyrir býsna marga hefur þessi saga verið með fyrstu textum sem þeim tókst að ráða í af eigin rammleik, en hún er ekki síður eftirminnileg fyrir sinn tæra og einfalda réttlætisboðskap sem oft er vitnað til. Auk Litlu gulu hænunnar eru í bókinni fleiri kunnuglegar sögur, svo sem um sætabrauðsdrenginn sprettharða, Unga litla og um pönnuköku sem fór að heiman. Iðunn Arna Björgvinsdóttir endurgerði myndir úr fyrstu útgáfu bókarinnar frá 1930.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15784, pre_title: "", title: "Ísbjörninn sem vildi gerast grænmetisæta", post_title: "", slug: "isbjorninn-sem-vildi-gerast-graenmetisaeta-15784", description: "Þessi tiltekni ísbjörn var ekki eins og flestir aðrir. Hann vildi ekki éta fisk, sel eða nokkur önnur dýr. \r\nÍsbjörninn ferðast frá Grænlandi til Íslands þar sem hann bragðar á grænmeti og ávöxtum. En að lokum áttar björninn sig á því að hann er kjötæta af náttúrunnar hendi. Bókin opnar á umræðu um grænmetisfæði og að koma vel fram við dýr.", long_description: "", page_count: 34, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 73, book_author_id: nil},
  {source_id: 15785, pre_title: "", title: "Undir yfirborðinu", post_title: "Norska laxeldisævintýrið – lærdómur fyrir Íslendinga?", slug: "undir-yfirbordinu-15785", description: "Norsk stórfyrirtæki hasla sér nú völl í laxeldi á Íslandi og beita sömu eldisaðferðum hér og í Noregi. Í þessari bók segir norski blaðamaðurinn Kjersti Sandvik sögu norska laxeldisævintýrisins og lýsir kostum þess og göllum.\r\nÞetta er bók sem enginn áhugamaður um þjóðfélagsmál, atvinnulíf og hreina íslenska náttúru getur látið fram hjá sér fara.", long_description: "", page_count: 398, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15786, pre_title: "", title: "Hugsað upphátt", post_title: "", slug: "hugsad-upphatt-15786", description: "Hugsað upphátt geymir úrval pistla sem Guðrún Egilson skrifaði í >>>Morgunblaðið<<< á fjörutíu ára tímabili.\r\nPistlar hennar vöktu jafnan mikla athygli, enda er Guðrúnu einstaklega lagið að segja mikla sögu í stuttu máli.\r\nPistlar Guðúnar eru hispurslausir og þar er brugðið upp lifandi og minnisstæðum myndum úr lífi hennar og íslenskum hversdagsveruleika.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15787, pre_title: "", title: "Líkami okkar, þeirra vígvöllur", post_title: "Þannig fer stríð með konur", slug: "likami-okkar-theirra-vigvollur-15787", description: "Í þessari mögnuðu bók lætur höfundurinn raddir kvenna heyrast og afhjúpar hvernig herir, hryðjuverkamenn og vígasveitir beita nauðgunum sem stríðsvopni í nútímaátökum til þess að niðurlægja, hræða og stunda kynþáttahreinsanir.\r\nÞessi sláandi bók, sem vakið hefur heimsathygli, er ákall til okkar um að hlusta og hafast að gegn vanræktasta glæpi heimsins.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15788, pre_title: "", title: "Kisa litla í felum", post_title: "", slug: "kisa-litla-i-felum-15788", description: "Lísa er flutt heim til ömmu sinnar ásamt pabba sínum og bróður. Án gömlu vinanna er hún ákaflega einmana. Hana langar í kettling, en pabbi hennar hefur sagt henni að amma hennar vilji ekki dýr inn á heimili sitt. Svo gerist það einn daginn að hræddur og hungraður kettlingur flækist til hennar. Lísa felur hann fyrst úti í gróðurhúsi og síðan inni í fataskáp í herberginu sínu – en hve lengi skyldi hún geta falið kettlinginn þar?", long_description: "", page_count: 128, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 77, book_author_id: nil},
  {source_id: 15789, pre_title: "", title: "Hvolpurinn sem gat ekki sofið", post_title: "", slug: "hvolpurinn-sem-gat-ekki-sofid-15789", description: "Lára rekst á heimilislausan hvolp á ruslahaug, í köldu húsasundi, og finnst hún verða að hjálpa honum. Henni tekst að laða hvolpinn heim til sín og honum virðist líða vel. En þá kemur upp vandamál. Á næturnar er það eitthvað sem hrjáir litla hvolpinn svo hann getur ekki sofið og heldur vöku fyrir öllum með stöðugu ýlfri – Hvað ætli Lára geti gert til að fá hvolpinn til að sofa á næturnar?", long_description: "", page_count: 128, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 77, book_author_id: nil},
  {source_id: 15790, pre_title: "", title: "Ísland", post_title: "Náttúra og undur", slug: "island-15790", description: "Í þessari fallegu bók eru ljósmyndir frá um 80 stöðum á Íslandi. Myndirnar sýna ýmsar fáfarnar og áhugaverðar náttúruperlur, tignarleg eldfjöll, hrikalegt landslag jöklanna og undraheim hraunhellanna, svo eitthvað sé nefnt. Stórkostlega náttúru Íslands dregur Ellert fram í mögnuðum ljósmyndum í bland við fróðleik um jarðfræði og náttúru landsins. \r\nLjósmyndir Ellerts Grétarssonar af íslensku landslagi og náttúru hafa vakið mikla athygli og unnið til alþjóðlegra verðlauna.", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 77, book_author_id: nil},
  {source_id: 15791, pre_title: "", title: "Hulduheimar", post_title: "Huldufólksbyggðir á Íslandi", slug: "hulduheimar-15791", description: "Bókin >>>Hulduheimar<<< hefur að geyma um eitt hundrað huldufólkssögur úr öllum landshornum. Farinn er hringurinn kringum landið og sagðar huldufólkssögur úr flestum héruðum. Sögurnar greina frá litríkum samskiptum huldufólksins við íbúa mannheima og bregða upp myndum úr veröld sem oftast var hulin augum, falin á bak við standberg og klettaþil. Ljósmyndir og annað myndefni fylgir hverjum stað auk staðbundinna korta.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 77, book_author_id: nil},
  {source_id: 15792, pre_title: "", title: "Lindís strýkur úr leikskólanum", post_title: "", slug: "lindis-strykur-ur-leikskolanum-15792", description: "Bókin fjallar um 4 ára stelpu sem heitir Lindís. Hún er í leikskólanum Krakkakór í Kópavogi.  Einn mánudag leiðist henni svo mikið að hún tekur til sinna ráða.  Sagan er raunveruleg og sýnir hvað Lindís getur áorkað þegar söknuðurinn verður afgerandi.  \r\n\r\nBókin er einstaklega fallega myndskreytt.\r\n\r\nÚtgefandi: Gudda Creative ehf.\r\n\r\nPrentun: Prentmet Oddi", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 78, book_author_id: nil},
  {source_id: 15793, pre_title: "", title: "Lindís og kúluhúsið", post_title: "", slug: "lindis-og-kuluhusid-15793", description: "Lindís sem er 4 ára fer í heimsókn til afa síns í kúluhúsið við Elliðavatn. \r\nLindís lærir af afa sínum hvernig hægt er að lifa með virðingu fyrir umhverfinu. \r\nBókin kemur fram með nýjung í barnabókmenntum, hvað varðar sjálfbærni og umhverfismennt. \r\nBókin er einstaklega hlý og falleg.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 78, book_author_id: nil},
  {source_id: 15794, pre_title: "Hæ Sámur", title: "123 - Tölustafirnir", post_title: "Límmiða- og þrautabók", slug: "123-tolustafirnir-15794", description: "Hversu vel þekkir þú tölustafina?  Viltu að Sámur og krílin leiði þig frá 1 og upp í 10, með því að nota límmiða til að klára verkefnin á hverri blaðsíðu?\r\nVinalegi hundurinn Sámur hvetur börn til þess að kanna umhverfi sitt og takast á við verkefni í sameiningu.", long_description: "", page_count: 36, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15795, pre_title: "", title: "Lindís vitjar neta", post_title: "", slug: "lindis-vitjar-neta-15795", description: "Lindís sem er 4 ára fer í páskaheimsókn til Ísafjarðar. \r\nÞað gerast ævintýri þar, þegar hún fer á grásleppuveiðar. Siglt var frá Sundahöfninni til að vitja neta og komið með marga fiska að landi.\r\nBókin er ævintýraleg, kærleiksrík og að sama skapi raunveruleg.  Áhersla á umhverfi og sjálfbærni.\r\n\r\nÚtgefandi: Gudda Creative ehf.\r\n\r\nPrentun: Prentmet Oddi", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 78, book_author_id: nil},
  {source_id: 15796, pre_title: "", title: "Fótbolti - Meistarataktar", post_title: "Lærðu að spila eins og þau!", slug: "fotbolti-meistarataktar-15796", description: "Viltu ná boltastjórn eins og Kylian Mbappé, sóla eins og Lionel Messi eða verja eins og Alisson Becker? Þessi bók hjálpar þér að ná meistaratöktum bestu leikmanna heims! Skýrar leiðbeiningar útskýra hvernig á að læra og æfa lykilatriði. Farið yfir helstu hæfileika 20 bestu leikmanna heims, yfirlit yfir bestu þjálfarana, ólík leikkerfi og bestu HM mörkin.", long_description: "", page_count: 116, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15797, pre_title: "", title: "Sá stóri, sá missti og sá landaði", post_title: "", slug: "sa-stori-sa-missti-og-sa-landadi-15797", description: "Bók fyrir allt áhugafólk um laxveiði, bæði byrjendur og lengra komna. Sigurður Héðinn, hinn landsþekkti veiðimaður, fer nákvæmlega yfir hvernig stórlaxinn tekur fluguna og skoðar líka veiðitækni og veiðigræjur. Að sjálfsögðu fylgja fjölmargar veiðisögur auk þess sem í bókinni er stórglæsilegur flugukafli, með bæði uppskriftum og myndum.", long_description: "", page_count: 146, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15798, pre_title: "Hæ Sámur", title: "GÓÐA NÓTT SÁMUR", post_title: "", slug: "goda-nott-samur-15798", description: "Það er háttatími í Krílakoti og krílin eiga erfitt með að sofna. Eiga þau einhvern tíma eftir að sofna og fá SVEFN-MERKIÐ? Komdu og vertu með Sámi í þessari yndislegu sögu sem gleður öll lítil kríli, sérstaklega þegar þau fara að sofa.", long_description: "", page_count: 22, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15799, pre_title: "", title: "Önd! Kanína!", post_title: "", slug: "ond-kanina-15799", description: "Það er undursamleg önd í þessari bók.\r\nNei, alls ekki. - Það er sæt lítil kanína.\r\nHa? Skoðaðu bara forsíðuna! - Þetta er önd!\r\nNeibb! Þetta er kanína. - Önd! -Kanína! - Önd!\r\n- Kanína! Hvað finnst þér? Þessi fjöruga bók sannar að þetta veltur allt á því hvernig þú lítur á málið. „Fyndin, sama hvernig litið er á málið“\r\n— The New York Times", long_description: "", page_count: 36, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15800, pre_title: "", title: "Segðu það steininum", post_title: "", slug: "segdu-thad-steininum-15800", description: "Úrval ljóða Jóhönnu Álfheiðar Steingrímsdóttur (1920-2002), gefið út á aldarafmæli skáldsins. Jóhanna sem var húsfreyja í Aðaldal sendi frá sér 14 bækur, stjórnaði útvarpsþáttum og stóð framarlega í baráttunni gegn virkjunaráformum í Laxá. Ljóðin sem hér birtast eru bundin og óbundin, og efnið af ýmsum toga. Ragnar Ingi Aðalsteinsson segir um kveðskap Jóhönnu: \"Bragurinn leikur í höndum hennar, orðgnótt og nákvæmni í orðavali einkenna kvæðin, orðaröðin, sem er oft grundvallaratriði í hefðbundnum ljóðum, vitnar um smekkvísi og virðingu fyrir ljóðahefðinni.\"", long_description: "", page_count: 88, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15801, pre_title: "", title: "Vala víkingur og Miðgarðsormurinn", post_title: "", slug: "vala-vikingur-og-midgardsormurinn-15801", description: "Vala víkingur ferðast um Goðheima ásamt skipi sínu, Breka dreka í ævintýraleit. Nú eru þau að leita að flugfiskum en skyndilega rekast þau á eitthvað stórt og ægilegt. Hvað ætli þau hafi fundið? Og er þetta þrumuguðinn Þór, syndandi svo langt frá landi?", long_description: "", page_count: 52, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 71, book_author_id: nil},
  {source_id: 15802, pre_title: "", title: "Sigríður á Tjörn", post_title: "Minningar og myndbrot frá langri ævi", slug: "sigridur-a-tjorn-15802", description: "Í bókinni bregður Sigríður Hafstað upp myndbrotum úr ævi sinni með sendibréfum, viðtölum og öðru efni sem frá henni er runnið. Hún ólst upp á stóru sveitaheimili í Vík í Skagafirði og stóð fyrir öðru slíku á Tjörn í Svarfaðardal. Þar fæddi hún og klæddi börn sín sjö og oft miklu fleiri börn, sinnti gestum, vann við bústörf og starfaði að félagsmálum, kórsöng og leiklist. Gekk á fjöll, rak héraðsfréttablað, var hreppstjóri og snerist í kringum niðjana.", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15803, pre_title: "", title: "Vél", post_title: "", slug: "vel-15803", description: ">>>Vél<<< heitir ný ljóðabók Steinunnar Arnbjargar Stefánsdóttur tónlistarkonu á Akureyri. Höfundur er ötull ljóðasmiður orða og tóna. Ljóð hennar má heyra í lögum og tónsmíðum, hennar eigin og annarra, en þau birtust fyrst á prenti í tímaritinu Stínu. >>>Vél<<< er þriðja bók Steinunnar, en áður hafa komið út hjá Sæmundi bækurnar >>>Uss<<< og >>>Fugl/blupl.<<<", long_description: "", page_count: 70, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15804, pre_title: "", title: "Er ekki á leið", post_title: "Strætóljóð", slug: "er-ekki-a-leid-15804", description: ">>>Er ekki á leið, strætóljóð<<< er fyrsta ljóðabók Elínar Gunnlaugsdóttur sem er tónskáld á Selfossi. Bókin lýsir strætóferðum þar sem skilin milli draums og veruleika liggja ekki alltaf í augum uppi. Bókin er prýdd ljósmyndum höfundar sem líkt og textinn draga fram smáleg einkenni hvunndagsins.", long_description: "", page_count: 72, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15805, pre_title: "", title: "Óhreinu börnin hennar Evu", post_title: "Holdsveiki í Noregi og á Íslandi", slug: "ohreinu-bornin-hennar-evu-15805", description: "Saga holdsveikinnar á Íslandi er samofin sögu hennar í Noregi. Sjúkdómurinn barst þaðan til Íslands í byrjun 15. aldar og í báðum löndum var hann upprættur nær sex öldum síðar. Í þessari bók er baráttan gegn þessum ógnvekjandi sjúkdómi rakin, fjallað um læknismeðferð og holdsveikispítalana sem settir voru á fót og lýst aðbúnaði sjúklinganna og hlutskipti þeirra.", long_description: "", page_count: 416, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15806, pre_title: "", title: "Martröð í Mykinesi", post_title: "Íslenska flugslysið í Færeyjum 1970", slug: "martrod-i-mykinesi-15806", description: "Laust fyrir hádegi 26. september 1970 brotlenti Fokker Friendship-vél Flugfélags Íslands í roki, þoku og rigningu undir tindinum Knúki á Mykinesi í Færeyjum. Alls voru 34 um borð í fullsetinni vélinni. Flak vélarinnar fannst ekki fyrr en nokkrum klukkustundum síðar. Þá voru átta látnir en 26 á lífi. Björgunaraðgerðir hófust við afar erfiðar aðstæður ...", long_description: "", page_count: 416, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15807, pre_title: "", title: "Úr hugarheimi séra Matthíasar", post_title: "", slug: "ur-hugarheimi-sera-matthiasar-15807", description: "Þjóðskáldið séra Matthías Jochumsson setti ríkan svip á samtíma sinn. Matthías fylgdist vel með hugmyndafræðilegum hræringum erlendis. Í þessari fróðlegu bók er fjallað um áhrifavalda Matthíasar og staðnæmst m.a. við hina gróskumiklu hreyfingu guðfræðinga og bókmenntamanna á austurströnd Bandaríkjanna sem þekkt er undir heitinu „transendentalismi“.", long_description: "", page_count: 300, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15809, pre_title: "", title: "Tvöfalt gler", post_title: "", slug: "tvofalt-gler-15809", description: ">>>Tvöfalt gler<<< kemur nú út í viðhafnarútgáfu. Bókin hefur á síðustu árum verið þýdd á fjölda erlendra tungumála. Hér er á ferðinni saga um gamalt fólk og gler sem er bæði gegnsætt og einangrandi. Á bak við það leynist líf sem lifað er af ítrustu kröftum, líf sem hamast á glerinu eins og fluga að hausti sem enn er sólgin í birtu.  Sögunni fylgir bókmenntaleg greining verksins eftir rithöfundana Hörpu Rún Kristjánsdóttur og Hermann Stefánsson. Fremst er minningaskrá þar sem vinir og samferðamenn votta höfundi virðingu sína.", long_description: "", page_count: 114, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15810, pre_title: "", title: "Hér er kominn gestur", post_title: "Um gesti og gangandi í aldanna rás", slug: "her-er-kominn-gestur-15810", description: "Þórður Tómasson, fyrrum safnstjóri Byggðasafnsins í Skógum, segir hér á sinn einstæða hátt frá ferðalögum og gestakomum í íslensku samfélagi allt fram á öndverða 20. öld. Höfundur fjallar um ferðabúnað, ferðahætti, þjóðtrú og þjóðhætti er snertu ferðir fólks. Gestrisni var mikils metin fyrrum enda oft um líf eða dauða að tefla fyrir ferðamanninn. Formálsorð ritar Margrét Hallgrímsdóttir þjóðminjavörður.", long_description: "", page_count: 190, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15811, pre_title: "", title: "Kötturinn sem átti milljón líf", post_title: "", slug: "kotturinn-sem-atti-milljon-lif-15811", description: "Þetta er saga um kött sem á sér milljón líf. Eigendum hans þykir öllum mjög vænt um hann. Kötturinn er hins vegar ófær um að endurgjalda þeim ást sína — þangað til hann lærir að elska aðra meira en sjálfan sig.\r\nHrífandi klassískt ævintýri eftir japanska verðlaunahöfundinn Yoko Sano sem hefur snert hjörtu barna jafnt sem fullorðinna um víða veröld.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15812, pre_title: "", title: "Múmínsnáðinn og vorundrið", post_title: "", slug: "muminsnadinn-og-vorundrid-15812", description: "Það er vetur í Múmíndal. Múmínfjölskyldan liggur í djúpum vetrardvala og bíður þess að Tikka-Tú veki hana þegar vorar.\r\nEn Múmínsnáðinn hrekkur upp við dularfull hljóð.\r\nTekst honum að herða upp hugann og kanna upp á eigin spýtur hvað um er að vera? Og hvaða undur bíða hans ef hann fer út fyrir hússins dyr?\r\nEin af hinum sívinsælu Múmínbókum sem byggðar eru á sögum Tove Jansson.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15813, pre_title: "", title: "Ég njósna með Múmínsnáðanum", post_title: "Snúðu hjólinu", slug: "eg-njosna-med-muminsnadanum-15813", description: "Múmínsnáðinn njósnar um alls konar hluti á göngu sinni um Múmíndal.\r\n\r\nSnúðu hjólinu á hverri opnu til að koma auga á allt sem hann sér — í garðinum, við ána og á ströndinni.\r\nEin af hinum sívinsælu Múmínbókum fyrir yngstu börnin.", long_description: "", page_count: 10, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15814, pre_title: "", title: "Depill á bókasafninu", post_title: "", slug: "depill-a-bokasafninu-15814", description: "Sögustundin er að byrja á bókasafninu …\r\nEn hvar er Depill? Lyftu flipunum og sjáðu hvort þú finnur hann!\r\nFrábær flipabók fyrir yngstu kynslóðina um hundinn ástsæla, Depil.", long_description: "", page_count: 16, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15815, pre_title: "", title: "Depill, hvaða hljóð er þetta?", post_title: "", slug: "depill-hvada-hljod-er-thetta-15815", description: "Depill, hvaða hljóð er þetta?\r\nHvaðan kemur það?\r\nLyftið flipunum og ýtið á takkana til að taka þátt í tónlistarveislunni.\r\nBók með skemmtilegum tóndæmum fyrir yngstu börnin.", long_description: "", page_count: 10, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15816, pre_title: "", title: "Ég er kórónuveiran!", post_title: "", slug: "eg-er-koronuveiran-15816", description: "Kórónuveiran hoppar á milli fólks.\r\nHvernig forðumst við hana?\r\nSóley litla vill ekki þvo sér um hendurnar. Hvað gerist þá?\r\nOg hvað gerist þegar Sóley loksins þvær sér um hendurnar? Hvernig fer þá fyrir kórónaveirunni?\r\nBráðsnjöll saga með frábærum teikningum fyrir krakka á öllum aldri.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15817, pre_title: "", title: "Ugla eignast vin", post_title: "", slug: "ugla-eignast-vin-15817", description: "Það skiptir ekki máli hvort maður er stór eða lítill, mús eða ugla — allir geta orðið vinir!\r\n\r\nHugljúf og falleg saga um óvænta vináttu.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15818, pre_title: "", title: "Maurildi", post_title: "Hafsfólkið 3", slug: "maurildi-15818", description: "Maurildi breiða sitt bláa skin yfir hafið og ís myndast með ógnarhraða. Djúpt undir sjávarborðinu skipuleggur sjávardísin hefnd sína — og öllum skerjagarðinum er ógnað …\r\nMaurildi er lokabindið í mögnuðum þríleik sem mæðgurnar Camilla og Viveca Sten hafa skrifað um Hafsfólkið. Fyrri bækurnar, >>>Hyldýpið<<< og >>>Sæþokan<<<, fengu frábærar viðtökur íslenskra lesenda.", long_description: "", page_count: 432, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15819, pre_title: "", title: "SKAM 2", post_title: "", slug: "skam-2-15819", description: "Norsku sjónvarpsþættirnir SKAM vöktu gríðarlega athygli og ganga nú í endurnýjun lífdagana í bókarformi.\r\nÞessi bók geymir senur sem aldrei voru teknar upp og samtöl sem voru klippt burt ásamt athugasemdum og hugleiðingum höfundarins, Julie Andem.\r\nÞetta er handritið að annarri þáttaröð SKAM, nákvæmlega eins og það var skrifað.", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15820, pre_title: "", title: "Bold-fjölskyldan fer í sumarfrí", post_title: "", slug: "bold-fjolskyldan-fer-i-sumarfri-15820", description: "Bold-fjölskyldan lítur kannski út fyrir að vera venjuleg fjölskylda, en hún á sér stórt og mikið LEYNDARMÁL...\r\nÞað er sumar og fjölskyldan hefur ákveðið að dveljast við sjávarsíðuna í fríinu sínu. En hún er rétt komin þangað þegar einn fjölskyldumeðlimur hverfur sporlaust. Nú reynir á snilli Bold-fjölskyldunnar því óvinurinn er EKKERT LAMB að leika sér við.\r\nÞriðja bókin um Bold-fjölskylduna eftir breska grínistann Julian Clary – með frábærum teikningum á hverri síðu.", long_description: "", page_count: 320, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15821, pre_title: "", title: "Óskabarn ógæfunnar", post_title: "", slug: "oskabarn-ogaefunnar-15821", description: "Nú eru liðnar næstum sjö vikur frá því að móðir mín dó og mig langar til að hefjast handa áður en þörfin fyrir að skrifa um hana, sem var svo sterk við jarðarförina, breytist aftur í sljólegt málleysi eins og þegar ég las fréttina um sjálfsmorðið ... Á þessum orðum hefst frásögn Nóbelsskáldsins Peters Handke af örlögum móður sinnar. Átakanleg saga sem lætur engan lesanda ósnortinn.", long_description: "", page_count: 106, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15822, pre_title: "", title: "Ótti markmannsins við vítaspyrnu", post_title: "", slug: "otti-markmannsins-vid-vitaspyrnu-15822", description: "Fyrrverandi markmaður telur sig hafa verið rekinn úr vinnunni. Hann fremur morð og ráfar síðan eirðarlaus um. Höfundur nýtir sér form glæpasögunnar en þó með öfugum formerkjum. Örvænting söguhetjunnar endurspeglast í frásagnarmátanum sem er í senn ljóðrænn og harmrænn og ristur rúnum sundrandi heims. Klassískt verk í evrópskum nútímabókmenntum eftir Nóbelsverðlaunahafann 2019.", long_description: "", page_count: 152, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15823, pre_title: "", title: "Gæðakonur", post_title: "", slug: "gaedakonur-15823", description: "Mildred Lathbury tilheyrir hópi einhleypra kvenna sem stundum eru kallaðar gæðakonur. Það er alltaf hægt að leita til þeirra og enginn vandi vex þeim í augum. En þær láta aldrei á sér bera, þær eru gæðakonur.\r\nMeð ísmeygilegri fyndni, nærfærni og snjöllum samtölum dregur Barbara Pym upp ómótstæðilega mynd af mannlífi hversdagsins í þessu hrífandi meistaraverki.", long_description: "", page_count: 314, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15824, pre_title: "", title: "Lila", post_title: "", slug: "lila-15824", description: "Lila er munaðarlaus. Hún er að dauða komin þegar utangarðskonan Doll nemur hana á brott. Við tekur líf á flótta, undir skugga ofbeldis, ógnar og örbirgðar, þar til Lila kemur til smábæjarins Gilead. Í nýjum og öruggum aðstæðum sem eiginkona prests þarf hún að laga reynslu sína að friðsælu lífi en jafnframt dómharðri trú og heimsmynd prestsins.", long_description: "", page_count: 336, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15825, pre_title: "", title: "Sögur frá Sovétríkjunum", post_title: "", slug: "sogur-fra-sovetrikjunum-15825", description: "Tuttugasta öldin var ekki síður merkilegt tímabil í sögu rússneskra bókmennta en sú nítjánda. Í þessa bók hefur Áslaug Agnarsdóttir þýtt nítján ólíkar sögur sem gefa fjölbreytta mynd af sovéskum bókmenntum allt frá byrjun aldarinnar og fram til fyrstu áranna eftir að Sovétríkin liðu undir lok.\r\nHöfundarnir eru allir í fremstu röð rússneskra rithöfunda og lýsa með ágætum daglegu lífi sovéskra borgara, sorgum þeirra og gleði og draumum um betra líf.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15826, pre_title: "", title: "Leiðin í Klukknaríki", post_title: "", slug: "leidin-i-klukknariki-15826", description: "Í miðdepli >>>Leiðarinnar í Klukknaríki<<< er förumaðurinn Bolle og flakk hans um þá Svíþjóð sem iðnvæðingin er að umturna. Með ótrúlegri nákvæmni endurskapar Martinson hverfandi heim en vekur líka spurningar um lífsgildi sem eru jafn brýn nú á tímum og þegar bókin kom fyrst (1948). Eitt af meistaraverkum sænskra bókmennta eftir Nóbelsskáldið Harry Martinson.", long_description: "", page_count: 400, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15827, pre_title: "", title: "Þeir sem græta góðu stúlkurnar", post_title: "", slug: "their-sem-graeta-godu-stulkurnar-15827", description: "Blaðakonan Gina fær tölvupóst frá konu sem lýsir „hræðilegri reynslu“ af því að vinna á stórri sjónvarpsstöð. Stuttu síðar deyr konan í slysi. Fleiri konur gefa sig fram. Þegar ein þeirra deyr óvænt verður Ginu ljóst að ósvífin öfl ætla að koma í veg fyrir að saga kvennanna komist upp á yfirborðið. Æsispennandi metsölubók eftir drottningu spennusagnanna.", long_description: "", page_count: 392, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15828, pre_title: "", title: "Pollýanna", post_title: "Sagan af stelpunni sem kom öllum í gott skap", slug: "pollyanna-15828", description: "Ungfrú Pollý hefur aldrei laðast að börnum. En þegar ellefu ára gömul systurdóttir hennar verður munaðarlaus getur hún ekki skorast undan því að taka hana að sér. Þessi stúlka er Pollýanna. Hún er alltaf glöð hvað sem á dynur. Bjartsýni hennar og gleði smitar út frá sér.\r\nDásamleg saga um það hvernig jákvætt hugarfar getur unnið bug á erfiðleikum lífsins.", long_description: "", page_count: 224, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15829, pre_title: "", title: "Victor Hugo var að deyja", post_title: "", slug: "victor-hugo-var-ad-deyja-15829", description: "Skáldið var að gefa upp andann. Fréttin flýgur um göturnar – inn í búðirnar, verkstæðin, skrifstofurnar. París er gripin hitasótt. Allir vilja votta virðingu og taka þátt í opinberu útförinni. Tvær milljónir manna þjappa  sér meðfram leið líkvagnsins þennan ógleymanlega dag. Mögnuð heimildaskáldsaga eftir höfund bókarinnar Þetta var bróðir minn ...", long_description: "", page_count: 212, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15830, pre_title: "", title: "Dottningin", post_title: "Þrúgandi spenna frá fyrstu síðu!", slug: "dottningin-15830", description: "Á tjaldstæði austur undir Eyjafjöllum hverfur barn af óhugnanlegum morðvettvangi. Lögreglumennirnir Jónas og Addi eru kallaðir til. Rannsókn þeirra er umfangsmikil. Í ljós kemur að við öfl er að eiga sem svífast einskis jafnvel þótt börn eigi í hlut.\r\nÆsispennandi spennusaga um svik, morð og valdatafl í ofbeldisfullum veruleika á Íslandi.", long_description: "", page_count: 252, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15831, pre_title: "", title: "Gervilimrur Gísla Rúnars", post_title: "Sem þú verður að kynna þér áður en þú drepst", slug: "gervilimrur-gisla-runars-15831", description: "Leikarinn ástsæli, Gísli Rúnar Jónsson, var snjall og hugmyndaríkur limrusmiður. Í þessari ótrúlegu bók eru limrur fyrir hvern dag ársins og hver þeirra snilldarlega myndskreytt af Viktoríu Buzukina. – Svona bók hefur aldrei áður komið út á Íslandi!", long_description: "", page_count: 448, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15832, pre_title: "", title: "Vigdís Jack", post_title: "Sveitastelpan sem varð prestsfrú", slug: "vigdis-jack-15832", description: "Eiginkona – mamma – amma – langamma – systir – vinkona – barnakerling – vinnuþjarkur – ævintýramenneskja – Dísa – Munda – Mrs. Jack. \r\nÞetta eru aðeins nokkur af þeim nöfnum sem fylgt hafa Vigdísi Jack á langri ævi – á Akranesi, í Grímsey, Vesturheimi, á Tjörn á Vatsnesi og í Kópavogi.\r\nEinlæg og lifandi frásögn um viðburðaríka ævi á miklum breytingatímum.", long_description: "", page_count: 240, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15833, pre_title: "", title: "Jóhannes Einarsson", post_title: "Minningabrot", slug: "johannes-einarsson-15833", description: "Jóhannes Einarsson verkfræðingur hefur átt fjölbreytta starfsævi, þótt hann sé fyrst og fremst kunnur fyrir að hafa haslað sér völl í alþjóðlegum flugheimi sem einn af framkvæmdastjórum Cargolux.\r\nÍ þessari bók rekur Jóhannes viðburðaríka starfsævi og segir hispurslaust frá því sem á daga hans hefur drifið — ekki síst hörðum átökum í viðskiptalífinu.", long_description: "", page_count: 224, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 76, book_author_id: nil},
  {source_id: 15834, pre_title: "", title: "Rigmarole and Flies", post_title: "", slug: "rigmarole-and-flies-15834", description: "Rigmarole and Flies er fyrsta enska þýðingin á Þulum Theodóru Thoroddsen og Flugum Jóns Thoroddsen. Ármann Jakobsson ritar formála og Christopher Crocker, þýðandi bókarinnar, inngang. Þulur (1916) Theodóru hafa fyrir löngu fest sig í sessi í íslenskum bókmenntum og menningu. Flugur Jóns komu út árið 1922, ekki löngu fyrir ótímabært andlát hans í Kaupmannahöfn, og er fyrsta útgefna ljóðabókin á íslensku í óbundnu máli.", long_description: "", page_count: 59, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 79, book_author_id: nil},
  {source_id: 15835, pre_title: "", title: "Two Lands, One Poet", post_title: "The Reflections of Stephan G. Stephansson Through Poetry", slug: "two-lands-one-poet-15835", description: "Tvímála útgáfa (íslenska og enska) á safni ljóða eftir Stephan G. Stephansson, eitt af lykilskáldum íslenskra og íslensk-kanadískra bókmennta. Enski hluti bókarinnar geymir sögulegt yfirlit yfir glímu þýðenda beggja vegna hafs við ljóð Klettaskáldsins, meðal annars þeirra Bernards Scudders og Jakobínu Johnson. Jón Atli Benediktsson, rektor Háskóla Íslands, ritar formála.", long_description: "", page_count: 296, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 79, book_author_id: nil},
  {source_id: 15836, pre_title: "", title: "Aldinmaukið hefur klárast", post_title: "Hræðileg barnasaga", slug: "aldinmaukid-hefur-klarast-15836", description: "Fjórir táningar og hundur fara saman í útilegu. Dularfull mál svífa yfir vötnum, draugalestir, njósnarar og grunsamlega ríkir bændur. Sagan er kunnugleg — en táningar eru ekki lengur eins og þeir voru eða voru ef til vill aðeins í bókum. \r\nGríma Kamban er enginn nýgræðingur í skáldskap en kemur nú í fyrsta sinn fram undir því heiti. Sérgrein Grímu er paródían.", long_description: "", page_count: 87, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 79, book_author_id: nil},
  {source_id: 15837, pre_title: "", title: "Röntgensól", post_title: "", slug: "rontgensol-15837", description: ">>>Röntgensól<<< er tólfta ljóðabók Kristians Guttesen.\r\n\tHún fjallar um ást, tengsl og rafeindaheila.\r\n\tÁður hefur Kristian gefið út ljóðaúrval og skáldsöguþýðingu en fyrir hana var hann tilnefndur til Íslensku þýðingaverðlaunanna 2007.\r\n\tVerk Kristians hafa m.a. verið þýdd á ensku og úkraínsku.", long_description: "", page_count: 83, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 74, book_author_id: nil},
  {source_id: 15838, pre_title: "", title: "Brjálsemissteinninn brottnuminn", post_title: "Ljóðaúrval", slug: "brjalsemissteinninn-brottnuminn-15838", description: "Alejandra Pizarnik er hálfgerð goðsögn í bókmenntum Rómönsku Ameríku. Lengi vel var hún nær óþekkt nema hjá bókmenntafólki en á síðustu árum hafa ljóð hennar ratað um allan heim og er hún í dag talinn með mikilmetnustu skáldum Argentínu. Þýðingin er mikill fengur fyrir íslenska lesendur.", long_description: "", page_count: 109, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 60, book_author_id: nil},
  {source_id: 15839, pre_title: "", title: "Taugaboð á háspennulínu", post_title: "", slug: "taugabod-a-haspennulinu-15839", description: "Fyrir verkið fékk skáldið Nýræktarstyrk Miðstöðvar íslenskra bókmennta. Ljóðabókin er tvískipt. Fyrri hlutinn segir frá sambandi ómálga barns og eldri manneskju sem er að tapa málinu. Í seinni hlutanum skoðar ljóðmælandi umhverfi sitt og þrár í gegnum vísindalegar skýringar á náttúrinni. \r\nLjóð um tjáningu og einangrun með lifandi myndum og marglaga tengingum sem koma á óvart.", long_description: "", page_count: 54, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 60, book_author_id: nil},
  {source_id: 15840, pre_title: "", title: "Valdið", post_title: "", slug: "valdid-15840", description: "Um allan heim eru konur að uppgötva mátt sinn og megin. Með einfaldri handarhreyfingu geta þær valdið gríðarlegum sársauka – jafnvel drepið. Smám saman átta karlmenn sig á því að þeir eru að missa tökin. Dagur stúlknanna er runninn upp – en hvernig endar hann?\r\n\"Mögnuð!\" - Margaret Atwood", long_description: "", page_count: 428, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15841, pre_title: "", title: "Harry Potter og leyniklefinn", post_title: "", slug: "harry-potter-og-leyniklefinn-15841", description: "Um sumarið var versti afmælisdagur Harrys Potter og hann gat ekki beðið eftir að komast aftur í Hogwartskóla. En svo fær hann viðvaranir frá einkennilegum húsálfi, Dobby, sem segir að fari hann í skólann muni hræðilegir hlutir gerast.\r\nHin stórbrotnu ævintýri Harrys Potter halda áfram.", long_description: "", page_count: 315, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15842, pre_title: "", title: "Vertu ósýnilegur", post_title: "Flóttasaga Ishmaels", slug: "vertu-osynilegur-15842", description: "Vertu ósýnilegur, flóttasaga Ishmaels, vakti gríðarlega athygli þegar hún kom fyrst út 2017. Hún hlaut Barnabókaverðlaun Reykjavíkur, Fjöruverðlaunin og Bókmenntaverðlaun starfsfólks bókaverslana.\r\n\"Skyldulesning fyrir allar manneskjur.\"\r\nSteinunn Inga Óttarsdóttir, Kbl.", long_description: "", page_count: 235, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15843, pre_title: "", title: "Strandir 1918: Ferðalag til fortíðar", post_title: "", slug: "strandir-1918-ferdalag-til-fortidar-15843", description: "Árið 1918 var merkilegt ár í sögu þjóðarinnar. Hér er athygli beint að viðburðum og daglegu lífi á Ströndum á þessum tíma, birtar dagbækur, ferðasögur og fræðigreinar. Fjallað er um líf og störf fólks í Strandasýslu fyrir rúmri öld, á tímum sem standa okkur býsna nærri. Samt er margt við daglegt amstur fólks fyrir 100 árum framandi í samtímanum.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 80, book_author_id: nil},
  {source_id: 15844, pre_title: "", title: "Herbergi í öðrum heimi", post_title: "", slug: "herbergi-i-odrum-heimi-15844", description: "„Hjartað slær óumbeðið og viðstöðulaust frá vöggu til grafar. Ég get ekki annað en vantreyst svo fáránlegum stöðugleika.“\r\n>>>Herbergi í öðrum heimi<<< hefur að geyma sjö smásögur. María Elísabet skrifar af öryggi og innsæi um þrá fólks eftir tengslum og uppgjöri. Með hárfínu skopskyni og hugmyndaauðgi kveður nýr höfundur sér hljóðs á eftirminnanlegan hátt.", long_description: "", page_count: 212, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 60, book_author_id: nil},
  {source_id: 15845, pre_title: "", title: "Sonur grafarans", post_title: "", slug: "sonur-grafarans-15845", description: "Sonur grafarans á margt óuppgert þegar afturgöngurnar í kirkjugarðinum taka til máls. Í myrku andrúmi ljóðanna leynast óvæntar myndir, leikandi húmor og snjallar persónugervingar. Brynjólfur Þorsteinsson vakti verðskuldaða athygli og fékk góða dóma fyrir fyrstu ljóðabókina sína. Hann fylgir henni eftir með ljóðabálk um eftirlífið og drauga.", long_description: "", page_count: 74, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 60, book_author_id: nil},
  {source_id: 15847, pre_title: "", title: "Les birki", post_title: "", slug: "les-birki-15847", description: ">>>Les birki<<< er fyrsta ljóðabók Kari sem áður hefur sent frá sér leikritið >>>Karma fyrir fugla<<< ásamt Kristínu Eiríksdóttur.\r\n\r\n>>>þegar gagnkrafturinn togar í þig / þá er röddin reipi / og í byrjun sumars / lesum við birki / fyllum koddaverin / safaríkum laufum<<<", long_description: "", page_count: 48, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 50, book_author_id: nil},
  {source_id: 15848, pre_title: "", title: "Dúna", post_title: "", slug: "duna-15848", description: ">>>Dúna<<< er mest selda vísindaskáldsaga allra tíma. Sagan gerist á eyðimerkurplánetunni Arrakis þar sem Páll Atreides og fjölskylda hans hafa verið svikin og örlagaríkri atburðarás hrint af stað. >>>Dúna<<< er epísk blanda af ævintýri, dulspeki, umhverfishyggju og pólitík.", long_description: "", page_count: 700, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 50, book_author_id: nil},
  {source_id: 15854, pre_title: "", title: "Fullkomin jólagjöf", post_title: "", slug: "fullkomin-jolagjof-15854", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15855, pre_title: "", title: "Flöskuskeytið", post_title: "", slug: "floskuskeytid-15855", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15856, pre_title: "", title: "Skrautleg skrúðganga", post_title: "", slug: "skrautleg-skrudganga-15856", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15857, pre_title: "", title: "Einstakur hópur", post_title: "", slug: "einstakur-hopur-15857", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15858, pre_title: "", title: "Nýr vinur", post_title: "", slug: "nyr-vinur-15858", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15859, pre_title: "", title: "Partýpúkar", post_title: "", slug: "partypukar-15859", description: "", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15860, pre_title: "", title: "Púkar á flækingi", post_title: "", slug: "pukar-a-flaekingi-15860", description: "", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15861, pre_title: "", title: "Bók 1", post_title: "", slug: "bok-1-15861", description: "", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15862, pre_title: "", title: "Bók 2", post_title: "", slug: "bok-2-15862", description: "", long_description: "", page_count: 96, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15863, pre_title: "", title: "Sígildar sögur", post_title: "", slug: "sigildar-sogur-15863", description: "Sögusafn fjölskyldunnar! \r\nTímalausar sögur Disney lifa áfram í þessu heillandi sögusafni sem myndskreytt er með fallegum klassískum teikningum. Bók sem gleður alla fjölskylduna.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 82, book_author_id: nil},
  {source_id: 15865, pre_title: "Mína og Sína Spæjarapæjur", title: "Klækjabrögð", post_title: "", slug: "klaekjabrogd-15865", description: "Mína og Sína lenda í æsilegum eltingarleikjum, skugglegum þjófum, hættulegum njósnaferðum, úthugsuðum klækjabrögðum, átökum við alþjóðleg glæpasamtök og störfum fyrir leyniþjónustu!", long_description: "", page_count: 222, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 82, book_author_id: nil},
  {source_id: 15866, pre_title: "", title: "Jólasyrpa 2020", post_title: "", slug: "jolasyrpa-2020-15866", description: "Jólin hefjast með Jólasyrpunni! Fjörug lesning sem kemur öllum í hátíðarskap!", long_description: "", page_count: 256, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 82, book_author_id: nil},
  {source_id: 15867, pre_title: "", title: "Tommi Klúður", post_title: "Mistök voru gerð", slug: "tommi-kludur-15867", description: "Tommi rekur einkaspæjarafyrirtækið >>>Algert Klúður<<< með viðskiptafélaga sínum. Hann er, að eigin sögn, stofnandi, forstjo&#769;ri og framkvæmdastjo&#769;ri bestu spæjarastofu i&#769; bænum - já sennilega a&#769; o&#776;llu landinu!", long_description: "", page_count: 320, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 82, book_author_id: nil},
  {source_id: 15868, pre_title: "", title: "Bakað", post_title: "með Elenoru Rós", slug: "bakad-15868", description: "Eldmóður Elenoru og ást hennar á bakstri skín í gegn í þessari einstöku bók sem inniheldur aragrúa ómótstæðilegra uppskrifta; allt frá einföldum súrdeigsbakstri, frönsku fínheitabakkelsi, berlínarbollum, súkkulaðivafningum, kleinuum, brauðréttum og samlokum sem slá í gegn.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 82, book_author_id: nil},
  {source_id: 15869, pre_title: "", title: "Týnda hreindýrið", post_title: "", slug: "tynda-hreindyrid-15869", description: "", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15870, pre_title: "", title: "Landsnefndin fyrri. Den islandske Landkommission 1770–1771, V", post_title: "", slug: "landsnefndin-fyrri-den-islandske-landkommission-1770-1771-v-15870", description: "Skjöl Landsnefndarinnar fyrri gefa einstæða innsýn í íslenskt samfélag um 1770, en nefndin ferðaðist um Ísland og safnaði upplýsingum um land og þjóð. Í þessu fimmta bindi af sex eru birt skjöl Landsnefndarinnar sjálfrar, embættisbækur, fundargerðir, greinargerðir og tillögur að tilskipunum.", long_description: "", page_count: 780, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 83, book_author_id: nil},
  {source_id: 15871, pre_title: "", title: "Jólaþrautir", post_title: "", slug: "jolathrautir-15871", description: "", long_description: "", page_count: 26, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15872, pre_title: "", title: "Jólalitabók", post_title: "", slug: "jolalitabok-15872", description: "", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15873, pre_title: "", title: "Jólafingrafjör", post_title: "", slug: "jolafingrafjor-15873", description: "", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15874, pre_title: "", title: "Jólalímmiðabók", post_title: "", slug: "jolalimmidabok-15874", description: "", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 81, book_author_id: nil},
  {source_id: 15875, pre_title: "", title: "The Little Book of the Icelanders at Christmas", post_title: "", slug: "the-little-book-of-the-icelanders-at-christmas-15875", description: "Í þessari myndskreyttu bók, sem er hluti af hinni vinsælu “Little Books” bókaröð, lýsir Alda Sigmundsdóttir í léttum og skemmtilegum dúr jólavenjum okkar Íslendinga frá byrjun Aðventunnar til lok Þrettándans. Smákökubakstur, jólahlaðborð, skötuveislur, jólasveinar, Gamlárskvöld, flugeldar og margt fleira. Tilvalin gjöf fyrir vini eða ættingja erlendis, eða erlenda vini hérlendis.", long_description: "", page_count: 136, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 84, book_author_id: nil},
  {source_id: 15876, pre_title: "", title: "The Little Book of Days in Iceland", post_title: "", slug: "the-little-book-of-days-in-iceland-15876", description: "Í þessari bók, sem er hluti af hinni vinsælu og rómuðu “Little Books” bókaröð, eru hátíðisdagar okkar Íslendinga og aðrir árstíðabundnir siðir til umfjöllunar í máli og myndum. Bókin er jafnframt dagatalsbók sem hægt er að nota sem stutta dagbók, til áminningar og fleira. Tilvalin gjöf fyrir vini eða ættingja erlendis, eða erlenda vini hérlendis.", long_description: "", page_count: 144, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 84, book_author_id: nil},
  {source_id: 15877, pre_title: "", title: "Á ferð og flugi með ömmu í Akrafjalli", post_title: "", slug: "a-ferd-og-flugi-med-ommu-i-akrafjalli-15877", description: "Amma gengur á Akrafjall með Smára og fræðir hann um örnefni og leyndardóma fjallsins. Á göngunni vakna spurningar. Hver var Geirmundur og hver var Guðfinna? Búa jólasveinar í fjallinu, skessur eða útilegumaður? Þessum spurningum og mörgum fleiri velta amma og Smári fyrir sér á göngu sinni. Við sumum fæst svar, öðrum ekki.\r\nFræðandi bók fyrir fólk á öllum aldri með frábærum myndum eftir Bjarna Þór.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 85, book_author_id: nil},
  {source_id: 15878, pre_title: "", title: "Stúlkan undir trénu", post_title: "", slug: "stulkan-undir-trenu-15878", description: "Í klettasprungu á Borgundarhólmi finnst lík unglingsstúlkunnar Susan Dahlgaard, sem hvarf sporlaust úr skólabúðum á eyjunni fyrir aldarfjórðungi. \r\nSara Blædel er einn vinsælasti glæpasagnahöfundur samtímans. Bækur hennar um Louise Rick hafa notið gríðarlegra vinsælda um allan heim.", long_description: "", page_count: 324, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 31, book_author_id: nil},
  {source_id: 15879, pre_title: "", title: "Heimskringla", post_title: "", slug: "heimskringla-15879", description: "Heimskringla kom út á Storytel á árinu í mögnuðum lestri Arnars Jónssonar. Heimskringla Snorra Sturlusonar er meðal helstu dýrgripa bókmenntasögu þjóðarinnar. Agað og vel samið verk sem geymir alls 16 sögur, þeirra á meðal Ólafs sögu helga og Ólafs sögu Tryggvasonar\r\nNjóttu þess að hlusta og lesa á Storytel Reader lesbrettinu.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15880, pre_title: "", title: "Fjötrar", post_title: "", slug: "fjotrar-15880", description: "Kona finnst látin í klefa sínum í fangelsinu á Hólmsheiði. Guðgeir og félagar hans rannsaka málið sem reynist hafa ótal þræði og teygja anga sína víða, alla leið til stóra skjálftans um aldamótin þar sem ungur maður hvarf sporlaust. \r\nBókin hlaut Blóðdropann 2020, verðlaun sem Hið íslenska glæpafélag veitir árlega fyrir bestu íslensku glæpasöguna.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15881, pre_title: "", title: "Uglan drepur bara á nóttunni", post_title: "", slug: "uglan-drepur-bara-a-nottunni-15881", description: "Unglingsstúlka sem hefur strokið frá upptökuheimili finnst látin úti í skógi; nakin, umkringd fjöðrum og með hvíta lilju í munni. Engu er líkara en hún hafi verið myrt í trúarlegri athöfn. Holger Munch, lögregluforingi er kallaður út úr barnaafmæli á staðinn og sér strax að til að leysa þessa gátu þarf hann að kalla saman sitt gamla rannsóknateymi.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15882, pre_title: "", title: "Blóðhefnd", post_title: "", slug: "blodhefnd-15882", description: "Fimmta bókin í seríunni um Kim Stone. Hörkuspennandi glæpasaga sem ómögulegt er að hætta að hlusta á fyrr en eftir að hin óvæntu endalok eru komin í ljós.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15883, pre_title: "", title: "Kalak", post_title: "", slug: "kalak-15883", description: "Sagan segir frá ungum manni sem alinn er upp í samfélagi Votta Jehóva í Noregi þar sem hinn drottnandi og refsandi Guð er alltumlykjandi. Tákn frelsisins er samkynhneigður faðir drengsins sem hefur yfirgefið fjölskylduna og haldið til Kaupmannahafnar. Sextán ára hefur Kim fengið nóg og strýkur til föður síns, en fer þar úr öskunni í eldinn.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15884, pre_title: "", title: "Faroese", post_title: "An Overview and Reference Grammar", slug: "faroese-15884", description: "Bókin kemur nú út í nýrri prentun. Hún kom fyrst út árið 2004 en er löngu orðin sígild sem ýtarlegasta handbók um færeyska málfræði sem komið hefur út. Bókin fjallar um ólík svið færeyskrar málfræði en einnig um málsögu, stafsetningu og málstefnu. Þá er ýtarleg lýsing á færeyskum mállýskum í bókinni.", long_description: "", page_count: 508, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15885, pre_title: "", title: "Kólibrímorðin", post_title: "", slug: "kolibrimordin-15885", description: "Fórnarlamb morðs er ung kona, í vasa hennar finnst hálsmen með mynd af Astekaguði. Annað morð: sama umhverfi, sams konar skartgripur. Hvernig velur morðinginn fórnarlömb sín? Tengjast morðin leynilegum trúarsöfnuði? Hver verður næsta fórnarlamb? Kati Hiekkapelto hefur slegið í gegn með bókum sínum. Elva Ósk les þennan magnþrungna spennutrylli.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15886, pre_title: "", title: "Ég fremur en þú & Eftir að þú fórst", post_title: "", slug: "eg-fremur-en-thu-eftir-ad-thu-forst-15886", description: "Hugljúfar og harmrænar ástarsögur sem farið hafa sannkallaða sigurför um heiminn og hafa nú verið kvikmyndaðar í Hollywood. Komdu þér vel fyrir og hlustaðu á þessar tvær í frábærum flutningi Álfrúnar Helgu Örnólfsdóttur.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15887, pre_title: "", title: "Orri óstöðvandi & Orri óstöðvandi - Hefnd glæponanna", post_title: "", slug: "orri-ostodvandi-orri-ostodvandi-hefnd-glaeponanna-15887", description: "Orri óstöðvandi er ofurhetjuútgáfan af Orra sem hann breytir sér í þegar hann þarf á hugrekki og sjálfstrausti að halda. Bækurnar eru fyndnar, skemmtilegar og sjálfstyrkjandi fyrir krakka á aldrinum 7 til 12 ára. Vignir Rafn glæðir sögurnar lífi.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15888, pre_title: "", title: "Dagbækur Berts – sería", post_title: "", slug: "dagbaekur-berts-seria-15888", description: "Leynileg ást, rokktónlist, áhyggjur, tilfinningar, klúður og vinátta - Bert er engum líkur! Bert-bækurnar eiga sér marga trygga aðdáendur á Íslandi og lifna nú við á ný í frábærum lestri Árna Beinteins Árnasonar.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15889, pre_title: "", title: "Hvítidauði", post_title: "", slug: "hvitidaudi-15889", description: "Árið 1983 deyja tveir starfsmenn á berklahæli skammt frá Akureyri og er ljóst að andlát þeirra bar ekki að með eðlilegum hætti. Ungur afbrotafræðingur vinnur að lokaritgerð um þetta undarlega mál árið 2012 og kemur þá ýmislegt óvænt í ljós.\r\nNjóttu þess að hlusta og lesa á Storytel Reader lesbrettinu.\r\nÚtgefandi Ragnar Jónasson – Aðeins á Storytel.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15892, pre_title: "", title: "Villinorn 5. Fjandablóð", post_title: "", slug: "villinorn-5-fjandablod-15892", description: "", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, book_author_id: nil},
  {source_id: 15893, pre_title: "", title: "Villinorn 4. Blóðkindin", post_title: "", slug: "villinorn-4-blodkindin-15893", description: "", long_description: "", page_count: 182, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, book_author_id: nil},
  {source_id: 15894, pre_title: "", title: "Heimaslátrun", post_title: "", slug: "heimaslatrun-15894", description: ">>>Heimaslátrun<<< er fjórða ljóðabók Davíðs og sú fyrsta í 17 ár. Bókin hefst á samnefndum ljóðabálki um samfélag og slátrunaraðferðir, en í síðari hlutanum eru gömul ljóð og ný undir heitinu >>>Vögguvísur og krepptir hnefar.<<<\r\n\r\n>>>ég hugsa og það er hættulegt<<<\r\n>>>ég borða og hlæ til að þurfa ekki að hugsa<<<\r\n>>>til að geta borðað holdið<<<\r\n>>>kálfshöfuð er ansi þungt<<<\r\n\r\n>>>í maga<<<", long_description: "", page_count: 98, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 86, book_author_id: nil},
  {source_id: 15895, pre_title: "", title: "5 mínútna ævintýri", post_title: "", slug: "5-minutna-aevintyri-15895", description: "Einu sinni var ... þannig hefjast mörg þeirra sígildu ævintýra sem við þekkjum og elskum. Í þessari vönduðu bók er að finna 32 ævintýri sem eru endursögð sem 5 mínútna sögur og hugsaðar sem gæðastund fyrir svefninn.", long_description: "", page_count: 196, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 1, book_author_id: nil},
  {source_id: 15896, pre_title: "", title: "Rannsóknir í heimspeki", post_title: "", slug: "rannsoknir-i-heimspeki-15896", description: "Rannsóknir í heimspeki er talið eitt allra merkasta heimspekirit tuttugustu aldar. Ritið kom fyrst út árið 1953 að Ludwig Wittgenstein, höfundi þess, látnum. Wittgenstein var ástríðufullur hugsuður gæddur miklum persónutöfrum en var mörgum samtíðarmönnum hálfgerð ráðgáta. Djúpstæð greining hans á tungumálinu og tengslum þess við mannshugann og umheiminn er leiðarstefið í þessu höfuðriti hans.", long_description: "", page_count: 260, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15897, pre_title: "", title: "Sögur Belkíns", post_title: "Stofnun Vigdísar Finnbogadóttur", slug: "sogur-belkins-15897", description: ">>>Sögur Belkíns<<< er fyrsta prósaverkið sem Aleksander Púshkín lauk við. Brugðið er á leik með þekkt stef, s.s. rómantíska hetju, draugasöguna, hugljúfar ástir og óvænt endalok. Sögurnar eru fullar lífsgleði, glettni og umhyggju fyrir sögupersónum og höfðu sumar ófyrirséð áhrif á rússneskar bókmenntir. Inngangur um verkið og þýðingasögu sagnanna fylgir þýðingunni.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 34, book_author_id: nil},
  {source_id: 15898, pre_title: "", title: "Sjálfsát", post_title: "Að éta sjálfan sig", slug: "sjalfsat-15898", description: ">>>Sjálfsát<<< er nýjasta smásögubókin frá venesúelíska-íslenska höfundinum Helen Cova. Hér blandast saman töfraraunsæi og myrk ævintýri í hverri smásögunni á fætur annarri sem færir íslensku firðina, myrkur íslensku vetranna og karabíska andrúmsloftið á einn og sama staðinn. Þetta er bók sem þú munt ekki geta lagt frá þér.\r\n>>>„Vellandi af sköpun, gáskamyrk og innspírerandi! Ég gat ekki hætt að vona að höfundurinn væri dóttir mín.“<<<\r\n   Steinar Bragi. \r\n\r\nÞessi bók er einnig fáanleg í búðum á ensku undir nafninu >>>Autosarcophagy<<<", long_description: "", page_count: 88, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 87, book_author_id: nil},
  {source_id: 15899, pre_title: "", title: "Dauðabókin", post_title: "", slug: "daudabokin-15899", description: "Ný bók um hinn sívinsæla leynilögreglumann Hörð Grímsson. Ung stúlka deyr í sumarpartíi í Borgarnesi og nokkru síðar er piltur myrtur við Rauðhóla. Í framhaldi fá vinir hins myrta torkennilegar vinabeiðnir á samfélagsmiðlum sem leiða þá að síðu sem kallast Dauðabókin. Hver stendur að baki síðunni og hvað vill Dauðabókin unga fólkinu?", long_description: "", page_count: 316, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15901, pre_title: "", title: "Íslenskir matþörungar", post_title: "Ofurfæða úr fjörunni", slug: "islenskir-matthorungar-15901", description: "Íslenskir matþörungar opnar okkur heim ofurfæðunnar úr fjörunni. Í fyrsta skipti birtist í íslenskri bók alhliða fróðleikur um undraheim matþörunga. Hér lærum við allt um sjálfbæra nýtingu, verkun og geymsluaðferðir auk þess sem bókin inniheldur fjölda girnilegra mataruppskrifta þar sem einn fremsti matreiðslumeistari þjóðarinnar galdrar réttina fram.", long_description: "", page_count: 262, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15902, pre_title: "", title: "Sundkýrin Sæunn", post_title: "", slug: "sundkyrin-saeunn-15902", description: "Sönn saga af eindæma hetju prýdd dásamlegum teikningum. Það er daglegt brauð að dýr lendi í hættu og oft bjarga þau sér á ótrúlegan hátt, en söguleg hetjudáð vestfirsku kýrinnar sem leiða átti til slátrunar á Flateyri er einstök. Þetta er saga af einni fræknustu dýrabjörgun Íslands sem allir krakkar ættu að lesa.", long_description: "", page_count: 42, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15903, pre_title: "Fólkið í blokkinni", title: "Gleðiloft og glópalán", post_title: "", slug: "glediloft-og-glopalan-15903", description: "Fólkið í blokkinni sló í gegn þegar Ólafur Haukur birti okkur fyrst hið makalausa og hrífandi mannlífssafn. Hér eru komnar glænýjar og spennandi sögur af fólkinu í blokkinni. Það skiptast á skin og skúrir eins og vera ber, en ef við hjálpumst öll að og hlæjum dálítið saman gengur allt betur. Ekki satt?", long_description: "", page_count: 140, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15904, pre_title: "", title: "Silfurvængir", post_title: "", slug: "silfurvaengir-15904", description: "Áhrifamikil saga um svik, upprisu og hefnd frá drottningu glæpasagnanna. Spennuflokkurinn um Faye hófst með metsölubókinni Gullbúrinu árið 2019 og nú er komið æsispennandi sjálfstætt framhald. Allt leikur í lyndi hjá Faye, sem hefur hafið nýtt líf erlendis. En á augabragði hrannast óveðursskýin upp yfir tilveru hennar.", long_description: "", page_count: 316, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15905, pre_title: "", title: "UNA prjónabók", post_title: "hlýtt og mjúkt fyrir börn, fullorðna og hunda", slug: "una-prjonabok-15905", description: "Þessi undurfagra prjónabók er samstarfsverkefni vinkvennanna Sjafnar og Sölku Sólar. Sjöfn hefur prjónað allt sitt líf en Salka tók fyrst upp prjónana fyrir um ári. Þær ákváðu að prjóna saman eina flík, sem varð að heilli línu – sem varð svo að þessari fallegu bók. Einkar hentug fyrir algjöra byrjendur en reynsluprjónarar verða ekki sviknir, ónei!", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15906, pre_title: "", title: "Í faðmi ljónsins", post_title: "ástarsaga", slug: "i-fadmi-ljonsins-15906", description: "Fótboltabók fyrir sófasérfræðinga. Enska knattspyrnan er þjóðaríþrótt okkar Íslendinga. Við gleðjumst og þjáumst yfir ensku knattspyrnunni eins og hún væri okkar eigin og það þykir jafn sjálfsagt að íslensk börn velji sér lið á Englandi og að þau gangi í skóla. En hvernig hófst þessi ástarsaga? Hvers vegna lentum við í faðmi ljónsins?", long_description: "", page_count: 245, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15907, pre_title: "Randver kjaftar frá", title: "Geggjað ævintýri", post_title: "", slug: "geggjad-aevintyri-15907", description: "Ný stórkostleg ævintýrabók eftir hinn hugrakka Randver, besta vin Kidda klaufa. Lestu um Rolla og vin hans, vöðvatröllið Knúsa, sem leggja í mikið ævintýri til að bjarga mömmu Rolla, sem Hvíti stríðsmaðurinn ógurlegi er búinn að ræna. Koma þeir lifandi til baka?", long_description: "", page_count: 218, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15908, pre_title: "", title: "Fuglaflipp", post_title: "", slug: "fuglaflipp-15908", description: "Nú reynir á sköpunargáfuna. Flettu og flippaðu haus, búk og stéli tíu frábærra fugla og gerðu þínar eigin þúsund nýju fuglategundir. Hvernig væri að búa til kólibrí-snæuglu-strút? Eða pelíkana-kakadúa-skallaörn? Með skemmtilegum staðreyndum á hverri síðu muntu kynnast þínum flippuðu og fiðruðu vinum enn betur.", long_description: "", page_count: 12, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15909, pre_title: "", title: "Krakkalögin okkar", post_title: "", slug: "krakkalogin-okkar-15909", description: "Hér er hún komin Krakkalögin okkar sem hefur að geyma tuttugu af vinsælustu lögum barnanna, skemmtileg, nýleg lög ásamt gömlum og sígildum gullmolum sem krakkarnir vilja syngja. Tónbækurnar okkar með fallegum undirleik Jóns Ólafssonar og dásamlegum myndskreytingum Úlfs Logasonar hafa fengið lofsamlegar viðtökur hjá börnunum á undanförnum árum.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15910, pre_title: "", title: "Hellirinn", post_title: "blóð, vopn og fussum fei", slug: "hellirinn-15910", description: "Afi Jaki leiðir Ástu og Kötu á vit ævintýranna, í æsispennandi langferð sem reynist mikil háskaför. Á ferðalaginu eignast systurnar nýja vini og saman lenda þau í ýmsum hættum þar sem töfrakúlur, skessuhor og sverð koma meðal annars við sögu. Bráðskemmtileg bók sem kemur sífellt á óvart.", long_description: "", page_count: 154, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15911, pre_title: "", title: "Yeats", post_title: "", slug: "yeats-15911", description: "Írska ljóðskáldið William Butler Yeats hlaut Nóbelsverðlaunin í bókmenntum árið 1923, þá 58 ára að aldri. Enn þann dag í dag er hann talinn einn þeirra fremstu í ljóðlistinni. Hér birtast á einni bók nokkur af frægustu ljóðum hans í nýjum íslenskum þýðingum Sölva Björns Sigurðssonar, sem hlaut Hin íslensku bókmenntaverðlaun fyrir skáldsöguna Seltu árið 2020. Sölvi Björn ritar jafnframt eftirmála.", long_description: "", page_count: 66, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15912, pre_title: "", title: "Hamir / Sheaths", post_title: "", slug: "hamir-sheaths-15912", description: "Í þessari vönduðu listaverkabók eru litprentanir af 32 teikningum og vatnslitamyndum ásamt ljóðrænum textum. Í samleik mynda og texta verður til frásögn og íhugun um hami sem virðast sífellt renna manni úr greipum, rétt eins og tíminn. Ljóðatextar Önnu Jóa birtast einnig í enskri þýðingu Söruh Brownsberger og eftirmáli Jóhannesar Dagssonar er á báðum málum. Bókin er gefin út í takmörkuðu upplagi og hægt að fá hana áritaða af listamanninum.", long_description: "", page_count: 70, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 89, book_author_id: nil},
  {source_id: 15913, pre_title: "", title: "Íslensk knattspyrna 2020", post_title: "", slug: "islensk-knattspyrna-2020-15913", description: "Ómissandi bók í safn alls knattspyrnuáhugafólks. Allt frá árinu 1981 hefur Víðir Sigurðsson haldið úti gríðarlegri heimildavinnu um íslenska knattspyrnu. Sú vinna hefur nú skilað sér í 40 bókum á 40 árum og er ljóst að þetta stórkostlega afrek Víðis á sér enga hliðstæðu. Hér má finna allt um íslenska knattspyrnu árið 2020, auk fjölda glæsilegra ljósmynda.", long_description: "", page_count: 272, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15914, pre_title: "", title: "Höfuðbók", post_title: "", slug: "hofudbok-15914", description: "Þegar þrenndartaug í höfði Ólafs Hauks Símonarsonar breytir daglegu lífi hans í hreint helvíti neitar hann að lýsa sig sigraðan. Höfuðbók segir frá raunverulegu fólki, lifandi og látnu, en einnig birtast skáldaðar og skemmtilegar persónur til að afvirkja sársauka og dauðaangist. Lesandi er vís til að hlæja og gráta á víxl við lesturinn.", long_description: "", page_count: 268, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15915, pre_title: "", title: "Draumaland", post_title: "Frá fæðingu til sex ára aldurs", slug: "draumaland-15915", description: "Stækkuð útgáfa hins geysivinsæla kjölfesturits höfundar um svefn og svefnvenjur 0-2 ára sem kom út á Íslandi 2006 og hefur verið þýdd á fjölmörg tungumál. Arna, sem er sérfræðingur á Barnaspítala Hringsins, skoðar heim barna í ljósi svefnsins og fjallar um svefnvenjur, lundarfar, þroska, líðan barna og foreldrahlutverk. Hagnýt og fræðandi bók.", long_description: "", page_count: 320, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15916, pre_title: "", title: "Tíu skref", post_title: "í átt að innihaldsríku lífi", slug: "tiu-skref-15916", description: "Fyrir nokkrum árum ákvað knattspyrnumaðurinn og sálfræðineminn Bergsveinn Ólafsson að setjast niður og kortleggja á tveimur vikum hvað einkenndi innihaldsríkt líf. Ekki leið á löngu uns hann áttaði sig á að þetta yrði mögulega stærsta verkefni hans í lífinu. „Skyldulesning fyrir fólk á öllum þroskastigum lífsins.“ Inga Dóra Sigfúsdóttir, prófessor.", long_description: "", page_count: 214, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15917, pre_title: "", title: "Fósturmissir", post_title: "ein af hverjum þremur", slug: "fosturmissir-15917", description: "Fósturmissir er oft sorgleg reynsla og finna mörg sem í gegnum hana ganga þörf fyrir að afla sér upplýsinga um allt sem missinum tengist. Hverjar eru líkurnar á fósturmissi, hverjar eru helstu orsakir og hvað tekur svo við? Bókin inniheldur fjölmargar reynslusögur auk þess sem höfundar fjalla um allt sem varpað getur ljósi á þessa erfiðu upplifun.", long_description: "", page_count: 224, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15918, pre_title: "Dagbók Kidda klaufa", title: "Snjóstríðið", post_title: "", slug: "snjostridid-15918", description: "Hér kemur 13. bókin um Kidda klaufa og vini hans, en bækurnar hafa verið einn allra vinsælasti bókaflokkurinn á Íslandi og víða um heim í nokkur ár. Nú er mikill og kaldur vetur og ekkert er gefið eftir í stríðinu sem skellur á í hverfinu þeirra Kidda og Randvers. Barist verður þar til allar snjókúlurnar klárast!", long_description: "", page_count: 218, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15919, pre_title: "Leyndarmál Lindu", title: "Sögur af ekki-svo flottri sjónvarpsstjörnu", post_title: "", slug: "sogur-af-ekki-svo-flottri-sjonvarpsstjornu-15919", description: "Bókaflokkurinn um Lindu hefur sannarlega slegið í gegn á alþjóðavísu. Allar stelpur þekkja Lindu og bestu vinkonur hennar, þær Bínu og Stínu, að við tölum ekki um helsta óvin hennar; Hildi Hermundar. Í þessari bók gerist Linda sjónvarpsstjarna og ekki eru allir ánægðir með það.", long_description: "", page_count: 342, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15920, pre_title: "Dagbók Kidda klaufa", title: "Flóttinn í sólina", post_title: "", slug: "flottinn-i-solina-15920", description: "Hinn geysivinsæli Kiddi og fjölskylda hans ákveða að flýja kuldann og stressið og fara til suðrænni slóða þar sem hægt er að slaka á og njóta lífsins á stuttbuxum eða sundfötum. En þau komast fljótt að því að lífið þar getur verið hættulegt, sérstaklega ef þú mætir eitruðum skepnum og brennur í sólinni. Kiddi hefur aldrei lent í öðrum eins.", long_description: "", page_count: 218, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15921, pre_title: "", title: "Liverpool", post_title: "Flottasti klúbbur í heimi", slug: "liverpool-15921", description: "Liverpool hafði ekki unnið meistaratitilinn í 30 ár. Aðdáendur voru orðnir vonlitlir en þá kom Þjóðverjinn Jürgen Klopp til skjalanna og setti á nokkrum árum saman eitt öflugasta fótboltalið sögunnar. Hér segir frá öllum stjörnunum sem tóku þátt í hinni mögnuðu sigurgöngu og jafnframt gömlum hetjum. Liverpool er sannarlega „meira en bara fótboltalið“.", long_description: "", page_count: 64, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15922, pre_title: "", title: "Ketóflex 3-3-1 mataræðið", post_title: "", slug: "ketoflex-3-3-1-mataraedid-15922", description: "Ketóflex er fullkomin og frelsandi leið til að ná kjörþyngd og yngja sig upp. Með henni nýtur þú kosta ketó-mataræðis enn betur því ketóflex byggist á sveigjanleika (3 dagar á ketó, 3 dagar í hleðslu og 1 frjáls dagur). Endurheimtu orkuna, jafnvægið og gleðina með ketóflex, lífsstílnum sem allir geta ráðið við án eftirsjár eftir gömlum siðum.", long_description: "", page_count: 250, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15923, pre_title: "", title: "Mörgæs með brostið hjarta", post_title: "ástarsaga", slug: "morgaes-med-brostid-hjarta-15923", description: "Óframfærinn rithöfundur vopnaður gamaldags ritvél berst við handrit að fyrstu skáldsögu sinni, á milli þess sem hann langar í meira kaffi. Mörgæs með mannlegar tilfinningar, ástfangin en um leið full af kvíða, leitar að tilgangi lífsins. Ferðalög um hverfandi ísbreiður og hvítar auðar blaðsíður leiða þau saman. Ástin á tímum hamfarahlýnunar.", long_description: "", page_count: 128, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15924, pre_title: "", title: "Marsfjólurnar", post_title: "", slug: "marsfjolurnar-15924", description: "Fyrsta bókin um einkaspæjarann Bernie Gunther. Mikilvæg skáldsaga sem notið hefur fádæma vinsælda frá því að hún kom út í lok níunda áratugarins. Æsispennandi og ófyrirsjáanleg fram á síðustu síðu en um leið fádæma breið þjóðfélagslýsing og lærdómsrík rússibanareið um samfélag sem stefnir hraðbyri inn í alræði og ógnarstjórn Adolfs Hitler.", long_description: "", page_count: 310, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15925, pre_title: "", title: "Ógnarhiti", post_title: "", slug: "ognarhiti-15925", description: "Ástralskur spennutryllir sem varð New York Times metsölubók. Lögreglumaðurinn Aaron Falk hefur ekki komið á heimaslóðir árum saman. Það er ekki fyrr en hann fær fréttir af því að æskuvinur hans hafi myrt fjölskyldu sína og síðan svipt sig lífi að Falk snýr aftur til að vera viðstaddur útförina. Í ólgandi hitabylgju ákveður Falk að rannsaka málið.", long_description: "", page_count: 350, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15926, pre_title: "", title: "Það sem fönnin felur", post_title: "", slug: "thad-sem-fonnin-felur-15926", description: "Sálfræðitryllir sem kemur sífellt á óvart. Sagan er ólík öllu öðru sem við höfum áður lesið. Hvað er það sem við sjáum ekki? Hver segir söguna og hvers vegna? Miskunnarlaust er villt um fyrir lesandanum, allt þar til síðasti bitinn í púsluspilinu fellur á sinn stað og allt blasir við.", long_description: "", page_count: 368, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15927, pre_title: "", title: "Hrein karfa", post_title: "", slug: "hrein-karfa-15927", description: "Lifandi og skemmtileg bók, stútfull af myndum, fyrir allt áhugafólk um körfubolta. NBA-deildinni eru gerð góð skil sem og íslenskum körfubolta. Ungir iðkendur fá ráð frá besta körfuboltafólki þjóðarinnar fyrr og síðar. Kjartan Atli er umsjónarmaður Domino's körfuboltakvölds sem fékk Edduverðlaunin sem sjónvarpsefni ársins 2019.", long_description: "", page_count: 160, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15929, pre_title: "", title: "Ofur-kalli og bakteríuskrímslið", post_title: "", slug: "ofur-kalli-og-bakteriuskrimslid-15929", description: "Camilla Läckberg er ekki aðeins einhver allra vinsælasti glæpasagnahöfundur heims heldur skrifar hún líka vinsælar bráðskemmtilegar barnabækur um sterkasta smábarn í heimi sem leysir erfið glæpamál eins og að drekka pelamjólk. Nú birtast ósýnilegir fjandmenn heima hjá Kalla og fjölskyldu hans! Ofur-Kalli þarf að grípa til nýrra ráða.", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15930, pre_title: "", title: "Þegar heimurinn lokaðist", post_title: "Petsamo-ferð Íslendinga 1940", slug: "thegar-heimurinn-lokadist-15930", description: "Árið er 1940 og heimurinn er vígvöllur. Víða um lönd eru Íslendingar sem þrá að sigla heim en komast hvergi. Loks samþykkja Þjóðverjar og Bretar að senda megi skip til að sækja Íslendinga í einni ferð. – Þetta er sagan af fólkinu sem lagði í sögulega hættuför og sigldi út á haf þar sem tundurdufl og kafbátar leyndust.", long_description: "", page_count: 320, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 88, book_author_id: nil},
  {source_id: 15931, pre_title: "", title: "Flogið aftur í tímann", post_title: "Suðvestur- og Suðurland þá og nú", slug: "flogid-aftur-i-timann-15931", description: ">>>Flogið aftur í tímann<<< sýnir borgina, bæi og þorp frá áttunda áratugnum og fram á daginn í dag. Flogið var um alla byggðakjarna á Suður- og Suðvesturlandi í sumar er leið. Staðir eru einnig sýndir frá líku sjónarhorni í fortíð og nútíð. Reynt er að sýna hverfi og byggðir í þéttbýli, þannig að sem  flestir geti séð húsin sín og bústaði. Stærri breytingar í nátturunni, m.a. í jöklum, eru einnig sýndar í glæsilegum myndum. Texti í bókinni er einnig á ensku.", long_description: "", page_count: 228, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 90, book_author_id: nil},
  {source_id: 15932, pre_title: "", title: "Bækur Þórarins Eldjárns", post_title: "", slug: "baekur-thorarins-eldjarns-15932", description: "Eldri bækur Þórarins Eldjárns eru nú loksins fáanlegar á ný og að þessu sinni sem hljóðbækur!\r\nStorytel gaf út þessa gimsteina bókmenntasögunnar á árinu, allt frá kvæðum fyrir börn til smásagnasafna og skáldsagna. Leyfðu þér að enduruppgötva þá við kertaljósið heima í stofu.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15933, pre_title: "", title: "Bækur Ólafs Hauks Símonarsonar", post_title: "", slug: "baekur-olafs-hauks-simonarsonar-15933", description: "Nokkrar eldri bóka Ólafs Hauks komu út hjá Storytel á árinu og eru því loks aðgengilegar á ný. Þarna er meðal annars hægt að hlusta á klassískar barna- og ungmennabækur eins og Meiri gauragang en líka þríleikinn um uppvaxtarsögu drengs í Vesturbænum sem hófst á Flugu á vegg. Taktu því rólega heima í stofu og enduruppgötvaðu þessar perlur.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15934, pre_title: "", title: "Eldri bækur Guðrúnar Evu Mínervudóttur", post_title: "", slug: "eldri-baekur-gudrunar-evu-minervudottur-15934", description: "Eldri bækur Guðrúnar Evu eru nú loksins fáanlegar á ný og að þessu sinni sem hljóðbækur!\r\nÁ árinu komu allflestar eldri bækur Guðrúnar Evu út hjá Storytel í yndislegum lestri færra leikara. Leyfðu þér að enduruppgötva þessar perlur við kertaljósið heima í stofu.\r\nUm er að ræða bækurnar:\r\n- Englaryk\r\n- Allt með kossi vekur\r\n- Yosoy\r\n- Sagan af sjóreknu píanóunum\r\n- Albúm\r\n- Á meðan hann horfir á þig ertu María mey", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15935, pre_title: "", title: "Sofðu", post_title: "", slug: "sofdu-15935", description: "Sofðu er röð leskafla sem byggja á vísindalega gagnreyndum aðferðum við að sleppa takinu á hugsunum og amstri dagsins, róa hugann og vinda ofan af spennunni í líkamanum, skrifuð af sálfræðingi sem unnið hefur á sviði svefnvandamála í 15 ár. Lestur Þorvaldar Davíðs veitir vellíðan og hvetur þig til að taka því sem að höndum ber af ró og æðruleysi.", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 62, book_author_id: nil},
  {source_id: 15936, pre_title: "", title: "Jói Kassi og horklessan", post_title: "", slug: "joi-kassi-og-horklessan-15936", description: "Jói Kassi lendir í slæmri horklessu sem hann þarf að losna við. Sagan segir frá hvernig hann þarf að fylgja reglum og fyrirmælum. Gamansöm og fræðandi saga og litabók um Covid 19.", long_description: "", page_count: 24, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 91, book_author_id: nil},
  {source_id: 15937, pre_title: "", title: "Glæpur við fæðingu", post_title: "", slug: "glaepur-vid-faedingu-15937", description: "Stórmerkileg saga uppistandarans og stjórnmálaskýrandans Trevor Noah sem ólst upp í skugga aðskilnaðarstefunnar í Suður-Afríku. Sjálf tilvist hans var glæpur, því samband móður hans og föður af ólíkum hörundslit var refsivert á þeim tíma. Trevor segir á heillandi hátt frá æsku sinni og unglingsárum í samfélagi sem enn er í sárum, fyrstu skrefunum í skemmtanabransanum og trúrækinni móður sem opnaði fyrir honum heiminn.", long_description: "", page_count: 368, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 41, book_author_id: nil},
  {source_id: 15938, pre_title: "", title: "Guð og menn", post_title: "", slug: "gud-og-menn-15938", description: "Hér ræðir giska venjulegur maður um ferðalag sitt með Guði - og hvernig það er að breyta lífi hans.\r\nEngin trúarganga er eins og enginn er nokkru sinni 100% trúaður eða fullkominn, gangan er stöðug. Trúin kemur ekki í neytendaumbúðum heldur er hún heimaframleidd, persónuleg og einstök. Einlægir pistlar sem rífa í.", long_description: "", page_count: 133, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 92, book_author_id: nil},
  {source_id: 15939, pre_title: "", title: "Lífið á vellinum", post_title: "", slug: "lifid-a-vellinum-15939", description: "Hvaða áhrif hafði amerísk varnarstöð á Keflavíkurflugvelli í hálfa öld og hvernig var sá kokteill ólíkra menningarheima? Hér er sjónum beint að hinu hversdagslega og því persónulega, að fólkinu sem þar bjó og við heyrum sögur þeirra.", long_description: "", page_count: 200, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 92, book_author_id: nil},
  {source_id: 15943, pre_title: "", title: "Sombína og dularfulla hvarfið", post_title: "", slug: "sombina-og-dularfulla-hvarfid-15943", description: "", long_description: "", page_count: 56, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15944, pre_title: "", title: "Sombína og sumarfríið við Myrkravatn", post_title: "", slug: "sombina-og-sumarfriid-vid-myrkravatn-15944", description: "", long_description: "", page_count: 60, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15946, pre_title: "", title: "Sumar í París", post_title: "", slug: "sumar-i-paris-15946", description: "", long_description: "", page_count: 430, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 57, book_author_id: nil},
  {source_id: 15947, pre_title: "", title: "Brúðkaup í desember", post_title: "", slug: "brudkaup-i-desember-15947", description: "", long_description: "", page_count: 416, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 57, book_author_id: nil},
  {source_id: 15949, pre_title: "", title: "Viltu vera vinur  minn?", post_title: "", slug: "viltu-vera-vinur-minn-15949", description: "", long_description: "", page_count: 32, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 56, book_author_id: nil},
  {source_id: 15950, pre_title: "", title: "Vinur minn, vindurinn", post_title: "", slug: "vinur-minn-vindurinn-15950", description: "", long_description: "", page_count: 40, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 56, book_author_id: nil},
  {source_id: 15951, pre_title: "", title: "Brennan á Flugumýri", post_title: "", slug: "brennan-a-flugumyri-15951", description: "„Hafið ykkur út strákar, eins og skot, reynið að komast út í gegnum litlustofu, forðist skálann eins og heitan eldinn,“ sagði Kormákur bryti þegar eldur fór að loga um hús á Flugumýri í október 1253. \r\nHér er fjallað um stórtíðindi á Sturlungaöld. Hvers vegna var saklaust fólk brennt inni og drepið? Jafnvel við brúðkaup? Svarið kemur í ljós í bókinni Brennan á Flugumýri.", long_description: "", page_count: 124, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 93, book_author_id: nil},
  {source_id: 15952, pre_title: "", title: "Ráf í Reykjavik", post_title: "", slug: "raf-i-reykjavik-15952", description: "Ljóðin eru tilbrigði við smásögu Ástu Sigurðardóttur Gatan í rigningu. Í aðalhlutverki er ráfandi götustelpa og í sólskini er hún glöð og þakklát fyrir lífið. Ljóðin eru óræð og lúmsk og reyna að koma á reglu í óreglu. „The femme fatale“ lifir góðu lífi í Reykjavík dagsins í dag og ráfar um götur í leit að festu.", long_description: "", page_count: 52, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 93, book_author_id: nil},
  {source_id: 15953, pre_title: "", title: "Kökur", post_title: "", slug: "kokur-15953", description: "Linda Ben hefur notið mikilla vinsælda fyrir ljúffengar uppskriftir sem hafa slegið í gegn á netinu og á samfélagsmiðlum. Kökur er fyrsta bók Lindu en hún hefur að geyma fjölda uppskrifta að dýrindis kökum, eftirréttum og bakkelsi fyrir öll tilefni. Þá má einnig finna vel varðveitt leyndarmál úr eldhúsi fjölskyldunnar og gagnleg ráð fyrir bakstur og veisluhöld.", long_description: "", page_count: 184, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 94, book_author_id: nil},
  {source_id: 15954, pre_title: "", title: "Skipulag", post_title: "", slug: "skipulag-15954", description: "Sólrún Diego tekur fyrir skilvirkar aðferðir við skipulag í frábærri bók fyrir alla sem vilja ráðstafa tímanum betur og einfalda verkefni. Í bókinni kemur hún víða við og fjallar um allt sem snýr að eigin skipulagi, fjölskyldunnar og heimilisins. Þá er veglegur hluti um veislur og viðburði og hvernig má skipuleggja allt frá litlum matarboðum yfir í brúðkaup.", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 94, book_author_id: nil},
  {source_id: 15956, pre_title: "", title: "Kortagögn og málefni kortasafna", post_title: "", slug: "kortagogn-og-malefni-kortasafna-15956", description: "", long_description: "", page_count: 356, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 95, book_author_id: nil},
  {source_id: 15957, pre_title: "", title: "Fjarkönnunargögn og skipulag landupplýsinga", post_title: "", slug: "fjarkonnunargogn-og-skipulag-landupplysinga-15957", description: "", long_description: "", page_count: 0, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 95, book_author_id: nil},
  {source_id: 15958, pre_title: "", title: "Ljóð og Ljóð", post_title: "", slug: "ljod-og-ljod-15958", description: ">>>Ljóð og Ljóð<<< er heildarsafn ljóða Sigurðar Guðmundssonar. Ljóðin birtust upphaflega í ýmsum ritum og spanna um hálfrar aldar tímabil, þau nýjustu nú frumbirt. Ljóð Sigurðar bera höfundi sínum skýrt vitni. Lesandinn er leiddur að hugmyndum og kenndum sem hann man ekki eftir að hafa kynnst fyrr. Ljóðin kveikja á perum og lokka lesandann með sér í andlegt fútt.", long_description: "", page_count: 152, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 96, book_author_id: nil},
  {source_id: 15959, pre_title: "", title: "Uppreisn Jóns Arasonar", post_title: "", slug: "uppreisn-jons-arasonar-15959", description: "Jón Arason hóf vopnaða uppreisn gegn Dönum 1548 með stuðningi Hamborgarmanna. Dr. Ásgeir Jónsson fer hér yfir ævintýralegt lífshlaup Jóns biskups og setur uppreisn hans í samhengi við átök Þjóðverja og Dana á þessum tíma.  Hver hefðu örlög Íslands orðið hefði Jón sigrað eða samið við konung? Vildi hann koma landinu undir Þýskalandskeisara. Hvað kostuðu siðaskiptin Ísland?", long_description: "", page_count: 144, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 27, book_author_id: nil},
  {source_id: 15960, pre_title: "", title: "Litli Garðurinn", post_title: "", slug: "litli-gardurinn-15960", description: ">>>Litli Garðurinn<<< er um atburð sem íslensk fjölskylda verður fyrir á Spáni. Hugmyndina að sögunni fékk höfundur úr viðtali við móður sem varð fyrir sömu reynslu og fjölskyldan. Atburðarásin leiðir lesandann milli tveggja tíma og heldur spennunni allt til enda. Litríkir karakterar glæða frásögnina kímni og dýpt. Bókin er fyrsta skáldsaga höfundar.", long_description: "", page_count: 342, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 97, book_author_id: nil},
  {source_id: 15962, pre_title: "", title: "Prumpusamloka", post_title: "", slug: "prumpusamloka-15962", description: "", long_description: "", page_count: 61, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15963, pre_title: "", title: "Geggjað ósanngjarnt!", post_title: "", slug: "geggjad-osanngjarnt-15963", description: "", long_description: "", page_count: 61, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 55, book_author_id: nil},
  {source_id: 15966, pre_title: "", title: "Hetjur norðurslóða", post_title: "Óður til Grænlenska sleðahundsins", slug: "hetjur-nordursloda-15966", description: "Sannkallað stórvirki þar sem Ragnar Axelsson leiðir okkur um hafísinn með magnaðri skrásetningu á sögu og lífsbaráttu Grænlendinga. Hetjur norðurslóða er ómótstæðileg ljósmyndaveisla, krydduð sögum grænlenskra veiðimanna. Gullfalleg bók sem jafnframt er óður til grænlenska sleðahundsins.", long_description: "", page_count: 388, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 98, book_author_id: nil},
  {source_id: 15967, pre_title: "", title: "Bertel Thorvaldsen", post_title: "", slug: "bertel-thorvaldsen-15967", description: ">>>Bertel Thorvaldsen<<< var á 19. öld viðurkenndur og dáður sem einn fremsti myndhöggvari Evrópu. Hér er rakin ævintýraleg saga listamannsins sem var af fátæku fólki en komst vegna hæfileika sinna til listnáms aðeins ellefu ára gamall. Móðir Bertels var jósk en faðir hans skagfirskur myndskeri. Að loknum glæstum námsferli í Kaupmannahöfn bjó Bertel í fjóra áratugi í Róm og tók þar þátt í litskrúðugu lista- og menningarlífi þeirra sem lengst náðu í vestrænum heimi þess tíma.", long_description: "", page_count: 400, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 53, book_author_id: nil},
  {source_id: 15968, pre_title: "", title: "Óðal óttans", post_title: "", slug: "odal-ottans-15968", description: "Skip strandar í foráttuveðri við hrjóstruga austurströnd Skotlands árið 1906. Rökstuddur grunur er uppi um að glæpur hafi verið framinn. Mörgum áratugum seinna, 1964, hefst önnur æsispennandi atburðarás. Þetta er saga um ógnir og ævintýr, skelfingu og hættur. Þrátt fyrir að sagan sé skrifuð fyrir ungmenni, er hún fyrir alla, sem vilja lesa spennandi sögur. \r\n   >>>Óðal óttans<<< er fyrsta bókin af fimm í nýrri röð ungmennabóka frá höfundi.", long_description: "", page_count: 182, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 99, book_author_id: nil},
  {source_id: 15969, pre_title: "", title: "Litli stormurinn sem gat ekki stormað", post_title: "", slug: "litli-stormurinn-sem-gat-ekki-stormad-15969", description: "Hugljúf, bráðskemmtileg og fallega myndskreytt saga eftir Laura Martin Merino, fjöllistakonu og Einar Thorberg Guðmundsson, tónlistarmann. Stormur litli frá Vindheimum og sonur sjálfs Vetur konungs, vill vera stór og öflugur eins og pabbi sinn. En honum er það lífsins ómögulegt. Hann ákveður því að leita að hlutverki sínu í tilverunni.", long_description: "", page_count: 30, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 29, book_author_id: nil},
  {source_id: 15970, pre_title: "", title: "Beckmann", post_title: "", slug: "beckmann-15970", description: "Dramatísk saga tréskurðarmeistarans Wilhelms Beckmanns sem flýði til Íslands undan Hitler 1935, settist hér að og vann að listsköpun sinni til dauðadags. Eftir hann eru listmunir í mörgum íslenskum kirkjum og allt lék í höndum hans. Of fáir kannast samt við nafn mannsins sem t.d. hannaði merki Hótels Borgar 1946 og notað er enn þann dag í dag.", long_description: "", page_count: 132, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 100, book_author_id: nil},
  {source_id: 15972, pre_title: "", title: "Prjónadagbókin mín", post_title: "", slug: "prjonadagbokin-min-15972", description: "Prjónadagbókin mín er persónuleg og falleg prjónaminningabók. Í bókina getur þú skráð þín prjónaverk á skemmtilegan hátt og búið til fallega og hagnýta skráningu á þeirri miklu vinnu og ást sem lögð er í hvert verk.", long_description: "", page_count: 124, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 101, book_author_id: nil},
  {source_id: 15973, pre_title: "", title: "Sjóaragrín, sögur og limrur", post_title: "", slug: "sjoaragrin-sogur-og-limrur-15973", description: "Hér hefur verið safnað saman \r\nbröndurum, limrum og léttum sögum, \r\nsem flestar snerta íslenska sjómenn.\r\nEfnið kemur úr ýmsum áttum og er\r\ngræskulaust grín um náungann.\r\nGuðbjartur Jónsson, frá Flateyri kemur við sögu og svo er Hafliða þáttur Magnússonar. \r\nÞetta er fjórði Vasapésinn, frá Flóka forlagi en þeir hafa notið mikilla vinsælda.", long_description: "", page_count: 80, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 102, book_author_id: nil},
  {source_id: 15974, pre_title: "", title: "Suðureyri athafnasaga", post_title: "", slug: "sudureyri-athafnasaga-15974", description: "Á Suðureyri í Tálknafirði ráku Norðmenn hvalveiðistöð á árunum 1893-1911 með tilheyrandi framkvæmdum. Pétur A. Ólafsson konsúll á Patreksfirði gerði út selveiðiskipið Kóp frá Suðureyri 1916-17. Aftur var starfrækt hvalveiðistöð á Suðureyri árin 1935-1939, og hún var þá var sú eina á landinu. Nú er Suðureyri í eyði, en starfsemin þar lagði grunninn að kauptúninu sem nú heitir Tálknafjörður.", long_description: "", page_count: 196, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 102, book_author_id: nil},
  {source_id: 15975, pre_title: "", title: "Smekkleysa 33 1/3", post_title: "", slug: "smekkleysa-33-1-3-15975", description: ">>>Smekkleysa 33 1/3<<<  er ríkulega myndskreytt rit gefið út í tilefni af 33 ára afmæli Smekkleysu. Margir þekktir listamenn eiga efni í ritinu sem inniheldur fjölmargar ljósmyndir, flestar eftir Björgu Sveinsdóttur. Hér er um að ræða fjölbreytt rit með spennandi efni, stuttum greinum, myndum, úrklippum og plakötum úr sögu eins litríkasta forlags landsins. Er bæði á íslensku og ensku.", long_description: "", page_count: 176, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 103, book_author_id: nil},
  {source_id: 15976, pre_title: "", title: "Einvígi allra tíma", post_title: "Spassky vs. Fischer í Reykjavík 1972", slug: "einvigi-allra-tima-15976", description: ">>>Einvígi allra tíma<<< er ný bók þar sem Guðmundur G. Þórarinsson - forseti Skáksambands Íslands á tíma einvígisins - fjallar um hinn magnaða aðdraganda einvígis og sögunum úr innsta hring atburða á meðan á einvíginu stóð. Höfundur gerir einnig upp hinn einstæða eftirleik áranna eftir einvígið þar sem Spassky flúði heimalandið og Fisher endaði á Íslandi - landflótta og eftirlýstur.", long_description: "", page_count: 308, minutes: nil, store_url: nil, sample_url: nil, audio_url: nil, publisher_id: 104, book_author_id: nil}
])
